/**
 * Feed Generation Service
 * Orchestrates personalized feed generation with SPEC.md algorithm
 */

import { BaseApplicationService, ApplicationServiceContext, ServiceResult } from './base.service';
import { Result } from '../domain/shared/base/Result';
import { FeedItem } from '../domain/feed/feed-item';
import { ProfileId } from '../domain/profile/value-objects/profile-id.value';
import {
  getFeedRepository,
  getProfileRepository,
  getSpaceRepository,
  getRitualRepository
} from '../infrastructure/repositories/factory';
import {
  IFeedRepository,
  IProfileRepository,
  ISpaceRepository,
  IRitualRepository
} from '../infrastructure/repositories/interfaces';

export interface FeedGenerationOptions {
  limit?: number;
  offset?: number;
  includeSpacePosts?: boolean;
  includeRSSPosts?: boolean;
  includeConnectionActivity?: boolean;
  includeEvents?: boolean;
  includeRituals?: boolean;
  sortBy?: 'algorithm' | 'recent' | 'engagement';
}

export interface FeedInsights {
  primaryContentType: string;
  engagementRate: number;
  averageScore: number;
  topSpaces: string[];
  suggestedAdjustments: string[];
}

export interface FeedContent {
  items: FeedItem[];
  insights: FeedInsights;
  nextOffset: number;
  hasMore: boolean;
}

export class FeedGenerationService extends BaseApplicationService {
  private feedRepo: IFeedRepository;
  private profileRepo: IProfileRepository;
  private spaceRepo: ISpaceRepository;
  private ritualRepo: IRitualRepository;

  constructor(context?: Partial<ApplicationServiceContext>) {
    super(context);
    this.feedRepo = getFeedRepository();
    this.profileRepo = getProfileRepository();
    this.spaceRepo = getSpaceRepository();
    this.ritualRepo = getRitualRepository();
  }

  /**
   * Generate personalized feed for a user
   * Implements SPEC.md algorithm with weighted factors
   */
  async generateFeed(
    userId: string,
    options: FeedGenerationOptions = {}
  ): Promise<Result<ServiceResult<FeedContent>>> {
    return this.execute(async () => {
      // Get user profile and context
      const userProfileId = ProfileId.create(userId).getValue();
      const profileResult = await this.profileRepo.findById(userProfileId.id);

      if (profileResult.isFailure) {
        return Result.fail<ServiceResult<FeedContent>>('User profile not found');
      }

      const profile = profileResult.getValue();
      const profileData = profile; // ProfileDTO doesn't need toData()

      // Get user's feed configuration
      const feedResult = await this.feedRepo.findByUserId(userProfileId);
      if (feedResult.isFailure) {
        return Result.fail<ServiceResult<FeedContent>>(feedResult.error!);
      }

      const feed = feedResult.getValue();

      // Get user's spaces and connections
      const userSpacesResult = await this.spaceRepo.findByMember(userProfileId.id);
      const userSpaces = userSpacesResult.isSuccess
        ? userSpacesResult.getValue().map((s: any) => s.id)
        : [];

      const userConnectionIds = profileData.connections;

      // Apply feed preferences from options
      if (options.includeSpacePosts !== undefined) {
        feed.updatePreferences({
          showSpacePosts: options.includeSpacePosts
        });
      }

      // Get feed content with algorithm
      const contentResult = await this.feedRepo.getFeedContent(
        userProfileId.id,
        userSpaces,
        userConnectionIds,
        options.limit || 20
      );

      if (contentResult.isFailure) {
        return Result.fail<ServiceResult<FeedContent>>(contentResult.error!);
      }

      let items = contentResult.getValue();

      // Apply additional filtering based on options (use domain logic)
      items = feed.applyContentFilters(items, {
        includeSpacePosts: options.includeSpacePosts,
        includeRSSPosts: options.includeRSSPosts,
        includeConnectionActivity: options.includeConnectionActivity,
        includeEvents: options.includeEvents,
        includeRituals: options.includeRituals
      });

      // Sort based on preference
      if (options.sortBy === 'recent') {
        items = items.sort((a: any, b: any) =>
          b.toData().createdAt.getTime() - a.toData().createdAt.getTime()
        );
      } else if (options.sortBy === 'engagement') {
        items = items.sort((a: any, b: any) =>
          b.toData().engagementCount - a.toData().engagementCount
        );
      }
      // Default is 'algorithm' which is already applied

      // Generate insights (use domain logic)
      const insights = feed.generateInsights(items);

      // Apply pagination
      const offset = options.offset || 0;
      const paginatedItems = items.slice(offset, offset + (options.limit || 20));

      const result: ServiceResult<FeedContent> = {
        data: {
          items: paginatedItems,
          insights,
          nextOffset: offset + paginatedItems.length,
          hasMore: offset + paginatedItems.length < items.length
        },
        metadata: {
          totalCount: items.length,
          pageSize: options.limit || 20,
          pageNumber: Math.floor(offset / (options.limit || 20)) + 1
        }
      };

      // Update feed last accessed
      await this.feedRepo.saveFeed(feed);

      return Result.ok<ServiceResult<FeedContent>>(result);
    }, 'FeedGeneration.generateFeed');
  }

  /**
   * Get trending content across campus
   */
  async getTrendingFeed(
    limit: number = 20
  ): Promise<Result<ServiceResult<FeedItem[]>>> {
    return this.execute(async () => {
      const trendingResult = await this.feedRepo.getTrendingContent(
        this.context.campusId,
        limit
      );

      if (trendingResult.isFailure) {
        return Result.fail<ServiceResult<FeedItem[]>>(trendingResult.error!);
      }

      const result: ServiceResult<FeedItem[]> = {
        data: trendingResult.getValue(),
        metadata: {
          totalCount: trendingResult.getValue().length
        }
      };

      return Result.ok<ServiceResult<FeedItem[]>>(result);
    }, 'FeedGeneration.getTrendingFeed');
  }

  /**
   * Get event-focused feed content
   */
  async getEventsFeed(
    limit: number = 20
  ): Promise<Result<ServiceResult<FeedItem[]>>> {
    return this.execute(async () => {
      const eventsResult = await this.feedRepo.getEventContent(
        this.context.campusId,
        limit
      );

      if (eventsResult.isFailure) {
        return Result.fail<ServiceResult<FeedItem[]>>(eventsResult.error!);
      }

      const result: ServiceResult<FeedItem[]> = {
        data: eventsResult.getValue(),
        metadata: {
          totalCount: eventsResult.getValue().length
        }
      };

      return Result.ok<ServiceResult<FeedItem[]>>(result);
    }, 'FeedGeneration.getEventsFeed');
  }

  /**
   * Get ritual-focused feed content
   */
  async getRitualsFeed(
    limit: number = 20
  ): Promise<Result<ServiceResult<FeedItem[]>>> {
    return this.execute(async () => {
      const ritualsResult = await this.feedRepo.getRitualContent(
        this.context.campusId,
        limit
      );

      if (ritualsResult.isFailure) {
        return Result.fail<ServiceResult<FeedItem[]>>(ritualsResult.error!);
      }

      const result: ServiceResult<FeedItem[]> = {
        data: ritualsResult.getValue(),
        metadata: {
          totalCount: ritualsResult.getValue().length
        }
      };

      return Result.ok<ServiceResult<FeedItem[]>>(result);
    }, 'FeedGeneration.getRitualsFeed');
  }

  /**
   * Record user interaction with feed item
   */
  async recordInteraction(
    userId: string,
    itemId: string,
    interactionType: 'view' | 'like' | 'comment' | 'share' | 'hide'
  ): Promise<Result<void>> {
    return this.execute(async () => {
      const userProfileId = ProfileId.create(userId).getValue();

      // Record the interaction
      const recordResult = await this.feedRepo.recordInteraction(
        userProfileId.id,
        itemId,
        interactionType,
        {
          timestamp: Date.now(),
          context: this.context
        }
      );

      if (recordResult.isFailure) {
        return Result.fail<void>(recordResult.error!);
      }

      // Update algorithm weights based on interaction (use domain logic)
      if (interactionType === 'like' || interactionType === 'comment') {
        const userProfileId = ProfileId.create(userId).getValue();
        const feedResult = await this.feedRepo.findByUserId(userProfileId);
        if (feedResult.isSuccess) {
          const feed = feedResult.getValue();
          feed.adjustWeightsFromFeedback('positive', 'engagement');
          await this.feedRepo.saveFeed(feed);
        }
      } else if (interactionType === 'hide') {
        const userProfileId = ProfileId.create(userId).getValue();
        const feedResult = await this.feedRepo.findByUserId(userProfileId);
        if (feedResult.isSuccess) {
          const feed = feedResult.getValue();
          feed.adjustWeightsFromFeedback('negative', 'hide');
          await this.feedRepo.saveFeed(feed);
        }
      }

      return Result.ok<void>();
    }, 'FeedGeneration.recordInteraction');
  }

  /**
   * Update user's feed preferences
   */
  async updateFeedPreferences(
    userId: string,
    preferences: {
      showSpacePosts?: boolean;
      showRSSPosts?: boolean;
      showConnectionActivity?: boolean;
      showEventPosts?: boolean;
      showRitualPosts?: boolean;
    }
  ): Promise<Result<void>> {
    return this.execute(async () => {
      const userProfileId = ProfileId.create(userId).getValue();

      // Get user's feed
      const feedResult = await this.feedRepo.findByUserId(userProfileId);
      if (feedResult.isFailure) {
        return Result.fail<void>(feedResult.error!);
      }

      const feed = feedResult.getValue();

      // Update preferences
      feed.updatePreferences(preferences);

      // Save updated feed
      const saveResult = await this.feedRepo.saveFeed(feed);
      if (saveResult.isFailure) {
        return Result.fail<void>(saveResult.error!);
      }

      return Result.ok<void>();
    }, 'FeedGeneration.updateFeedPreferences');
  }

  /**
   * Subscribe to real-time feed updates
   */
  subscribeToFeedUpdates(
    userId: string,
    callback: (items: FeedItem[]) => void
  ): () => void {
    const userProfileId = ProfileId.create(userId).getValue();
    return this.feedRepo.subscribeToFeed(userProfileId.id, callback);
  }

  // Business logic methods removed - now in EnhancedFeed aggregate:
  // - applyContentFilters() -> EnhancedFeed.applyContentFilters()
  // - generateFeedInsights() -> EnhancedFeed.generateInsights()
  // - generateAdjustmentSuggestions() -> EnhancedFeed.getSuggestedAdjustments()
  // - updateAlgorithmWeights() -> EnhancedFeed.adjustWeightsFromFeedback()
}

/**
 * Get Feed Query
 * Fetches the personalized feed for a user
 */

import { FeedItem } from '../../../domain/feed/feed-item';

export interface GetFeedQuery {
  userId: string;
  campusId: string;
  limit?: number;
  offset?: number;
  filters?: {
    spaceIds?: string[];
    types?: string[];
    sortBy?: 'recent' | 'popular' | 'relevant';
  };
}

export interface GetFeedQueryResult {
  items: FeedItem[];
  hasMore: boolean;
  nextOffset: number;
  totalCount?: number;
}

export class GetFeedQueryHandler {
  constructor(private readonly feedService?: any) {}

  async execute(query: GetFeedQuery): Promise<GetFeedQueryResult> {
    const constraints = this.buildFeedQuery(query);
    const limit = query.limit ?? 20;
    const offset = query.offset ?? 0;

    let rawItems: FeedItem[] = [];

    if (this.feedService && typeof this.feedService.fetch === 'function') {
      rawItems =
        (await this.feedService.fetch({
          constraints,
          limit: limit + offset,
          offset,
          filters: query.filters,
          userId: query.userId,
          campusId: query.campusId
        })) ?? [];
    }

    const sortedItems = this.applySorting([...rawItems], query.filters?.sortBy);
    const paginatedItems = sortedItems.slice(offset, offset + limit);
    const totalCount = sortedItems.length;
    const nextOffset = offset + paginatedItems.length;

    return {
      items: paginatedItems,
      hasMore: nextOffset < totalCount,
      nextOffset,
      totalCount
    };
  }

  // Helper method for building feed queries
  private buildFeedQuery(query: GetFeedQuery): any {
    const constraints: any[] = [];

    // Campus isolation
    if (query.campusId) {
      constraints.push(['campusId', '==', query.campusId]);
    }

    // Space filtering
    if (query.filters?.spaceIds?.length) {
      constraints.push(['spaceId', 'in', query.filters.spaceIds]);
    }

    // Type filtering
    if (query.filters?.types?.length) {
      constraints.push(['type', 'in', query.filters.types]);
    }

    return constraints;
  }

  // Helper method for sorting
  private applySorting(items: FeedItem[], sortBy?: string): FeedItem[] {
    switch (sortBy) {
      case 'popular':
        // Sort by engagement metrics (likes, comments, shares)
        return items.sort((a, b) => {
          const aScore = (a as any).engagementScore || 0;
          const bScore = (b as any).engagementScore || 0;
          return bScore - aScore;
        });
      case 'relevant':
        // Sort by relevance score
        return items.sort((a, b) => {
          const aScore = (a as any).relevanceScore || 0;
          const bScore = (b as any).relevanceScore || 0;
          return bScore - aScore;
        });
      case 'recent':
      default:
        return items.sort((a, b) => {
          const aTime = a.createdAt?.getTime() || 0;
          const bTime = b.createdAt?.getTime() || 0;
          return bTime - aTime;
        });
    }
  }
}

/**
 * Profile Onboarding Service
 * Orchestrates the complete user onboarding flow
 */

import { BaseApplicationService, ApplicationServiceContext, ServiceResult } from './base.service';
import { Result } from '../domain/shared/base/Result';
import { Profile } from '../domain/profile/aggregates/profile.aggregate';
import { UBEmail } from '../domain/profile/value-objects/ub-email.value';
import { ProfileHandle } from '../domain/profile/value-objects/profile-handle.value';
import { ProfileId } from '../domain/profile/value-objects/profile-id.value';
import {
  getProfileRepository,
  getSpaceRepository,
  getFeedRepository
} from '../infrastructure/repositories/factory';
import {
  IProfileRepository,
  ISpaceRepository,
  IFeedRepository
} from '../infrastructure/repositories/interfaces';

export interface OnboardingData {
  email: string;
  handle: string;
  firstName: string;
  lastName: string;
  bio?: string;
  major?: string;
  graduationYear?: number;
  dorm?: string;
  interests?: string[];
  profileImageUrl?: string;
}

export interface OnboardingResult {
  profile: Profile;
  suggestedSpaces: Array<{ id: string; name: string; memberCount: number }>;
  nextSteps: Array<{ action: string; description: string; priority: number }>;
}

export class ProfileOnboardingService extends BaseApplicationService {
  private profileRepo: IProfileRepository;
  private spaceRepo: ISpaceRepository;
  private feedRepo: IFeedRepository;

  constructor(context?: Partial<ApplicationServiceContext>) {
    super(context);
    this.profileRepo = getProfileRepository();
    this.spaceRepo = getSpaceRepository();
    this.feedRepo = getFeedRepository();
  }

  /**
   * Complete profile onboarding flow
   */
  async completeOnboarding(data: OnboardingData): Promise<Result<ServiceResult<OnboardingResult>>> {
    return this.execute(async () => {
      // Step 1: Validate email domain
      const emailValidation = await this.validateEmailDomain(data.email);
      if (emailValidation.isFailure) {
        return Result.fail<ServiceResult<OnboardingResult>>(emailValidation.error!);
      }

      // Step 2: Check handle availability
      const handleAvailable = await this.checkHandleAvailability(data.handle);
      if (handleAvailable.isFailure) {
        return Result.fail<ServiceResult<OnboardingResult>>(handleAvailable.error!);
      }

      // Step 3: Create profile
      const profileResult = await this.createProfile(data);
      if (profileResult.isFailure) {
        return Result.fail<ServiceResult<OnboardingResult>>(profileResult.error!);
      }

      const profile = profileResult.getValue();

      // Step 4: Initialize user feed
      const profileId = ProfileId.create(profile.id).getValue();
      await this.initializeFeed(profileId);

      // Step 5: Get space suggestions based on interests and major
      const suggestedSpaces = await this.getSuggestedSpaces(
        data.major,
        data.interests || []
      );

      // Step 6: Auto-join default spaces for new users
      await this.joinDefaultSpaces(profile);

      // Step 7: Generate next steps for user (using domain logic)
      const spacesForNextSteps = suggestedSpaces.getValue().slice(0, 5).map(space => ({
        id: space.id.id,
        name: space.name.name
      }));
      const domainNextSteps = profile.getOnboardingNextSteps(spacesForNextSteps);

      // Map domain next steps to expected format
      const nextSteps = domainNextSteps.map((step: any, index: number) => ({
        action: step.title || step.action,
        description: step.description,
        priority: index + 1
      }));

      const result: ServiceResult<OnboardingResult> = {
        data: {
          profile,
          suggestedSpaces: suggestedSpaces.getValue().slice(0, 5).map(space => ({
            id: space.id.id,
            name: space.name.name,
            memberCount: space.getMemberCount()
          })),
          nextSteps
        },
        warnings: profile.getOnboardingWarnings()
      };

      return Result.ok<ServiceResult<OnboardingResult>>(result);
    }, 'ProfileOnboarding.completeOnboarding');
  }

  /**
   * Update onboarding progress
   */
  async updateOnboardingProgress(
    profileId: string,
    step: string,
    completed: boolean
  ): Promise<Result<void>> {
    return this.execute(async () => {
      const profileIdVO = ProfileId.create(profileId).getValue();
      const profileResult = await this.profileRepo.findById(profileIdVO);

      if (profileResult.isFailure) {
        return Result.fail<void>('Profile not found');
      }

      const profile = profileResult.getValue();

      // Track onboarding progress (would be stored in metadata)
      console.log(`Onboarding progress for ${profileId}: ${step} = ${completed}`);

      // Check if onboarding is complete (using domain logic)
      const onboardingStatus = profile.getOnboardingStatus();
      if (onboardingStatus.isComplete && !profile.isOnboarded) {
        // Note: completeOnboarding expects AcademicInfo, need to access academicInfo properly
        const interests = profile.interests;
        const spaces = profile.spaces;

        if (interests.length === 0 || spaces.length === 0) {
          return Result.ok<void>();
        }

        const requiresAcademicInfo = profile.userType.isStudent();
        if (requiresAcademicInfo && !profile.academicInfo) {
          return Result.ok<void>();
        }

        const completeResult = profile.completeOnboarding(
          profile.academicInfo,
          interests,
          spaces
        );

        if (completeResult.isSuccess) {
          await this.profileRepo.save(profile);
        }
      }

      return Result.ok<void>();
    }, 'ProfileOnboarding.updateOnboardingProgress');
  }

  /**
   * Get onboarding status (delegates to domain logic)
   */
  async getOnboardingStatus(profileId: string): Promise<Result<{
    isComplete: boolean;
    completedSteps: string[];
    remainingSteps: string[];
    percentComplete: number;
  }>> {
    return this.execute(async () => {
      const profileIdVO = ProfileId.create(profileId).getValue();
      const profileResult = await this.profileRepo.findById(profileIdVO);

      if (profileResult.isFailure) {
        return Result.fail('Profile not found');
      }

      const profile = profileResult.getValue();

      // Use domain logic
      const domainStatus = profile.getOnboardingStatus();

      // Map to service response format
      const status = {
        isComplete: domainStatus.isComplete,
        completedSteps: domainStatus.completedSteps,
        remainingSteps: domainStatus.remainingSteps,
        percentComplete: domainStatus.completionPercentage
      };

      return Result.ok(status);
    }, 'ProfileOnboarding.getOnboardingStatus');
  }

  // Private helper methods

  private async validateEmailDomain(email: string): Promise<Result<void>> {
    const emailResult = UBEmail.create(email);
    if (emailResult.isFailure) {
      return Result.fail<void>('Invalid email format or domain');
    }
    return Result.ok<void>();
  }

  private async checkHandleAvailability(handle: string): Promise<Result<void>> {
    const handleResult = ProfileHandle.create(handle);
    if (handleResult.isFailure) {
      return Result.fail<void>(handleResult.error!);
    }

    const existingProfile = await this.profileRepo.findByHandle(handleResult.getValue().value);
    if (existingProfile.isSuccess) {
      return Result.fail<void>('Handle is already taken');
    }

    return Result.ok<void>();
  }

  private async createProfile(data: OnboardingData): Promise<Result<Profile>> {
    // Create value objects
    const emailResult = UBEmail.create(data.email);
    const handleResult = ProfileHandle.create(data.handle);

    if (emailResult.isFailure) {
      return Result.fail<Profile>(emailResult.error!);
    }

    if (handleResult.isFailure) {
      return Result.fail<Profile>(handleResult.error!);
    }

    // Generate profile ID
    const profileId = ProfileId.create(
      `profile_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
    ).getValue();

    // Create profile
    const profileResult = Profile.create({
      profileId: profileId,
      email: emailResult.getValue(),
      handle: handleResult.getValue(),
      personalInfo: {
        firstName: data.firstName,
        lastName: data.lastName,
        bio: data.bio,
        major: data.major,
        graduationYear: data.graduationYear,
        dorm: data.dorm
      }
    });

    if (profileResult.isFailure) {
      return Result.fail<Profile>(profileResult.error!);
    }

    const profile = profileResult.getValue();

    // Add interests
    if (data.interests) {
      for (const interest of data.interests) {
        profile.addInterest(interest);
      }
    }

    // Add profile image
    if (data.profileImageUrl) {
      await profile.addPhoto(data.profileImageUrl);
    }

    // Save profile
    const saveResult = await this.profileRepo.save(profile);
    if (saveResult.isFailure) {
      return Result.fail<Profile>(saveResult.error!);
    }

    return Result.ok<Profile>(profile);
  }

  private async initializeFeed(profileId: ProfileId): Promise<void> {
    const feed = await this.feedRepo.findByUserId(profileId);
    if (feed.isFailure) {
      console.error('Failed to initialize feed:', feed.error);
    }
  }

  private async getSuggestedSpaces(
    major?: string,
    interests: string[] = []
  ): Promise<Result<any[]>> {
    const suggestions = [];

    // Get spaces by major
    if (major) {
      const majorSpaces = await this.spaceRepo.findByType('study-group', this.context.campusId);
      if (majorSpaces.isSuccess) {
        suggestions.push(...majorSpaces.getValue());
      }
    }

    // Get trending spaces
    const trendingSpaces = await this.spaceRepo.findTrending(this.context.campusId, 10);
    if (trendingSpaces.isSuccess) {
      suggestions.push(...trendingSpaces.getValue());
    }

    // Deduplicate
    let uniqueSpaces = Array.from(
      new Map(suggestions.map(space => [(space as any).id?.id || (space as any).spaceId?.value, space])).values()
    );

    if (interests.length > 0) {
      const keywords = interests.map((interest) => interest.toLowerCase());
      uniqueSpaces = uniqueSpaces.sort((a, b) => {
        const aScore = this.scoreSpaceInterestMatch(a, keywords);
        const bScore = this.scoreSpaceInterestMatch(b, keywords);
        return bScore - aScore;
      });
    }

    return Result.ok(uniqueSpaces);
  }

  private scoreSpaceInterestMatch(space: unknown, keywords: string[]): number {
    const candidate = space as any;
    const name = (candidate?.name?.name ?? candidate?.name ?? '').toString().toLowerCase();
    const description = (candidate?.description?.value ?? candidate?.description ?? '').toString().toLowerCase();

    return keywords.reduce((score, keyword) => {
      let currentScore = score;
      if (name.includes(keyword)) {
        currentScore += 2;
      }
      if (description.includes(keyword)) {
        currentScore += 1;
      }
      return currentScore;
    }, 0);
  }

  private async joinDefaultSpaces(profile: Profile): Promise<void> {
    // Auto-join campus-wide default spaces
    const defaultSpaceNames = ['Welcome Space', 'New Students', 'Campus Updates'];

    for (const spaceName of defaultSpaceNames) {
      try {
        // This would be implemented when space joining is added
        console.log(`Auto-joining ${spaceName} for user ${profile.profileId.value}`);
      } catch (error) {
        console.error(`Failed to auto-join ${spaceName}:`, error);
      }
    }
  }

  // Business logic methods removed - now in Profile aggregate:
  // - generateNextSteps() -> Profile.getOnboardingNextSteps()
  // - generateOnboardingWarnings() -> Profile.getOnboardingWarnings()
  // - isOnboardingComplete() -> Profile.getOnboardingStatus()
}

/**
 * Ritual Participation Service
 * Orchestrates ritual participation, progress tracking, and rewards
 */

import { BaseApplicationService, ApplicationServiceContext, ServiceResult } from './base.service';
import { Result } from '../domain/shared/base/Result';
import { Ritual } from '../domain/rituals/aggregates/ritual.aggregate';
import { RitualId } from '../domain/rituals/value-objects/ritual-id.value';
import { Participation } from '../domain/rituals/entities/participation';
import { ProfileId } from '../domain/profile/value-objects/profile-id.value';
import { CampusId } from '../domain/profile/value-objects/campus-id.value';

// Temporary interface until Milestone entity is properly implemented
interface Milestone {
  id: string;
  title: string;
  name: string;
  description: string;
  targetValue: number;
  currentValue: number;
  isCompleted: boolean;
  completedAt?: Date;
  participantCompletions: any[];
  rewards: {
    type: 'points' | 'badge' | 'achievement';
    value: string;
    description: string;
  }[];
}
import {
  IRitualRepository,
  IProfileRepository,
  IFeedRepository
} from '../infrastructure/repositories/interfaces';

export interface RitualCreationData {
  name: string;
  description: string;
  ritualType: 'short' | 'anticipatory' | 'yearbook';  // FIXED: Use correct types from SPEC.md
  category: 'social' | 'academic' | 'wellness' | 'community';
  duration: string;  // "1 week", "2 weeks", "3 weeks", "variable"
  startDate: Date;
  endDate?: Date;
  goals: Array<{
    description: string;
    type: 'individual' | 'space' | 'campus' | 'stretch';
    targetValue: number;
  }>;
  requirements: Array<{
    action: string;
    target: number;
    validation: 'manual' | 'automatic' | 'peer';
  }>;
  rewards: Array<{
    type: 'badge' | 'feature_unlock' | 'special_access' | 'recognition' | 'points';
    name: string;
    description: string;
    value?: string | number;
  }>;
  settings?: {
    maxParticipants?: number;
