  };
}

export interface RitualProgress {
  ritual: Ritual;
  participation: Participation;
  completionPercentage: number;
  currentStreak: number;
  rank: number;
  nextMilestone?: Milestone;
  recentAchievements: Array<{
    name: string;
    earnedAt: Date;
    points: number;
  }>;
}

export interface LeaderboardEntry {
  profileId: string;
  displayName: string;
  totalPoints: number;
  rank: number;
  completedMilestones: number;
  streak: number;
}

export class RitualParticipationService extends BaseApplicationService {
  private ritualRepo: IRitualRepository;
  private profileRepo: IProfileRepository;
  private feedRepo: IFeedRepository;

  constructor(
    context?: Partial<ApplicationServiceContext>,
    ritualRepo?: IRitualRepository,
    profileRepo?: IProfileRepository,
    feedRepo?: IFeedRepository
  ) {
    super(context);
    // Mock repositories for now - would be injected in production
    this.ritualRepo = ritualRepo || {} as IRitualRepository;
    this.profileRepo = profileRepo || {} as IProfileRepository;
    this.feedRepo = feedRepo || {} as IFeedRepository;
  }

  /**
   * Create a new ritual campaign
   */
  async createRitual(
    creatorId: string,
    data: RitualCreationData
  ): Promise<Result<Ritual>> {
    return this.execute(async () => {
      // Validate creator
      const creatorProfileId = ProfileId.create(creatorId).getValue();
      const creatorResult = await this.profileRepo.findById(creatorProfileId);

      if (creatorResult.isFailure) {
        return Result.fail<Ritual>('Creator profile not found');
      }

      // Generate ritual ID
      const ritualId = RitualId.create(
        `ritual_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
      ).getValue();

      // Validate ritual type and duration
      if (data.ritualType === 'short' && data.duration !== '1 week') {
        return Result.fail<Ritual>('Short rituals must be 1 week duration');
      }
      if (data.ritualType === 'yearbook' && data.duration !== '3 weeks') {
        return Result.fail<Ritual>('Yearbook rituals must be 3 weeks duration');
      }

      // Create ritual using correct types from SPEC.md
      const ritualResult = Ritual.create({
        ritualId: ritualId,
        name: data.name,
        description: data.description,
        type: data.ritualType,  // FIXED: No mapping needed - use correct type directly
        category: data.category,
        duration: data.duration,
        campusId: CampusId.createUBBuffalo().getValue(),
        startDate: data.startDate,
        endDate: data.endDate,
        createdBy: creatorProfileId,
        targetAudience: 'all',
        visibility: data.settings?.isVisible ? 'public' : 'hidden',
        goals: data.goals.map((g, index) => ({
          id: `goal_${index}`,
          description: g.description,
          type: g.type,
          targetValue: g.targetValue,
          currentValue: 0,
          isCompleted: false
        })),
        requirements: data.requirements,
        rewards: data.rewards.map((r, index) => ({
          id: `reward_${index}`,
          type: r.type,
          name: r.name,
          description: r.description,
          value: r.value
        })),
        targetParticipation: data.settings?.maxParticipants,
        status: 'draft'
      });

      if (ritualResult.isFailure) {
        return Result.fail<Ritual>(ritualResult.error!);
      }

      const ritual = ritualResult.getValue();

      // Save ritual
      const saveResult = await this.ritualRepo.save(ritual);
      if (saveResult.isFailure) {
        return Result.fail<Ritual>(saveResult.error!);
      }

      // Creator automatically participates
      await this.joinRitual(creatorId, ritual.ritualId.value);

      return Result.ok<Ritual>(ritual);
    }, 'RitualParticipation.createRitual');
  }

  /**
   * Join a ritual
   */
  async joinRitual(
    userId: string,
    ritualId: string
  ): Promise<Result<ServiceResult<Participation>>> {
    return this.execute(async () => {
      const userProfileId = ProfileId.create(userId).getValue();
      const ritualIdVO = RitualId.create(ritualId).getValue();

      // Get ritual
      const ritualResult = await this.ritualRepo.findById(ritualIdVO);
      if (ritualResult.isFailure) {
        return Result.fail<ServiceResult<Participation>>('Ritual not found');
      }

      const ritual = ritualResult.getValue();

      // Check if already participating
      const existingParticipation = await this.ritualRepo.findParticipation(
        ritualIdVO,
        userProfileId
      );

      if (existingParticipation.isSuccess) {
        return Result.fail<ServiceResult<Participation>>('Already participating in this ritual');
      }

      // Check ritual constraints
      const joinResult = ritual.addParticipant(userProfileId);
      if (joinResult.isFailure) {
        return Result.fail<ServiceResult<Participation>>(joinResult.error!);
      }

      // Create participation
      const participationResult = Participation.create({
        ritualId: ritualIdVO,
        profileId: userProfileId
      });

      if (participationResult.isFailure) {
        return Result.fail<ServiceResult<Participation>>(participationResult.error!);
      }

      const participation = participationResult.getValue();

      // Save participation
      await this.ritualRepo.saveParticipation(participation);

      // Update ritual with new participant
      await this.ritualRepo.save(ritual);

      const result: ServiceResult<Participation> = {
        data: participation,
        warnings: ritual.getParticipationWarnings()  // Use domain logic
      };

      return Result.ok<ServiceResult<Participation>>(result);
    }, 'RitualParticipation.joinRitual');
  }

  /**
   * Record progress on a ritual milestone
   */
  async recordProgress(
    userId: string,
    ritualId: string,
    milestoneId: string,
    progress: number
  ): Promise<Result<RitualProgress>> {
    return this.execute(async () => {
      const userProfileId = ProfileId.create(userId).getValue();
      const ritualIdVO = RitualId.create(ritualId).getValue();

      // Get participation
      const participationResult = await this.ritualRepo.findParticipation(
        ritualIdVO,
        userProfileId
      );

      if (participationResult.isFailure) {
        return Result.fail<RitualProgress>('Not participating in this ritual');
      }

      const participation = participationResult.getValue();

      // Get ritual
      const ritualResult = await this.ritualRepo.findById(ritualIdVO);
      if (ritualResult.isFailure) {
        return Result.fail<RitualProgress>('Ritual not found');
      }

      const ritual = ritualResult.getValue();

      // Update milestone progress
      participation.updateMilestoneProgress(milestoneId, progress);

      // Check if milestone completed
      const milestone = ritual.toData().milestones.find((m: any) => m.id === milestoneId);
      if (milestone && progress >= milestone.targetValue) {
        // Complete milestone
        participation.completeMilestone(milestoneId);

        // Award points (use domain logic)
        const points = ritual.calculateGoalPoints(milestoneId);
        participation.addPoints(points);

        // Add achievement
        participation.addAchievement(`Completed: ${milestone.name}`);

        // Note: updateMilestoneProgress method not available on Ritual aggregate
        // Milestone progress is tracked through participation records
      }

      // Update streak
      participation.updateStreak(participation.streakCount + 1);

      // Save participation
      await this.ritualRepo.saveParticipation(participation);

      // Save ritual if modified
      await this.ritualRepo.save(ritual);

      // Get current progress
      const progressData = await this.calculateRitualProgress(
        ritual,
        participation
      );

      return Result.ok<RitualProgress>(progressData);
    }, 'RitualParticipation.recordProgress');
  }

  /**
   * Get user's ritual progress
   */
  async getRitualProgress(
    userId: string,
    ritualId: string
  ): Promise<Result<RitualProgress>> {
    return this.execute(async () => {
      const userProfileId = ProfileId.create(userId).getValue();
      const ritualIdVO = RitualId.create(ritualId).getValue();

      // Get participation
      const participationResult = await this.ritualRepo.findParticipation(
        ritualIdVO,
        userProfileId
      );

      if (participationResult.isFailure) {
        return Result.fail<RitualProgress>('Not participating in this ritual');
      }

      const participation = participationResult.getValue();

      // Get ritual
      const ritualResult = await this.ritualRepo.findById(ritualIdVO);
      if (ritualResult.isFailure) {
        return Result.fail<RitualProgress>('Ritual not found');
      }

      const ritual = ritualResult.getValue();

      const progress = await this.calculateRitualProgress(ritual, participation);

      return Result.ok<RitualProgress>(progress);
    }, 'RitualParticipation.getRitualProgress');
  }

  /**
   * Get ritual leaderboard
   */
  async getLeaderboard(
    ritualId: string,
    limit: number = 20
  ): Promise<Result<ServiceResult<LeaderboardEntry[]>>> {
    return this.execute(async () => {
      const ritualIdVO = RitualId.create(ritualId).getValue();

      // Get ritual
      const ritualResult = await this.ritualRepo.findById(ritualIdVO);
      if (ritualResult.isFailure) {
        return Result.fail<ServiceResult<LeaderboardEntry[]>>('Ritual not found');
      }

      // Get leaderboard participations
      const leaderboardResult = await this.ritualRepo.findLeaderboard(
        ritualIdVO,
        limit
      );

      if (leaderboardResult.isFailure) {
        return Result.fail<ServiceResult<LeaderboardEntry[]>>(leaderboardResult.error!);
      }

      const participations = leaderboardResult.getValue();

      // Build leaderboard entries
      const entries: LeaderboardEntry[] = [];

      for (let i = 0; i < participations.length; i++) {
        const participation = participations[i];
        const participationData = participation.toData();

        // Get profile for display name
        const profileResult = await this.profileRepo.findById(participationData.profileId);
        const profile = profileResult.getValue();
        const displayName = profileResult.isSuccess
          ? `${profile.personalInfo?.firstName || ''} ${profile.personalInfo?.lastName || ''}`.trim() || 'Anonymous'
          : 'Anonymous';

        entries.push({
          profileId: participationData.profileId.id,
          displayName,
          totalPoints: participationData.totalPoints,
          rank: i + 1,
          completedMilestones: participationData.completedMilestones.length,
          streak: participationData.streak.currentDays
        });
      }

      const result: ServiceResult<LeaderboardEntry[]> = {
        data: entries,
        metadata: {
          totalCount: entries.length,
          hasMore: entries.length === limit
        }
      };

      return Result.ok<ServiceResult<LeaderboardEntry[]>>(result);
    }, 'RitualParticipation.getLeaderboard');
  }

  /**
   * Get user's active rituals
   */
  async getUserRituals(
    userId: string
  ): Promise<Result<ServiceResult<Ritual[]>>> {
    return this.execute(async () => {
      const userProfileId = ProfileId.create(userId).getValue();

      const ritualsResult = await this.ritualRepo.findByParticipant(userProfileId);

      if (ritualsResult.isFailure) {
        return Result.fail<ServiceResult<Ritual[]>>(ritualsResult.error!);
      }

      const rituals = ritualsResult.getValue();

      // Filter to active rituals
      const activeRituals = rituals.filter((r: any) => r.toData().status === 'active');

      const result: ServiceResult<Ritual[]> = {
        data: activeRituals,
        metadata: {
          totalCount: activeRituals.length
        }
      };

      return Result.ok<ServiceResult<Ritual[]>>(result);
    }, 'RitualParticipation.getUserRituals');
  }

  /**
   * Get available rituals to join
   */
  async getAvailableRituals(): Promise<Result<ServiceResult<Ritual[]>>> {
    return this.execute(async () => {
      const activeResult = await this.ritualRepo.findActive(this.context.campusId);

      if (activeResult.isFailure) {
        return Result.fail<ServiceResult<Ritual[]>>(activeResult.error!);
      }

      const rituals = activeResult.getValue()
        .filter((r: any) => r.toData().settings.isVisible);

      const result: ServiceResult<Ritual[]> = {
        data: rituals,
        metadata: {
          totalCount: rituals.length
        }
      };

      return Result.ok<ServiceResult<Ritual[]>>(result);
    }, 'RitualParticipation.getAvailableRituals');
  }

  /**
   * Subscribe to ritual updates
   */
  subscribeToRitual(
    ritualId: string,
    callback: (ritual: Ritual | null) => void
  ): () => void {
    const ritualIdVO = RitualId.create(ritualId).getValue();
    return this.ritualRepo.subscribeToRitual(ritualIdVO, callback);
  }

  /**
   * Subscribe to active rituals feed
   */
  subscribeToActiveRituals(
    callback: (rituals: Ritual[]) => void
  ): () => void {
    return this.ritualRepo.subscribeToActiveRituals(this.context.campusId, callback);
  }

  // Private helper methods

  private async calculateRitualProgress(
    ritual: Ritual,
    participation: Participation
  ): Promise<RitualProgress> {
    const ritualData = ritual.toData();
    const participationData = participation.toData();

    // Calculate completion percentage
    const totalMilestones = ritualData.milestones.length;
    const completedMilestones = participationData.completedMilestones.length;
    const completionPercentage = totalMilestones > 0
      ? Math.round((completedMilestones / totalMilestones) * 100)
      : 0;

    // Find next milestone
    const nextMilestone = ritualData.milestones.find(
      (m: any) => !participationData.completedMilestones.includes(m.id)
    );

    // Get rank (would query from leaderboard in production)
    const leaderboard = await this.ritualRepo.findLeaderboard(ritual.id, 100);
    const rank = leaderboard.isSuccess
      ? leaderboard.getValue().findIndex((p: any) => p.profileId.id === participationData.profileId.id) + 1
      : 0;

    return {
      ritual,
      participation,
      completionPercentage,
      currentStreak: participationData.streak,
      rank,
      nextMilestone,
      recentAchievements: participationData.achievements.slice(-3)
    };
  }

  // Business logic methods removed - now in Ritual aggregate:
  // - calculateMilestonePoints() -> Ritual.calculateGoalPoints()
  // - generateParticipationWarnings() -> Ritual.getParticipationWarnings()
  // - mapRitualType() -> REMOVED (type bug fixed - using correct types directly)
}

/**
 * Search Query
 * Unified search across the platform
 */

export enum SearchType {
  ALL = 'all',
  USERS = 'users',
  SPACES = 'spaces',
  POSTS = 'posts',
  TOOLS = 'tools',
  EVENTS = 'events'
}

export interface SearchResultItem {
  id: string;
  type: SearchType;
  title: string;
  description?: string;
  imageUrl?: string;
  url: string;
  metadata?: {
    [key: string]: any;
  };
  relevanceScore?: number;
  createdAt?: Date;
}

export interface SearchQuery {
  query: string;
  type?: SearchType;
  campusId: string;
  filters?: {
    userId?: string;
    spaceIds?: string[];
    tags?: string[];
    dateRange?: {
      start: Date;
      end: Date;
    };
  };
  limit?: number;
  offset?: number;
}

export interface SearchQueryResult {
  items: SearchResultItem[];
  totalCount: number;
  facets?: {
    types: { [key: string]: number };
    tags?: { [key: string]: number };
  };
  suggestions?: string[];
  hasMore: boolean;
}

export class SearchQueryHandler {
  constructor(private readonly searchService?: any) {}

  async execute(query: SearchQuery): Promise<SearchQueryResult> {
    // Implementation would integrate with search service
    // Minimal usage to satisfy linter and provide basic structure
    const { terms } = this.parseSearchQuery(query.query);
    const suggestions = this.generateSuggestions(query.query, []);
    const normalizedTermSuggestions = terms
      .map(term => term.trim().toLowerCase())
      .filter(term => term.length > 0);
    const combinedSuggestions = Array.from(
      new Set([...suggestions, ...normalizedTermSuggestions])
    );

    return {
      items: [],
      totalCount: 0,
      facets: {
        types: {}
      },
      suggestions: combinedSuggestions,
      hasMore: false,
    };
  }

  // Helper method to parse search query
  private parseSearchQuery(query: string): {
    terms: string[];
    filters: { [key: string]: string };
  } {
    const terms: string[] = [];
    const filters: { [key: string]: string } = {};

    // Simple parsing logic - would be more sophisticated in production
    const parts = query.split(' ');
    for (const part of parts) {
      if (part.includes(':')) {
        const [key, value] = part.split(':');
        filters[key] = value;
      } else {
        terms.push(part);
      }
    }

    return { terms, filters };
  }

  // Helper method to rank results
  private rankResults(items: SearchResultItem[], query: string): SearchResultItem[] {
    const queryLower = query.toLowerCase();

    return items.map(item => {
      let score = 0;

      // Title match
      if (item.title.toLowerCase().includes(queryLower)) {
        score += 10;
      }

      // Description match
      if (item.description?.toLowerCase().includes(queryLower)) {
        score += 5;
      }

      // Exact match bonus
      if (item.title.toLowerCase() === queryLower) {
        score += 20;
      }

      return { ...item, relevanceScore: score };
    }).sort((a, b) => (b.relevanceScore || 0) - (a.relevanceScore || 0));
  }

  // Helper method to generate search suggestions
  private generateSuggestions(query: string, results: SearchResultItem[]): string[] {
    const suggestions = new Set<string>();

    // Add variations of the query
    if (query.length > 3) {
      suggestions.add(query + 's'); // Plural
      suggestions.add(query.slice(0, -1)); // Singular
    }

    // Add common terms from results
    results.slice(0, 5).forEach(item => {
      const words = item.title.split(' ');
      words.forEach(word => {
        if (word.length > 3 && word.toLowerCase() !== query.toLowerCase()) {
          suggestions.add(word.toLowerCase());
        }
      });
    });

    return Array.from(suggestions).slice(0, 5);
  }
}

