/**
 * FeedItem Entity
 * Represents an item in a user's feed
 */

import { Entity } from '../shared/base/Entity.base';
import { Result } from '../shared/base/Result';
import { FeedItemId } from './value-objects/feed-item-id.value';
import { ProfileId } from '../profile/value-objects/profile-id.value';
import { SpaceId } from '../spaces/value-objects/space-id.value';

export interface FeedItemContent {
  title?: string;
  text: string;
  mediaUrls: string[];
  authorId: string;
  authorName: string;
  authorPhoto?: string;
}

export interface FeedItemSource {
  type: 'space' | 'ritual' | 'event' | 'announcement' | 'user';
  spaceId?: SpaceId;
  ritualId?: string;
  eventId?: string;
}

export interface FeedItemInteraction {
  type: 'like' | 'comment' | 'share' | 'view';
  userId: ProfileId;
  timestamp: Date;
}

interface FeedItemProps {
  itemId: FeedItemId;
  content: FeedItemContent;
  source: FeedItemSource;
  relevanceScore: number;
  interactions: FeedItemInteraction[];
  createdAt: Date;
  isVisible: boolean;
  isTrending: boolean;
  isPinned: boolean;
  expiresAt?: Date;
}

export class FeedItem extends Entity<FeedItemProps> {
  get itemId(): FeedItemId {
    return this.props.itemId;
  }

  get content(): FeedItemContent {
    return this.props.content;
  }

  get source(): FeedItemSource {
    return this.props.source;
  }

  get relevanceScore(): number {
    return this.props.relevanceScore;
  }

  get interactions(): FeedItemInteraction[] {
    return this.props.interactions;
  }

  get isVisible(): boolean {
    return this.props.isVisible;
  }

  get isTrending(): boolean {
    return this.props.isTrending;
  }

  get isPinned(): boolean {
    return this.props.isPinned;
  }

  get createdAt(): Date {
    return this.props.createdAt;
  }

  get expiresAt(): Date | undefined {
    return this.props.expiresAt;
  }

  private constructor(props: FeedItemProps, id?: string) {
    super(props, id || `feeditem_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`);
  }

  public static create(props: {
    itemId: FeedItemId;
    content: FeedItemContent;
    source: FeedItemSource;
    relevanceScore?: number;
    isVisible?: boolean;
    isTrending?: boolean;
    isPinned?: boolean;
    expiresAt?: Date;
    createdAt?: Date;
  }, id?: string): Result<FeedItem> {
    if (!props.content.text || props.content.text.trim().length === 0) {
      return Result.fail<FeedItem>('Feed item content text is required');
    }

    const itemProps: FeedItemProps = {
      itemId: props.itemId,
      content: props.content,
      source: props.source,
      relevanceScore: props.relevanceScore || 1.0,
      interactions: [],
      createdAt: props.createdAt || new Date(),
      isVisible: props.isVisible !== false,
      isTrending: props.isTrending || false,
      isPinned: props.isPinned || false,
      expiresAt: props.expiresAt
    };

    return Result.ok<FeedItem>(new FeedItem(itemProps, id));
  }

  public addInteraction(interaction: FeedItemInteraction): void {
    // Avoid duplicate interactions
    const exists = this.props.interactions.some(
      i => i.type === interaction.type &&
           i.userId.value === interaction.userId.value
    );

    if (!exists) {
      this.props.interactions.push(interaction);
      this.updateRelevanceScore();
    }
  }

  public removeInteraction(userId: ProfileId, type: string): void {
    this.props.interactions = this.props.interactions.filter(
      i => !(i.userId.value === userId.value && i.type === type)
    );
    this.updateRelevanceScore();
  }

  public setVisibility(isVisible: boolean): void {
    this.props.isVisible = isVisible;
  }

  public setTrending(isTrending: boolean): void {
    this.props.isTrending = isTrending;
  }

  public setPinned(isPinned: boolean): void {
    this.props.isPinned = isPinned;
  }

  public isExpired(): boolean {
    if (!this.props.expiresAt) return false;
    return new Date() > this.props.expiresAt;
  }

  private updateRelevanceScore(): void {
    // Simple relevance scoring based on interactions
    const likes = this.props.interactions.filter(i => i.type === 'like').length;
    const comments = this.props.interactions.filter(i => i.type === 'comment').length;
    const shares = this.props.interactions.filter(i => i.type === 'share').length;

    this.props.relevanceScore = 1.0 + (likes * 0.1) + (comments * 0.3) + (shares * 0.5);

    // Boost for trending items
    if (this.props.isTrending) {
      this.props.relevanceScore *= 1.5;
    }

    // Decay based on age
    const ageInHours = (Date.now() - this.props.createdAt.getTime()) / (1000 * 60 * 60);
    this.props.relevanceScore *= Math.exp(-ageInHours / 24); // Exponential decay over 24 hours
  }

  public toData(): any {
    return {
      id: this.id,
      itemId: this.props.itemId.value,
      content: this.props.content,
      source: this.props.source,
      relevanceScore: this.props.relevanceScore,
      interactions: this.props.interactions.map(i => ({
        type: i.type,
        userId: i.userId.value,
        timestamp: i.timestamp
      })),
      engagementCount: this.props.interactions.length, // Calculated from interactions
      createdAt: this.props.createdAt,
      isVisible: this.props.isVisible,
      isTrending: this.props.isTrending,
      isPinned: this.props.isPinned,
      expiresAt: this.props.expiresAt
    };
  }
}

/**
 * HIVE Feed Algorithm Service
 *
 * Implements behavioral psychology-based content scoring according to SPEC.md:394-435
 * Score = (AnxietyRelief  0.4) + (SocialProof  0.3) + (InsiderAccess  0.3)
 *
 * Key Principles:
 * - Variable ratio reinforcement (slot machine dynamic)
 * - Investment escalation (view  react  comment  repost  requote)
 * - Social proof signals ("3 friends reacted")
 * - FOMO generation (limited time, exclusive access)
 */

import type { Profile } from '../../profile/aggregates/profile.aggregate'
import type { Space } from '../../spaces/aggregates/space.aggregate'
import { SpaceCategoryEnum } from '../../spaces/value-objects/space-category.value'

// Use Profile as User (they're the same in HIVE)
type User = Profile

// Temporary types until they're added to core package
export interface FeedPost {
  id: string
  content: string
  authorId: string
  authorRole?: 'leader' | 'moderator' | 'member'
  spaceId: string
  timestamp: string
  expiresAt?: string
  reactions?: { friendsCount?: number }
  engagement?: { hourlyRate?: number }
}

interface Connection {
  id: string
  userId: string
  connectedUserId: string
  status: 'pending' | 'accepted' | 'blocked'
  createdAt: Date
}

export interface FeedAlgorithmConfig {
  anxietyReliefWeight: number // 0.4 per SPEC
  socialProofWeight: number   // 0.3 per SPEC
  insiderAccessWeight: number // 0.3 per SPEC
  campusId: string           // 'ub-buffalo' per SPEC
}

export interface ScoredPost extends FeedPost {
  algorithmScore: number
  anxietyReliefScore: number
  socialProofScore: number
  insiderAccessScore: number
  scoreBreakdown: {
    type: 'anxiety_relief' | 'social_proof' | 'insider_access' | 'mixed'
    primaryFactor: string
    confidence: number // 0-1
  }
}

export interface AnxietyReliefSignals {
  // Immediate student anxiety keywords
  studyStress: boolean    // "midterm", "finals", "stressed", "cramming"
  loneliness: boolean     // "lonely", "hang out", "anyone want to"
  fomo: boolean          // "tonight", "missing out", "what's happening"
  academicHelp: boolean  // "help with", "study group", "tutoring"
  foodInsecurity: boolean // "dining hall", "free food", "cheap eats"
  socialAnxiety: boolean // "nervous", "first time", "don't know anyone"
}

export interface SocialProofSignals {
  friendsEngaged: number        // Friends who reacted/commented
  connectionsInSpace: number    // Connections already in this space
  campusVelocity: number       // Engagement rate in last 2 hours
  mutualConnections: number    // Shared connections with author
  dormProximity: boolean       // Same dorm/floor as user
  majorRelevance: boolean      // Same major as user
}

export interface InsiderAccessSignals {
  spaceMemberCount: number     // Smaller = more exclusive
  requiresInvite: boolean      // Invite-only space
  newMemberRestricted: boolean // <7 days can't post
  leaderOnly: boolean         // Only leaders can post this type
  timeExclusive: boolean      // Limited time event/content
  greekExclusive: boolean     // Greek life exclusive content
}

export class FeedAlgorithmService {
  private config: FeedAlgorithmConfig

  constructor(config: FeedAlgorithmConfig) {
    this.config = config
  }

  /**
   * Score posts for feed ranking according to behavioral psychology
   */
  scorePost(
    post: FeedPost,
    user: User,
    space: Space,
    userConnections: Connection[]
  ): ScoredPost {
    const anxietyReliefScore = this.calculateAnxietyRelief(post, user, space)
    const socialProofScore = this.calculateSocialProof(post, user, userConnections)
    const insiderAccessScore = this.calculateInsiderAccess(post, space, user)

    const algorithmScore =
      (anxietyReliefScore * this.config.anxietyReliefWeight) +
      (socialProofScore * this.config.socialProofWeight) +
      (insiderAccessScore * this.config.insiderAccessWeight)

    const scoreBreakdown = this.determineScoreType(
      anxietyReliefScore,
      socialProofScore,
      insiderAccessScore
    )

    return {
      ...post,
      algorithmScore,
      anxietyReliefScore,
      socialProofScore,
      insiderAccessScore,
      scoreBreakdown
    }
  }

  /**
   * Calculate anxiety relief score (0-100)
   * Higher scores for content that solves immediate student anxieties
   */
  private calculateAnxietyRelief(post: FeedPost, user: User, space: Space): number {
    const signals = this.extractAnxietySignals(post.content)
    let score = 0

    // Study stress relief - highest priority during academic periods
    if (signals.studyStress) {
      score += 40
      // Boost during finals/midterm periods (would come from calendar context)
      score += 20
    }

    // Loneliness relief - critical for first-year students
    if (signals.loneliness) {
      score += 35
      // Note: Profile doesn't have nested profile property, access graduationYear directly
      if ((user as any).academicInfo?.graduationYear && this.isFirstYear((user as any).academicInfo.graduationYear)) {
        score += 15
      }
    }

    // FOMO relief - time-sensitive content
    if (signals.fomo) {
      score += 30
      // Boost for events happening soon
      if (post.timestamp && this.isHappeningSoon(post.timestamp)) {
        score += 20
      }
    }

    // Academic help - major-specific boost
    if (signals.academicHelp) {
      score += 25
      if (space.category.value === SpaceCategoryEnum.ACADEMIC && space.name.toString().includes(user.personalInfo?.major || '')) {
        score += 25
      }
    }

    // Food insecurity relief
    if (signals.foodInsecurity) {
      score += 20
      // Boost during meal times
      if (this.isMealTime()) {
        score += 15
      }
    }

    // Social anxiety relief
    if (signals.socialAnxiety) {
      score += 25
      if (space.category.value === SpaceCategoryEnum.SOCIAL) {
        score += 20 // Social spaces feel safer for social anxiety
      }
    }

    return Math.min(score, 100)
  }

  /**
   * Calculate social proof score (0-100)
   * Higher scores when friends/connections are already engaged
   */
  private calculateSocialProof(
    post: FeedPost,
    user: User,
    userConnections: Connection[]
  ): number {
    const signals = this.extractSocialProofSignals(post, user, userConnections)
    let score = 0

    // Friends already engaged - strongest signal
    if (signals.friendsEngaged > 0) {
      score += 30 + (signals.friendsEngaged * 15) // Cap at ~75
    }

    // Connections in space - "where your friends are"
    if (signals.connectionsInSpace > 0) {
      score += 20 + (signals.connectionsInSpace * 5) // Cap at ~45
    }

    // Campus velocity - trending content
    if (signals.campusVelocity > 10) { // 10+ engagements in 2h
      score += 25
    }

    // Mutual connections with author
    if (signals.mutualConnections > 0) {
      score += 15 + (signals.mutualConnections * 3)
    }

    // Dorm proximity - "your floor"
    if (signals.dormProximity) {
      score += 20
    }

    // Major relevance
    if (signals.majorRelevance) {
      score += 15
    }

    return Math.min(score, 100)
  }

  /**
   * Calculate insider access score (0-100)
   * Higher scores for exclusive/hard-to-find communities
   */
  private calculateInsiderAccess(post: FeedPost, space: Space, user: User): number {
    const signals = this.extractInsiderAccessSignals(post, space, user)
    let score = 0

    // Smaller communities feel more exclusive
    if (signals.spaceMemberCount < 20) {
      score += 40
    } else if (signals.spaceMemberCount < 50) {
      score += 25
    } else if (signals.spaceMemberCount < 100) {
      score += 15
    }

    // Invite-only spaces
    if (signals.requiresInvite) {
      score += 35
    }

    // New member restrictions - "you're in the inner circle"
    if (signals.newMemberRestricted) {
      score += 25
    }

    // Leader-only content
    if (signals.leaderOnly) {
      score += 20
    }

    // Time-exclusive content
    if (signals.timeExclusive) {
      score += 30
    }

    // Greek exclusive
    if (signals.greekExclusive) {
      score += 25
    }

    return Math.min(score, 100)
  }

  /**
   * Extract anxiety relief signals from post content
   */
  private extractAnxietySignals(content: string): AnxietyReliefSignals {
    const lowerContent = content.toLowerCase()

    return {
      studyStress: /\b(midterm|finals?|stressed|cramming|exam|quiz|studying|library|study\s+group)\b/.test(lowerContent),
      loneliness: /\b(lonely|hang\s+out|anyone\s+want|join\s+me|looking\s+for\s+friends)\b/.test(lowerContent),
      fomo: /\b(tonight|missing\s+out|what's\s+happening|going\s+on|events?\s+today)\b/.test(lowerContent),
      academicHelp: /\b(help\s+with|study\s+group|tutoring|homework|assignment)\b/.test(lowerContent),
      foodInsecurity: /\b(dining\s+hall|free\s+food|cheap\s+eats|hungry|meal\s+plan)\b/.test(lowerContent),
      socialAnxiety: /\b(nervous|first\s+time|don't\s+know\s+anyone|shy|anxious)\b/.test(lowerContent)
    }
  }

  /**
   * Extract social proof signals
   */
  private extractSocialProofSignals(
    post: FeedPost,
    user: User,
    userConnections: Connection[]
  ): SocialProofSignals {
    void user;
    void userConnections;
    // This would integrate with actual user data
    // For now, return mock implementation
    return {
      friendsEngaged: post.reactions?.friendsCount || 0,
      connectionsInSpace: 0, // Would query actual connections in space
      campusVelocity: post.engagement?.hourlyRate || 0,
      mutualConnections: 0, // Would calculate mutual connections with author
      dormProximity: false, // Would check if same dorm
      majorRelevance: false // Would check if same major
    }
  }

  /**
   * Extract insider access signals
   */
  private extractInsiderAccessSignals(
    post: FeedPost,
    space: Space,
    user: User
  ): InsiderAccessSignals {
    void user;
    return {
      spaceMemberCount: space.memberCount || 0,
      requiresInvite: !space.isPublic, // Use isPublic as proxy for invite requirement
      newMemberRestricted: space.settings?.requireApproval || false,
      leaderOnly: post.authorRole === 'leader',
      timeExclusive: post.expiresAt ? new Date(post.expiresAt) > new Date() : false,
      greekExclusive: space.category.value === SpaceCategoryEnum.SOCIAL && space.name.toString().toLowerCase().includes('fraternity|sorority|greek')
    }
  }

  /**
   * Determine primary score type for analytics
   */
  private determineScoreType(
    anxietyRelief: number,
    socialProof: number,
    insiderAccess: number
  ) {
    const max = Math.max(anxietyRelief, socialProof, insiderAccess)

    if (anxietyRelief === max) {
      return {
        type: 'anxiety_relief' as const,
        primaryFactor: 'Solves immediate student anxiety',
        confidence: anxietyRelief / 100
      }
    } else if (socialProof === max) {
      return {
        type: 'social_proof' as const,
        primaryFactor: 'Friends are already engaged',
        confidence: socialProof / 100
      }
    } else {
      return {
        type: 'insider_access' as const,
        primaryFactor: 'Exclusive community content',
        confidence: insiderAccess / 100
      }
    }
  }

  /**
   * Helper methods for time-based scoring
   */
  private isFirstYear(graduationYear: number): boolean {
    const currentYear = new Date().getFullYear()
    return graduationYear - currentYear <= 1
  }

  private isHappeningSoon(timestamp: string): boolean {
    const eventTime = Date.parse(timestamp)

    if (Number.isNaN(eventTime)) {
      return false
    }

    const now = Date.now()
    const twoHoursInMs = 2 * 60 * 60 * 1000
    return eventTime >= now && eventTime - now <= twoHoursInMs
  }

  private isMealTime(): boolean {
    const hour = new Date().getHours()
    return (hour >= 7 && hour <= 9) || // Breakfast
           (hour >= 11 && hour <= 14) || // Lunch
           (hour >= 17 && hour <= 20)    // Dinner
  }

  /**
   * Variable ratio reinforcement - not every scroll has gold
   * Implements slot machine psychology per SPEC
   */
  applyVariableRatio(posts: ScoredPost[]): ScoredPost[] {
    return posts.map((post, index) => {
      // Random perfect discoveries - creates addictive scrolling
      const diminishingFactor = Math.max(0.05, 0.15 - index * 0.01)
      const isGoldenPost = Math.random() < diminishingFactor

      if (isGoldenPost) {
        return {
          ...post,
          algorithmScore: post.algorithmScore * 1.5,
          scoreBreakdown: {
            ...post.scoreBreakdown,
            type: 'mixed',
            primaryFactor: ' Perfect discovery',
            confidence: 1.0
          }
        }
      }

      return post
    })
  }
}

/**
 * Default configuration following SPEC requirements
 */
export const defaultFeedConfig: FeedAlgorithmConfig = {
  anxietyReliefWeight: 0.4, // Primary - solve student problems
  socialProofWeight: 0.3,   // Secondary - friend validation
  insiderAccessWeight: 0.3, // Secondary - exclusive access
  campusId: 'ub-buffalo'
}

/**
 * Connection Aggregate
 * Represents a connection between two profiles
 */

import { AggregateRoot } from '../../shared/base/AggregateRoot.base';
import { Result } from '../../shared/base/Result';
import { ProfileId } from '../value-objects/profile-id.value';
import { ConnectionId } from '../value-objects/connection-id.value';

export enum ConnectionType {
  FRIEND = 'friend',
  FOLLOWER = 'follower',
  FOLLOWING = 'following',
  BLOCKED = 'blocked',
  PENDING = 'pending'
}

export enum ConnectionSource {
  SEARCH = 'search',
  SUGGESTION = 'suggestion',
  MUTUAL = 'mutual',
  SPACE = 'space',
  EVENT = 'event',
  QR_CODE = 'qr_code'
}

interface ConnectionProps {
  connectionId: ConnectionId;
  profileId1: ProfileId;
  profileId2: ProfileId;
  type: ConnectionType;
  source: ConnectionSource;
  requestedBy: ProfileId;
  acceptedBy?: ProfileId;
  isActive: boolean;
  createdAt: Date;
  updatedAt: Date;
  acceptedAt?: Date;
  rejectedAt?: Date;
  blockedAt?: Date;
  mutualSpaces: string[];
  interactionCount: number;
  metadata?: Record<string, any>;
}

export class Connection extends AggregateRoot<ConnectionProps> {
  get connectionId(): ConnectionId {
    return this.props.connectionId;
  }

  get profileId1(): ProfileId {
    return this.props.profileId1;
  }

  get profileId2(): ProfileId {
    return this.props.profileId2;
  }

  get type(): ConnectionType {
    return this.props.type;
  }

  get source(): ConnectionSource {
    return this.props.source;
  }

  get isActive(): boolean {
    return this.props.isActive;
  }

  get isMutual(): boolean {
    return this.props.type === ConnectionType.FRIEND && this.props.acceptedBy !== undefined;
  }

  get createdAt(): Date {
    return this.props.createdAt;
  }

  get updatedAt(): Date {
    return this.props.updatedAt;
  }

  get acceptedAt(): Date | undefined {
    return this.props.acceptedAt;
  }

  get rejectedAt(): Date | undefined {
    return this.props.rejectedAt;
  }

  get blockedAt(): Date | undefined {
    return this.props.blockedAt;
  }

  get metadata(): Record<string, any> | undefined {
    return this.props.metadata;
  }

  get mutualSpaces(): string[] {
    return this.props.mutualSpaces;
  }

  get interactionCount(): number {
    return this.props.interactionCount;
  }

  get status(): string {
    // Map the connection type to a status string that the repository expects
    switch (this.props.type) {
      case ConnectionType.FRIEND:
        if (this.props.acceptedBy) return 'accepted';
        return 'pending';
      case ConnectionType.FOLLOWER:
        return 'following';
      case ConnectionType.FOLLOWING:
        return 'following';
      case ConnectionType.BLOCKED:
        return 'blocked';
      case ConnectionType.PENDING:
        return 'pending';
      default:
        return 'pending';
    }
  }

  get requestedBy(): ProfileId {
    return this.props.requestedBy;
  }

  get acceptedBy(): ProfileId | undefined {
    return this.props.acceptedBy;
  }

  private constructor(props: ConnectionProps, id?: string) {
    super(props, id || `connection_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`);
  }

  public static create(
    props: {
      profileId1: ProfileId;
      profileId2: ProfileId;
      type?: ConnectionType;
      source?: ConnectionSource;
      requestedBy: ProfileId;
    },
    id?: string
  ): Result<Connection> {
    // Ensure profileId1 < profileId2 for consistent ordering
    const [orderedId1, orderedId2] = [props.profileId1.value, props.profileId2.value].sort();
    const profile1 = ProfileId.create(orderedId1).getValue();
    const profile2 = ProfileId.create(orderedId2).getValue();

    const connectionIdResult = ConnectionId.createFromProfiles(
      profile1.value,
      profile2.value
    );

    if (connectionIdResult.isFailure) {
      return Result.fail<Connection>(connectionIdResult.error!);
    }

    const connectionProps: ConnectionProps = {
      connectionId: connectionIdResult.getValue(),
      profileId1: profile1,
      profileId2: profile2,
      type: props.type || ConnectionType.PENDING,
      source: props.source || ConnectionSource.SEARCH,
      requestedBy: props.requestedBy,
      isActive: true,
      createdAt: new Date(),
      updatedAt: new Date(),
      mutualSpaces: [],
      interactionCount: 0
    };

    return Result.ok<Connection>(new Connection(connectionProps, id));
  }

  public accept(acceptedBy: ProfileId): Result<void> {
    if (this.props.type !== ConnectionType.PENDING) {
      return Result.fail<void>('Connection is not pending');
    }

    // Verify the accepter is the other party
    if (acceptedBy.value === this.props.requestedBy.value) {
      return Result.fail<void>('Cannot accept your own connection request');
    }

    if (acceptedBy.value !== this.props.profileId1.value &&
        acceptedBy.value !== this.props.profileId2.value) {
      return Result.fail<void>('Accepter is not part of this connection');
    }

    this.props.acceptedBy = acceptedBy;
    this.props.type = ConnectionType.FRIEND;
    this.props.acceptedAt = new Date();
    this.props.updatedAt = new Date();

    return Result.ok<void>();
  }

  public reject(): Result<void> {
    if (this.props.type !== ConnectionType.PENDING) {
      return Result.fail<void>('Connection is not pending');
    }

    this.props.isActive = false;
    this.props.rejectedAt = new Date();
    this.props.updatedAt = new Date();

    return Result.ok<void>();
  }

  public block(blockedBy: ProfileId): Result<void> {
    // Verify the blocker is part of this connection
    if (blockedBy.value !== this.props.profileId1.value &&
        blockedBy.value !== this.props.profileId2.value) {
      return Result.fail<void>('Blocker is not part of this connection');
    }

    this.props.type = ConnectionType.BLOCKED;
    this.props.isActive = false;
    this.props.blockedAt = new Date();
    this.props.updatedAt = new Date();

    return Result.ok<void>();
  }

  public unblock(): Result<void> {
    if (this.props.type !== ConnectionType.BLOCKED) {
      return Result.fail<void>('Connection is not blocked');
    }

    this.props.type = ConnectionType.PENDING;
    this.props.isActive = true;
    this.props.updatedAt = new Date();

    return Result.ok<void>();
  }

  public disconnect(): void {
    this.props.isActive = false;
    this.props.updatedAt = new Date();
  }

  public addMutualSpace(spaceId: string): void {
    if (!this.props.mutualSpaces.includes(spaceId)) {
      this.props.mutualSpaces.push(spaceId);
      this.props.updatedAt = new Date();
    }
  }

  public removeMutualSpace(spaceId: string): void {
    this.props.mutualSpaces = this.props.mutualSpaces.filter(id => id !== spaceId);
    this.props.updatedAt = new Date();
  }

  public incrementInteraction(): void {
    this.props.interactionCount++;
    this.props.updatedAt = new Date();
  }

  public getOtherProfileId(profileId: ProfileId): ProfileId | null {
    if (profileId.value === this.props.profileId1.value) {
      return this.props.profileId2;
    }
    if (profileId.value === this.props.profileId2.value) {
      return this.props.profileId1;
    }
    return null;
  }

  public involves(profileId: ProfileId): boolean {
    return profileId.value === this.props.profileId1.value ||
           profileId.value === this.props.profileId2.value;
  }

  public toData(): any {
    return {
      id: this.id,
      connectionId: this.props.connectionId.value,
      profileId1: this.props.profileId1.value,
      profileId2: this.props.profileId2.value,
      type: this.props.type,
      source: this.props.source,
      requestedBy: this.props.requestedBy.value,
      acceptedBy: this.props.acceptedBy?.value,
      isActive: this.props.isActive,
      isMutual: this.isMutual,
      status: this.status,
      createdAt: this.props.createdAt,
      updatedAt: this.props.updatedAt,
      acceptedAt: this.props.acceptedAt,
      rejectedAt: this.props.rejectedAt,
      blockedAt: this.props.blockedAt,
      mutualSpaces: this.props.mutualSpaces,
      interactionCount: this.props.interactionCount,
      metadata: this.props.metadata
    };
  }
}

/**
 * Profile Aggregate
 * Represents a campus profile with full features
 */

import { AggregateRoot } from '../../shared/base/AggregateRoot.base';
