/**
 * Domain Event Base Infrastructure
 * Foundation for event-driven architecture in HIVE
 */

export interface DomainEventMetadata {
  correlationId?: string;
  causationId?: string;
  userId?: string;
  timestamp: Date;
}

export abstract class DomainEvent {
  public readonly occurredOn: Date;
  public readonly metadata: DomainEventMetadata;

  constructor(metadata?: Partial<DomainEventMetadata>) {
    this.occurredOn = new Date();
    this.metadata = {
      timestamp: this.occurredOn,
      ...metadata
    };
  }

  abstract get aggregateId(): string;
  abstract get eventName(): string;
  abstract get eventVersion(): number;

  toJSON(): Record<string, unknown> {
    return {
      eventName: this.eventName,
      eventVersion: this.eventVersion,
      aggregateId: this.aggregateId,
      occurredOn: this.occurredOn.toISOString(),
      metadata: this.metadata,
      payload: this.getPayload()
    };
  }

  protected abstract getPayload(): Record<string, unknown>;
}

export interface DomainEventHandler<T extends DomainEvent> {
  handle(event: T): Promise<void>;
}

export interface EventStore {
  save(event: DomainEvent): Promise<void>;
  getEvents(aggregateId: string): Promise<DomainEvent[]>;
}

export class EventBus {
  private handlers: Map<string, DomainEventHandler<any>[]> = new Map();

  subscribe<T extends DomainEvent>(
    eventName: string,
    handler: DomainEventHandler<T>
  ): void {
    if (!this.handlers.has(eventName)) {
      this.handlers.set(eventName, []);
    }
    this.handlers.get(eventName)!.push(handler);
  }

  async publish(event: DomainEvent): Promise<void> {
    const handlers = this.handlers.get(event.eventName) || [];
    await Promise.all(handlers.map(handler => handler.handle(event)));
  }

  async publishMany(events: DomainEvent[]): Promise<void> {
    for (const event of events) {
      await this.publish(event);
    }
  }
}

// Aggregate root with event support
export abstract class AggregateRoot {
  private _domainEvents: DomainEvent[] = [];

  get domainEvents(): DomainEvent[] {
    return this._domainEvents;
  }

  protected addDomainEvent(event: DomainEvent): void {
    this._domainEvents.push(event);
  }

  clearEvents(): void {
    this._domainEvents = [];
  }
}

/**
 * Specification Pattern
 * For building complex queries in a composable way
 */

export interface Specification<T> {
  isSatisfiedBy(candidate: T): boolean;
  and(other: Specification<T>): Specification<T>;
  or(other: Specification<T>): Specification<T>;
  not(): Specification<T>;
}

export abstract class CompositeSpecification<T> implements Specification<T> {
  abstract isSatisfiedBy(candidate: T): boolean;

  and(other: Specification<T>): Specification<T> {
    return new AndSpecification(this, other);
  }

  or(other: Specification<T>): Specification<T> {
    return new OrSpecification(this, other);
  }

  not(): Specification<T> {
    return new NotSpecification(this);
  }
}

class AndSpecification<T> extends CompositeSpecification<T> {
  constructor(
    private left: Specification<T>,
    private right: Specification<T>
  ) {
    super();
  }

  isSatisfiedBy(candidate: T): boolean {
    return this.left.isSatisfiedBy(candidate) && this.right.isSatisfiedBy(candidate);
  }
}

class OrSpecification<T> extends CompositeSpecification<T> {
  constructor(
    private left: Specification<T>,
    private right: Specification<T>
  ) {
    super();
  }

  isSatisfiedBy(candidate: T): boolean {
    return this.left.isSatisfiedBy(candidate) || this.right.isSatisfiedBy(candidate);
  }
}

class NotSpecification<T> extends CompositeSpecification<T> {
  constructor(private spec: Specification<T>) {
    super();
  }

  isSatisfiedBy(candidate: T): boolean {
    return !this.spec.isSatisfiedBy(candidate);
  }
}

/**
 * Feed Specifications
 */
export class FeedItemSpecifications {
  static fromSpace(spaceId: string): Specification<any> {
    return new FromSpaceSpecification(spaceId);
  }

  static byAuthor(authorId: string): Specification<any> {
    return new ByAuthorSpecification(authorId);
  }

  static isRecent(hoursAgo: number): Specification<any> {
    return new IsRecentSpecification(hoursAgo);
  }

  static hasMinimumEngagement(minEngagement: number): Specification<any> {
    return new HasMinimumEngagementSpecification(minEngagement);
  }

  static isTrending(): Specification<any> {
    return new IsTrendingSpecification();
  }

  static hasTag(tag: string): Specification<any> {
    return new HasTagSpecification(tag);
  }
}

class FromSpaceSpecification extends CompositeSpecification<any> {
  constructor(private spaceId: string) {
    super();
  }

  isSatisfiedBy(candidate: any): boolean {
    return candidate.source?.spaceId === this.spaceId;
  }
}

class ByAuthorSpecification extends CompositeSpecification<any> {
  constructor(private authorId: string) {
    super();
  }

  isSatisfiedBy(candidate: any): boolean {
    return candidate.content?.authorId?.id === this.authorId;
  }
}

class IsRecentSpecification extends CompositeSpecification<any> {
  private cutoffTime: Date;

  constructor(hoursAgo: number) {
    super();
    this.cutoffTime = new Date(Date.now() - hoursAgo * 60 * 60 * 1000);
  }

  isSatisfiedBy(candidate: any): boolean {
    return candidate.createdAt > this.cutoffTime;
  }
}

class HasMinimumEngagementSpecification extends CompositeSpecification<any> {
  constructor(private minEngagement: number) {
    super();
  }

  isSatisfiedBy(candidate: any): boolean {
    const engagements = candidate.interactions?.filter((i: any) => i.isEngagement()) || [];
    return engagements.length >= this.minEngagement;
  }
}

class IsTrendingSpecification extends CompositeSpecification<any> {
  isSatisfiedBy(candidate: any): boolean {
    return candidate.isTrending === true;
  }
}

class HasTagSpecification extends CompositeSpecification<any> {
  constructor(private tag: string) {
    super();
  }

  isSatisfiedBy(candidate: any): boolean {
    return candidate.tags?.includes(this.tag) || false;
  }
}

/**
 * Ritual Specifications
 */
export class RitualSpecifications {
  static isActive(): Specification<any> {
    return new IsActiveRitualSpecification();
  }

  static hasType(type: string): Specification<any> {
    return new HasTypeSpecification(type);
  }

  static hasAvailableSlots(): Specification<any> {
    return new HasAvailableSlotsSpecification();
  }

  static isEndingSoon(daysRemaining: number): Specification<any> {
    return new IsEndingSoonSpecification(daysRemaining);
  }

  static hasMinimumParticipants(min: number): Specification<any> {
    return new HasMinimumParticipantsSpecification(min);
  }

  static isJoinableBy(userId: string): Specification<any> {
    return new IsJoinableBySpecification(userId);
  }
}

class IsActiveRitualSpecification extends CompositeSpecification<any> {
  isSatisfiedBy(candidate: any): boolean {
    const now = new Date();
    return candidate.status?.isActive() &&
           candidate.startDate <= now &&
           candidate.endDate >= now;
  }
}

class HasTypeSpecification extends CompositeSpecification<any> {
  constructor(private type: string) {
    super();
  }

  isSatisfiedBy(candidate: any): boolean {
    return candidate.type?.type === this.type;
  }
}

class HasAvailableSlotsSpecification extends CompositeSpecification<any> {
  isSatisfiedBy(candidate: any): boolean {
    // Assuming max 1000 participants per ritual
    const maxParticipants = 1000;
    return (candidate.participantCount || 0) < maxParticipants;
  }
}

class IsEndingSoonSpecification extends CompositeSpecification<any> {
  constructor(private daysRemaining: number) {
    super();
  }

  isSatisfiedBy(candidate: any): boolean {
    const now = new Date();
    const daysLeft = Math.ceil(
      (candidate.endDate.getTime() - now.getTime()) / (1000 * 60 * 60 * 24)
    );
    return daysLeft <= this.daysRemaining && daysLeft > 0;
  }
}

class HasMinimumParticipantsSpecification extends CompositeSpecification<any> {
  constructor(private min: number) {
    super();
  }

  isSatisfiedBy(candidate: any): boolean {
    return (candidate.participantCount || 0) >= this.min;
  }
}

class IsJoinableBySpecification extends CompositeSpecification<any> {
  constructor(private userId: string) {
    super();
  }

  isSatisfiedBy(candidate: any): boolean {
    // Check if user can join (not already participating)
    const isParticipating = candidate.participations?.some(
      (p: any) => p.participantId?.id === this.userId && p.isActive
    );
    return candidate.canJoin?.() && !isParticipating;
  }
}

/**
 * Usage Examples:
 *
 * // Find trending items from user's spaces in the last 24 hours
 * const spec = FeedItemSpecifications.isTrending()
 *   .and(FeedItemSpecifications.isRecent(24))
 *   .and(FeedItemSpecifications.fromSpace(userSpaceId));
 *
 * const filteredItems = feedItems.filter(item => spec.isSatisfiedBy(item));
 *
 * // Find active challenge rituals ending soon with good participation
 * const ritualSpec = RitualSpecifications.isActive()
 *   .and(RitualSpecifications.hasType('challenge'))
 *   .and(RitualSpecifications.isEndingSoon(7))
 *   .and(RitualSpecifications.hasMinimumParticipants(50));
 *
 * const urgentRituals = rituals.filter(r => ritualSpec.isSatisfiedBy(r));
 */

/**
 * Space Aggregate
 * Represents a community space with full DDD business logic
 */

import { AggregateRoot } from '../../shared/base/AggregateRoot.base';
import { Result } from '../../shared/base/Result';
import { SpaceId } from '../value-objects/space-id.value';
import { SpaceName } from '../value-objects/space-name.value';
import { SpaceDescription } from '../value-objects/space-description.value';
import { SpaceCategory } from '../value-objects/space-category.value';
import { CampusId } from '../../profile/value-objects/campus-id.value';
import { ProfileId } from '../../profile/value-objects/profile-id.value';
import { Tab } from '../entities/tab';
import { Widget } from '../entities/widget';
import { SpaceCreatedEvent } from '../events/space-created.event';
import { MemberJoinedEvent } from '../events/member-joined.event';
import { MemberRemovedEvent } from '../events/member-removed.event';
import { MemberRoleUpdatedEvent } from '../events/member-role-updated.event';
import { PostCreatedEvent } from '../events/post-created.event';

export interface SpaceMember {
  profileId: ProfileId;
  role: 'admin' | 'moderator' | 'member';
  joinedAt: Date;
}

export interface SpaceSettings {
  allowInvites: boolean;
  requireApproval: boolean;
  allowRSS: boolean;
  maxMembers?: number;
  isPublic: boolean;
}

export interface RushMode {
  isActive: boolean;
  startDate?: Date;
  endDate?: Date;
  requirements?: string[];
}

export interface SpaceProps {
  spaceId: SpaceId;
  name: SpaceName;
  description: SpaceDescription;
  category: SpaceCategory;
  campusId: CampusId;
  createdBy: ProfileId;
  members: SpaceMember[];
  tabs: Tab[];
  widgets: Widget[];
  settings: SpaceSettings;
  rssUrl?: string;
  visibility: 'public' | 'private';
  isActive: boolean;
  isVerified: boolean;
  trendingScore: number;
  rushMode?: RushMode;
  createdAt: Date;
  updatedAt: Date;
  lastActivityAt: Date;
  postCount: number;
}

export class Space extends AggregateRoot<SpaceProps> {
  get spaceId(): SpaceId {
    return this.props.spaceId;
  }

  get name(): SpaceName {
    return this.props.name;
  }

  get description(): SpaceDescription {
    return this.props.description;
  }

  get category(): SpaceCategory {
    return this.props.category;
  }

  get campusId(): CampusId {
    return this.props.campusId;
  }

  get memberCount(): number {
    return this.props.members.length;
  }

  get isPublic(): boolean {
    return this.props.visibility === 'public';
  }

  get tabs(): Tab[] {
    return this.props.tabs;
  }

  get widgets(): Widget[] {
    return this.props.widgets;
  }

  get isVerified(): boolean {
    return this.props.isVerified;
  }

  get trendingScore(): number {
    return this.props.trendingScore;
  }

  get rushMode(): RushMode | undefined {
    return this.props.rushMode;
  }

  get postCount(): number {
    return this.props.postCount;
  }

  get members(): SpaceMember[] {
    return this.props.members;
  }

  get adminCount(): number {
    return this.getAdminCount();
  }

  get lastActivityAt(): Date {
    return this.props.lastActivityAt;
  }

  get createdAt(): Date {
    return this.props.createdAt;
  }

  get updatedAt(): Date {
    return this.props.updatedAt;
  }

  // Compatibility getters for Space interface
  get spaceIdValue(): SpaceId {
    return this.props.spaceId;
  }

  get visibility(): 'public' | 'private' {
    return this.props.visibility;
  }

  get settings(): SpaceSettings {
    return this.props.settings;
  }

  get spaceType(): string {
    return this.props.category.value;
  }

  // Alias for backward compatibility (many components use 'type' instead of 'spaceType')
  get type(): string {
    return this.props.category.value;
  }

  get posts(): any[] {
    // Posts are managed separately - return empty array for interface compatibility
    return [];
  }

  public getMemberCount(): number {
    return this.props.members.length;
  }


  private constructor(props: SpaceProps, id?: string) {
    super(props, id || `space_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`);
  }

  public static create(
    props: {
      spaceId: SpaceId;
      name: SpaceName;
      description: SpaceDescription;
      category: SpaceCategory;
      campusId: CampusId;
      createdBy: ProfileId;
      settings?: Partial<SpaceSettings>;
      visibility?: 'public' | 'private';
      rssUrl?: string;
    },
    id?: string
  ): Result<Space> {
    const defaultSettings: SpaceSettings = {
      allowInvites: true,
      requireApproval: false,
      allowRSS: false,
      isPublic: props.visibility === 'public',
      ...props.settings
    };

    const creator: SpaceMember = {
      profileId: props.createdBy,
      role: 'admin',
      joinedAt: new Date()
    };

    const spaceProps: SpaceProps = {
      spaceId: props.spaceId,
      name: props.name,
      description: props.description,
      category: props.category,
      campusId: props.campusId,
      createdBy: props.createdBy,
      members: [creator],
      tabs: [],
      widgets: [],
      settings: defaultSettings,
      rssUrl: props.rssUrl,
      visibility: props.visibility || 'public',
      isActive: true,
      isVerified: false,
      trendingScore: 0,
      rushMode: undefined,
      createdAt: new Date(),
      updatedAt: new Date(),
      lastActivityAt: new Date(),
      postCount: 0
    };

    const space = new Space(spaceProps, id);

    // Create default tabs
    space.createDefaultTabs();

    // Fire domain event
    space.addDomainEvent(
      new SpaceCreatedEvent(
        space.id,
        props.name.value,
        props.category.value,
        props.createdBy.value
      )
    );

    return Result.ok<Space>(space);
  }

  public addMember(profileId: ProfileId, role: 'member' | 'moderator' = 'member'): Result<void> {
    if (this.isMember(profileId)) {
      return Result.fail<void>('User is already a member');
    }

    if (this.props.settings.maxMembers && this.memberCount >= this.props.settings.maxMembers) {
      return Result.fail<void>('Space has reached maximum member capacity');
    }

    this.props.members.push({
      profileId,
      role,
      joinedAt: new Date()
    });

    this.updateLastActivity();

    // Fire domain event
    this.addDomainEvent(
      new MemberJoinedEvent(this.id, profileId.value, role, this.memberCount)
    );

    return Result.ok<void>();
  }

  public removeMember(profileId: ProfileId): Result<void> {
    const memberIndex = this.props.members.findIndex(
      m => m.profileId.value === profileId.value
    );

    if (memberIndex === -1) {
      return Result.fail<void>('User is not a member');
    }

    const member = this.props.members[memberIndex];

    // Can't remove last admin
    if (member.role === 'admin' && this.getAdminCount() === 1) {
      return Result.fail<void>('Cannot remove the last admin');
    }

    this.props.members.splice(memberIndex, 1);
    this.updateLastActivity();

    // Fire domain event
    this.addDomainEvent(
      new MemberRemovedEvent(this.id, profileId.value, this.memberCount)
    );

    return Result.ok<void>();
  }

  public isMember(profileId: ProfileId): boolean {
    return this.props.members.some(m => m.profileId.value === profileId.value);
  }

  public getMemberRole(profileId: ProfileId): string | null {
    const member = this.props.members.find(m => m.profileId.value === profileId.value);
    return member ? member.role : null;
  }

  public updateMemberRole(
    profileId: ProfileId,
    newRole: 'admin' | 'moderator' | 'member'
  ): Result<void> {
    const member = this.props.members.find(m => m.profileId.value === profileId.value);

    if (!member) {
      return Result.fail<void>('User is not a member');
    }

    // Can't demote last admin
    if (member.role === 'admin' && newRole !== 'admin' && this.getAdminCount() === 1) {
      return Result.fail<void>('Cannot demote the last admin');
    }

    const oldRole = member.role;
    member.role = newRole;
    this.updateLastActivity();

    // Fire domain event
    this.addDomainEvent(
      new MemberRoleUpdatedEvent(this.id, profileId.value, oldRole, newRole)
    );

    return Result.ok<void>();
  }

  public addTab(tab: Tab): Result<void> {
    if (this.props.tabs.find(t => t.name === tab.name)) {
      return Result.fail<void>('Tab with this name already exists');
    }

    this.props.tabs.push(tab);
    this.updateLastActivity();
    return Result.ok<void>();
  }

  public addWidget(widget: Widget): Result<void> {
    this.props.widgets.push(widget);
    this.updateLastActivity();
    return Result.ok<void>();
  }

  public incrementPostCount(postId?: string, authorId?: string): void {
    this.props.postCount++;
    this.updateLastActivity();

    // Fire domain event if post details provided
    if (postId && authorId) {
      this.addDomainEvent(
        new PostCreatedEvent(this.id, postId, authorId, this.props.postCount)
      );
    }
  }

  public updateSettings(settings: Partial<SpaceSettings>): Result<void> {
    if (
      typeof settings.maxMembers === 'number' &&
      settings.maxMembers > 0 &&
      settings.maxMembers < this.memberCount
    ) {
      return Result.fail<void>('Cannot set maxMembers below current member count');
    }

    this.props.settings = { ...this.props.settings, ...settings };
    this.props.updatedAt = new Date();

    return Result.ok<void>();
  }

  private getAdminCount(): number {
    return this.props.members.filter(m => m.role === 'admin').length;
  }

  private updateLastActivity(): void {
    this.props.lastActivityAt = new Date();
    this.props.updatedAt = new Date();
  }

  private createDefaultTabs(): void {
    const feedTab = Tab.create({
      name: 'Feed',
      type: 'feed',
      isDefault: true,
      order: 0,
      widgets: [],
      isVisible: true
    });

    if (feedTab.isSuccess) {
      this.props.tabs.push(feedTab.getValue());
    }
  }

  /**
   * Space Discovery Business Logic (Moved from SpaceDiscoveryService)
   */

  public getWelcomeMessage(): string {
    const name = this.props.name.value;
    const description = this.props.description.value;
    return `Welcome to ${name}! ${description}`;
  }

  public getSuggestedActions(): Array<{ action: string; description: string }> {
    const actions: Array<{ action: string; description: string }> = [];

    // If space has posts, suggest reading them
    if (this.props.postCount > 0) {
      actions.push({
        action: 'read_recent_posts',
        description: 'Catch up on recent discussions'
      });
    }

    // Always suggest introducing yourself
    actions.push({
      action: 'introduce_yourself',
      description: 'Post an introduction to let others know you\'re here'
    });

    // Suggest sharing resources for study groups
    if (this.props.category.value === 'study-group') {
      actions.push({
        action: 'share_resources',
        description: 'Share helpful study materials or notes'
      });
    }

    // Suggest inviting friends
    actions.push({
      action: 'invite_friends',
      description: 'Invite classmates who might be interested'
    });

    return actions;
  }

  // Temporary setters for repository layer - should be removed once proper construction is implemented
  public setIsVerified(isVerified: boolean): void {
    (this.props as any).isVerified = isVerified;
  }


  public setPostCount(count: number): void {
    (this.props as any).postCount = count;
  }

  public setMemberCount(count: number): void {
    // Note: This is for setting cached count from database
    // The actual count should be calculated from members.length
    (this.props as any).memberCount = count;
  }

  public setTrendingScore(score: number): void {
    (this.props as any).trendingScore = score;
  }

  public setLastActivityAt(date: Date): void {
    (this.props as any).lastActivityAt = date;
  }

  public setCreatedAt(date: Date): void {
    (this.props as any).createdAt = date;
  }

  public setUpdatedAt(date: Date): void {
    (this.props as any).updatedAt = date;
  }

  public setTabs(tabs: Tab[]): void {
    (this.props as any).tabs = tabs;
  }

  public setWidgets(widgets: Widget[]): void {
    (this.props as any).widgets = widgets;
  }

  public toData(): any {
    return {
      id: this.id,
      spaceId: this.props.spaceId,
      name: this.props.name,
      description: this.props.description,
      category: this.props.category,
      campusId: this.props.campusId,
      createdBy: this.props.createdBy,
      members: this.props.members,
      tabs: this.props.tabs,
      widgets: this.props.widgets,
      settings: this.props.settings,
      rssUrl: this.props.rssUrl,
      visibility: this.props.visibility,
      isActive: this.props.isActive,
      memberCount: this.memberCount,
      postCount: this.props.postCount,
      posts: [], // Posts are stored as subcollection, not in aggregate
      createdAt: this.props.createdAt,
      updatedAt: this.props.updatedAt,
      lastActivityAt: this.props.lastActivityAt
    };
  }
}

/**
 * Widget Entity
 * Represents a widget component in a space
 */

import { Entity } from '../../shared/base/Entity.base';
import { Result } from '../../shared/base/Result';

interface WidgetProps {
  type: 'calendar' | 'poll' | 'links' | 'files' | 'rss' | 'custom';
  title: string;
  config: Record<string, any>;
  isVisible: boolean;
  order: number;
  position: { x: number; y: number; width: number; height: number };
  isEnabled: boolean;
}

export class Widget extends Entity<WidgetProps> {
  get type(): string {
    return this.props.type;
  }

  get title(): string {
    return this.props.title;
  }

  get config(): Record<string, any> {
    return this.props.config;
  }

  get isVisible(): boolean {
    return this.props.isVisible;
  }

  get order(): number {
    return this.props.order;
  }

  get position(): { x: number; y: number; width: number; height: number } {
    return this.props.position;
  }

  get isEnabled(): boolean {
    return this.props.isEnabled;
  }

  private constructor(props: WidgetProps, id?: string) {
    super(props, id || `widget_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`);
  }

  public static create(props: Partial<WidgetProps> & { type: WidgetProps['type']; title: string }, id?: string): Result<Widget> {
    if (!props.title || props.title.trim().length === 0) {
      return Result.fail<Widget>('Widget title is required');
    }

    const widgetProps: WidgetProps = {
      type: props.type,
      title: props.title,
      config: props.config || {},
      isVisible: props.isVisible !== undefined ? props.isVisible : true,
      order: props.order || 0,
      position: props.position || { x: 0, y: 0, width: 200, height: 200 },
      isEnabled: props.isEnabled !== undefined ? props.isEnabled : true
    };

    return Result.ok<Widget>(new Widget(widgetProps, id));
  }

  public updateConfig(config: Record<string, any>): void {
    this.props.config = { ...this.props.config, ...config };
  }

  public setVisibility(isVisible: boolean): void {
    this.props.isVisible = isVisible;
  }

  public setOrder(order: number): void {
    this.props.order = order;
  }

  public setTitle(title: string): Result<void> {
    if (!title || title.trim().length === 0) {
      return Result.fail<void>('Widget title cannot be empty');
    }
    this.props.title = title;
    return Result.ok<void>();
  }
}

