/**
 * Event Dispatcher
 * Utility to dispatch domain events from aggregates after persistence
 */

import { AggregateRoot } from '../../domain/shared/base/AggregateRoot.base';
import { getEventBus } from './event-bus';

export class EventDispatcher {
  /**
   * Dispatch all pending events from an aggregate
   * Call this AFTER successfully persisting the aggregate
   */
  static async dispatchEventsForAggregate(aggregate: AggregateRoot<any>): Promise<void> {
    const events = aggregate.domainEvents;

    if (events.length === 0) {
      return;
    }

    const eventBus = getEventBus();

    // Publish all events
    await eventBus.publishAll(events);

    // Clear events from aggregate after successful dispatch
    aggregate.clearEvents();
  }

  /**
   * Dispatch events from multiple aggregates
   */
  static async dispatchEventsForAggregates(aggregates: AggregateRoot<any>[]): Promise<void> {
    for (const aggregate of aggregates) {
      await this.dispatchEventsForAggregate(aggregate);
    }
  }

  /**
   * Mark event as dispatched without actually dispatching
   * (Useful for testing)
   */
  static markEventAsDispatched(aggregate: AggregateRoot<any>, eventIndex: number = 0): void {
    const events = aggregate.domainEvents;
    if (events[eventIndex]) {
      aggregate.markEventAsDispatched(events[eventIndex]);
    }
  }
}

/**
 * Firebase Event Dispatcher
 * Publishes domain events to Firestore for real-time updates
 */

import { collection, addDoc, Timestamp } from 'firebase/firestore';
// import { db } from '@hive/firebase'; // Temporarily disabled - package needs to be fixed
import { IEventDispatcher } from '../repositories/interfaces';
// import { logger } from '../../lib/logger'; // File doesn't exist

// Temporary db and logger
const db = null as any;
const logger = { info: console.log, error: console.error, warn: console.warn };

export class FirebaseEventDispatcher implements IEventDispatcher {
  private handlers: Map<string, Set<(event: any) => Promise<void>>> = new Map();
  private readonly eventsCollection = 'domain_events';

  async dispatch(events: any[]): Promise<void> {
    if (!events || events.length === 0) return;

    try {
      // Batch write events to Firestore
      const batch = events.map(async (event) => {
        // Add metadata
        const eventWithMetadata = {
          ...event,
          id: `evt_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
          occurredAt: event.timestamp || Timestamp.now(),
          version: 1,
          processed: false
        };

        // Persist to Firestore for audit trail and real-time listeners
        await addDoc(collection(db, this.eventsCollection), eventWithMetadata);

        // Notify local handlers
        await this.notifyHandlers(event.eventType, eventWithMetadata);

        return eventWithMetadata;
      });

      await Promise.all(batch);
    } catch (error) {
      logger.error('Failed to dispatch events', { error });
      throw error;
    }
  }

  subscribe(eventType: string, handler: (event: any) => Promise<void>): void {
    if (!this.handlers.has(eventType)) {
      this.handlers.set(eventType, new Set());
    }
    this.handlers.get(eventType)!.add(handler);
  }

  unsubscribe(eventType: string, handler: (event: any) => Promise<void>): void {
    const handlers = this.handlers.get(eventType);
    if (handlers) {
      handlers.delete(handler);
      if (handlers.size === 0) {
        this.handlers.delete(eventType);
      }
    }
  }

  private async notifyHandlers(eventType: string, event: any): Promise<void> {
    const handlers = this.handlers.get(eventType);
    if (!handlers || handlers.size === 0) return;

    // Execute handlers in parallel
    const handlerPromises = Array.from(handlers).map(async (handler) => {
      try {
        await handler(event);
      } catch (error) {
        logger.error('Event handler failed', {
          eventType,
          eventId: event.id,
          error
        });
        // Don't throw - continue with other handlers
      }
    });

    await Promise.all(handlerPromises);
  }
}

// Singleton instance
export const eventDispatcher = new FirebaseEventDispatcher();

/**
 * Feature Flags for Progressive Profile System Enhancement
 * Allows gradual rollout of new DDD features without breaking existing functionality
 */

export interface FeatureFlags {
  // Profile Domain Enhancements
  PROFILE_CAMPUS_ISOLATION: boolean;
  PROFILE_PRIVACY_CONTROLS: boolean;
  PROFILE_SOCIAL_GRAPH: boolean;
  PROFILE_VERIFICATION_BADGES: boolean;
  PROFILE_ACTIVITY_TRACKING: boolean;
  PROFILE_DOMAIN_EVENTS: boolean;
  PROFILE_ADVANCED_SEARCH: boolean;
  PROFILE_WIDGET_PRIVACY: boolean;

  // Connection Features
  CONNECTION_STRENGTH_CALCULATION: boolean;
  CONNECTION_MUTUAL_TRACKING: boolean;
  CONNECTION_BLOCKING: boolean;

  // Faculty & Leadership
  FACULTY_VERIFICATION: boolean;
  STUDENT_LEADER_BADGES: boolean;
  ATHLETE_VERIFICATION: boolean;

  // Performance & Analytics
  PROFILE_VIEW_TRACKING: boolean;
  PROFILE_COMPLETION_SCORE: boolean;
  PROFILE_ENGAGEMENT_METRICS: boolean;
}

class FeatureFlagService {
  private flags: FeatureFlags;

  constructor() {
    // Default configuration - can be overridden by environment variables
    this.flags = {
      // Start with core features enabled
      PROFILE_CAMPUS_ISOLATION: true,
      PROFILE_PRIVACY_CONTROLS: true,
      PROFILE_SOCIAL_GRAPH: false, // Phase 2
      PROFILE_VERIFICATION_BADGES: false, // Phase 2
      PROFILE_ACTIVITY_TRACKING: false, // Phase 3
      PROFILE_DOMAIN_EVENTS: true,
      PROFILE_ADVANCED_SEARCH: false, // Phase 3
      PROFILE_WIDGET_PRIVACY: false, // Phase 2

      // Connection features - Phase 2
      CONNECTION_STRENGTH_CALCULATION: false,
      CONNECTION_MUTUAL_TRACKING: false,
      CONNECTION_BLOCKING: false,

      // Faculty & Leadership - Phase 2
      FACULTY_VERIFICATION: false,
      STUDENT_LEADER_BADGES: false,
      ATHLETE_VERIFICATION: false,

      // Performance & Analytics - Phase 3
      PROFILE_VIEW_TRACKING: false,
      PROFILE_COMPLETION_SCORE: false,
      PROFILE_ENGAGEMENT_METRICS: false,
    };

    // Override with environment variables if available
    this.loadFromEnvironment();
  }

  private loadFromEnvironment(): void {
    // Check for environment variable overrides
    const envFlags = process.env.HIVE_FEATURE_FLAGS;
    if (envFlags) {
      try {
        const overrides = JSON.parse(envFlags);
        this.flags = { ...this.flags, ...overrides };
      } catch (error) {
        console.warn('Failed to parse HIVE_FEATURE_FLAGS:', error);
      }
    }

    // Individual flag overrides (for easier deployment control)
    Object.keys(this.flags).forEach(key => {
      const envKey = `FF_${key}`;
      const envValue = process.env[envKey];
      if (envValue !== undefined) {
        (this.flags as any)[key] = envValue === 'true';
      }
    });
  }

  isEnabled(flag: keyof FeatureFlags): boolean {
    return this.flags[flag] ?? false;
  }

  enableFlag(flag: keyof FeatureFlags): void {
    this.flags[flag] = true;
  }

  disableFlag(flag: keyof FeatureFlags): void {
    this.flags[flag] = false;
  }

  getAllFlags(): FeatureFlags {
    return { ...this.flags };
  }

  // Helper methods for checking feature groups
  isPrivacyEnabled(): boolean {
    return this.flags.PROFILE_PRIVACY_CONTROLS;
  }

  isSocialGraphEnabled(): boolean {
    return this.flags.PROFILE_SOCIAL_GRAPH;
  }

  isVerificationEnabled(): boolean {
    return this.flags.PROFILE_VERIFICATION_BADGES ||
           this.flags.FACULTY_VERIFICATION ||
           this.flags.STUDENT_LEADER_BADGES ||
           this.flags.ATHLETE_VERIFICATION;
  }

  // Phase-based rollout helpers
  isPhase1Enabled(): boolean {
    return this.flags.PROFILE_CAMPUS_ISOLATION &&
           this.flags.PROFILE_PRIVACY_CONTROLS &&
           this.flags.PROFILE_DOMAIN_EVENTS;
  }

  isPhase2Enabled(): boolean {
    return this.isPhase1Enabled() &&
           this.flags.PROFILE_SOCIAL_GRAPH &&
           this.flags.PROFILE_VERIFICATION_BADGES &&
           this.flags.CONNECTION_STRENGTH_CALCULATION;
  }

  isPhase3Enabled(): boolean {
    return this.isPhase2Enabled() &&
           this.flags.PROFILE_ACTIVITY_TRACKING &&
           this.flags.PROFILE_ADVANCED_SEARCH &&
           this.flags.PROFILE_VIEW_TRACKING;
  }
}

// Singleton instance
export const featureFlags = new FeatureFlagService();

// Export convenience function
export function isFeatureEnabled(flag: keyof FeatureFlags): boolean {
  return featureFlags.isEnabled(flag);
}

/**
 * Real-time Feed Listener
 * Subscribes to feed updates using Firestore listeners
 */

import {
  collection,
  query,
  where,
  orderBy,
  limit,
  onSnapshot,
  Timestamp,
  DocumentData,
  Unsubscribe
} from 'firebase/firestore';
import { db } from '@hive/firebase';

export interface FeedUpdate {
  id: string;
  type: 'post' | 'event' | 'ritual_update' | 'requote';
  content: {
    text: string;
    mediaUrls: string[];
    authorId: string;
    authorName: string;
    authorHandle: string;
    authorAvatar?: string;
  };
  spaceId?: string;
  spaceName?: string;
  engagement: {
    reactions: number;
    comments: number;
    reposts: number;
    requotes: number;
  };
  isPromoted: boolean;
  createdAt: Date;
  relevanceScore: number;
}

export interface FeedListenerOptions {
  campusId: string;
  userId?: string;
  filter?: 'all' | 'my_spaces' | 'trending' | 'events' | 'rituals';
  limitCount?: number;
  onUpdate: (updates: FeedUpdate[]) => void;
  onError?: (error: Error) => void;
}

export class FeedListener {
  private listeners: Map<string, Unsubscribe> = new Map();
  private feedCache: Map<string, FeedUpdate[]> = new Map();

  /**
   * Subscribe to real-time feed updates
   */
  subscribe(options: FeedListenerOptions): string {
    const listenerId = `feed_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

    // Build query based on filters
    const feedQuery = this.buildFeedQuery(options);

    // Set up real-time listener
    const unsubscribe = onSnapshot(
      feedQuery,
      (snapshot) => {
        const updates: FeedUpdate[] = [];

        snapshot.docChanges().forEach((change) => {
          const data = change.doc.data();
          const feedItem = this.mapToFeedUpdate(change.doc.id, data);

          if (change.type === 'added' || change.type === 'modified') {
            updates.push(feedItem);
          }
        });

        // Update cache
        if (updates.length > 0) {
          this.updateCache(listenerId, updates);
          options.onUpdate(this.getCachedFeed(listenerId));
        }
      },
      (error) => {
        console.error('Feed listener error:', error);
        options.onError?.(error);
      }
    );

    this.listeners.set(listenerId, unsubscribe);
    return listenerId;
  }

  /**
   * Subscribe to space-specific feed
   */
  subscribeToSpace(spaceId: string, options: Omit<FeedListenerOptions, 'filter'>): string {
    const listenerId = `space_${spaceId}_${Date.now()}`;

    const spaceQuery = query(
      collection(db, 'spaces', spaceId, 'posts'),
      orderBy('createdAt', 'desc'),
      limit(options.limitCount || 50)
    );

    const unsubscribe = onSnapshot(
      spaceQuery,
      (snapshot) => {
        const updates: FeedUpdate[] = [];

        snapshot.docs.forEach((doc) => {
          const data = doc.data();
          updates.push(this.mapToFeedUpdate(doc.id, data, spaceId));
        });

        this.feedCache.set(listenerId, updates);
        options.onUpdate(updates);
      },
      (error) => {
        console.error('Space feed listener error:', error);
        options.onError?.(error);
      }
    );

    this.listeners.set(listenerId, unsubscribe);
    return listenerId;
  }

  /**
   * Subscribe to promoted posts (campus-wide)
   */
  subscribeToPromoted(campusId: string, options: Omit<FeedListenerOptions, 'filter'>): string {
    const listenerId = `promoted_${campusId}_${Date.now()}`;

    const promotedQuery = query(
      collection(db, 'promoted_posts'),
      where('campusId', '==', campusId),
      where('expiresAt', '>', Timestamp.now()),
      orderBy('expiresAt', 'desc'),
      orderBy('promotedAt', 'desc'),
      limit(options.limitCount || 20)
    );

    const unsubscribe = onSnapshot(
      promotedQuery,
      async (snapshot) => {
        const updates: FeedUpdate[] = [];

        for (const doc of snapshot.docs) {
          const data = doc.data();
          const feedItem = await this.fetchAndMapPromotedPost(data);
          if (feedItem) {
            updates.push(feedItem);
          }
        }

        this.feedCache.set(listenerId, updates);
        options.onUpdate(updates);
      },
      (error) => {
        console.error('Promoted feed listener error:', error);
        options.onError?.(error);
      }
    );

    this.listeners.set(listenerId, unsubscribe);
    return listenerId;
  }

  /**
   * Subscribe to user's personal feed (based on followed spaces)
   */
  subscribeToUserFeed(userId: string, campusId: string, options: Omit<FeedListenerOptions, 'userId'>): string {
    const listenerId = `user_${userId}_${Date.now()}`;

    // This would ideally query a pre-computed feed collection
    // For MVP, we'll query recent posts from joined spaces
    const feedQuery = query(
      collection(db, 'feed_items'),
      where('campusId', '==', campusId),
      where('visibility', '==', 'public'),
      orderBy('createdAt', 'desc'),
      limit(options.limitCount || 100)
    );

    const unsubscribe = onSnapshot(
      feedQuery,
      (snapshot) => {
        const updates: FeedUpdate[] = [];

        snapshot.docs.forEach((doc) => {
          const data = doc.data();
          updates.push(this.mapToFeedUpdate(doc.id, data));
        });

        // Sort by relevance score client-side
        updates.sort((a, b) => b.relevanceScore - a.relevanceScore);

        this.feedCache.set(listenerId, updates);
        options.onUpdate(updates);
      },
      (error) => {
        console.error('User feed listener error:', error);
        options.onError?.(error);
      }
    );

    this.listeners.set(listenerId, unsubscribe);
    return listenerId;
  }

  /**
   * Unsubscribe from feed updates
   */
  unsubscribe(listenerId: string): void {
    const unsubscribe = this.listeners.get(listenerId);
    if (unsubscribe) {
      unsubscribe();
      this.listeners.delete(listenerId);
      this.feedCache.delete(listenerId);
    }
  }

  /**
   * Unsubscribe from all feed listeners
   */
  unsubscribeAll(): void {
    this.listeners.forEach((unsubscribe) => unsubscribe());
    this.listeners.clear();
    this.feedCache.clear();
  }

  /**
   * Get cached feed for a listener
   */
  getCachedFeed(listenerId: string): FeedUpdate[] {
    return this.feedCache.get(listenerId) || [];
  }

  // Private helper methods

  private buildFeedQuery(options: FeedListenerOptions) {
    const baseCollection = collection(db, 'feed_items');
    const constraints: any[] = [
      where('campusId', '==', options.campusId),
      orderBy('createdAt', 'desc')
    ];

    // Add filter-specific constraints
    switch (options.filter) {
      case 'trending':
        constraints.push(where('isTrending', '==', true));
        break;
      case 'events':
        constraints.push(where('type', '==', 'event'));
        break;
      case 'rituals':
        constraints.push(where('type', '==', 'ritual_update'));
        break;
      case 'my_spaces':
        if (options.userId) {
          // This would need a different approach - query user's spaces first
          constraints.push(where('visibility', '==', 'public'));
        }
        break;
      default:
        constraints.push(where('visibility', '==', 'public'));
    }

    constraints.push(limit(options.limitCount || 50));

    return query(baseCollection, ...constraints);
  }

  private mapToFeedUpdate(id: string, data: DocumentData, spaceId?: string): FeedUpdate {
    return {
      id,
      type: data.type || 'post',
      content: {
        text: data.content?.text || data.text || '',
        mediaUrls: data.content?.mediaUrls || data.mediaUrls || [],
        authorId: data.authorId || data.userId || '',
        authorName: data.authorName || 'Unknown User',
        authorHandle: data.authorHandle || 'unknown',
        authorAvatar: data.authorAvatar
      },
      spaceId: spaceId || data.spaceId,
      spaceName: data.spaceName,
      engagement: {
        reactions: data.reactions || 0,
        comments: data.commentCount || 0,
        reposts: data.reposts || 0,
        requotes: data.requotes || 0
      },
      isPromoted: data.isPromoted || false,
      createdAt: data.createdAt?.toDate() || new Date(),
      relevanceScore: data.relevanceScore || 0
    };
  }

  private async fetchAndMapPromotedPost(promotedData: DocumentData): Promise<FeedUpdate | null> {
    try {
      // Fetch the actual post data from the space
      // Implementation would fetch the actual post
      // For now, return mapped promoted data
      return this.mapToFeedUpdate(promotedData.postId, promotedData);
    } catch (error) {
      console.error('Error fetching promoted post:', error);
      return null;
    }
  }

  private updateCache(listenerId: string, updates: FeedUpdate[]): void {
    const existing = this.feedCache.get(listenerId) || [];

    // Merge updates with existing, avoiding duplicates
    const merged = [...existing];

    updates.forEach((update) => {
      const index = merged.findIndex((item) => item.id === update.id);
      if (index >= 0) {
        merged[index] = update; // Update existing
      } else {
        merged.unshift(update); // Add new at beginning
      }
    });

    // Keep only the most recent items (limit to 200)
    const trimmed = merged.slice(0, 200);

    this.feedCache.set(listenerId, trimmed);
  }
}

