/**
 * Firebase Connection Repository Implementation
 * Handles social connections between profiles
 */

import {
  collection,
  doc,
  getDoc,
  getDocs,
  setDoc,
  updateDoc,
  deleteDoc,
  query,
  where,
  orderBy,
  limit as firestoreLimit,
  Timestamp
} from 'firebase/firestore';
import { db } from '@hive/firebase';
import { IConnectionRepository } from '../interfaces';
import { Result } from '../../../domain/shared/base/Result';
import { Connection } from '../../../domain/profile/aggregates/connection';
import { ConnectionId } from '../../../domain/profile/value-objects/connection-id.value';
import { ProfileId } from '../../../domain/profile/value-objects/profile-id.value';

export class FirebaseConnectionRepository implements IConnectionRepository {
  private readonly collectionName = 'connections';

  async findById(id: ConnectionId | any): Promise<Result<Connection>> {
    try {
      const connectionId = typeof id === 'string' ? id : id.value;
      const docRef = doc(db, this.collectionName, connectionId);
      const docSnap = await getDoc(docRef);

      if (!docSnap.exists()) {
        return Result.fail<Connection>('Connection not found');
      }

      const data = docSnap.data();
      return this.toDomain(connectionId, data);
    } catch (error) {
      return Result.fail<Connection>(`Failed to find connection: ${error}`);
    }
  }

  async findByProfiles(profileId1: string, profileId2: string): Promise<Result<Connection>> {
    try {
      // Connections use ordered profile IDs, so create consistent query
      const [orderedId1, orderedId2] = [profileId1, profileId2].sort();
      const q = query(
        collection(db, this.collectionName),
        where('profileId1', '==', orderedId1),
        where('profileId2', '==', orderedId2),
        firestoreLimit(1)
      );

      const snapshot = await getDocs(q);

      if (snapshot.empty) {
        return Result.fail<Connection>('Connection not found');
      }

      const doc = snapshot.docs[0];
      return this.toDomain(doc.id, doc.data());
    } catch (error) {
      return Result.fail<Connection>(`Failed to find connection: ${error}`);
    }
  }

  async findUserConnections(profileId: string, type?: string): Promise<Result<Connection[]>> {
    try {
      // Get connections where user is either profileId1 or profileId2
      const constraints: any[] = [
        where('isActive', '==', true),
        orderBy('createdAt', 'desc'),
        firestoreLimit(200)
      ];

      if (type) {
        constraints.unshift(where('type', '==', type));
      }

      // Query connections where user is profileId1
      const q1 = query(
        collection(db, this.collectionName),
        where('profileId1', '==', profileId),
        ...constraints
      );

      // Query connections where user is profileId2
      const q2 = query(
        collection(db, this.collectionName),
        where('profileId2', '==', profileId),
        ...constraints
      );

      const [snapshot1, snapshot2] = await Promise.all([
        getDocs(q1),
        getDocs(q2)
      ]);

      const connections: Connection[] = [];
      const processedIds = new Set<string>();

      // Process connections where user is profileId1
      for (const doc of snapshot1.docs) {
        if (!processedIds.has(doc.id)) {
          const result = await this.toDomain(doc.id, doc.data());
          if (result.isSuccess) {
            connections.push(result.getValue());
            processedIds.add(doc.id);
          }
        }
      }

      // Process connections where user is profileId2
      for (const doc of snapshot2.docs) {
        if (!processedIds.has(doc.id)) {
          const result = await this.toDomain(doc.id, doc.data());
          if (result.isSuccess) {
            connections.push(result.getValue());
            processedIds.add(doc.id);
          }
        }
      }

      return Result.ok<Connection[]>(connections);
    } catch (error) {
      return Result.fail<Connection[]>(`Failed to find connections: ${error}`);
    }
  }

  async getConnectionCount(profileId: string, type: string): Promise<number> {
    try {
      const constraints = [
        where('isActive', '==', true),
        where('type', '==', type)
      ];

      // Count connections where user is profileId1
      const q1 = query(
        collection(db, this.collectionName),
        where('profileId1', '==', profileId),
        ...constraints
      );

      // Count connections where user is profileId2
      const q2 = query(
        collection(db, this.collectionName),
        where('profileId2', '==', profileId),
        ...constraints
      );

      const [snapshot1, snapshot2] = await Promise.all([
        getDocs(q1),
        getDocs(q2)
      ]);

      // Use Set to avoid counting duplicates
      const connectionIds = new Set<string>();
      snapshot1.docs.forEach(doc => connectionIds.add(doc.id));
      snapshot2.docs.forEach(doc => connectionIds.add(doc.id));

      return connectionIds.size;
    } catch (error) {
      console.error('Failed to count connections:', error);
      return 0;
    }
  }

  async save(connection: Connection): Promise<Result<void>> {
    try {
      const data = this.toPersistence(connection);
      const docRef = doc(db, this.collectionName, connection.connectionId.value);

      if (connection.createdAt) {
        // Update existing
        await updateDoc(docRef, {
          ...data,
          updatedAt: Timestamp.now()
        });
      } else {
        // Create new
        await setDoc(docRef, {
          ...data,
          createdAt: Timestamp.now(),
          updatedAt: Timestamp.now()
        });
      }

      return Result.ok<void>();
    } catch (error) {
      return Result.fail<void>(`Failed to save connection: ${error}`);
    }
  }

  async delete(id: ConnectionId | any): Promise<Result<void>> {
    try {
      const connectionId = typeof id === 'string' ? id : id.value;
      const docRef = doc(db, this.collectionName, connectionId);
      await deleteDoc(docRef);
      return Result.ok<void>();
    } catch (error) {
      return Result.fail<void>(`Failed to delete connection: ${error}`);
    }
  }

  // Helper methods for domain mapping
  private async toDomain(id: string, data: any): Promise<Result<Connection>> {
    try {
      // Create profile IDs
      const profileId1 = ProfileId.create(data.profileId1);
      if (profileId1.isFailure) {
        return Result.fail<Connection>(profileId1.error!);
      }

      const profileId2 = ProfileId.create(data.profileId2);
      if (profileId2.isFailure) {
        return Result.fail<Connection>(profileId2.error!);
      }

      const requestedBy = ProfileId.create(data.requestedBy);
      if (requestedBy.isFailure) {
        return Result.fail<Connection>(requestedBy.error!);
      }

      // Create connection
      const connectionResult = Connection.create({
        profileId1: profileId1.getValue(),
        profileId2: profileId2.getValue(),
        type: data.type,
        source: data.source,
        requestedBy: requestedBy.getValue()
      }, id);

      if (connectionResult.isFailure) {
        return Result.fail<Connection>(connectionResult.error!);
      }

      return Result.ok<Connection>(connectionResult.getValue());
    } catch (error) {
      return Result.fail<Connection>(`Failed to map to domain: ${error}`);
    }
  }

  private toPersistence(connection: Connection): any {
    return {
      connectionId: connection.connectionId.value,
      profileId1: connection.profileId1.value,
      profileId2: connection.profileId2.value,
      type: connection.type,
      source: connection.source,
      requestedBy: connection.requestedBy.value,
      acceptedBy: connection.acceptedBy?.value,
      isActive: connection.isActive,
      acceptedAt: connection.acceptedAt ? Timestamp.fromDate(connection.acceptedAt) : null,
      rejectedAt: connection.rejectedAt ? Timestamp.fromDate(connection.rejectedAt) : null,
      blockedAt: connection.blockedAt ? Timestamp.fromDate(connection.blockedAt) : null,
      mutualSpaces: connection.mutualSpaces,
      interactionCount: connection.interactionCount,
      metadata: connection.metadata || {}
    };
  }
}

/**
 * Firebase Feed Repository Implementation
 * Handles feed persistence and retrieval
 */

import {
  collection,
  doc,
  getDoc,
  getDocs,
  setDoc,
  updateDoc,
  deleteDoc,
  query,
  where,
  orderBy,
  limit as firestoreLimit,
  Timestamp,
  addDoc,
  onSnapshot,
  writeBatch
} from 'firebase/firestore';
import { db } from '@hive/firebase';
import { IFeedRepository } from '../interfaces';
import { Result } from '../../../domain/shared/base/Result';
import { EnhancedFeed } from '../../../domain/feed/enhanced-feed';
import { FeedId } from '../../../domain/feed/value-objects';
import { ProfileId } from '../../../domain/profile/value-objects/profile-id.value';

export class FirebaseFeedRepository implements IFeedRepository {
  private readonly collectionName = 'feeds';
  private readonly feedItemsCollection = 'feed_items';

  async findById(id: FeedId | any): Promise<Result<EnhancedFeed>> {
    try {
      const feedId = typeof id === 'string' ? id : id.id;
      const docRef = doc(db, this.collectionName, feedId);
      const docSnap = await getDoc(docRef);

      if (!docSnap.exists()) {
        return Result.fail<EnhancedFeed>('Feed not found');
      }

      const data = docSnap.data();
      return this.toDomain(feedId, data);
    } catch (error) {
      return Result.fail<EnhancedFeed>(`Failed to find feed: ${error}`);
    }
  }

  async findByUserId(userId: ProfileId | any): Promise<Result<EnhancedFeed>> {
    try {
      const profileId = typeof userId === 'string' ? userId : userId.id;

      // Try to find existing feed
      const q = query(
        collection(db, this.collectionName),
        where('userId', '==', profileId),
        firestoreLimit(1)
      );
      const snapshot = await getDocs(q);

      if (snapshot.empty) {
        // Create new feed if doesn't exist
        const newFeedResult = await this.createFeedForUser(profileId);
        return newFeedResult;
      }

      const doc = snapshot.docs[0];
      const feedResult = await this.toDomain(doc.id, doc.data());

      if (feedResult.isSuccess) {
        // Load feed items
        await this.loadFeedItems(feedResult.getValue());
      }

      return feedResult;
    } catch (error) {
      return Result.fail<EnhancedFeed>(`Failed to find user feed: ${error}`);
    }
  }

  async findByCampus(campusId: string): Promise<Result<EnhancedFeed[]>> {
    try {
      const q = query(
        collection(db, this.collectionName),
        where('campusId', '==', campusId),
        orderBy('updatedAt', 'desc'),
        firestoreLimit(100)
      );
      const snapshot = await getDocs(q);

      const feeds: EnhancedFeed[] = [];
      for (const doc of snapshot.docs) {
        const result = await this.toDomain(doc.id, doc.data());
        if (result.isSuccess) {
          feeds.push(result.getValue());
        }
      }

      return Result.ok<EnhancedFeed[]>(feeds);
    } catch (error) {
      return Result.fail<EnhancedFeed[]>(`Failed to find campus feeds: ${error}`);
    }
  }

  async addFeedItem(feedId: string, item: any): Promise<Result<void>> {
    try {
      // Add item to feed_items collection
      const itemData = {
        feedId,
        type: item.type || 'post',
        contentType: item.contentType || 'post',
        content: {
          text: item.content?.text || '',
          mediaUrls: item.content?.mediaUrls || [],
          authorId: item.content?.authorId || item.authorId,
          authorName: item.content?.authorName,
          authorHandle: item.content?.authorHandle
        },
        source: {
          type: item.source?.type || 'space',
          spaceId: item.source?.spaceId || item.spaceId,
          eventId: item.source?.eventId,
          ritualId: item.source?.ritualId
        },
        campusId: item.campusId,
        visibility: item.visibility || 'public',
        isPromoted: item.isPromoted || false,
        promotionData: item.promotionData,
        relevanceScore: item.relevanceScore || 0,
        interactions: item.interactions || [],
        createdAt: Timestamp.now(),
        updatedAt: Timestamp.now()
      };

      await addDoc(collection(db, this.feedItemsCollection), itemData);

      // Update feed's lastUpdated timestamp
      const feedRef = doc(db, this.collectionName, feedId);
      await updateDoc(feedRef, {
        lastRefreshed: Timestamp.now(),
        updatedAt: Timestamp.now()
      });

      return Result.ok<void>();
    } catch (error) {
      return Result.fail<void>(`Failed to add feed item: ${error}`);
    }
  }

  async removeFeedItem(feedId: string, itemId: string): Promise<Result<void>> {
    try {
      // Delete from feed_items collection
      const q = query(
        collection(db, this.feedItemsCollection),
        where('feedId', '==', feedId),
        where('__name__', '==', itemId),
        firestoreLimit(1)
      );
      const snapshot = await getDocs(q);

      if (!snapshot.empty) {
        await deleteDoc(snapshot.docs[0].ref);
      }

      // Update feed timestamp
      const feedRef = doc(db, this.collectionName, feedId);
      await updateDoc(feedRef, {
        updatedAt: Timestamp.now()
      });

      return Result.ok<void>();
    } catch (error) {
      return Result.fail<void>(`Failed to remove feed item: ${error}`);
    }
  }

  async save(feed: EnhancedFeed): Promise<Result<void>> {
    try {
      const data = this.toPersistence(feed);
      const docRef = doc(db, this.collectionName, feed.feedId.value);

      if (feed.lastUpdated && feed.lastUpdated.getTime() > 0) {
        // Update existing
        await updateDoc(docRef, {
          ...data,
          updatedAt: Timestamp.now()
        });
      } else {
        // Create new
        await setDoc(docRef, {
          ...data,
          createdAt: Timestamp.now(),
          updatedAt: Timestamp.now()
        });
      }

      // Save feed items separately
      await this.saveFeedItems(feed);

      return Result.ok<void>();
    } catch (error) {
      return Result.fail<void>(`Failed to save feed: ${error}`);
    }
  }

  async delete(id: FeedId | any): Promise<Result<void>> {
    try {
      const feedId = typeof id === 'string' ? id : id.id;

      // Delete feed items first
      const itemsQuery = query(
        collection(db, this.feedItemsCollection),
        where('feedId', '==', feedId)
      );
      const itemsSnapshot = await getDocs(itemsQuery);

      const deletePromises = itemsSnapshot.docs.map(doc => deleteDoc(doc.ref));
      await Promise.all(deletePromises);

      // Delete feed document
      const docRef = doc(db, this.collectionName, feedId);
      await deleteDoc(docRef);

      return Result.ok<void>();
    } catch (error) {
      return Result.fail<void>(`Failed to delete feed: ${error}`);
    }
  }

  // Helper methods
  private async createFeedForUser(userId: string): Promise<Result<EnhancedFeed>> {
    try {
      // Get user's campus from profile
      const profileDoc = await getDoc(doc(db, 'users', userId));
      if (!profileDoc.exists()) {
        return Result.fail<EnhancedFeed>('User profile not found');
      }

      const userData = profileDoc.data();
      const campusId = userData.campusId || 'ub-buffalo';

      // Create new feed
      const feedResult = EnhancedFeed.createWithCampus(
        { id: userId, equals: () => false } as any,
        campusId
      );

      if (feedResult.isFailure) {
        return Result.fail<EnhancedFeed>(feedResult.error!);
      }

      const feed = feedResult.getValue();

      // Save to database
      await this.save(feed);

      return Result.ok<EnhancedFeed>(feed);
    } catch (error) {
      return Result.fail<EnhancedFeed>(`Failed to create feed: ${error}`);
    }
  }

  private async loadFeedItems(feed: EnhancedFeed): Promise<void> {
    try {
      // Load recent feed items
      const q = query(
        collection(db, this.feedItemsCollection),
        where('feedId', '==', feed.feedId.value),
        orderBy('createdAt', 'desc'),
        firestoreLimit(100)
      );
      const snapshot = await getDocs(q);

      snapshot.docs.forEach(() => {
        // Add items to feed (would need to implement addItem method on EnhancedFeed)
        // For now, items are loaded separately
      });
    } catch (error) {
      console.error('Failed to load feed items:', error);
    }
  }

  private async saveFeedItems(feed: EnhancedFeed): Promise<void> {
    try {
      const items = feed.items;

      if (items.length === 0) {
        return;
      }

      const batch = writeBatch(db);

      items.forEach(item => {
        const itemRef = doc(
          db,
          this.feedItemsCollection,
          `${feed.feedId.value}_${item.itemId.value}`
        );

        batch.set(itemRef, {
          feedId: feed.feedId.value,
          itemId: item.itemId.value,
          content: item.content,
          source: item.source,
          relevanceScore: item.relevanceScore,
          interactions: item.interactions.map(interaction => ({
            type: interaction.type,
            userId: interaction.userId.value,
            timestamp: Timestamp.fromDate(interaction.timestamp)
          })),
          engagementCount: item.interactions.length,
          createdAt: Timestamp.fromDate(item.createdAt),
          isVisible: item.isVisible,
          isTrending: item.isTrending,
          isPinned: item.isPinned,
          expiresAt: item.expiresAt
            ? Timestamp.fromDate(item.expiresAt)
            : undefined,
          updatedAt: Timestamp.now()
        });
      });

      await batch.commit();
    } catch (error) {
      console.error('Failed to save feed items:', error);
    }
  }

  private async toDomain(id: string, data: any): Promise<Result<EnhancedFeed>> {
    try {
      // Create feed
      const feedResult = EnhancedFeed.createWithCampus(
        { id: data.userId, equals: () => false } as any,
        data.campusId || 'ub-buffalo'
      );

      if (feedResult.isFailure) {
        return Result.fail<EnhancedFeed>(feedResult.error!);
      }

      const feed = feedResult.getValue();

      // Note: EnhancedFeed doesn't have direct setters for these properties
      // They are managed internally through the props
      // The feed's lastUpdated property is set during creation and updates

      // Apply filter if exists
      if (data.currentFilter) {
        feed.applyFilter({
          type: data.currentFilter as any
        });
      }

      return Result.ok<EnhancedFeed>(feed);
    } catch (error) {
      return Result.fail<EnhancedFeed>(`Failed to map to domain: ${error}`);
    }
  }

  private toPersistence(feed: EnhancedFeed): any {
    return {
      userId: feed.userId.value,
      campusId: feed.campusId.value,
      itemCount: feed.itemCount,
      lastUpdated: Timestamp.fromDate(feed.lastUpdated)
    };
  }

  // Additional methods required by interface
  async saveFeed(feed: EnhancedFeed): Promise<Result<void>> {
    // Delegate to existing save method
    return this.save(feed);
  }

  async getFeedContent(
    userId: string,
    userSpaces: any[],
    userConnections: any[],
    limitCount: number = 50
  ): Promise<Result<any[]>> {
    try {
      // Get posts from user's spaces
      const spaceIds = userSpaces.map(s => typeof s === 'object' ? s.id : s);
      const connectionIds = userConnections.map(c => typeof c === 'object' ? c.id : c);

      const allPostIds = new Set<string>();
      const feedItems: any[] = [];

      // Get posts from spaces
      if (spaceIds.length > 0) {
        const spacePosts = query(
          collection(db, 'posts'),
          where('spaceId', 'in', spaceIds.slice(0, 10)), // Firestore limit
          orderBy('createdAt', 'desc'),
          firestoreLimit(limitCount)
        );
        const spaceSnapshot = await getDocs(spacePosts);
        spaceSnapshot.docs.forEach(doc => {
          if (!allPostIds.has(doc.id)) {
            allPostIds.add(doc.id);
            feedItems.push({ id: doc.id, ...doc.data(), source: 'space' });
          }
        });
      }

      // Get posts from connections
      if (connectionIds.length > 0) {
        const connectionPosts = query(
          collection(db, 'posts'),
          where('authorId', 'in', connectionIds.slice(0, 10)),
          orderBy('createdAt', 'desc'),
          firestoreLimit(limitCount)
        );
        const connectionSnapshot = await getDocs(connectionPosts);
        connectionSnapshot.docs.forEach(doc => {
          if (!allPostIds.has(doc.id)) {
            allPostIds.add(doc.id);
            feedItems.push({ id: doc.id, ...doc.data(), source: 'connection' });
          }
        });
      }

      // Sort by timestamp
      feedItems.sort((a, b) => {
        const aTime = a.createdAt?.toMillis?.() || 0;
        const bTime = b.createdAt?.toMillis?.() || 0;
        return bTime - aTime;
      });

      return Result.ok<any[]>(feedItems.slice(0, limitCount));
    } catch (error) {
      return Result.fail<any[]>(`Failed to get feed content: ${error}`);
    }
  }

  async getTrendingContent(campusId: string, limitCount: number = 20): Promise<Result<any[]>> {
    try {
      const q = query(
        collection(db, 'posts'),
        where('campusId', '==', campusId),
        where('trendingScore', '>', 0),
        orderBy('trendingScore', 'desc'),
        orderBy('createdAt', 'desc'),
        firestoreLimit(limitCount)
      );
      const snapshot = await getDocs(q);

      const items = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
        type: 'trending'
      }));

      return Result.ok<any[]>(items);
    } catch (error) {
      return Result.fail<any[]>(`Failed to get trending content: ${error}`);
    }
  }

  async getEventContent(campusId: string, limitCount: number = 20): Promise<Result<any[]>> {
    try {
      const now = Timestamp.now();
      const q = query(
        collection(db, 'events'),
        where('campusId', '==', campusId),
        where('startTime', '>=', now),
        orderBy('startTime', 'asc'),
        firestoreLimit(limitCount)
      );
      const snapshot = await getDocs(q);

      const items = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
        type: 'event'
      }));

      return Result.ok<any[]>(items);
    } catch (error) {
      return Result.fail<any[]>(`Failed to get event content: ${error}`);
    }
  }

  async getRitualContent(campusId: string, limitCount: number = 20): Promise<Result<any[]>> {
    try {
      const q = query(
        collection(db, 'rituals'),
        where('campusId', '==', campusId),
        where('isActive', '==', true),
        orderBy('nextOccurrence', 'asc'),
        firestoreLimit(limitCount)
      );
      const snapshot = await getDocs(q);

      const items = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
        type: 'ritual'
      }));

      return Result.ok<any[]>(items);
    } catch (error) {
      return Result.fail<any[]>(`Failed to get ritual content: ${error}`);
    }
  }

  async recordInteraction(
    userId: string,
    itemId: string,
    interactionType: string,
    metadata?: Record<string, unknown>
  ): Promise<Result<void>> {
    try {
      const interactionData = {
        userId,
        itemId,
        type: interactionType,
        metadata: metadata || {},
        createdAt: Timestamp.now()
      };

      await addDoc(collection(db, 'interactions'), interactionData);
      return Result.ok<void>();
    } catch (error) {
      return Result.fail<void>(`Failed to record interaction: ${error}`);
    }
  }

  subscribeToFeed(userId: string, callback: (items: any[]) => void): () => void {
    // Real-time subscription to feed updates
    const q = query(
      collection(db, this.feedItemsCollection),
      where('userId', '==', userId),
      orderBy('createdAt', 'desc'),
      firestoreLimit(100)
    );

    const unsubscribe = onSnapshot(q, (snapshot) => {
      const items = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      callback(items);
    });

    return unsubscribe;
  }
}

/**
 * Firebase Profile Repository Implementation
 * Handles profile persistence with Firebase
 */

import {
  collection,
  doc,
  getDoc,
  getDocs,
  setDoc,
  updateDoc,
  deleteDoc,
  query,
  where,
  orderBy,
  limit as firestoreLimit,
  Timestamp
} from 'firebase/firestore';
import { db } from '@hive/firebase';
import { IProfileRepository } from '../interfaces';
import { Result } from '../../../domain/shared/base/Result';
import { Profile } from '../../../domain/profile/aggregates/profile.aggregate';
import { ProfileId } from '../../../domain/profile/value-objects/profile-id.value';
import { UBEmail } from '../../../domain/profile/value-objects';
import { ProfileHandle } from '../../../domain/profile/value-objects/profile-handle.value';
import { CampusId } from '../../../domain/profile/value-objects/campus-id.value';
import { UserType } from '../../../domain/profile/value-objects/user-type.value';
import { ProfilePrivacy } from '../../../domain/profile/value-objects/profile-privacy.value';
import { isFeatureEnabled } from '../../feature-flags';

export class FirebaseProfileRepository implements IProfileRepository {
  private readonly collectionName = 'users';
  private readonly connectionsCollection = 'connections';

  async findById(id: ProfileId | any): Promise<Result<Profile>> {
    try {
      const profileId = typeof id === 'string' ? id : id.id;
      const docRef = doc(db, this.collectionName, profileId);
      const docSnap = await getDoc(docRef);

      if (!docSnap.exists()) {
        return Result.fail<Profile>('Profile not found');
      }

      const data = docSnap.data();
      return this.toDomain(profileId, data);
    } catch (error) {
      return Result.fail<Profile>(`Failed to find profile: ${error}`);
    }
  }

  async findByEmail(email: string): Promise<Result<Profile>> {
    try {
      const q = query(
        collection(db, this.collectionName),
        where('email', '==', email),
        firestoreLimit(1)
      );
      const snapshot = await getDocs(q);

      if (snapshot.empty) {
        return Result.fail<Profile>('Profile not found');
      }

      const doc = snapshot.docs[0];
      return this.toDomain(doc.id, doc.data());
    } catch (error) {
      return Result.fail<Profile>(`Failed to find profile: ${error}`);
    }
  }

  async findByHandle(handle: string): Promise<Result<Profile>> {
    try {
      const q = query(
        collection(db, this.collectionName),
        where('handle', '==', handle.toLowerCase()),
        firestoreLimit(1)
      );
      const snapshot = await getDocs(q);

      if (snapshot.empty) {
        return Result.fail<Profile>('Profile not found');
      }

      const doc = snapshot.docs[0];
      return this.toDomain(doc.id, doc.data());
    } catch (error) {
      return Result.fail<Profile>(`Failed to find profile: ${error}`);
    }
  }

  async findByCampus(campusId: string, limitCount: number = 50): Promise<Result<Profile[]>> {
    try {
      const q = query(
        collection(db, this.collectionName),
        where('campusId', '==', campusId),
        where('isActive', '==', true),
        orderBy('createdAt', 'desc'),
        firestoreLimit(limitCount)
      );
      const snapshot = await getDocs(q);

      const profiles: Profile[] = [];
      for (const doc of snapshot.docs) {
        const result = await this.toDomain(doc.id, doc.data());
        if (result.isSuccess) {
          profiles.push(result.getValue());
        }
      }

      return Result.ok<Profile[]>(profiles);
    } catch (error) {
      return Result.fail<Profile[]>(`Failed to find profiles: ${error}`);
    }
  }

  async exists(handle: string): Promise<boolean> {
    try {
      const q = query(
        collection(db, this.collectionName),
        where('handle', '==', handle.toLowerCase()),
        firestoreLimit(1)
      );
      const snapshot = await getDocs(q);
      return !snapshot.empty;
    } catch {
      return false;
    }
  }

  async searchByName(searchQuery: string, campusId: string): Promise<Result<Profile[]>> {
    try {
      // Firebase doesn't support full-text search natively
      // For MVP, we'll do a simple prefix search on firstName and lastName
      const q = query(
        collection(db, this.collectionName),
        where('campusId', '==', campusId),
        where('isActive', '==', true),
        orderBy('firstName'),
        firestoreLimit(20)
      );

      const snapshot = await getDocs(q);
      const profiles: Profile[] = [];
      const searchLower = searchQuery.toLowerCase();

      for (const doc of snapshot.docs) {
        const data = doc.data();
        const fullName = `${data.firstName} ${data.lastName}`.toLowerCase();
        const handle = data.handle?.toLowerCase() || '';

        if (fullName.includes(searchLower) || handle.includes(searchLower)) {
          const result = await this.toDomain(doc.id, data);
          if (result.isSuccess) {
            profiles.push(result.getValue());
          }
        }
      }

      return Result.ok<Profile[]>(profiles);
    } catch (error) {
      return Result.fail<Profile[]>(`Search failed: ${error}`);
    }
  }

  async save(profile: Profile): Promise<Result<void>> {
    try {
      const data = this.toPersistence(profile);
      const docRef = doc(db, this.collectionName, profile.id);

      if (profile.createdAt) {
        // Update existing
        await updateDoc(docRef, {
          ...data,
          updatedAt: Timestamp.now()
        });
      } else {
        // Create new
        await setDoc(docRef, {
          ...data,
          createdAt: Timestamp.now(),
          updatedAt: Timestamp.now()
        });
      }

      return Result.ok<void>();
    } catch (error) {
      return Result.fail<void>(`Failed to save profile: ${error}`);
    }
  }

  async delete(id: ProfileId | any): Promise<Result<void>> {
    try {
      const profileId = typeof id === 'string' ? id : id.id;
      const docRef = doc(db, this.collectionName, profileId);
      await deleteDoc(docRef);
      return Result.ok<void>();
    } catch (error) {
      return Result.fail<void>(`Failed to delete profile: ${error}`);
    }
  }

  // Helper methods for domain mapping
  private async toDomain(id: string, data: any): Promise<Result<Profile>> {
    try {
      // Handle legacy profiles without enhanced features
      if (!isFeatureEnabled('PROFILE_CAMPUS_ISOLATION') || !data.campusId) {
        data.campusId = 'ub-buffalo'; // Default for legacy profiles
      }

      const profileData = {
        id,
        email: data.email,
        handle: data.handle,
        firstName: data.firstName,
        lastName: data.lastName,
        bio: data.bio,
        major: data.major,
        graduationYear: data.graduationYear,
        interests: data.interests || [],
        photos: data.photos || [],
        isActive: data.isActive !== false,
        isVerified: data.isVerified || false,
        userType: data.userType || 'student',
        campusId: data.campusId,
        privacy: data.privacy || {},
        badges: data.badges || [],
        blockedUsers: data.blockedUsers || [],
        lastSeen: data.lastSeen?.toDate(),
        createdAt: data.createdAt?.toDate(),
        updatedAt: data.updatedAt?.toDate(),
        onboardingCompleted: data.onboardingCompleted || false,
        activityScore: data.activityScore || 0,
        followerCount: data.followerCount || 0,
        followingCount: data.followingCount || 0,
        connectionCount: data.connectionCount || 0
      };

      // Create value objects
      const emailResult = UBEmail.create(profileData.email);
      if (emailResult.isFailure) {
        return Result.fail<Profile>(emailResult.error!);
      }

      const handleResult = ProfileHandle.create(profileData.handle);
      if (handleResult.isFailure) {
        return Result.fail<Profile>(handleResult.error!);
      }

      const campusIdResult = CampusId.create(profileData.campusId);
      if (campusIdResult.isFailure) {
        return Result.fail<Profile>(campusIdResult.error!);
      }

      const userTypeResult = UserType.create(profileData.userType);
      if (userTypeResult.isFailure) {
        return Result.fail<Profile>(userTypeResult.error!);
      }

      // Create profile
      const profile = Profile.create({
        profileId: ProfileId.create(id).getValue(),
        email: emailResult.getValue(),
        handle: handleResult.getValue(),
        userType: userTypeResult.getValue(),
        campusId: campusIdResult.getValue(),
        personalInfo: {
          firstName: profileData.firstName,
          lastName: profileData.lastName,
          bio: profileData.bio,
          major: profileData.major,
          graduationYear: profileData.graduationYear,
          profilePhoto: (profileData as any).profilePhoto || profileData.photos?.[0],
          coverPhoto: (profileData as any).coverPhoto || profileData.photos?.[1]
        },
        socialInfo: {
          interests: profileData.interests || [],
          clubs: (profileData as any).clubs || [],
          sports: (profileData as any).sports || []
        }
      });

      if (profile.isFailure) {
        return Result.fail<Profile>(profile.error!);
      }

      const enhancedProfile = profile.getValue();

      // Set additional properties using setter methods where needed
      if (profileData.isVerified !== undefined) {
        enhancedProfile.setIsVerified(profileData.isVerified);
      }
      if (profileData.activityScore !== undefined) {
        enhancedProfile.setActivityScore(profileData.activityScore);
      }

      // Set privacy settings
      if (profileData.privacy && isFeatureEnabled('PROFILE_PRIVACY_CONTROLS')) {
        const privacyResult = ProfilePrivacy.create(profileData.privacy);
        if (privacyResult.isSuccess) {
          enhancedProfile.setPrivacy(privacyResult.getValue());
        }
      }

      return Result.ok<Profile>(enhancedProfile);
    } catch (error) {
      return Result.fail<Profile>(`Failed to map to domain: ${error}`);
    }
  }

  private toPersistence(profile: Profile): any {
    return {
      email: profile.email.value,
      handle: profile.handle.value.toLowerCase(),
      firstName: profile.firstName,
      lastName: profile.lastName,
      bio: profile.bio,
      major: profile.major,
      graduationYear: profile.graduationYear,
      interests: profile.interests,
      photos: profile.photos,
      isActive: profile.isActive,
      isVerified: profile.isVerified,
      userType: profile.userType.value,
      campusId: profile.campusId.id,
      privacy: profile.privacy ? {
        profileVisibility: profile.privacy.profileVisibility,
        showEmail: profile.privacy.showEmail,
        showConnections: profile.privacy.showConnections,
        allowConnectionRequests: profile.privacy.allowConnectionRequests
      } : {},
      badges: profile.badges,
      blockedUsers: profile.blockedUsers,
      lastSeen: profile.lastSeen ? Timestamp.fromDate(profile.lastSeen) : null,
      onboardingCompleted: profile.onboardingCompleted,
      activityScore: profile.activityScore,
      followerCount: profile.followerCount,
      followingCount: profile.followingCount,
      connectionCount: profile.connectionCount
    };
  }

  // Additional methods required by interface
  async findOnboardedProfiles(maxCount: number = 100): Promise<Result<Profile[]>> {
    try {
      const q = query(
        collection(db, this.collectionName),
        where('onboardingCompleted', '==', true),
        where('isActive', '==', true),
        orderBy('createdAt', 'desc'),
        firestoreLimit(maxCount)
      );
      const snapshot = await getDocs(q);

      const profiles: Profile[] = [];
      for (const doc of snapshot.docs) {
        const result = await this.toDomain(doc.id, doc.data());
        if (result.isSuccess) {
          profiles.push(result.getValue());
        }
      }

      return Result.ok<Profile[]>(profiles);
    } catch (error) {
      return Result.fail<Profile[]>(`Failed to find onboarded profiles: ${error}`);
    }
  }

  async findByInterest(interest: string, limitCount: number = 50): Promise<Result<Profile[]>> {
    try {
      const q = query(
        collection(db, this.collectionName),
        where('interests', 'array-contains', interest),
        where('isActive', '==', true),
        orderBy('activityScore', 'desc'),
        firestoreLimit(limitCount)
      );
      const snapshot = await getDocs(q);

      const profiles: Profile[] = [];
      for (const doc of snapshot.docs) {
        const result = await this.toDomain(doc.id, doc.data());
        if (result.isSuccess) {
          profiles.push(result.getValue());
        }
      }

      return Result.ok<Profile[]>(profiles);
    } catch (error) {
      return Result.fail<Profile[]>(`Failed to find profiles by interest: ${error}`);
    }
  }

  async findByMajor(major: string, limitCount: number = 50): Promise<Result<Profile[]>> {
    try {
      const q = query(
        collection(db, this.collectionName),
        where('major', '==', major),
        where('isActive', '==', true),
        orderBy('createdAt', 'desc'),
        firestoreLimit(limitCount)
      );
      const snapshot = await getDocs(q);

      const profiles: Profile[] = [];
      for (const doc of snapshot.docs) {
        const result = await this.toDomain(doc.id, doc.data());
        if (result.isSuccess) {
          profiles.push(result.getValue());
        }
      }

      return Result.ok<Profile[]>(profiles);
    } catch (error) {
      return Result.fail<Profile[]>(`Failed to find profiles by major: ${error}`);
    }
  }

  async findConnectionsOf(profileId: string): Promise<Result<Profile[]>> {
    try {
      // Find all connections for this profile
      const q1 = query(
        collection(db, this.connectionsCollection),
        where('fromProfileId', '==', profileId),
        where('status', '==', 'accepted')
      );

      const q2 = query(
        collection(db, this.connectionsCollection),
        where('toProfileId', '==', profileId),
        where('status', '==', 'accepted')
      );

      const [snapshot1, snapshot2] = await Promise.all([getDocs(q1), getDocs(q2)]);

      // Collect all connected profile IDs
      const connectedProfileIds = new Set<string>();
      snapshot1.docs.forEach(doc => {
        const data = doc.data();
        connectedProfileIds.add(data.toProfileId);
      });
      snapshot2.docs.forEach(doc => {
        const data = doc.data();
        connectedProfileIds.add(data.fromProfileId);
      });

      // Fetch all connected profiles
      const profiles: Profile[] = [];
      for (const connectedId of Array.from(connectedProfileIds)) {
        const result = await this.findById(connectedId);
        if (result.isSuccess) {
          profiles.push(result.getValue());
        }
      }

      return Result.ok<Profile[]>(profiles);
    } catch (error) {
      return Result.fail<Profile[]>(`Failed to find connections: ${error}`);
    }
  }

  async getTotalCampusUsers(campusId: string): Promise<Result<number>> {
    try {
      const q = query(
        collection(db, this.collectionName),
        where('campusId', '==', campusId),
        where('isActive', '==', true)
      );
      const snapshot = await getDocs(q);
      return Result.ok<number>(snapshot.size);
    } catch (error) {
      return Result.fail<number>(`Failed to count campus users: ${error}`);
    }
  }
}

/**
