  getDocs,
  setDoc,
  updateDoc,
  deleteDoc,
  query,
  where,
  orderBy,
  limit as firestoreLimit,
  Timestamp,
  increment
} from 'firebase/firestore';
import { db } from '@hive/firebase';
import { IToolRepository } from '../interfaces';
import { Result } from '../../../domain/shared/base/Result';
