      const q = query(
        collection(db, this.collectionName),
        where('campusId', '==', campusId),
        where('status', '==', 'published'),
        where('permissions.canFork', '==', true),
        orderBy('analytics.forks', 'desc'),
        firestoreLimit(30)
      );
      const snapshot = await getDocs(q);

      const tools: Tool[] = [];
