/**
 * SPEC-COMPLIANT PRESENCE SERVICE
 *
 * Per SPEC.md:
 * - Real-time online presence tracking
 * - Ghost mode support (appear offline while online)
 * - Campus-isolated presence data
 * - Automatic cleanup on disconnect
 *
 * Behavioral Design: Creates FOMO when you see someone online you want to connect with
 */

export type PresenceStatus = 'online' | 'away' | 'ghost' | 'offline';

export interface PresenceData {
  uid: string;
  campusId: string;
  status: PresenceStatus;
  lastSeen: Timestamp;
  isGhostMode: boolean;
  deviceInfo?: {
    platform: 'web' | 'mobile';
    browser?: string;
  };
}

class PresenceService {
  private presenceRef: any = null;
  private currentUser: User | null = null;
  private isGhostMode: boolean = false;
  private activityTimer: NodeJS.Timeout | null = null;
  private unsubscribe: (() => void) | null = null;

  /**
   * Initialize presence for a user
   */
  async initializePresence(user: User, campusId: string = 'ub-buffalo') {
    if (!user) return;

    this.currentUser = user;
    const presenceDocRef = doc(db, 'presence', user.uid);

    // Set initial online status
    const presenceData: PresenceData = {
      uid: user.uid,
      campusId,
      status: this.isGhostMode ? 'ghost' : 'online',
      lastSeen: serverTimestamp() as Timestamp,
      isGhostMode: this.isGhostMode,
      deviceInfo: {
        platform: 'web',
        browser: navigator.userAgent.includes('Chrome') ? 'Chrome' :
                navigator.userAgent.includes('Safari') ? 'Safari' :
                navigator.userAgent.includes('Firefox') ? 'Firefox' : 'Other'
      }
    };

    try {
      // Set online status
      await setDoc(presenceDocRef, presenceData, { merge: true });

      // Note: onDisconnect is only available with Realtime Database, not Firestore
      // User cleanup will need to be handled differently (e.g., periodic cleanup or on logout)

      // Start activity monitoring
      this.startActivityMonitoring();

      // Store reference for cleanup
      this.presenceRef = presenceDocRef;

      console.log(' Presence initialized for user:', user.uid);
    } catch (error) {
      console.error(' Error initializing presence:', error);
    }
  }

  /**
   * Toggle ghost mode
   */
  async toggleGhostMode(enabled: boolean) {
    this.isGhostMode = enabled;

    if (!this.currentUser || !this.presenceRef) return;

    const newStatus: PresenceStatus = enabled ? 'ghost' : 'online';

    try {
      await setDoc(this.presenceRef, {
        status: newStatus,
        isGhostMode: enabled,
        lastSeen: serverTimestamp()
      }, { merge: true });

      console.log(` Ghost mode ${enabled ? 'enabled' : 'disabled'}`);
    } catch (error) {
      console.error(' Error toggling ghost mode:', error);
    }
  }

  /**
   * Update user status (online, away, ghost)
   */
  async updateStatus(status: PresenceStatus) {
    if (!this.currentUser || !this.presenceRef) return;

    // If ghost mode is on, always show as ghost/offline
    const effectiveStatus = this.isGhostMode ? 'ghost' : status;

    try {
      await setDoc(this.presenceRef, {
        status: effectiveStatus,
        lastSeen: serverTimestamp()
      }, { merge: true });

      console.log(` Status updated to: ${effectiveStatus}`);
    } catch (error) {
      console.error(' Error updating status:', error);
    }
  }

  /**
   * Monitor user activity and set to away after inactivity
   */
  private startActivityMonitoring() {
    const INACTIVE_THRESHOLD = 5 * 60 * 1000; // 5 minutes

    const resetActivityTimer = () => {
      // Clear existing timer
      if (this.activityTimer) {
        clearTimeout(this.activityTimer);
      }

      // Set status to online if not in ghost mode
      if (!this.isGhostMode) {
        this.updateStatus('online');
      }

      // Set new timer for away status
      this.activityTimer = setTimeout(() => {
        if (!this.isGhostMode) {
          this.updateStatus('away');
        }
      }, INACTIVE_THRESHOLD);
    };

    // Listen for user activity
    ['mousedown', 'mousemove', 'keypress', 'scroll', 'touchstart'].forEach(event => {
      document.addEventListener(event, resetActivityTimer, true);
    });

    // Start the timer
    resetActivityTimer();
  }

  /**
   * Subscribe to another user's presence
   */
  subscribeToUserPresence(
    uid: string,
    callback: (presence: PresenceData | null) => void
  ): () => void {
    const presenceDocRef = doc(db, 'presence', uid);

    const unsubscribe = onSnapshot(presenceDocRef, (snapshot) => {
      if (snapshot.exists()) {
        const data = snapshot.data() as PresenceData;

        // If user is in ghost mode, show as offline to others
        if (data.isGhostMode && uid !== this.currentUser?.uid) {
          callback({
            ...data,
            status: 'offline'
          });
        } else {
          callback(data);
        }
      } else {
        callback(null);
      }
    }, (error) => {
      console.error(' Error subscribing to presence:', error);
      callback(null);
    });

    return unsubscribe;
  }

  /**
   * Get current presence for a user
   */
  async getUserPresence(uid: string): Promise<PresenceData | null> {
    try {
      const presenceDoc = await getDoc(doc(db, 'presence', uid));

      if (presenceDoc.exists()) {
        const data = presenceDoc.data() as PresenceData;

        // If user is in ghost mode, show as offline to others
        if (data.isGhostMode && uid !== this.currentUser?.uid) {
          return {
            ...data,
            status: 'offline'
          };
        }

        return data;
      }

      return null;
    } catch (error) {
      console.error(' Error getting user presence:', error);
      return null;
    }
  }

  /**
   * Clean up presence on logout
   */
  async cleanup() {
    if (this.activityTimer) {
      clearTimeout(this.activityTimer);
    }

    if (this.unsubscribe) {
      this.unsubscribe();
    }

    if (this.presenceRef && this.currentUser) {
      try {
        await setDoc(this.presenceRef, {
          status: 'offline',
          lastSeen: serverTimestamp()
        }, { merge: true });
      } catch (error) {
        console.error(' Error cleaning up presence:', error);
      }
    }

    this.presenceRef = null;
    this.currentUser = null;
    this.isGhostMode = false;
  }

  /**
   * Check if a user appears online (accounting for ghost mode)
   */
  isUserOnline(presence: PresenceData | null): boolean {
    if (!presence) return false;

    // For the current user, show true status even in ghost mode
    if (presence.uid === this.currentUser?.uid) {
      return presence.status === 'online' || presence.status === 'away';
    }

    // For others, ghost mode appears as offline
    if (presence.isGhostMode) return false;

    return presence.status === 'online' || presence.status === 'away';
  }

  /**
   * Get display status for UI (handles ghost mode visibility)
   */
  getDisplayStatus(presence: PresenceData | null, viewerUid?: string): PresenceStatus {
    if (!presence) return 'offline';

    // Show true status to self
    if (presence.uid === viewerUid) {
      return presence.status;
    }

    // Ghost mode appears as offline to others
    if (presence.isGhostMode) {
      return 'offline';
    }

    return presence.status;
  }
}

// Export singleton instance
export const presenceService = new PresenceService();

/**
 * HIVE Profile System Types
 * Complete type definitions matching the Profile PRD
 * Mobile-first, real-time, connection-aware
 */

import { Timestamp } from 'firebase-admin/firestore';

// ============================================
// Module 1: Identity System
// ============================================

export interface PhotoCarouselItem {
  id: string;
  url: string;
  thumbnailUrl?: string;
  order: number;
  tags: string[];
  uploadedAt: Date;
  context?: 'academic' | 'social' | 'residential' | 'activities';
}

export interface PhotoCarousel {
  photos: PhotoCarouselItem[];
  currentIndex: number;
  rotationInterval: 30000; // 30 seconds
  lastUpdated: Date;
  freshnessThreshold: number; // 6 weeks in milliseconds
}

export interface AcademicIdentity {
  name: string; // Required
  year: 'freshman' | 'sophomore' | 'junior' | 'senior' | 'graduate';
  majors: string[];
  minors: string[];
  pronouns?: string;
  graduationYear: number;
}

export interface Badge {
  id: string;
  type: 'builder' | 'student_leader' | 'contributor' | 'early_adopter';
  name: string;
  description: string;
  earnedAt: Date;
  displayOrder: number;
}

// ============================================
// Module 2: Connection System
// ============================================

export enum ConnectionType {
  NONE = 'none',
  CONNECTION = 'connection', // Automatic from shared spaces
  FRIEND = 'friend' // Intentional with mutual approval
}

export interface Connection {
  userId: string;
  type: ConnectionType;
  sharedSpaces: string[];
  connectedAt: Date;
  lastInteraction?: Date;
}

export interface Friend extends Connection {
  type: ConnectionType.FRIEND;
  friendsSince: Date;
  mutualFriends: number;
}

// ============================================
// Module 3: Presence System
// ============================================

export type VibeStatus =
  | ' Surviving'
  | ' Thriving'
  | ' Chaos mode'
  | ' Seeking caffeine'
  | ' Victory lap'
  | string; // Custom up to 30 chars

export interface AvailabilityBeacon {
  active: boolean;
  duration: 1 | 2 | 4; // hours
  message?: string; // 20 chars max
  expiresAt: Date;
  visibilityRules: {
    friends: 'always';
    connections: 'sharedSpacesOnly';
    campus?: 'never';
  };
}

export interface PresenceState {
  vibe: VibeStatus;
  vibeUpdatedAt: Date;
  beacon?: AvailabilityBeacon;
  lastActive: Date;
  isOnline: boolean;
  currentActivity?: {
    type: 'studying' | 'in_space' | 'at_event' | 'available';
    context?: string;
  };
}

// ============================================
// Module 4: Bento Grid System
// ============================================

export type GridSize = '1x1' | '2x1' | '2x2' | '1x2';

export interface BentoCard {
  id: string;
  type: 'spaces_hub' | 'friends_network' | 'schedule_overlap' |
        'active_now' | 'discovery' | 'vibe_check' | 'custom';
  position: { x: number; y: number };
  size: GridSize;
  visible: boolean;
  config?: Record<string, any>;
}

export interface BentoGridLayout {
  cards: BentoCard[];
  mobileLayout: BentoCard[]; // Separate mobile layout
  lastModified: Date;
}

// ============================================
// Module 5: Privacy Controls
// ============================================

export enum VisibilityLevel {
  GHOST = 'ghost',
  FRIENDS_ONLY = 'friends',
  CONNECTIONS = 'connections',
  CAMPUS = 'campus'
}

export interface PrivacySettings {
  ghostMode: boolean;
  visibilityLevel: VisibilityLevel;
  scheduleSharing: {
    friends: boolean;
    connections: boolean;
  };
  availabilityBroadcast: {
    friends: boolean;
    connections: boolean;
    campus: boolean;
  };
  discoveryParticipation: boolean;
  spaceActivityVisibility: Map<string, boolean>; // Per-space settings
}

// ============================================
// Module 6: Intelligence Layer
// ============================================

export interface ScheduleBlock {
  id: string;
  title: string;
  type: 'class' | 'study' | 'work' | 'personal' | 'extracurricular';
  startTime: Date;
  endTime: Date;
  location?: string;
  recurring?: {
    pattern: 'daily' | 'weekly' | 'monthly';
    endDate?: Date;
  };
}

export interface ScheduleOverlap {
  userId: string;
  overlapWindows: Array<{
    start: Date;
    end: Date;
    duration: number; // minutes
  }>;
  rarity: 'common' | 'uncommon' | 'rare';
  suggestedMeetup?: {
    time: Date;
    location: string;
    confidence: number; // 0-1
  };
}

export interface DiscoverySuggestion {
  userId: string;
  score: number;
  reasons: string[];
  factors: {
    mutualFriends: number;
    sharedSpaces: number;
    scheduleOverlap: number;
    academicMatch: number;
    interactionStyle: number;
  };
}

// ============================================
// Main Profile System Interface
// ============================================

export interface ProfileSystem {
  // Core Identity
  userId: string;
  campusId: string; // 'ub-buffalo' for vBETA
  handle: string;

  // Module 1: Identity
  identity: {
    academic: AcademicIdentity;
    photoCarousel: PhotoCarousel;
    badges: Badge[];
  };

  // Module 2: Connections
  connections: {
    friends: Friend[];
    connections: Connection[];
    pendingRequests: string[];
    blockedUsers: string[];
  };

  // Module 3: Presence
  presence: PresenceState;

  // Module 4: Display
  grid: BentoGridLayout;

  // Module 5: Privacy
  privacy: PrivacySettings;

  // Module 6: Intelligence
  intelligence: {
    schedule: ScheduleBlock[];
    overlaps: ScheduleOverlap[];
    suggestions: DiscoverySuggestion[];
    lastCalculated: Date;
  };

  // Metadata
  createdAt: Date | Timestamp;
  updatedAt: Date | Timestamp;
  completeness: number; // 0-100
  isSetupComplete: boolean;
}

// ============================================
// API Response Types
// ============================================

export interface ProfileView {
  // What others see based on connection level
  userId: string;
  handle: string;
  identity: Partial<ProfileSystem['identity']>;
  presence?: Partial<PresenceState>;
  connectionType: ConnectionType;
  sharedContext?: {
    spaces: string[];
    mutualFriends: number;
  };
}

export interface ProfileUpdate {
  // Partial updates to profile
  identity?: Partial<ProfileSystem['identity']>;
  presence?: Partial<PresenceState>;
  privacy?: Partial<PrivacySettings>;
  grid?: Partial<BentoGridLayout>;
}

// ============================================
// Real-time Event Types
// ============================================

export interface ProfileRealtimeEvent {
  type: 'presence_update' | 'beacon_activated' | 'vibe_changed' |
        'friend_request' | 'connection_created' | 'ghost_mode_toggled';
  userId: string;
  timestamp: Date;
  data: any;
}

// ============================================
// Component Props Types
// ============================================

export interface ProfilePageProps {
  profile: ProfileSystem;
  viewerConnectionType: ConnectionType;
  isOwnProfile: boolean;
  onUpdate?: (update: ProfileUpdate) => void;
  onConnectionAction?: (action: 'friend' | 'unfriend' | 'block') => void;
}

export interface BentoCardProps {
  card: BentoCard;
  profile: ProfileSystem;
  onConfigure?: (config: Record<string, any>) => void;
  onResize?: (size: GridSize) => void;
  onMove?: (position: { x: number; y: number }) => void;
  isMobile: boolean;
}

export interface PhotoCarouselProps {
  carousel: PhotoCarousel;
  editable: boolean;
  onPhotoUpload?: (file: File, tags: string[]) => void;
  onPhotoDelete?: (photoId: string) => void;
  onReorder?: (photos: PhotoCarouselItem[]) => void;
}

// ============================================
// Utility Types
// ============================================

export type ProfileCompleteness = {
  percentage: number;
  missingFields: string[];
  suggestions: Array<{
    field: string;
    importance: 'critical' | 'important' | 'nice_to_have';
    action: string;
  }>;
};

export type ProfileAnalytics = {
  profileViews: number;
  connectionRequests: number;
  beaconActivations: number;
  discoveryAppearances: number;
  period: 'day' | 'week' | 'month';
};

// ============================================
// Hook Return Types
// ============================================

export interface UseProfileReturn {
  profile: ProfileSystem | null;
  isLoading: boolean;
  error: Error | null;

  // Actions
  updateProfile: (update: ProfileUpdate) => Promise<void>;
  uploadPhoto: (file: File, tags: string[]) => Promise<void>;
  setVibe: (status: VibeStatus) => Promise<void>;
  activateBeacon: (duration: 1 | 2 | 4, message?: string) => Promise<void>;
  toggleGhostMode: () => Promise<void>;

  // Grid management
  saveGridLayout: (layout: BentoGridLayout) => Promise<void>;
  resetGrid: () => Promise<void>;

  // Connection actions
  sendFriendRequest: (userId: string) => Promise<void>;
  acceptFriendRequest: (userId: string) => Promise<void>;
  removeFriend: (userId: string) => Promise<void>;

  // Real-time subscriptions
  subscribeToPresence: (userIds: string[]) => () => void;
  subscribeToBeacons: () => () => void;
}

// Backwards compatibility type aliases
export type HiveProfile = ProfileSystem;
export type UnifiedHiveProfile = ProfileSystem;

