import { NextRequest, NextResponse } from "next/server";
import { db, auth as firebaseAdmin } from "@/lib/firebase-admin";
import { sendSignInLinkToEmail, signInWithEmailLink as _signInWithEmailLink } from "firebase/auth";
import { auth, logger, findAvailableHandle } from "@hive/core";

async function checkHandleAvailability(handle: string): Promise<boolean> {
  const handleDoc = await db.collection("handles").doc(handle).get();
  return !handleDoc.exists;
}

export async function POST(request: NextRequest) {
  try {
    const { email, dev } = await request.json();

    if (!email) {
      return NextResponse.json(
        { error: "Email is required", ok: false },
        { status: 400 }
      );
    }

    // Development mode bypass
    if (dev === "true" || process.env.NODE_ENV === "development") {
      logger.info(`Development mode verification for: ${email}`);

      try {
        let user;
        try {
          user = await firebaseAdmin.getUserByEmail(email);
        } catch (error: unknown) {
          if (error && typeof error === 'object' && 'code' in error && error.code === "auth/user-not-found") {
            // Create new user in development
            const displayName = email.split("@")[0];
            const handle = await findAvailableHandle(
              displayName,
              checkHandleAvailability
            );

            user = await firebaseAdmin.createUser({
              email,
              emailVerified: true,
              displayName,
            });

            // Reserve the handle
            await db.collection("handles").doc(handle).set({
              userId: user.uid,
              createdAt: new Date(),
            });

            // Create user document with onboarding state
            await db
              .collection("users")
              .doc(user.uid)
              .set({
                uid: user.uid,
                email,
                displayName,
                handle,
                createdAt: new Date(),
                isNewUser: true,
                verificationLevel: "verified",
                onboarding: {
                  email,
                  displayName,
                  handle,
                  isComplete: false,
                  verificationLevel: "verified",
                  isStudentLeader: false,
                  builderOptIn: false,
                  consentGiven: false,
                },
              });

            logger.info(`Created new user in development: ${user.uid}`);
          } else {
            throw error;
          }
        }

        const customToken = await firebaseAdmin.createCustomToken(user.uid);
        const userRecord = await firebaseAdmin.getUser(user.uid);

        return NextResponse.json({
          ok: true,
          idToken: customToken,
          user: {
            uid: userRecord.uid,
            email: userRecord.email,
            displayName: userRecord.displayName,
            isNewUser:
              !userRecord.metadata.creationTime ||
              Date.now() -
                new Date(userRecord.metadata.creationTime).getTime() <
                60000,
          },
        });
      } catch (error) {
        logger.error("Development verification error:", error);
        return NextResponse.json(
          { error: "Development verification failed", ok: false },
          { status: 500 }
        );
      }
    }

    // Production magic link verification
    try {
      const actionCodeSettings = {
        url: `${
          process.env.NEXT_PUBLIC_APP_URL
        }/auth/verify?email=${encodeURIComponent(email)}`,
        handleCodeInApp: true,
      };

      await sendSignInLinkToEmail(auth, email, actionCodeSettings);

      return NextResponse.json({
        ok: true,
        message: "Verification email sent",
      });
    } catch (error: unknown) {
      logger.error("Magic link verification error:", error);

      if (error && typeof error === 'object' && 'code' in error && error.code === "auth/invalid-email") {
        return NextResponse.json(
          { error: "Invalid email address", ok: false },
          { status: 400 }
        );
      }

      return NextResponse.json(
        { error: "Failed to send verification email", ok: false },
        { status: 500 }
      );
    }
  } catch (error) {
    logger.error("Verification endpoint error:", error);
    return NextResponse.json(
      { error: "Internal server error", ok: false },
      { status: 500 }
    );
  }
}

"use client";

import React, { useState, useEffect } from "react";
import { useRouter } from "next/navigation";
import { useAuth } from "@hive/auth-logic";
import { logger, type OnboardingState, type AcademicLevel } from "@hive/core";
import { Loader2 } from "lucide-react";
import { useOnboardingStore } from "@/lib/stores/onboarding";
import { DisplayNameAvatar as DisplayNameAvatarBase } from "@/components/onboarding/steps/display-name-avatar";
import { AcademicCard as AcademicCardBase } from "@/components/onboarding/steps/academic-card";

interface OnboardingStepClientProps {
  step: string;
}

interface StepComponentProps {
  onNext: (nextStep?: number, data?: Partial<OnboardingState>) => void;
  onPrev: () => void;
  data: Partial<OnboardingState>;
}

// Wrapper components that adapt the existing components to the expected interface
const DisplayNameAvatar: React.FC<StepComponentProps> = ({ onNext, data }) => {
  return (
    <DisplayNameAvatarBase
      onNext={(stepData: Partial<OnboardingState>) => onNext(undefined, stepData)}
      data={data}
    />
  );
};

const LeaderStep: React.FC<StepComponentProps> = ({ onNext }) => {
  const { update } = useOnboardingStore();

  const handleLeaderChoice = async (isStudentLeader: boolean) => {
    await update({ isStudentLeader });
    onNext(undefined, { isStudentLeader });
  };

  return (
    <div className="flex min-h-screen items-center justify-center p-4">
      <div className="w-full max-w-lg bg-surface border border-border rounded-lg p-6">
        <div className="text-center space-y-2 mb-6">
          <div className="w-12 h-12 bg-surface-01 rounded-full flex items-center justify-center mx-auto">
            <svg className="w-6 h-6 text-foreground" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 3l14 0 0 14-14 0z" />
            </svg>
          </div>
          <h2 className="text-xl font-display text-foreground">
            Student Leadership
          </h2>
          <p className="text-muted font-sans">
            Are you involved in student leadership or organizations?
          </p>
        </div>

        <div className="space-y-4">
          <button
            onClick={() => handleLeaderChoice(true)}
            className="w-full p-4 rounded-lg border-2 border-border hover:border-foreground/20 hover:bg-surface-01 transition-all text-left"
          >
            <div className="flex items-start space-x-3">
              <div className="w-8 h-8 bg-surface-01 rounded-full flex items-center justify-center flex-shrink-0 mt-1">
                <svg className="w-4 h-4 text-foreground" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 3l14 0 0 14-14 0z" />
                </svg>
              </div>
              <div className="flex-1">
                <h3 className="font-medium text-foreground mb-1">
                  Yes, I'm a leader
                </h3>
                <p className="text-sm text-muted">
                  I hold leadership positions in student organizations, clubs, or governance
                </p>
              </div>
            </div>
          </button>

          <button
            onClick={() => handleLeaderChoice(false)}
            className="w-full p-4 rounded-lg border-2 border-border hover:border-foreground/20 hover:bg-surface-01 transition-all text-left"
          >
            <div className="flex items-start space-x-3">
              <div className="w-8 h-8 bg-surface-01 rounded-full flex items-center justify-center flex-shrink-0 mt-1">
                <svg className="w-4 h-4 text-foreground" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z" />
                </svg>
              </div>
              <div className="flex-1">
                <h3 className="font-medium text-foreground mb-1">
                  Not at this time
                </h3>
                <p className="text-sm text-muted">
                  I'm focused on academics or not currently in leadership roles
                </p>
              </div>
            </div>
          </button>
        </div>
      </div>
    </div>
  );
};

const AcademicCard: React.FC<StepComponentProps> = () => {
  return (
    <div className="flex min-h-screen items-center justify-center p-4">
      <AcademicCardBase />
    </div>
  );
};

const Interests: React.FC<StepComponentProps> = ({ onNext }) => {
  const { update } = useOnboardingStore();

  const handleInterestsSubmit = async (interestData: Partial<OnboardingState>) => {
    await update(interestData);
    onNext(undefined, interestData);
  };

  return (
    <div className="flex min-h-screen items-center justify-center p-4">
      <div className="w-full max-w-lg bg-surface border border-border rounded-lg p-6">
        <div className="text-center space-y-2 mb-6">
          <h2 className="text-xl font-display text-foreground">
            Your Interests
          </h2>
          <p className="text-muted font-sans">
            What are you passionate about? This helps us connect you with relevant communities.
          </p>
        </div>
        <button
          onClick={() => handleInterestsSubmit({ interests: [] })}
          className="w-full bg-foreground text-background font-medium py-3 px-4 rounded-lg hover:bg-foreground/90 transition-colors"
        >
          Continue
        </button>
      </div>
    </div>
  );
};

const STEPS: Record<string, React.ComponentType<StepComponentProps>> = {
  "1": DisplayNameAvatar, // Personal identity - builds connection
  "2": LeaderStep, // Student leader question for space claiming
  "3": AcademicCard, // Academic context - sets university context
  "4": Interests, // Personalization - customize experience
} as const;

// Use the same check as auth hook for consistency
// If Firebase env vars are missing, we're in dev mode
const isDevMode = !process.env.NEXT_PUBLIC_FIREBASE_API_KEY;

const initialDevData: Partial<OnboardingState> = {
  schoolId: "ub",
  schoolName: "University at Buffalo",
  displayName: "Dev User",
  handle: "dev-user",
  onboardingCompleted: false,
  isStudentLeader: false,
  academicLevel: "undergraduate" as AcademicLevel,
  majors: ["Computer Science"],
  graduationYear: new Date().getFullYear() + 4,
  interests: [],
  consentGiven: false,
};

export function OnboardingStepClient({ step }: OnboardingStepClientProps) {
  const router = useRouter();
  const { user, isLoading: isAuthLoading } = useAuth();
  const { data: onboardingData, update } = useOnboardingStore();
  const [isInitialized, setIsInitialized] = useState(false);

  // Debug logging
  console.warn("üêõ OnboardingStepClient Debug:", {
    step,
    isDevMode,
    isAuthLoading,
    isInitialized,
    hasOnboardingData: !!onboardingData,
    userExists: !!user,
    envVars: {
      hasFirebaseKey: !!process.env.NEXT_PUBLIC_FIREBASE_API_KEY,
      nodeEnv: process.env.NODE_ENV,
    },
  });

  const handleNext = async (
    nextStep?: number,
    data?: Partial<OnboardingState>
  ) => {
    const updatedData = { ...onboardingData, ...data };
    update(updatedData);
    logger.info("Updating onboarding data:", updatedData);

    if (nextStep) {
      router.push(`/onboarding/${nextStep}`);
    } else {
      // Determine next step based on current step
      if (step === "4") {
        router.push("/onboarding/complete");
      } else {
        const currentNum = parseInt(step);
        router.push(`/onboarding/${currentNum + 1}`);
      }
    }
  };

  const handlePrev = () => {
    if (step !== "1") {
      const currentNum = parseInt(step);
      if (currentNum > 1) {
        router.push(`/onboarding/${currentNum - 1}`);
      }
    }
  };

  // Initialize onboarding data in dev mode
  useEffect(() => {
    console.warn("üîÑ Initialization useEffect triggered:", {
      isDevMode,
      hasOnboardingData: !!onboardingData,
      isAuthLoading,
    });

    if (isDevMode) {
      // In dev mode, always initialize with mock data
      if (!onboardingData) {
        logger.info("üî• Development mode: initializing with mock data");
        console.warn("üìù Setting initial dev data");
        update(initialDevData);
      }
      console.warn("‚úÖ Setting isInitialized = true (dev mode)");
      setIsInitialized(true);
    } else {
      // In production mode, wait for auth to load
      if (!isAuthLoading) {
        console.warn("‚úÖ Setting isInitialized = true (production mode)");
        setIsInitialized(true);
      }
    }
  }, [onboardingData, update, isAuthLoading]);

  // Handle invalid step identifiers (support numeric steps only)
  const validSteps = Object.keys(STEPS);
  if (!validSteps.includes(step)) {
    router.replace("/onboarding/1");
    return null;
  }

  // Show loading state only when actually loading
  const shouldShowLoading = isDevMode
    ? !isInitialized
    : isAuthLoading || !isInitialized;

  if (shouldShowLoading) {
    console.warn("üîÑ Showing loading state:", {
      reason: isDevMode
        ? "waiting for dev mode initialization"
        : isAuthLoading
        ? "auth loading in production"
        : "not initialized",
      isDevMode,
      isAuthLoading,
      isInitialized,
    });

    return (
      <div className="flex min-h-screen items-center justify-center bg-background">
        <div className="flex flex-col items-center gap-4">
          <Loader2 className="h-8 w-8 animate-spin text-accent" />
          <p className="text-muted font-sans">Loading your profile...</p>
          {process.env.NODE_ENV === "development" && (
            <div className="text-xs text-muted mt-2 max-w-md">
              Debug: isDevMode={isDevMode.toString()}, isAuthLoading=
              {isAuthLoading.toString()}, isInitialized=
              {isInitialized.toString()}
            </div>
          )}
        </div>
      </div>
    );
  }

  // Development mode handling - always proceed
  if (isDevMode) {
    logger.info("üî• Development mode: proceeding with mock user");
    const StepComponent = STEPS[step];
    if (!StepComponent) {
      return (
        <div className="flex min-h-screen items-center justify-center p-4 bg-background">
          <div className="text-center">
            <h1 className="text-2xl font-bold mb-4 text-foreground">Step {step} Not Found</h1>
            <p className="text-muted mb-4">
              The requested onboarding step does not exist.
            </p>
            <button
              onClick={() => router.push("/onboarding/1")}
              className="px-4 py-2 bg-foreground text-background rounded-md font-medium"
            >
              Go to Step 1
            </button>
          </div>
        </div>
      );
    }

    return (
      <div className="bg-background">
        <StepComponent
          onNext={handleNext}
          onPrev={handlePrev}
          data={onboardingData || initialDevData}
        />
      </div>
    );
  }

  // Production auth check
  if (!user) {
    router.push("/auth/school-select");
    return null;
  }

  // Validation and routing logic
  useEffect(() => {
    if (!isInitialized) return;

    // Step-specific validation
    switch (step) {
      case "1":
        // Step 1 (Profile) can always be accessed
        break;
      case "2":
        // Step 2 (Leader) requires name and handle from Step 1
        if (!onboardingData?.displayName || !onboardingData?.handle) {
          router.replace("/onboarding/1");
        }
        break;
      case "3":
        // Step 3 (Academic) requires previous steps completed
        if (
          !onboardingData?.displayName ||
          !onboardingData?.handle ||
          onboardingData?.isStudentLeader === undefined
        ) {
          router.replace("/onboarding/1");
        }
        break;
      case "4":
        // Step 4 (Interests) requires all previous steps completed
        if (
          !onboardingData?.displayName ||
          !onboardingData?.handle ||
          onboardingData?.isStudentLeader === undefined ||
          !onboardingData?.academicLevel ||
          !onboardingData?.majors?.length
        ) {
          router.replace("/onboarding/1");
        }
        break;
    }
  }, [isInitialized, onboardingData, router, step]);

  const StepComponent = STEPS[step];
  if (!StepComponent) {
    return (
      <div className="flex min-h-screen items-center justify-center p-4 bg-background">
        <div className="text-center">
          <h1 className="text-2xl font-bold mb-4 text-foreground">Step {step} Not Found</h1>
          <p className="text-muted mb-4">
            The requested onboarding step does not exist.
          </p>
          <button
            onClick={() => router.push("/onboarding/1")}
            className="px-4 py-2 bg-foreground text-background rounded-md font-medium"
          >
            Go to Step 1
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-background">
      <StepComponent
        onNext={handleNext}
        onPrev={handlePrev}
        data={onboardingData || {}}
      />
    </div>
  );
}

"use client";

import { useState, useEffect, useRef } from "react";

import { Button, Input, Label } from "@hive/ui";
import { useOnboardingStore } from "@/lib/stores/onboarding";
import { env, type OnboardingState } from "@hive/core";
import { useHandleAvailability } from "@/hooks/use-handle-availability";
import {
  XIcon,
  Upload,
  Camera,
  CheckCircle,
  AlertCircle,
  Loader2,
} from "lucide-react";
import ReactCrop, { type Crop } from "react-image-crop";
import "react-image-crop/dist/ReactCrop.css";
import { functions } from "@/lib/firebase-client";
import { httpsCallable } from "firebase/functions";
import Image from "next/image";

interface DisplayNameAvatarProps {
  onNext: (data: {
    displayName: string;
    handle: string;
    avatarUrl?: string;
  }) => void;
  data: Partial<OnboardingState>;
}

function generateHandle(name: string): string {
  return name
    .toLowerCase()
    .replace(/[^a-z0-9 ]/g, "") // Remove special characters and emojis
    .replace(/\s+/g, ".") // Replace spaces with dots
    .slice(0, 30); // Limit length
}

function validateName(name: string): boolean {
  // Check for emojis using regex
  const emojiRegex =
    /[\p{Extended_Pictographic}\u{1F3FB}-\u{1F3FF}\u{1F9B0}-\u{1F9B3}]/u;
  if (emojiRegex.test(name)) return false;

  // Check for full name (at least two words)
  const words = name.trim().split(/\s+/);
  if (words.length < 2) return false;

  // Check for special characters
  const specialCharsRegex = /[!@#$%^&*()_+=[\]{};':"\\|,.<>/?]+/;
  if (specialCharsRegex.test(name)) return false;

  return true;
}

export function DisplayNameAvatar({
  onNext,
  data: onboardingData,
}: DisplayNameAvatarProps) {
  const { update } = useOnboardingStore();
  const [displayName, setDisplayName] = useState(onboardingData?.displayName ?? "");
  const [handle, setHandle] = useState(onboardingData?.handle ?? "");
  const [avatarUrl, setAvatarUrl] = useState(onboardingData?.avatarUrl ?? "");
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [crop, setCrop] = useState<Crop>();
  const [imgSrc, setImgSrc] = useState("");
  const [isUploading, setIsUploading] = useState(false);
  const [error, setError] = useState("");
  const fileInputRef = useRef<HTMLInputElement>(null);

  // Real-time handle availability checking
  const handleAvailability = useHandleAvailability(handle);

  // Update handle when name changes
  useEffect(() => {
    if (displayName) {
      setHandle(generateHandle(displayName));
    }
  }, [displayName]);

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      // Validate file type
      if (!file.type.startsWith("image/")) {
        setError("Please select an image file");
        return;
      }

      // Validate file size (5MB limit)
      if (file.size > 5 * 1024 * 1024) {
        setError("Image must be less than 5MB");
        return;
      }

      setSelectedFile(file);
      setError("");

      // Create preview URL
      const reader = new FileReader();
      reader.addEventListener("load", () =>
        setImgSrc(reader.result?.toString() || "")
      );
      reader.readAsDataURL(file);
    }
  };

  const handleUploadClick = () => {
    fileInputRef.current?.click();
  };

  const handleRemovePhoto = () => {
    setSelectedFile(null);
    setAvatarUrl("");
    if (fileInputRef.current) {
      fileInputRef.current.value = "";
    }
  };

  const uploadCroppedImage = async (): Promise<string> => {
    if (!selectedFile || !crop) {
      throw new Error("No image or crop data to upload.");
    }

    // This is a placeholder for the actual canvas cropping logic
    // In a real implementation, you would use a canvas to draw the cropped image
    // and then get the blob data.
    const croppedImageBlob = selectedFile;

    setIsUploading(true);

    try {
      const generateUploadUrl = httpsCallable(
        functions,
        "generateAvatarUploadUrl"
      );
      const uploadUrlResult = await generateUploadUrl({
        fileType: croppedImageBlob.type,
        fileSize: croppedImageBlob.size,
      });

      const { url, filePath } = uploadUrlResult.data as {
        success: boolean;
        url: string;
        filePath: string;
      };

      await fetch(url, {
        method: "PUT",
        body: croppedImageBlob,
        headers: { "Content-Type": croppedImageBlob.type },
      });

      // Construct the public URL
      const bucket = env.NEXT_PUBLIC_FIREBASE_STORAGE_BUCKET ?? "demo-project.appspot.com";
      return `https://storage.googleapis.com/${bucket}/${filePath}`;
    } catch (error) {
      console.error("Upload failed", error);
      throw new Error("Could not upload image.");
    } finally {
      setIsUploading(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateName(displayName)) {
      setError(
        "Please enter your full name (first and last) without emojis or special characters"
      );
      return;
    }

    if (!handle || handle.length < 3) {
      setError("Please enter a username with at least 3 characters");
      return;
    }

    if (handleAvailability.available === false) {
      setError("Please choose an available username");
      return;
    }

    if (handleAvailability.isChecking) {
      setError("Please wait while we check username availability");
      return;
    }

    setIsUploading(true);
    setError("");

    try {
      let finalAvatarUrl = avatarUrl;

      // Upload file if selected
      if (selectedFile) {
        finalAvatarUrl = await uploadCroppedImage();
      }

      const updatedData = {
        displayName,
        handle,
        avatarUrl: finalAvatarUrl,
      };

      // Update the store
      update(updatedData);

      // Call onNext with the data
      onNext(updatedData);
    } catch (error) {
      console.error("Error submitting form:", error);
      setError("Failed to save profile. Please try again.");
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <div className="flex min-h-screen items-center justify-center p-4 bg-background">
      <div className="w-full max-w-lg bg-surface border border-border rounded-lg p-6 space-y-6">
        {/* Header */}
        <div className="text-center space-y-2">
          <h2 className="text-2xl font-display font-medium text-foreground">
            Create Your Profile
          </h2>
          <p className="text-muted font-sans">
            Tell us about yourself to get started
          </p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Avatar Upload Section */}
          <div className="flex flex-col items-center space-y-4">
            <div className="relative">
              {avatarUrl || selectedFile ? (
                <div className="relative">
                  <Image
                    src={avatarUrl || (selectedFile ? URL.createObjectURL(selectedFile) : "")}
                    alt="Profile"
                    width={80}
                    height={80}
                    className="rounded-full object-cover border-2 border-border"
                  />
                  <button
                    type="button"
                    onClick={handleRemovePhoto}
                    className="absolute -top-1 -right-1 w-6 h-6 bg-surface-02 border border-border rounded-full flex items-center justify-center text-muted hover:text-accent transition-colors"
                  >
                    <XIcon className="w-3 h-3" />
                  </button>
                </div>
              ) : (
                <div className="w-20 h-20 bg-surface-01 border-2 border-dashed border-border rounded-full flex items-center justify-center">
                  <Upload className="w-6 h-6 text-muted" />
                </div>
              )}
            </div>

            <div className="flex space-x-2">
              <button
                type="button"
                onClick={handleUploadClick}
                className="flex items-center space-x-2 px-3 py-2 bg-surface-01 border border-accent text-accent rounded-lg hover:bg-accent/5 transition-colors text-sm"
              >
                <Camera className="w-4 h-4" />
                <span>{avatarUrl || selectedFile ? "Change" : "Add Photo"}</span>
              </button>

              {(avatarUrl || selectedFile) && (
                <button
                  type="button"
                  onClick={handleRemovePhoto}
                  className="px-3 py-2 text-muted hover:text-accent transition-colors text-sm"
                >
                  Remove
                </button>
              )}
            </div>

            <input
              ref={fileInputRef}
              type="file"
              accept="image/*"
              onChange={handleFileSelect}
              className="hidden"
            />
          </div>

          {/* Form Fields */}
          <div className="space-y-4">
            <div className="space-y-2">
              <Label className="text-foreground font-medium">Full Name</Label>
              <Input
                value={displayName}
                onChange={(e) => setDisplayName(e.target.value)}
                placeholder="Enter your full name"
                className="bg-surface-01 border-border text-foreground placeholder:text-muted"
              />
            </div>

            <div className="space-y-2">
              <Label className="text-foreground font-medium">Username</Label>
              <div className="relative">
                <Input
                  value={handle}
                  onChange={(e) => setHandle(e.target.value.toLowerCase())}
                  placeholder="username"
                  className="bg-surface-01 border-border text-foreground placeholder:text-muted pl-8"
                />
                <span className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted">
                  @
                </span>
              </div>

              {/* Handle availability feedback */}
              {handle.length >= 3 && (
                <div className="flex items-center space-x-2 text-sm">
                  {handleAvailability.isChecking ? (
                    <>
                      <Loader2 className="w-4 h-4 animate-spin text-muted" />
                      <span className="text-muted">Checking availability...</span>
                    </>
                  ) : handleAvailability.available === true ? (
                    <>
                      <CheckCircle className="w-4 h-4 text-accent" />
                      <span className="text-accent">@{handle} is available</span>
                    </>
                  ) : handleAvailability.available === false ? (
                    <>
                      <AlertCircle className="w-4 h-4 text-muted" />
                      <span className="text-muted">@{handle} is taken</span>
                    </>
                  ) : null}
                </div>
              )}
            </div>
          </div>

          {/* Error Message */}
          {error && (
            <div className="p-3 bg-surface-02 border border-border rounded-lg">
              <p className="text-sm text-muted">{error}</p>
            </div>
          )}

          {/* Submit Button */}
          <Button
            type="submit"
            disabled={
              !displayName ||
              !handle ||
              handle.length < 3 ||
              handleAvailability.isChecking ||
              handleAvailability.available === false ||
              isUploading
            }
            className="w-full bg-accent text-background font-medium hover:bg-accent/90 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {isUploading ? (
              <>
                <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                Saving...
              </>
            ) : (
              "Continue"
            )}
          </Button>
        </form>

        {/* Image Cropping Modal */}
        {selectedFile && imgSrc && (
          <div className="fixed inset-0 bg-background/80 backdrop-blur-sm flex items-center justify-center p-4 z-50">
            <div className="bg-surface border border-border rounded-lg p-6 max-w-md w-full">
              <h3 className="text-lg font-medium text-foreground mb-4">Crop Your Photo</h3>

              <ReactCrop
                crop={crop}
                onChange={(c) => setCrop(c)}
                className="mb-4"
              >
                <Image
                  src={imgSrc}
                  alt="Crop preview"
                  width={400}
                  height={400}
                  className="max-w-full"
                />
              </ReactCrop>

              <div className="flex space-x-2">
                <Button
                  onClick={() => {
                    setSelectedFile(null);
                    setImgSrc("");
                  }}
                  variant="outline"
                  className="flex-1 bg-surface-01 border-border text-foreground hover:bg-surface-02"
                >
                  Cancel
                </Button>
                <Button
                  onClick={() => {
                    // Handle crop save
                    setSelectedFile(null);
                    setImgSrc("");
                  }}
                  className="flex-1 bg-accent text-background hover:bg-accent/90"
                >
                  Save
                </Button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { Card, Button, Badge, Input } from '@hive/ui';
import { useOnboardingStore } from '@/lib/stores/onboarding';
import { Search, Users, GraduationCap, Building, Home, Heart, ChevronRight, CheckCircle, Loader2 } from 'lucide-react';
import { logger } from '@hive/core';

interface SpaceData {
  id: string;
  name: string;
  description: string;
  memberCount: number;
  type: string;
  tags?: Array<{
    type: string;
    sub_type: string;
  }>;
  status: string;
}

interface SpacesResponse {
  success: boolean;
  spaces: {
    academic: SpaceData[];
    campusLiving: SpaceData[];
    greekLife: SpaceData[];
    studentOrganizations: SpaceData[];
    universityOrganizations: SpaceData[];
  };
  totalCount: number;
}

interface SpaceCategory {
  id: string;
  title: string;
  description: string;
  icon: React.ComponentType<{ className?: string }>;
  autoJoin?: boolean;
}

const SPACE_CATEGORIES: SpaceCategory[] = [
  {
    id: 'academic',
    title: 'Academic Programs',
    description: 'Connect with students in your major and related fields',
    icon: GraduationCap,
    autoJoin: true,
  },
  {
    id: 'campusLiving',
    title: 'Campus Living',
    description: 'Connect with your dorm, residence hall, or housing community',
    icon: Home,
  },
  {
    id: 'greekLife',
    title: 'Greek Life',
    description: 'Fraternities, sororities, and Greek organizations',
    icon: Heart,
  },
  {
    id: 'studentOrganizations',
    title: 'Student Organizations',
    description: 'Clubs, societies, and student-run groups',
    icon: Users,
  },
  {
    id: 'universityOrganizations',
    title: 'University Organizations',
    description: 'Official university departments and programs',
    icon: Building,
  }
];

export default function SpaceDiscoveryStep() {
  const router = useRouter();
  const { data: onboardingData, update: updateOnboardingData } = useOnboardingStore();

  const [spaces, setSpaces] = useState<SpacesResponse['spaces'] | null>(null);
  const [loading, setLoading] = useState(true);
  const [selectedSpaces, setSelectedSpaces] = useState<string[]>(onboardingData?.selectedSpaces ?? []);
  const [searchQuery, setSearchQuery] = useState('');
  const [autoJoinSpaces, setAutoJoinSpaces] = useState<string[]>([]);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Fetch spaces from API
  useEffect(() => {
    const fetchSpaces = async () => {
      try {
        const majors = onboardingData?.majors?.join(',') || '';
        const response = await fetch(`/api/spaces/onboarding-discovery?majors=${encodeURIComponent(majors)}`);

        if (!response.ok) {
          throw new Error('Failed to fetch spaces');
        }

        const data: SpacesResponse = await response.json();
        setSpaces(data.spaces);

                 // Auto-select academic spaces based on user's majors
         if (onboardingData?.majors && data.spaces.academic.length > 0) {
           const matchingAcademicSpaces = data.spaces.academic
             .filter(space => 
               space.tags?.some(tag => 
                 onboardingData.majors?.some(major => 
                   major.toLowerCase().includes(tag.sub_type.toLowerCase()) ||
                   tag.sub_type.toLowerCase().includes(major.toLowerCase())
                 )
               )
             )
             .map(space => space.id);

          setAutoJoinSpaces(matchingAcademicSpaces);
          setSelectedSpaces(prev => [...new Set([...prev, ...matchingAcademicSpaces])]);
        }

      } catch (error) {
        logger.error('Failed to fetch spaces:', error);
      } finally {
        setLoading(false);
      }
    };

         void fetchSpaces();
  }, [onboardingData?.majors]);

  const handleSpaceToggle = (spaceId: string) => {
    setSelectedSpaces(prev => 
      prev.includes(spaceId) 
        ? prev.filter(id => id !== spaceId)
        : [...prev, spaceId]
    );
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      // Update onboarding data with selected spaces
      updateOnboardingData({
        selectedSpaces,
        autoJoinSpaces,
        spaceDiscoveryCompleted: true
      });

      logger.info('Space discovery completed', { 
        selectedSpaces: selectedSpaces.length,
        autoJoinSpaces: autoJoinSpaces.length 
      });

      // Navigate to display name step
      router.push('/onboarding/3');
    } catch (error) {
      logger.error('Failed to save space selections', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const getFilteredSpaces = (categorySpaces: SpaceData[]) => {
    if (!searchQuery) return categorySpaces;

    return categorySpaces.filter(space =>
      space.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      space.description.toLowerCase().includes(searchQuery.toLowerCase())
    );
  };

  if (loading) {
    return (
      <div className="max-w-2xl mx-auto p-6">
        <div className="text-center">
          <Loader2 className="h-8 w-8 animate-spin mx-auto mb-4 text-accent" />
          <h2 className="text-xl font-semibold mb-2">Loading Spaces</h2>
          <p className="text-muted-foreground">Finding communities that match your interests...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-2xl mx-auto p-6">
      <div className="text-center mb-8">
        <h1 className="text-3xl font-bold text-foreground mb-3">
          Discover Your Communities
        </h1>
        <p className="text-lg text-muted-foreground">
          Find and join spaces that match your interests and involvement on campus
        </p>
      </div>

      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Auto-Join Section */}
        {autoJoinSpaces.length > 0 && (
          <Card className="p-6 border-accent/20 bg-accent/5">
            <div className="flex items-center gap-3 mb-4">
              <CheckCircle className="h-5 w-5 text-accent" />
              <h3 className="font-semibold text-foreground">Auto-Joining Based on Your Major</h3>
            </div>
            <div className="space-y-2">
              {spaces?.academic
                .filter(space => autoJoinSpaces.includes(space.id))
                .map((space) => (
                  <div key={space.id} className="flex items-center justify-between p-3 bg-surface-02 rounded-lg">
                    <div className="flex items-center gap-3">
                      <GraduationCap className="h-4 w-4 text-accent" />
                      <div>
                        <span className="font-medium">{space.name}</span>
                        <p className="text-sm text-muted-foreground">{space.memberCount} members</p>
                      </div>
                    </div>
                    <Badge variant="outline" className="text-accent border-accent">
                      Auto-Joining
                    </Badge>
                  </div>
                ))}
            </div>
            <p className="text-sm text-muted-foreground mt-3">
              We&apos;ll automatically connect you with students in your academic programs
            </p>
          </Card>
        )}

        {/* Search */}
        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
          <Input
            type="text"
            placeholder="Search for specific organizations or interests..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-10 bg-surface-02 border-border/20"
          />
        </div>

        {/* Space Categories */}
        <div className="space-y-6">
          {SPACE_CATEGORIES.map((category) => {
            const Icon = category.icon;
            const categorySpaces = spaces?.[category.id as keyof typeof spaces] || [];
            const filteredSpaces = getFilteredSpaces(categorySpaces);

            if (filteredSpaces.length === 0) return null;

            return (
              <div key={category.id}>
                <div className="flex items-center gap-3 mb-4">
                  <div className="p-2 rounded-lg bg-surface-03">
                    <Icon className="h-5 w-5" />
                  </div>
                  <div>
                    <h3 className="font-semibold text-foreground">{category.title}</h3>
                    <p className="text-sm text-muted-foreground">{category.description}</p>
                  </div>
                </div>

                <div className="grid gap-3">
                  {filteredSpaces.slice(0, 6).map((space) => {
                    const isSelected = selectedSpaces.includes(space.id);
                    const isAutoJoin = autoJoinSpaces.includes(space.id);

                    return (
                      <Card 
                        key={space.id}
                        className={`p-4 cursor-pointer transition-all hover:border-accent/40 ${
                          isSelected ? 'border-accent bg-accent/5' : 'border-border/20'
                        }`}
                        onClick={() => !isAutoJoin && handleSpaceToggle(space.id)}
                      >
                        <div className="flex items-center justify-between">
                          <div className="flex-1">
                            <div className="flex items-center gap-3">
                              <h4 className="font-medium text-foreground">{space.name}</h4>
                              {isSelected && <CheckCircle className="h-4 w-4 text-accent" />}
                            </div>
                            <p className="text-sm text-muted-foreground mt-1 line-clamp-2">
                              {space.description}
                            </p>
                            <div className="flex items-center gap-4 mt-2">
                              <span className="text-xs text-muted-foreground">
                                {space.memberCount} members
                              </span>
                              {isAutoJoin && (
                                <Badge variant="outline" className="text-xs text-accent border-accent">
                                  Auto-Join
                                </Badge>
                              )}
                            </div>
                          </div>
                        </div>
                      </Card>
                    );
                  })}

                  {categorySpaces.length > 6 && (
                    <p className="text-sm text-muted-foreground text-center py-2">
                      +{categorySpaces.length - 6} more spaces available
                    </p>
                  )}
                </div>
              </div>
            );
          })}
        </div>

        {/* Selection Summary */}
        {selectedSpaces.length > 0 && (
          <Card className="p-4 bg-surface-02">
            <div className="flex items-center justify-between">
              <span className="text-sm text-muted-foreground">
                {selectedSpaces.length} space{selectedSpaces.length !== 1 ? 's' : ''} selected
              </span>
              <Button
                type="button"
                variant="ghost"
                size="sm"
                onClick={() => setSelectedSpaces(autoJoinSpaces)}
                className="text-muted-foreground hover:text-foreground"
              >
                Reset to auto-join only
              </Button>
            </div>
          </Card>
        )}

        {/* Navigation */}
        <div className="flex justify-between pt-6">
          <Button
            type="button"
            variant="ghost"
            onClick={() => router.push('/onboarding/1')}
            className="text-muted-foreground hover:text-foreground"
          >
            ‚Üê Back
          </Button>

          <Button
            type="submit"
            disabled={isSubmitting}
            className="bg-foreground text-background hover:bg-foreground/90 font-medium px-8"
          >
            {isSubmitting ? 'Saving...' : 'Continue'}
            {!isSubmitting && <ChevronRight className="ml-2 h-4 w-4" />}
          </Button>
        </div>
      </form>
    </div>
  );
}

import { test, expect } from "@playwright/test";
import { setupTestUser, cleanupTestData, type TestUser } from "./helpers/test-setup";
import type { AuthUser as _AuthUser } from "@hive/auth-logic";

interface TestSpace {
  id: string;
  name: string;
  description: string;
}

test.describe("Space Feed Error Scenarios", () => {
  let testUser: TestUser;
  let testSpace: TestSpace;
  let user: TestUser;

  test.beforeEach(async ({ page }) => {
    testUser = await setupTestUser(page);
    testSpace = {
      id: `test-space-${Date.now()}`,
      name: "Test Space",
      description: "A space for testing feed errors",
    };

    // Setup authenticated user
    await page.goto("/auth/login");
    await page.fill('[data-testid="email-input"]', testUser.email);
    await page.click('[data-testid="send-magic-link"]');
    await page.goto(
      `/auth/verify?token=test-token&email=${encodeURIComponent(testUser.email)}`
    );

    // Navigate to test space
    await page.goto(`/spaces/${testSpace.id}`);

    user = await setupTestUser(page);
  });

  test.afterEach(async ({ page }) => {
    await cleanupTestData(page);
  });

  test("should handle network failures gracefully", async ({ page }) => {
    // Test 1: Feed loading failure
    await page.route("/api/spaces/*/posts*", (route) => {
      route.fulfill({
        status: 500,
        contentType: "application/json",
        body: JSON.stringify({ error: "Database connection failed" }),
      });
    });

    await page.reload();

    // Verify error state
    await expect(page.locator('[data-testid="feed-error"]')).toBeVisible();
    await expect(
      page.locator('[data-testid="feed-error-message"]')
    ).toContainText("Failed to load posts");
    await expect(page.locator('[data-testid="retry-button"]')).toBeVisible();

    // Test retry functionality
    await page.unroute("/api/spaces/*/posts*");
    await page.click('[data-testid="retry-button"]');

    await expect(page.locator('[data-testid="feed-composer"]')).toBeVisible();
    await expect(page.locator('[data-testid="feed-error"]')).not.toBeVisible();

    // Test 2: Post creation failure
    await page.route("/api/spaces/*/posts", (route) => {
      if (route.request().method() === "POST") {
        route.fulfill({
          status: 503,
          contentType: "application/json",
          body: JSON.stringify({ error: "Service temporarily unavailable" }),
        });
      } else {
        route.continue();
      }
    });

    await page.fill(
      '[data-testid="post-content-input"]',
      "This post should fail"
    );
    await page.click('[data-testid="post-submit"]');

    // Verify error message
    await expect(page.locator('[data-testid="post-error"]')).toBeVisible();
    await expect(page.locator('[data-testid="post-error"]')).toContainText(
      "Service temporarily unavailable"
    );

    // Verify post content is preserved for retry
    await expect(
      page.locator('[data-testid="post-content-input"]')
    ).toHaveValue("This post should fail");

    // Test 3: Reaction failure
    await page.unroute("/api/spaces/*/posts");

    // Create a post first
    await page.fill(
      '[data-testid="post-content-input"]',
      "Test post for reaction failure"
    );
    await page.click('[data-testid="post-submit"]');
    await expect(
      page.locator('[data-testid="post-card"]').first()
    ).toBeVisible();

    // Mock reaction endpoint failure
    await page.route("/api/spaces/*/posts/*/react", (route) => {
      route.fulfill({
        status: 429,
        contentType: "application/json",
        body: JSON.stringify({ error: "Too many requests" }),
      });
    });

    await page.click('[data-testid="heart-reaction"]');

    // Verify reaction error (should be subtle, not blocking)
    await expect(page.locator('[data-testid="reaction-error"]')).toBeVisible();
    await expect(page.locator('[data-testid="reaction-error"]')).toContainText(
      "Failed to react"
    );

    // Error should auto-dismiss after a few seconds
    await expect(
      page.locator('[data-testid="reaction-error"]')
    ).not.toBeVisible({ timeout: 5000 });
  });

  test("should handle slow network conditions", async ({ page }) => {
    // Simulate slow network
    await page.route("/api/spaces/*/posts*", async (route) => {
      await new Promise((resolve) => setTimeout(resolve, 3000)); // 3 second delay
      route.continue();
    });

    await page.reload();

    // Verify loading state
    await expect(page.locator('[data-testid="feed-loading"]')).toBeVisible();
    await expect(page.locator('[data-testid="loading-spinner"]')).toBeVisible();

    // Wait for content to load
    await expect(page.locator('[data-testid="feed-composer"]')).toBeVisible({
      timeout: 10000,
    });
    await expect(
      page.locator('[data-testid="feed-loading"]')
    ).not.toBeVisible();

    // Test slow post creation
    await page.route("/api/spaces/*/posts", async (route) => {
      if (route.request().method() === "POST") {
        await new Promise((resolve) => setTimeout(resolve, 2000)); // 2 second delay
        route.continue();
      } else {
        route.continue();
      }
    });

    await page.fill(
      '[data-testid="post-content-input"]',
      "Slow post creation test"
    );
    await page.click('[data-testid="post-submit"]');

    // Verify loading state during post creation
    await expect(page.locator('[data-testid="post-submit"]')).toContainText(
      "Posting..."
    );
    await expect(page.locator('[data-testid="post-submit"]')).toBeDisabled();

    // Wait for post to be created
    await expect(page.locator('[data-testid="post-submit"]')).toContainText(
      "Post"
    );
    await expect(page.locator('[data-testid="post-submit"]')).toBeEnabled();
    await expect(
      page.locator('[data-testid="post-card"]').first()
    ).toBeVisible();
  });

  test("should validate post content", async ({ page }) => {
    // Test 1: Empty content
    await page.click('[data-testid="post-submit"]');

    // Submit button should be disabled for empty content
    await expect(page.locator('[data-testid="post-submit"]')).toBeDisabled();

    // Test 2: Content too long
    const longContent = "a".repeat(501); // Exceeds 500 char limit
    await page.fill('[data-testid="post-content-input"]', longContent);

    // Verify character count warning
    await expect(page.locator('[data-testid="char-count"]')).toHaveClass(
      /text-destructive/
    );
    await expect(page.locator('[data-testid="char-count"]')).toContainText(
      "-1"
    );

    // Submit should be disabled
    await expect(page.locator('[data-testid="post-submit"]')).toBeDisabled();

    // Test 3: Content at limit
    const limitContent = "a".repeat(500);
    await page.fill('[data-testid="post-content-input"]', limitContent);

    await expect(page.locator('[data-testid="char-count"]')).toContainText("0");
    await expect(page.locator('[data-testid="post-submit"]')).toBeEnabled();

    // Test 4: Profanity filter
    await page.fill(
      '[data-testid="post-content-input"]',
      "This is spam advertisement content"
    );
    await page.click('[data-testid="post-submit"]');

    await expect(page.locator('[data-testid="profanity-error"]')).toBeVisible();
    await expect(page.locator('[data-testid="profanity-error"]')).toContainText(
      "inappropriate content"
    );

    // Test 5: Special characters and emojis
    await page.fill(
      '[data-testid="post-content-input"]',
      "Test with emojis üéâüöÄ and special chars: @#$%^&*()"
    );
    await page.click('[data-testid="post-submit"]');

    // Should work fine
    await expect(
      page.locator('[data-testid="post-card"]').first()
    ).toBeVisible();
    await expect(
      page.locator('[data-testid="post-content"]').first()
    ).toContainText("üéâüöÄ");
  });

  test("should handle authentication errors", async ({ page }) => {
    // Mock authentication failure
    await page.route("/api/spaces/*/posts", (route) => {
      route.fulfill({
        status: 401,
        contentType: "application/json",
        body: JSON.stringify({ error: "Unauthorized" }),
      });
    });

    await page.fill(
      '[data-testid="post-content-input"]',
      "This should fail due to auth"
    );
    await page.click('[data-testid="post-submit"]');

    // Should redirect to login or show auth error
    await expect(page.locator('[data-testid="auth-error"]')).toBeVisible();
    await expect(page.locator('[data-testid="auth-error"]')).toContainText(
      "Please log in again"
    );
  });

  test("should handle permission errors", async ({ page }) => {
    // Mock permission denied (user not member of space)
    await page.route("/api/spaces/*/posts", (route) => {
      route.fulfill({
        status: 403,
        contentType: "application/json",
        body: JSON.stringify({ error: "Not a member of this space" }),
