#!/usr/bin/env node
/* eslint-env node */
/**
 * Fix import paths in feature stories to point to correct atomic component locations
 */

import fs from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';
import { execSync } from 'child_process';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// Map of component names to their actual locations in src/atomic/
const componentLocationMap = {
  // Atoms
  'alert': '../../atomic/atoms/alert',
  'avatar': '../../atomic/atoms/avatar',
  'badge': '../../atomic/atoms/badge',
  'button': '../../atomic/atoms/button',
  'card': '../../atomic/atoms/card',
  'check-icon': '../../atomic/atoms/check-icon',
  'checkbox': '../../atomic/atoms/checkbox',
  'command': '../../atomic/atoms/command',
  'dialog': '../../atomic/atoms/dialog',
  'grid': '../../atomic/atoms/grid',
  'hive-button': '../../atomic/atoms/hive-button',
  'hive-card': '../../atomic/atoms/hive-card',
  'hive-confirm-modal': '../../atomic/atoms/hive-confirm-modal',
  'hive-input': '../../atomic/atoms/hive-input',
  'hive-logo-dynamic': '../../atomic/atoms/hive-logo-dynamic',
  'hive-logo': '../../atomic/atoms/hive-logo',
  'hive-modal': '../../atomic/atoms/hive-modal',
  'hive-progress': '../../atomic/atoms/hive-progress',
  'input-enhanced': '../../atomic/atoms/input-enhanced',
  'input': '../../atomic/atoms/input',
  'label': '../../atomic/atoms/label',
  'notification-bell': '../../atomic/atoms/notification-bell',
  'notification-item': '../../atomic/atoms/notification-item',
  'presence-indicator': '../../atomic/atoms/presence-indicator',
  'progress': '../../atomic/atoms/progress',
  'select': '../../atomic/atoms/select',
  'simple-avatar': '../../atomic/atoms/simple-avatar',
  'skeleton': '../../atomic/atoms/skeleton',
  'skip-nav': '../../atomic/atoms/skip-nav',
  'slider': '../../atomic/atoms/slider',
  'switch': '../../atomic/atoms/switch',
  'tabs': '../../atomic/atoms/tabs',
  'tech-sleek-showcase': '../../atomic/atoms/tech-sleek-showcase',
  'textarea-enhanced': '../../atomic/atoms/textarea-enhanced',
  'textarea': '../../atomic/atoms/textarea',
  'top-bar-nav': '../../atomic/atoms/top-bar-nav',
  'universal-atoms': '../../atomic/atoms/universal-atoms',
  'navigation-preferences': '../../atomic/atoms/navigation-preferences',

  // Molecules
  'completion-psychology-enhancer': '../../atomic/molecules/completion-psychology-enhancer',
  'crisis-relief-interface': '../../atomic/molecules/crisis-relief-interface',
  'form-field': '../../atomic/molecules/form-field',
  'friend-request-manager': '../../atomic/molecules/friend-request-manager',
  'hive-avatar-upload-with-crop': '../../atomic/molecules/hive-avatar-upload-with-crop',
  'interest-selector': '../../atomic/molecules/interest-selector',
  'notification-dropdown': '../../atomic/molecules/notification-dropdown',
  'notification-toast-manager': '../../atomic/molecules/notification-toast-manager',
  'page-container': '../../atomic/molecules/page-container',
  'photo-carousel': '../../atomic/molecules/photo-carousel',
  'privacy-control': '../../atomic/molecules/privacy-control',
  'social-proof-accelerator': '../../atomic/molecules/social-proof-accelerator',

  // Organisms
  'complete-hive-tools-system': '../../atomic/organisms/complete-hive-tools-system',
  'hivelab-widget': '../../atomic/organisms/hivelab-widget',
  'navigation-shell': '../../atomic/organisms/navigation-shell',
  'notification-system': '../../atomic/organisms/notification-system',
  'profile-activity-widget': '../../atomic/organisms/profile-activity-widget',
  'profile-bento-grid': '../../atomic/organisms/profile-bento-grid',
  'profile-completion-card': '../../atomic/organisms/profile-completion-card',
  'profile-connections-widget': '../../atomic/organisms/profile-connections-widget',
  'profile-identity-widget': '../../atomic/organisms/profile-identity-widget',
  'profile-spaces-widget': '../../atomic/organisms/profile-spaces-widget',
  'welcome-mat': '../../atomic/organisms/welcome-mat',

  // Templates
  'profile-view-layout': '../../atomic/templates/profile-view-layout',
};

// Find all story files using find command
const featuresDir = path.join(__dirname, '..', 'src', 'features');
const storyFiles = execSync(`find "${featuresDir}" -name "*.stories.tsx"`, { encoding: 'utf-8' })
  .trim()
  .split('\n')
  .filter(Boolean);

console.log(`Found ${storyFiles.length} story files to fix\n`);

let fixedCount = 0;
let errorCount = 0;

for (const storyFile of storyFiles) {
  try {
    let content = fs.readFileSync(storyFile, 'utf-8');
    let modified = false;

    // Find all import statements with relative paths
    const importRegex = /import\s+({[^}]+}|[^{}\s]+)\s+from\s+['"]\.\/([^'"]+)['"]/g;

    content = content.replace(importRegex, (match, importedItems, componentPath) => {
      // Check if this component is in our map
      if (componentLocationMap[componentPath]) {
        modified = true;
        const correctPath = componentLocationMap[componentPath];
        console.log(`  ${path.basename(storyFile)}: ./${componentPath} ‚Üí ${correctPath}`);
        return `import ${importedItems} from '${correctPath}'`;
      }
      return match;
    });

    if (modified) {
      fs.writeFileSync(storyFile, content, 'utf-8');
      fixedCount++;
    }
  } catch (error) {
    console.error(`Error processing ${storyFile}:`, error);
    errorCount++;
  }
}

console.log(`\n‚úÖ Fixed ${fixedCount} story files`);
if (errorCount > 0) {
  console.log(`‚ùå Failed to fix ${errorCount} files`);
}

#!/usr/bin/env node

/* eslint-env node */
/* eslint no-console: "off" */
/* eslint no-undef: "off" */

/**
 * HIVE Storybook Import Standardization Script
 * Automatically fixes all broken import paths in Storybook stories
/**
 *This script systematically processes all .stories.* files and:
 * - Fixes broken button/input/component imports to use enhanced versions
 * - Corrects lib/utils path depth issues
 * - Standardizes component import paths to proper atomic structure
 * - Removes or fixes imports to non-existent components
 */

import fs from 'fs';
import path from 'path';
import { glob } from 'glob';

// Comprehensive import path fixes for all known issues
const IMPORT_FIXES = [
  // === ENHANCED COMPONENT FIXES ===
  // Button component standardization
  {
    pattern: /import\s*{\s*([^}]*\bButton\b[^}]*)\s*}\s*from\s*['"]\.\/button['"];?/g,
    replacement: "import { $1 } from './button-enhanced';"
  },

  // Input component standardization
  {
    pattern: /import\s*{\s*([^}]*\bInput\b[^}]*)\s*}\s*from\s*['"]\.\/input['"];?/g,
    replacement: "import { $1 } from './input-enhanced';"
  },

  // Select component standardization
  {
    pattern: /import\s*{\s*([^}]*\bSelect\b[^}]*)\s*}\s*from\s*['"]\.\/select['"];?/g,
    replacement: "import { $1 } from './select-radix';"
  },

  // Textarea component standardization
  {
    pattern: /import\s*{\s*([^}]*\bTextarea\b[^}]*)\s*}\s*from\s*['"]\.\/textarea['"];?/g,
    replacement: "import { $1 } from './textarea-enhanced';"
  },

  // Checkbox component standardization
  {
    pattern: /import\s*{\s*([^}]*\bCheckbox\b[^}]*)\s*}\s*from\s*['"]\.\/checkbox['"];?/g,
    replacement: "import { $1 } from './checkbox-enhanced';"
  },

  // Radio component standardization
  {
    pattern: /import\s*{\s*([^}]*\bRadio\b[^}]*)\s*}\s*from\s*['"]\.\/radio['"];?/g,
    replacement: "import { $1 } from './radio-enhanced';"
  }

  // Switch component standardization
  {
    pattern: /import\s*{\s*([^}]*\bSwitch\b[^}]*)\s*}\s*from\s*['"]\.\/switch['"];?/g,
    replacement: "import { $1 } from './switch-enhanced';"
  }

  // === LIB/UTILS PATH FIXES ===
  // Fix incorrect depth for lib/utils imports
  {
    pattern: /import\s*{\s*([^}]*)\s*}\s*from\s*['"]\.\.\/\.\.\/\.\.\/lib\/utils['"];?/g,
    replacement: "import { $1 } from '../../lib/utils';"
  }

  // Fix any quadruple-dot lib/utils paths
  {
    pattern: /import\s*{\s*([^}]*)\s*}\s*from\s*['"]\.\.\/\.\.\/\.\.\/\.\.\/lib\/utils['"];?/g,
    replacement: "import { $1 } from '../../lib/utils';"
  }

  // === COMPONENT PATH
  STANDARDIZATION ===
  // Fix components/ui paths to proper atomic paths
  {
    pattern: /import\s*{\s*([^}]*\bContainer\b[^}]*)\s*}\s*from\s*['"]\.\.\/\.\.\/components\/ui\/container['"];?/g,
    replacement: "import { $1 } from '../../atomic/atoms/container';"
  }

  {
    pattern: /import\s*{\s*([^}]*\bBadge\b[^}]*)\s*}\s*from\s*['"]\.\.\/\.\.\/components\/ui\/badge['"];?/g,
    replacement: "import { $1 } from '../../atomic/atoms/badge';"
  }

  {
    pattern: /import\s*{\s*([^}]*\bButton\b[^}]*)\s*}\s*from\s*['"]\.\.\/\.\.\/components\/ui\/button['"];?/g,
    replacement: "import { $1 } from '../../atomic/atoms/button-enhanced';"
  }

  // Fix atomic/ui paths (incorrect) to components/ui paths (correct)
  {
    pattern: /from\s*['"]\.\.\/\.\.\/atomic\/ui\// g    replacement: "from '../../components/ui/"
  }

  // === CLEAN UP RELATIVE PATH
  ISSUES ===
  // Standardize double quotes to single quotes for consistency
  {
    pattern: /from\s*"([^"]+)"/g,
    replacement: "from '$1'"
  }
];

// Additional problematic patterns to handle

const CLEANUP_PATTERNS = [
  // Remove duplicate imports
  {
    pattern: /(import\s*{\s*[^}]*}\s*from\s*'[^']+';?\s*\n)\s*\1/g,
    replacement: "$1"
  }

  // Clean up extra semicolons
  {
    pattern: /;+/g,
    replacement: ";"
  }

  // Fix spacing in

  import statements
  {
    pattern: /import\s*{\s*([^}]+)\s*}\s*from\s*'([^']+)'\s*;?/g,
    replacement: "import { $1 } from '$2';"
  }
];

// Files to completely remove (broken beyond repair)

const FILES_TO_REMOVE = [
  'src/stories/10-Auth-Onboarding/Complete-Auth-Onboarding-Journey.stories.tsx',
  // Add any other irreparable files here
];

/**
 * Process a single story file and fix all imports
 */
/* eslint-disable @typescript-eslint/no-unused-vars */
function _fixImportsInFile(filePath) {
  try {
    let _content = fs.readFileSync(filePath, 'utf8');
    let _modified = false;

    // Apply all

    import fixes
    IMPORT_FIXES.forEach(fix => {
      const _newContent = content.replace(fix.pattern, fix.replacement);
      if (newContent !== content) {
        content = newContent;
        modified = true;
      }
    });

    // Apply cleanup patterns
    CLEANUP_PATTERNS.forEach(cleanup => {
      const _newContent = content.replace(cleanup.pattern, cleanup.replacement);
      if (newContent !== content) {
        content = newContent;
        modified = true;
      }
    });

    // Write back if modified
    if (modified) {
      fs.writeFileSync(filePath, content, 'utf8');
      const _relativePath = path.relative(process.cwd(), filePath);
      console.log(`‚úÖ Fixed imports in: ${relativePath}`);
      return true;
    }

    return false;
  } catch (_error) {
    console.error(`‚ùå Error processing ${filePath}:`, error.message);
    return false;
  }
}

/**
 * Remove files that are broken beyond repair
 */
function _removeProblematicFiles() {
  let _removedCount = 0;

  FILES_TO_REMOVE.forEach(filePath => {
    try {
      if (fs.existsSync(filePath)) {
        fs.unlinkSync(filePath);
        console.log(`üóëÔ∏è  Removed broken file: ${filePath}`);
        removedCount++;
      }
    } catch (_error) {
      console.error(`‚ùå Error removing ${filePath}:`, error.message);
    }
  });

  return removedCount;
}

/**
 * Validate that all stories can be imported (basic syntax check)
 */
function _validateStoryFiles(storyFiles) {
  let _validCount = 0;
  let _invalidFiles = [];

  storyFiles.forEach(filePath => {
    try {
      const _content = fs.readFileSync(filePath, 'utf8');

      // Basic syntax validation
      if (content.includes('import') && content.includes('export')) {
        // Look for obvious issues
        const _hasUnresolvedImports = content.match(/import.*from\s*['"][^'"]*non-existent[^'"]*['"]/);
        const _hasMalformedImports = content.match(/import.*from\s*['"]['"]/);

        if (!hasUnresolvedImports && !hasMalformedImports) {
          validCount++;
        } else {
          invalidFiles.push(filePath);
        }
      } else {
        invalidFiles.push(filePath);
      }
    } catch (_error) {
      invalidFiles.push(filePath);
    }
  });

  return { validCount, invalidFiles };
}

/**
 * Main execution function
 */
function _fixAllStoryImports() {
  console.log('üîß HIVE Storybook Import Standardization Starting...\n');

  // Step
  1: Remove problematic files
  console.log('üìÅ Removing broken files...');
  const _removedCount = removeProblematicFiles();
  if (removedCount > 0) {
    console.log(`üóëÔ∏è  Removed ${removedCount} broken files\n`);
  }

  // Step
  2: Find all story files
  const _storyFiles = glob.sync('src/**/*.stories.{ts,tsx,js,jsx}', {
    cwd: process.cwd(),
    absolute: true
  });

  console.log(`üìÅ Found ${storyFiles.length} story files to process\n`);

  // Step
  3: Process each story file
  let _processedCount = 0;
  let _fixedCount = 0;

  storyFiles.forEach(filePath => {
    processedCount++;
    const _wasFixed = fixImportsInFile(filePath);
    if (wasFixed) {
      fixedCount++;
    }
  });

  // Step
  4: Validate results
  console.log(`\nüîç Validating story files...`);
  const { validCount, invalidFiles } = validateStoryFiles(storyFiles);

  // Step
  5: Report results
  console.log(`\nüìä Import Standardization Complete:`);
  console.log(`   ‚Ä¢ Files processed: ${processedCount}`);
  console.log(`   ‚Ä¢ Files fixed: ${fixedCount}`);
  console.log(`   ‚Ä¢ Files removed: ${removedCount}`);
  console.log(`   ‚Ä¢ Files unchanged: ${processedCount - fixedCount}`);
  console.log(`   ‚Ä¢ Valid stories: ${validCount}/${storyFiles.length}`);

  if (invalidFiles.length > 0) {
    console.log(`\n‚ö†Ô∏è  Files that may need manual attention:`);
    invalidFiles.slice(0, 5).forEach(file => {
      console.log(`   ‚Ä¢ ${path.relative(process.cwd(), file)}`);
    });
    if (invalidFiles.length > 5) {
      console.log(`   ... and ${invalidFiles.length - 5} more`);
    }
  }

  if (fixedCount > 0 || removedCount > 0) {
    console.log(`\n‚úÖ Storybook import standardization completed successfully!`);
    console.log(`\nüî® Next steps:`);
    console.log(`   1. Run 'pnpm build-storybook' to verify all imports work`);
    console.log(`   2. Check Storybook with 'pnpm storybook' for visual validation`);
    console.log(`   3. Consider running 'pnpm story:validate' for comprehensive checks`);
  } else {
    console.log(`\n‚ú® All imports already standardized - no changes needed!`);
  }

  return {
    processed: processedCount,
    fixed: fixedCount,
    removed: removedCount,
    valid: validCount,
    invalid: invalidFiles.length
  };
}

// Execute the script
// fixAllStoryImports();

#!/usr/bin/env node
/* eslint-env node */
/**
 * Reorganize Storybook stories from atomic design to vertical slices (features)
 *
 * This script:
 * 1. Reads all existing story files
 * 2. Maps them to product features
 * 3. Copies them to feature folders
 * 4. Updates story titles to match feature organization
 */

const fs = require('fs');
const path = require('path');

// Feature mapping based on component names
const FEATURE_MAP = {
  // 01-Onboarding
  'onboarding': '01-Onboarding',
  'welcome': '01-Onboarding',
  'interest-selector': '01-Onboarding',
  'completion-psychology': '01-Onboarding',
  'hive-progress': '01-Onboarding',

  // 02-Profile
  'profile': '02-Profile',
  'avatar': '02-Profile',
  'presence-indicator': '02-Profile',

  // 03-Spaces
  'space': '03-Spaces',
  'photo-carousel': '03-Spaces',
  'privacy-control': '03-Spaces',

  // 04-Feed
  // (to be added)

  // 05-HiveLab
  'hivelab': '05-HiveLab',
  'tool': '05-HiveLab',

  // 06-Rituals
  'ritual': '06-Rituals',

  // 07-Notifications
  'notification': '07-Notifications',

  // 08-Navigation
  'navigation': '08-Navigation',
  'top-bar-nav': '08-Navigation',
  'skip-nav': '08-Navigation',

  // 09-Social
  'friend-request': '09-Social',
  'social-proof': '09-Social',
  'crisis-relief': '09-Social',

  // 10-Forms
  'form-field': '10-Forms',
  'input': '10-Forms',
  'textarea': '10-Forms',
  'checkbox': '10-Forms',
  'select': '10-Forms',
  'slider': '10-Forms',
  'switch': '10-Forms',
  'label': '10-Forms',
  'command': '10-Forms',

  // 11-Shared (everything else)
  'button': '11-Shared',
  'card': '11-Shared',
  'modal': '11-Shared',
  'dialog': '11-Shared',
  'alert': '11-Shared',
  'badge': '11-Shared',
  'progress': '11-Shared',
  'skeleton': '11-Shared',
  'tabs': '11-Shared',
  'grid': '11-Shared',
  'page-container': '11-Shared',
  'hive-logo': '11-Shared',
  'check-icon': '11-Shared',
  'tech-sleek': '11-Shared',
  'universal-atoms': '11-Shared',
};

function getFeatureFolder(filename) {
  const baseName = path.basename(filename, '.stories.tsx').toLowerCase();

  // Check each feature keyword
  for (const [keyword, feature] of Object.entries(FEATURE_MAP)) {
    if (baseName.includes(keyword)) {
      return feature;
    }
  }

  // Default to Shared for unmatched components
  return '11-Shared';
}

function updateStoryTitle(content, filename, feature) {
  const componentName = path.basename(filename, '.stories.tsx')
    .split('-')
    .map(word => word.charAt(0).toUpperCase() + word.slice(1))
    .join(' ');

  // Update the title to feature-based path
  return content.replace(
    /title:\s*['"]([^'"]+)['"]/,
    `title: '${feature}/${componentName}'`
  );
}

function main() {
  const srcDir = path.join(__dirname, '../src');
  const atomicDir = path.join(srcDir, 'atomic');
  const featuresDir = path.join(srcDir, 'features');

  console.log('üîÑ Reorganizing Storybook stories to vertical slices...\n');

  // Find all story files in atomic folder
  const findStories = (dir) => {
    const files = [];
    const items = fs.readdirSync(dir, { withFileTypes: true });

    for (const item of items) {
      const fullPath = path.join(dir, item.name);
      if (item.isDirectory()) {
        files.push(...findStories(fullPath));
      } else if (item.name.endsWith('.stories.tsx')) {
        files.push(fullPath);
      }
    }

    return files;
  };

  const storyFiles = findStories(atomicDir);
  console.log(`Found ${storyFiles.length} story files\n`);

  const stats = {
    copied: 0,
    skipped: 0,
    errors: 0,
  };

  for (const storyFile of storyFiles) {
    try {
      const feature = getFeatureFolder(storyFile);
      const filename = path.basename(storyFile);
      const targetDir = path.join(featuresDir, feature);
      const targetFile = path.join(targetDir, filename);

      // Read and update content
      let content = fs.readFileSync(storyFile, 'utf8');
      content = updateStoryTitle(content, storyFile, feature);

      // Ensure target directory exists
      if (!fs.existsSync(targetDir)) {
        fs.mkdirSync(targetDir, { recursive: true });
      }

      // Write updated story
      fs.writeFileSync(targetFile, content);
      console.log(`‚úÖ ${filename} ‚Üí ${feature}/`);
      stats.copied++;

    } catch (error) {
      console.error(`‚ùå Error processing ${path.basename(storyFile)}:`, error.message);
      stats.errors++;
    }
  }

  console.log(`\nüìä Summary:`);
  console.log(`   Copied: ${stats.copied}`);
  console.log(`   Errors: ${stats.errors}`);
  console.log(`\n‚úÖ Reorganization complete!`);
  console.log(`\nNext steps:`);
  console.log(`1. Review stories in src/features/`);
  console.log(`2. Update .storybook/main.ts story patterns`);
  console.log(`3. Restart Storybook to see changes`);
}

main();

