          <div className="text-center text-sm text-muted-foreground mb-4">
            {pullDistance === 0 && !isRefreshing && "‚Üì Pull down to refresh"}
            {pullDistance > 0 && pullDistance < 80 && "‚Üì Keep pulling..."}
            {pullDistance >= 80 && !isRefreshing && "‚Üë Release to refresh"}
