/**
 * Mini-Map Component
 *
 * Overview map showing entire canvas with viewport indicator.
 * Allows quick navigation by clicking or dragging viewport rectangle.
 */

'use client';

import React, { useRef, useState, useCallback } from 'react';
import { cn } from '../../../lib/utils';
import type { Viewport, Page, Element } from '../../../types/hivelab.types';

export interface MiniMapProps {
  /** Current viewport */
  viewport: Viewport;
  /** All pages on canvas */
  pages: Page[];
  /** Current page ID */
  currentPageId?: string;
  /** Container dimensions (to calculate viewport rectangle) */
  containerWidth: number;
  containerHeight: number;
  /** Mini-map width in pixels */
  width?: number;
  /** Mini-map height in pixels */
  height?: number;
  /** Position on screen */
  position?: 'top-left' | 'top-right' | 'bottom-left' | 'bottom-right';
  /** Callback when viewport should change (from click/drag) */
  onViewportChange?: (viewport: Partial<Viewport>) => void;
  /** Additional class names */
  className?: string;
  /** Show element count */
  showElementCount?: boolean;
}

export function MiniMap({
  viewport,
  pages,
  currentPageId,
  containerWidth,
  containerHeight,
  width = 200,
  height = 150,
  position = 'bottom-left',
  onViewportChange,
  className,
  showElementCount = true,
}: MiniMapProps) {
  const miniMapRef = useRef<SVGSVGElement>(null);
  const [isDragging, setIsDragging] = useState(false);

  // ============================================================================
  // Calculate Canvas Bounds
  // ============================================================================

  const canvasBounds = React.useMemo(() => {
    if (pages.length === 0) {
      return { minX: 0, minY: 0, maxX: 1000, maxY: 1000 };
    }

    let minX = Infinity;
    let minY = Infinity;
    let maxX = -Infinity;
    let maxY = -Infinity;

    pages.forEach(page => {
      minX = Math.min(minX, page.x);
      minY = Math.min(minY, page.y);
      maxX = Math.max(maxX, page.x + page.width);
      maxY = Math.max(maxY, page.y + page.height);
    });

    // Add padding
    const padding = 100;
    return {
      minX: minX - padding,
      minY: minY - padding,
      maxX: maxX + padding,
      maxY: maxY + padding,
    };
  }, [pages]);

  const canvasWidth = canvasBounds.maxX - canvasBounds.minX;
  const canvasHeight = canvasBounds.maxY - canvasBounds.minY;

  // ============================================================================
  // Scale Factors
  // ============================================================================

  const scaleX = width / canvasWidth;
  const scaleY = height / canvasHeight;
  const scale = Math.min(scaleX, scaleY);

  // ============================================================================
  // Viewport Rectangle
  // ============================================================================

  const viewportRect = React.useMemo(() => {
    // Calculate visible area in canvas coordinates
    const visibleX = -viewport.x / viewport.zoom;
    const visibleY = -viewport.y / viewport.zoom;
    const visibleWidth = containerWidth / viewport.zoom;
    const visibleHeight = containerHeight / viewport.zoom;

    // Convert to mini-map coordinates
    return {
      x: (visibleX - canvasBounds.minX) * scale,
      y: (visibleY - canvasBounds.minY) * scale,
      width: visibleWidth * scale,
      height: visibleHeight * scale,
    };
  }, [viewport, containerWidth, containerHeight, canvasBounds, scale]);

  // ============================================================================
  // Position Classes
  // ============================================================================

  const positionClasses = {
    'top-left': 'top-4 left-4',
    'top-right': 'top-4 right-4',
    'bottom-left': 'bottom-20 left-4',
    'bottom-right': 'bottom-20 right-4',
  };

  // ============================================================================
  // Interaction Handlers
  // ============================================================================

  const handleMouseDown = useCallback((e: React.MouseEvent<SVGSVGElement>) => {
    if (!onViewportChange) return;

    setIsDragging(true);

    const handleMove = (moveEvent: MouseEvent) => {
      const rect = miniMapRef.current?.getBoundingClientRect();
      if (!rect) return;

      const x = moveEvent.clientX - rect.left;
      const y = moveEvent.clientY - rect.top;

      // Convert mini-map coordinates to canvas coordinates
      const canvasX = x / scale + canvasBounds.minX;
      const canvasY = y / scale + canvasBounds.minY;

      // Center viewport on clicked position
      onViewportChange({
        x: -(canvasX * viewport.zoom) + containerWidth / 2,
        y: -(canvasY * viewport.zoom) + containerHeight / 2,
      });
    };

    const handleUp = () => {
      setIsDragging(false);
      window.removeEventListener('mousemove', handleMove);
      window.removeEventListener('mouseup', handleUp);
    };

    window.addEventListener('mousemove', handleMove);
    window.addEventListener('mouseup', handleUp);

    // Immediate navigation to click position
    const rect = miniMapRef.current?.getBoundingClientRect();
    if (!rect) return;

    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    const canvasX = x / scale + canvasBounds.minX;
    const canvasY = y / scale + canvasBounds.minY;

    onViewportChange({
      x: -(canvasX * viewport.zoom) + containerWidth / 2,
      y: -(canvasY * viewport.zoom) + containerHeight / 2,
    });
  }, [onViewportChange, scale, canvasBounds, viewport.zoom, containerWidth, containerHeight]);

  // ============================================================================
  // Render
  // ============================================================================

  return (
    <div
      className={cn(
        'fixed z-50',
        'bg-background/95 backdrop-blur-sm',
        'border rounded-lg shadow-lg',
        'p-2',
        positionClasses[position],
        className
      )}
    >
      <svg
        ref={miniMapRef}
        width={width}
        height={height}
        className={cn(
          'border border-border/50 rounded',
          onViewportChange && 'cursor-pointer'
        )}
        onMouseDown={handleMouseDown}
      >
        {/* Background */}
        <rect
          width={width}
          height={height}
          fill="hsl(var(--muted) / 0.3)"
        />

        {/* Pages */}
        {pages.map(page => {
          const pageX = (page.x - canvasBounds.minX) * scale;
          const pageY = (page.y - canvasBounds.minY) * scale;
          const pageWidth = page.width * scale;
          const pageHeight = page.height * scale;

          const isCurrentPage = page.id === currentPageId;

          return (
            <g key={page.id}>
              {/* Page rectangle */}
              <rect
                x={pageX}
                y={pageY}
                width={pageWidth}
                height={pageHeight}
                fill={isCurrentPage ? 'hsl(var(--primary) / 0.3)' : 'hsl(var(--muted-foreground) / 0.2)'}
                stroke={isCurrentPage ? 'hsl(var(--primary))' : 'hsl(var(--border))'}
                strokeWidth={isCurrentPage ? 2 : 1}
                rx={2}
              />

              {/* Elements (as tiny dots) */}
              {page.elements.map(element => {
                const elementX = (element.x - canvasBounds.minX) * scale;
                const elementY = (element.y - canvasBounds.minY) * scale;

                return (
                  <circle
                    key={element.id}
                    cx={elementX + element.width * scale / 2}
                    cy={elementY + element.height * scale / 2}
                    r={Math.max(1, 2 * scale)}
                    fill="hsl(var(--foreground) / 0.4)"
                  />
                );
              })}
            </g>
          );
        })}

        {/* Viewport rectangle */}
        <rect
          x={viewportRect.x}
          y={viewportRect.y}
          width={viewportRect.width}
          height={viewportRect.height}
          fill="none"
          stroke="hsl(var(--primary))"
          strokeWidth={2}
          strokeDasharray="4 2"
          rx={2}
          className={cn(
            'pointer-events-none',
            isDragging && 'opacity-50'
          )}
        />
      </svg>

      {/* Stats */}
      {showElementCount && (
        <div className="mt-1.5 text-xs text-muted-foreground text-center">
          {pages.reduce((sum, page) => sum + page.elements.length, 0)} elements
        </div>
      )}
    </div>
  );
}

MiniMap.displayName = 'MiniMap';

import * as React from "react"
import * as RechartsPrimitive from "recharts"

import { cn } from "../../lib/utils"

// Format: { THEME_NAME: CSS_SELECTOR }
const THEMES = { light: "", dark: ".dark" } as const

export type ChartConfig = {
  [k in string]: {
    label?: React.ReactNode
    icon?: React.ComponentType
  } & (
    | { color?: string; theme?: never }
    | { color?: never; theme: Record<keyof typeof THEMES, string> }
  )
}

type ChartContextProps = {
  config: ChartConfig
}

const ChartContext = React.createContext<ChartContextProps | null>(null)

function useChart() {
  const context = React.useContext(ChartContext)

  if (!context) {
    throw new Error("useChart must be used within a <ChartContainer />")
  }

  return context
}

const ChartContainer = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> & {
    config: ChartConfig
    children: React.ComponentProps<
      typeof RechartsPrimitive.ResponsiveContainer
    >["children"]
  }
>(({ id, className, children, config, ...props }, ref) => {
  const uniqueId = React.useId()
  const chartId = `chart-${id || uniqueId.replace(/:/g, "")}`

  return (
    <ChartContext.Provider value={{ config }}>
      <div
        data-chart={chartId}
        ref={ref}
        className={cn(
          "flex aspect-video justify-center text-xs [&_.recharts-cartesian-axis-tick_text]:fill-muted-foreground [&_.recharts-cartesian-grid_line[stroke='#ccc']]:stroke-border/50 [&_.recharts-curve.recharts-tooltip-cursor]:stroke-border [&_.recharts-dot[stroke='#fff']]:stroke-transparent [&_.recharts-layer]:outline-none [&_.recharts-polar-grid_[stroke='#ccc']]:stroke-border [&_.recharts-radial-bar-background-sector]:fill-muted [&_.recharts-rectangle.recharts-tooltip-cursor]:fill-muted [&_.recharts-reference-line_[stroke='#ccc']]:stroke-border [&_.recharts-sector[stroke='#fff']]:stroke-transparent [&_.recharts-sector]:outline-none [&_.recharts-surface]:outline-none",
          className
        )}
        {...props}
      >
        <ChartStyle id={chartId} config={config} />
        <RechartsPrimitive.ResponsiveContainer>
          {children}
        </RechartsPrimitive.ResponsiveContainer>
      </div>
    </ChartContext.Provider>
  )
})
ChartContainer.displayName = "Chart"

const ChartStyle = ({ id, config }: { id: string; config: ChartConfig }) => {
  const colorConfig = Object.entries(config).filter(
    ([, config]) => config.theme || config.color
  )

  if (!colorConfig.length) {
    return null
  }

  return (
    <style
      dangerouslySetInnerHTML={{
        __html: Object.entries(THEMES)
          .map(
            ([theme, prefix]) => `
${prefix} [data-chart=${id}] {
${colorConfig
  .map(([key, itemConfig]) => {
    const color =
      itemConfig.theme?.[theme as keyof typeof itemConfig.theme] ||
      itemConfig.color
    return color ? `  --color-${key}: ${color};` : null
  })
  .join("\n")}
}
`
          )
          .join("\n"),
      }}
    />
  )
}

const ChartTooltip = RechartsPrimitive.Tooltip

const ChartTooltipContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<typeof RechartsPrimitive.Tooltip> &
    React.ComponentProps<"div"> & {
      hideLabel?: boolean
      hideIndicator?: boolean
      indicator?: "line" | "dot" | "dashed"
      nameKey?: string
      labelKey?: string
    }
>(
  (
    {
      active,
      payload,
      className,
      indicator = "dot",
      hideLabel = false,
      hideIndicator = false,
      label,
      labelFormatter,
      labelClassName,
      formatter,
      color,
      nameKey,
      labelKey,
    },
    ref
  ) => {
    const { config } = useChart()

    const tooltipLabel = React.useMemo(() => {
      if (hideLabel || !payload?.length) {
        return null
      }

      const [item] = payload
      const key = `${labelKey || item?.dataKey || item?.name || "value"}`
      const itemConfig = getPayloadConfigFromPayload(config, item, key)
      const value =
        !labelKey && typeof label === "string"
          ? config[label as keyof typeof config]?.label || label
          : itemConfig?.label

      if (labelFormatter) {
        return (
          <div className={cn("font-medium", labelClassName)}>
            {labelFormatter(value, payload)}
          </div>
        )
      }

      if (!value) {
        return null
      }

      return <div className={cn("font-medium", labelClassName)}>{value}</div>
    }, [
      label,
      labelFormatter,
      payload,
      hideLabel,
      labelClassName,
      config,
      labelKey,
    ])

    if (!active || !payload?.length) {
      return null
    }

    const nestLabel = payload.length === 1 && indicator !== "dot"

    return (
      <div
        ref={ref}
        className={cn(
          "grid min-w-[8rem] items-start gap-1.5 rounded-lg border border-border/50 bg-background px-2.5 py-1.5 text-xs shadow-xl",
          className
        )}
      >
        {!nestLabel ? tooltipLabel : null}
        <div className="grid gap-1.5">
          {payload
            .filter((item) => item.type !== "none")
            .map((item, index) => {
              const key = `${nameKey || item.name || item.dataKey || "value"}`
              const itemConfig = getPayloadConfigFromPayload(config, item, key)
              const indicatorColor = color || item.payload.fill || item.color

              return (
                <div
                  key={item.dataKey}
                  className={cn(
                    "flex w-full flex-wrap items-stretch gap-2 [&>svg]:h-2.5 [&>svg]:w-2.5 [&>svg]:text-muted-foreground",
                    indicator === "dot" && "items-center"
                  )}
                >
                  {formatter && item?.value !== undefined && item.name ? (
                    formatter(item.value, item.name, item, index, item.payload)
                  ) : (
                    <>
                      {itemConfig?.icon ? (
                        <itemConfig.icon />
                      ) : (
                        !hideIndicator && (
                          <div
                            className={cn(
                              "shrink-0 rounded-[2px] border-[--color-border] bg-[--color-bg]",
                              {
                                "h-2.5 w-2.5": indicator === "dot",
                                "w-1": indicator === "line",
                                "w-0 border-[1.5px] border-dashed bg-transparent":
                                  indicator === "dashed",
                                "my-0.5": nestLabel && indicator === "dashed",
                              }
                            )}
                            style={
                              {
                                "--color-bg": indicatorColor,
                                "--color-border": indicatorColor,
                              } as React.CSSProperties
                            }
                          />
                        )
                      )}
                      <div
                        className={cn(
                          "flex flex-1 justify-between leading-none",
                          nestLabel ? "items-end" : "items-center"
                        )}
                      >
                        <div className="grid gap-1.5">
                          {nestLabel ? tooltipLabel : null}
                          <span className="text-muted-foreground">
                            {itemConfig?.label || item.name}
                          </span>
                        </div>
                        {item.value && (
                          <span className="font-mono font-medium tabular-nums text-foreground">
                            {item.value.toLocaleString()}
                          </span>
                        )}
                      </div>
                    </>
                  )}
                </div>
              )
            })}
        </div>
      </div>
    )
  }
)
ChartTooltipContent.displayName = "ChartTooltip"

const ChartLegend = RechartsPrimitive.Legend

const ChartLegendContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> &
    Pick<RechartsPrimitive.LegendProps, "payload" | "verticalAlign"> & {
      hideIcon?: boolean
      nameKey?: string
    }
>(
  (
    { className, hideIcon = false, payload, verticalAlign = "bottom", nameKey },
    ref
  ) => {
    const { config } = useChart()

    if (!payload?.length) {
      return null
    }

    return (
      <div
        ref={ref}
        className={cn(
          "flex items-center justify-center gap-4",
          verticalAlign === "top" ? "pb-3" : "pt-3",
          className
        )}
      >
        {payload
          .filter((item) => item.type !== "none")
          .map((item) => {
            const key = `${nameKey || item.dataKey || "value"}`
            const itemConfig = getPayloadConfigFromPayload(config, item, key)

            return (
              <div
                key={item.value}
                className={cn(
                  "flex items-center gap-1.5 [&>svg]:h-3 [&>svg]:w-3 [&>svg]:text-muted-foreground"
                )}
              >
                {itemConfig?.icon && !hideIcon ? (
                  <itemConfig.icon />
                ) : (
                  <div
                    className="h-2 w-2 shrink-0 rounded-[2px]"
                    style={{
                      backgroundColor: item.color,
                    }}
                  />
                )}
                {itemConfig?.label}
              </div>
            )
          })}
      </div>
    )
  }
)
ChartLegendContent.displayName = "ChartLegend"

// Helper to extract item config from a payload.
function getPayloadConfigFromPayload(
  config: ChartConfig,
  payload: unknown,
  key: string
) {
  if (typeof payload !== "object" || payload === null) {
    return undefined
  }

  const payloadPayload =
    "payload" in payload &&
    typeof payload.payload === "object" &&
    payload.payload !== null
      ? payload.payload
      : undefined

  let configLabelKey: string = key

  if (
    key in payload &&
    typeof payload[key as keyof typeof payload] === "string"
  ) {
    configLabelKey = payload[key as keyof typeof payload] as string
  } else if (
    payloadPayload &&
    key in payloadPayload &&
    typeof payloadPayload[key as keyof typeof payloadPayload] === "string"
  ) {
    configLabelKey = payloadPayload[
      key as keyof typeof payloadPayload
    ] as string
  }

  return configLabelKey in config
    ? config[configLabelKey]
    : config[key as keyof typeof config]
}

export {
  ChartContainer,
  ChartTooltip,
  ChartTooltipContent,
  ChartLegend,
  ChartLegendContent,
  ChartStyle,
}

/**
 * Port Component
 *
 * Connection point on an element (input or output).
 * Handles hover states, connection creation, and type compatibility indication.
 */

'use client';

import React from 'react';
import { cn } from '../../../lib/utils';
import type { DataType, Port as PortType } from '../../../types/hivelab.types';
import { DATA_TYPE_COLORS } from '../../../types/hivelab.types';

export interface PortProps {
  /** Port data */
  port: PortType;
  /** Is this port currently being hovered */
  isHovered?: boolean;
  /** Is this port compatible with current connection draft */
  isCompatible?: boolean;
  /** Is this port part of an active connection */
  isConnected?: boolean;
  /** Is a connection currently being created */
  isConnecting?: boolean;
  /** Mouse down handler (start connection) */
  onMouseDown?: (e: React.MouseEvent) => void;
  /** Click handler (complete connection) */
  onClick?: (e: React.MouseEvent) => void;
  /** Mouse enter handler */
  onMouseEnter?: () => void;
  /** Mouse leave handler */
  onMouseLeave?: () => void;
  /** Additional class names */
  className?: string;
  /** Show label next to port */
  showLabel?: boolean;
}

export function Port({
  port,
  isHovered = false,
  isCompatible = false,
  isConnected = false,
  isConnecting = false,
  onMouseDown,
  onClick,
  onMouseEnter,
  onMouseLeave,
  className,
  showLabel = false,
}: PortProps) {
  // Get primary data type for color
  const primaryType = Array.isArray(port.type) ? port.type[0] : port.type;
  const color = DATA_TYPE_COLORS[primaryType];

  // Port size based on state
  const baseSize = 12;
  const hoverSize = 16;
  const size = isHovered || isConnecting ? hoverSize : baseSize;

  return (
    <div
      className={cn(
        'flex items-center gap-2',
        port.side === 'input' ? 'flex-row' : 'flex-row-reverse',
        className
      )}
    >
      {/* Port Circle */}
      <button
        type="button"
        className={cn(
          'relative rounded-full border-2 transition-all duration-200',
          'focus:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2',
          'cursor-pointer',
          isHovered && 'scale-110',
          isCompatible && 'scale-125 animate-pulse',
          !isCompatible && isConnecting && 'opacity-30 cursor-not-allowed'
        )}
        style={{
          width: size,
          height: size,
          backgroundColor: isConnected ? color : 'transparent',
          borderColor: color,
        }}
        onMouseDown={onMouseDown}
        onClick={onClick}
        onMouseEnter={onMouseEnter}
        onMouseLeave={onMouseLeave}
        title={`${port.name} (${port.side === 'input' ? 'Input' : 'Output'}): ${primaryType}${port.required ? ' - Required' : ''}`}
      >
        {/* Inner dot when not connected */}
        {!isConnected && (
          <div
            className="absolute inset-0 m-auto rounded-full transition-all"
            style={{
              width: size / 3,
              height: size / 3,
              backgroundColor: color,
              opacity: isHovered ? 1 : 0.5,
            }}
          />
        )}

        {/* Glow effect when compatible */}
        {isCompatible && (
          <div
            className="absolute inset-0 rounded-full blur-sm"
            style={{
              backgroundColor: color,
              opacity: 0.6,
              transform: 'scale(1.5)',
            }}
          />
        )}
      </button>

      {/* Label */}
      {showLabel && (
        <span
          className={cn(
            'text-xs font-medium transition-opacity',
            isHovered ? 'opacity-100' : 'opacity-70',
            port.side === 'input' ? 'text-left' : 'text-right'
          )}
        >
          {port.name}
          {port.required && <span className="text-destructive ml-0.5">*</span>}
        </span>
      )}
    </div>
  );
}

Port.displayName = 'Port';

 "use client"

import * as React from "react"
import * as LabelPrimitive from "@radix-ui/react-label"
import { Slot } from "@radix-ui/react-slot"
import {
  Controller,
  FormProvider,
  useFormContext,
  type ControllerProps,
  type FieldPath,
  type FieldValues,
} from "react-hook-form"

import { cn } from "../../lib/utils"
import { Label } from "./label"

const Form = FormProvider

type FormFieldContextValue<
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>
> = {
  name: TName
}

const FormFieldContext = React.createContext<FormFieldContextValue>(
  {} as FormFieldContextValue
)

const FormField = <
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>
>({
  ...props
}: ControllerProps<TFieldValues, TName>) => {
  return (
    <FormFieldContext.Provider value={{ name: props.name }}>
      <Controller {...props} />
    </FormFieldContext.Provider>
  )
}

const useFormField = () => {
  const fieldContext = React.useContext(FormFieldContext)
  const itemContext = React.useContext(FormItemContext)
  const { getFieldState, formState } = useFormContext()

  const fieldState = getFieldState(fieldContext.name, formState)

  if (!fieldContext) {
    throw new Error("useFormField should be used within <FormField>")
  }

  const { id } = itemContext

  return {
    id,
    name: fieldContext.name,
    formItemId: `${id}-form-item`,
    formDescriptionId: `${id}-form-item-description`,
    formMessageId: `${id}-form-item-message`,
    ...fieldState,
  }
}

type FormItemContextValue = {
  id: string
}

const FormItemContext = React.createContext<FormItemContextValue>(
  {} as FormItemContextValue
)

const FormItem = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const id = React.useId()

  return (
    <FormItemContext.Provider value={{ id }}>
      <div ref={ref} className={cn("space-y-2", className)} {...props} />
    </FormItemContext.Provider>
  )
})
FormItem.displayName = "FormItem"

const FormLabel = React.forwardRef<
  React.ElementRef<typeof LabelPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof LabelPrimitive.Root>
>(({ className, ...props }, ref) => {
  const { error, formItemId } = useFormField()

  return (
    <Label
      ref={ref}
      className={cn(error && "text-destructive", className)}
      htmlFor={formItemId}
      {...props}
    />
  )
})
FormLabel.displayName = "FormLabel"

const FormControl = React.forwardRef<
  React.ElementRef<typeof Slot>,
  React.ComponentPropsWithoutRef<typeof Slot>
>(({ ...props }, ref) => {
  const { error, formItemId, formDescriptionId, formMessageId } = useFormField()

  return (
    <Slot
      ref={ref}
      id={formItemId}
      aria-describedby={
        !error
          ? `${formDescriptionId}`
          : `${formDescriptionId} ${formMessageId}`
      }
      aria-invalid={!!error}
      {...props}
    />
  )
})
FormControl.displayName = "FormControl"

const FormDescription = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement>
>(({ className, ...props }, ref) => {
  const { formDescriptionId } = useFormField()

  return (
    <p
      ref={ref}
      id={formDescriptionId}
      className={cn("text-[0.8rem] text-muted-foreground", className)}
      {...props}
    />
  )
})
FormDescription.displayName = "FormDescription"

const FormMessage = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement>
>(({ className, children, ...props }, ref) => {
  const { error, formMessageId } = useFormField()
  const body = error ? String(error?.message ?? "") : children

  if (!body) {
    return null
  }

  return (
    <p
      ref={ref}
      id={formMessageId}
      className={cn("text-[0.8rem] font-medium text-destructive", className)}
      {...props}
    >
      {body}
    </p>
  )
})
FormMessage.displayName = "FormMessage"

export {
  useFormField,
  Form,
  FormItem,
  FormLabel,
  FormControl,
  FormDescription,
  FormMessage,
  FormField,
  FormProvider,
  Controller,
  useFormContext,
}

