/**
 * Connection Layer Component
 *
 * SVG layer rendering all connections (wires) for a page.
 * Handles connection selection, hover states, and draft connections.
 */

'use client';

import React from 'react';
import { ConnectionWire } from '../../atoms/elements/connection-wire';
import type { Connection, Element, Port, Position } from '../../../types/hivelab.types';
import { generateConnectionPath, generateBezierPath, getPortById } from '../../../lib/hivelab-utils';
import { DATA_TYPE_COLORS } from '../../../types/hivelab.types';

export interface ConnectionLayerProps {
  /** All elements on this page */
  elements: Element[];
  /** All connections on this page */
  connections: Connection[];
  /** Selected connection ID */
  selectedConnectionId?: string | null;
  /** Hovered connection ID */
  hoveredConnectionId?: string | null;
  /** Draft connection (being created) */
  draftConnection?: {
    sourceElementId: string;
    sourcePortId: string;
    mousePosition: Position;
    sourceType?: any;
  } | null;
  /** Connection click handler */
  onConnectionClick?: (connectionId: string, e: React.MouseEvent) => void;
  /** Connection hover handlers */
  onConnectionHover?: (connectionId: string | null) => void;
  /** Show animated data flow on all connections */
  animated?: boolean;
  /** Additional class names */
  className?: string;
}

export function ConnectionLayer({
  elements,
  connections,
  selectedConnectionId,
  hoveredConnectionId,
  draftConnection,
  onConnectionClick,
  onConnectionHover,
  animated = false,
  className,
}: ConnectionLayerProps) {
  // Helper to get port position
  const getPortPosition = (elementId: string, portId: string): Position | null => {
    const element = elements.find(el => el.id === elementId);
    if (!element) return null;

    const port = getPortById(element, portId);
    if (!port) return null;

    const ports = port.side === 'input' ? element.inputs : element.outputs;
    const portIndex = ports.findIndex(p => p.id === portId);
    if (portIndex === -1) return null;

    const portSpacing = element.height / (ports.length + 1);
    const yOffset = portSpacing * (portIndex + 1);

    return {
      x: port.side === 'input' ? element.x : element.x + element.width,
      y: element.y + yOffset,
    };
  };

  // Render draft connection (while dragging)
  const renderDraftConnection = () => {
    if (!draftConnection) return null;

    const sourcePos = getPortPosition(
      draftConnection.sourceElementId,
      draftConnection.sourcePortId
    );

    if (!sourcePos) return null;

    const path = generateBezierPath(sourcePos, draftConnection.mousePosition);

    // Get color from source port type
    const sourceElement = elements.find(el => el.id === draftConnection.sourceElementId);
    const sourcePort = sourceElement ? getPortById(sourceElement, draftConnection.sourcePortId) : null;
    const primaryType = sourcePort
      ? (Array.isArray(sourcePort.type) ? sourcePort.type[0] : sourcePort.type)
      : 'any';
    const color = DATA_TYPE_COLORS[primaryType];

    return (
      <ConnectionWire
        key="draft"
        path={path}
        color={color}
        strokeWidth={2}
        className="pointer-events-none opacity-60"
      />
    );
  };

  return (
    <svg
      className={`absolute inset-0 w-full h-full pointer-events-none ${className || ''}`}
      style={{ zIndex: 0 }}
    >
      {/* Render all connections */}
      <g className="connections pointer-events-auto">
        {connections.map(connection => {
          const sourcePos = getPortPosition(connection.sourceElementId, connection.sourcePortId);
          const targetPos = getPortPosition(connection.targetElementId, connection.targetPortId);

          if (!sourcePos || !targetPos) return null;

          // Generate path
          const path = generateBezierPath(sourcePos, targetPos);

          // Get color from source port
          const sourceElement = elements.find(el => el.id === connection.sourceElementId);
          const sourcePort = sourceElement
            ? getPortById(sourceElement, connection.sourcePortId)
            : null;
          const primaryType = sourcePort
            ? (Array.isArray(sourcePort.type) ? sourcePort.type[0] : sourcePort.type)
            : 'any';
          const color = connection.color || DATA_TYPE_COLORS[primaryType];

          return (
            <ConnectionWire
              key={connection.id}
              path={path}
              color={color}
              isSelected={selectedConnectionId === connection.id}
              isHovered={hoveredConnectionId === connection.id}
              animated={animated}
              onClick={(e: React.MouseEvent) => {
                e.stopPropagation();
                onConnectionClick?.(connection.id, e);
              }}
              onMouseEnter={() => onConnectionHover?.(connection.id)}
              onMouseLeave={() => onConnectionHover?.(null)}
            />
          );
        })}
      </g>

      {/* Render draft connection (on top) */}
      <g className="draft-connection">
        {renderDraftConnection()}
      </g>
    </svg>
  );
}

ConnectionLayer.displayName = 'ConnectionLayer';

/**
 * Inter-Page Arrow Component
 *
 * Visual indicator for navigation connections between pages in multi-page tools.
 * Shows connections from router elements to target pages on the canvas.
 */

'use client';

import React from 'react';
import { cn } from '../../../lib/utils';
import type { Position } from '../../../types/hivelab.types';
import { ArrowRight, ExternalLink } from 'lucide-react';

export interface InterPageArrowProps {
  /** Source page position and size */
  sourcePage: {
    x: number;
    y: number;
    width: number;
    height: number;
  };
  /** Target page position and size */
  targetPage: {
    x: number;
    y: number;
    width: number;
    height: number;
  };
  /** Source page name */
  sourceName: string;
  /** Target page name */
  targetName: string;
  /** Router element that creates this navigation */
  routerElement?: {
    id: string;
    name: string;
  };
  /** Is this arrow selected */
  isSelected?: boolean;
  /** Is this arrow being hovered */
  isHovered?: boolean;
  /** Arrow color (default: primary) */
  color?: string;
  /** Click handler */
  onClick?: (e: React.MouseEvent) => void;
  /** Additional class names */
  className?: string;
}

export function InterPageArrow({
  sourcePage,
  targetPage,
  sourceName,
  targetName,
  routerElement,
  isSelected = false,
  isHovered = false,
  color = 'hsl(var(--primary))',
  onClick,
  className,
}: InterPageArrowProps) {
  // Calculate start and end points (center of page edges)
  const sourceCenter = {
    x: sourcePage.x + sourcePage.width / 2,
    y: sourcePage.y + sourcePage.height / 2,
  };

  const targetCenter = {
    x: targetPage.x + targetPage.width / 2,
    y: targetPage.y + targetPage.height / 2,
  };

  // Determine which edges to connect based on relative positions
  const dx = targetCenter.x - sourceCenter.x;
  const dy = targetCenter.y - sourceCenter.y;

  let start: Position;
  let end: Position;

  // Horizontal connection (left-to-right or right-to-left)
  if (Math.abs(dx) > Math.abs(dy)) {
    if (dx > 0) {
      // Source right edge ‚Üí Target left edge
      start = { x: sourcePage.x + sourcePage.width, y: sourceCenter.y };
      end = { x: targetPage.x, y: targetCenter.y };
    } else {
      // Source left edge ‚Üí Target right edge
      start = { x: sourcePage.x, y: sourceCenter.y };
      end = { x: targetPage.x + targetPage.width, y: targetCenter.y };
    }
  } else {
    // Vertical connection (top-to-bottom or bottom-to-top)
    if (dy > 0) {
      // Source bottom edge ‚Üí Target top edge
      start = { x: sourceCenter.x, y: sourcePage.y + sourcePage.height };
      end = { x: targetCenter.x, y: targetPage.y };
    } else {
      // Source top edge ‚Üí Target bottom edge
      start = { x: sourceCenter.x, y: sourcePage.y };
      end = { x: targetCenter.x, y: targetPage.y + targetPage.height };
    }
  }

  // Calculate label position (midpoint)
  const labelX = (start.x + end.x) / 2;
  const labelY = (start.y + end.y) / 2;

  // Calculate arrow angle for rotation
  const angle = Math.atan2(end.y - start.y, end.x - start.x) * (180 / Math.PI);

  return (
    <g className={cn('inter-page-arrow', className)}>
      {/* Invisible hit area for easier clicking */}
      <line
        x1={start.x}
        y1={start.y}
        x2={end.x}
        y2={end.y}
        stroke="transparent"
        strokeWidth={20}
        className="cursor-pointer"
        onClick={onClick}
      />

      {/* Visible dashed line */}
      <line
        x1={start.x}
        y1={start.y}
        x2={end.x}
        y2={end.y}
        stroke={color}
        strokeWidth={isSelected ? 3 : isHovered ? 2.5 : 2}
        strokeDasharray="8 4"
        className={cn(
          'transition-all',
          isHovered && 'opacity-80'
        )}
        opacity={isSelected ? 1 : 0.6}
      />

      {/* Arrowhead at end */}
      <polygon
        points={`0,0 -12,-6 -12,6`}
        fill={color}
        opacity={isSelected ? 1 : 0.6}
        transform={`translate(${end.x}, ${end.y}) rotate(${angle})`}
        className="transition-all"
      />

      {/* Label background */}
      <foreignObject
        x={labelX - 80}
        y={labelY - 16}
        width={160}
        height={32}
        className="pointer-events-auto"
        onClick={onClick}
      >
        <div
          className={cn(
            'flex items-center justify-center gap-1.5 h-full',
            'bg-background/95 backdrop-blur-sm border rounded-md shadow-sm',
            'px-2 py-1 cursor-pointer transition-all',
            isSelected && 'ring-2 ring-primary',
            isHovered && 'bg-background'
          )}
          style={{
            borderColor: isSelected ? color : undefined,
          }}
        >
          <ExternalLink className="h-3 w-3" style={{ color }} />
          <span className="text-xs font-medium truncate max-w-[120px]">
            {targetName}
          </span>
        </div>
      </foreignObject>

      {/* Router element indicator (if provided) */}
      {routerElement && (
        <foreignObject
          x={start.x - 40}
          y={start.y - 12}
          width={80}
          height={24}
          className="pointer-events-none"
        >
          <div className="flex items-center justify-center h-full bg-background/80 border rounded text-xs px-1.5">
            <span className="truncate" style={{ color }}>
              {routerElement.name}
            </span>
          </div>
        </foreignObject>
      )}
    </g>
  );
}

InterPageArrow.displayName = 'InterPageArrow';

