"use client"

import * as React from "react"
import {
  Carousel,
  CarouselContent,
  CarouselItem,
  CarouselApi,
} from "../atoms/carousel"
import { cn } from "../../lib/utils"

export interface PhotoCarouselProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Array of photo URLs (max 5 for profile galleries) */
  photos: string[]
  /** Alt text prefix (will append index) */
  altPrefix?: string
  /** Aspect ratio class */
  aspectRatio?: "square" | "portrait" | "wide"
}

const PhotoCarousel = React.forwardRef<HTMLDivElement, PhotoCarouselProps>(
  ({ className, photos, altPrefix = "Photo", aspectRatio = "portrait", ...props }, ref) => {
    const [api, setApi] = React.useState<CarouselApi>()
    const [current, setCurrent] = React.useState(0)
    const [count, setCount] = React.useState(0)

    // Limit to 5 photos max
    const displayPhotos = photos.slice(0, 5)

    React.useEffect(() => {
      if (!api) {
        return
      }

      setCount(api.scrollSnapList().length)
      setCurrent(api.selectedScrollSnap())

      api.on("select", () => {
        setCurrent(api.selectedScrollSnap())
      })
    }, [api])

    const aspectRatioClass = {
      square: "aspect-square",
      portrait: "aspect-[4/5]", // Tinder-style portrait
      wide: "aspect-video",
    }[aspectRatio]

    return (
      <div ref={ref} className={cn("w-full", className)} {...props}>
        <Carousel setApi={setApi} opts={{ loop: true }}>
          <CarouselContent>
            {displayPhotos.map((photo, index) => (
              <CarouselItem key={index}>
                <div className={cn("relative overflow-hidden rounded-2xl bg-muted", aspectRatioClass)}>
                  <img
                    src={photo}
                    alt={`${altPrefix} ${index + 1}`}
                    className="h-full w-full object-cover"
                  />
                </div>
              </CarouselItem>
            ))}
          </CarouselContent>

          {/* Dot indicators (Tinder-style) */}
          {count > 1 && (
            <div className="absolute top-4 left-0 right-0 flex justify-center gap-1.5 z-10">
              {Array.from({ length: count }).map((_, index) => (
                <button
                  key={index}
                  onClick={() => api?.scrollTo(index)}
                  className={cn(
                    "h-1 rounded-full transition-all",
                    index === current
                      ? "w-8 bg-white"
                      : "w-1 bg-white/50 hover:bg-white/75"
                  )}
                  aria-label={`Go to photo ${index + 1}`}
                />
              ))}
            </div>
          )}

          {/* Photo counter */}
          {count > 1 && (
            <div className="absolute bottom-4 right-4 px-3 py-1 bg-black/50 backdrop-blur-sm rounded-full text-white text-xs font-medium">
              {current + 1} / {count}
            </div>
          )}
        </Carousel>
      </div>
    )
  }
)

PhotoCarousel.displayName = "PhotoCarousel"

export { PhotoCarousel }

/**
 * Ritual Progress Card Component
 *
 * Implements campus-wide ritual tracking with behavioral psychology:
 * - Progress rings with Instagram story quality
 * - 70% completion target per SPEC
 * - Streak counters and milestone rewards
 * - Social proof and leaderboard integration
 * - Check-in buttons with haptic feedback simulation
 */

"use client"

import * as React from "react"
import { cn } from "../../lib/utils"
import { Card } from "../atoms/card"
import { Button } from "../atoms/button"
import { Badge } from "../atoms/badge"
import { Avatar } from "../atoms/avatar"
import {
  CheckCircle,
  Calendar,
  Users,
  Crown,
  Flame,
  Target,
  TrendingUp,
  Zap,
  Award,
  Clock,
} from "lucide-react"

export interface RitualData {
  id: string
  title: string
  description: string
  category: 'health' | 'academic' | 'social' | 'campus' | 'seasonal'

  // Progress & Completion
  progress: {
    current: number      // Current streak or completion count
    target: number       // Target for completion (70% per SPEC)
    percentage: number   // Current completion percentage
    isCompleted: boolean
    streakDays: number   // Current streak
    longestStreak: number
  }

  // Participation
  participation: {
    totalParticipants: number
    friendsParticipating: string[] // Friend names
    myCheckIns: number
    lastCheckIn?: string // ISO date
    hasCheckedInToday: boolean
  }

  // Timing
  duration: {
    startDate: string    // ISO date
    endDate: string      // ISO date
    daysRemaining: number
    isActive: boolean
  }

  // Rewards & Motivation
  rewards: {
    points: number       // Points earned
    badges: string[]     // Badge IDs earned
    nextMilestone?: {
      description: string
      pointsNeeded: number
      reward: string
    }
  }

  // Social Features
  leaderboard?: {
    myRank: number
    topParticipants: Array<{
      name: string
      avatar?: string
      score: number
      isMe?: boolean
    }>
  }

  // Psychology
  motivationalMessage?: string
  urgencyLevel: 'low' | 'medium' | 'high'
  isNewRitual?: boolean
}

export interface RitualProgressCardProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Ritual data */
  ritual: RitualData

  /** Check-in handler */
  onCheckIn?: (ritualId: string) => void

  /** View details handler */
  onViewDetails?: (ritualId: string) => void

  /** View leaderboard handler */
  onViewLeaderboard?: (ritualId: string) => void

  /** Share handler */
  onShare?: (ritualId: string) => void

  /** Show compact version */
  compact?: boolean

  /** Enable behavioral psychology features */
  enablePsychology?: boolean
}

const RitualProgressCard = React.forwardRef<HTMLDivElement, RitualProgressCardProps>(
  (
    {
      className,
      ritual,
      onCheckIn,
      onViewDetails,
      onViewLeaderboard,
      onShare,
      compact = false,
      enablePsychology = true,
      ...props
    },
    ref
  ) => {
    const [isAnimating, setIsAnimating] = React.useState(false)

    // Handle check-in with animation
    const handleCheckIn = () => {
      if (!ritual.participation.hasCheckedInToday) {
        setIsAnimating(true)
        onCheckIn?.(ritual.id)
        setTimeout(() => setIsAnimating(false), 500)
      }
    }

    // Calculate ring colors based on category
    const getRingColor = () => {
      switch (ritual.category) {
        case 'health': return 'stroke-green-500'
        case 'academic': return 'stroke-blue-500'
        case 'social': return 'stroke-purple-500'
        case 'campus': return 'stroke-[#FFD700]'
        case 'seasonal': return 'stroke-orange-500'
        default: return 'stroke-gray-500'
      }
    }

    // Progress ring radius and circumference
    const radius = compact ? 35 : 45
    const circumference = 2 * Math.PI * radius
    const strokeDashoffset = circumference - (ritual.progress.percentage / 100) * circumference

    // Category icon
    const getCategoryIcon = () => {
      switch (ritual.category) {
        case 'health': return <Zap className="h-4 w-4" />
        case 'academic': return <Target className="h-4 w-4" />
        case 'social': return <Users className="h-4 w-4" />
        case 'campus': return <Crown className="h-4 w-4" />
        case 'seasonal': return <Calendar className="h-4 w-4" />
        default: return <Award className="h-4 w-4" />
      }
    }

    return (
      <Card
        ref={ref}
        className={cn(
          "relative overflow-hidden transition-all duration-fast ease-smooth",
          "bg-gradient-to-br from-black/90 to-gray-900/90 backdrop-blur-sm",
          "border border-white/10 hover:border-white/20",
          compact ? "p-3" : "p-4",
          isAnimating && "scale-105 shadow-lg shadow-[#FFD700]/20",
          className
        )}
        {...props}
      >
        {/* New Ritual Badge */}
        {ritual.isNewRitual && (
          <div className="absolute top-2 right-2">
            <Badge className="bg-[#FFD700] text-black text-xs font-semibold animate-pulse">
              New!
            </Badge>
          </div>
        )}

        <div className="flex items-start gap-4">
          {/* Progress Ring */}
          <div className="relative flex-shrink-0">
            <svg
              className="transform -rotate-90 transition-all duration-smooth ease-smooth"
              width={compact ? 80 : 100}
              height={compact ? 80 : 100}
            >
              {/* Background circle */}
              <circle
                cx={compact ? 40 : 50}
                cy={compact ? 40 : 50}
                r={radius}
                stroke="rgb(255 255 255 / 0.1)"
                strokeWidth="4"
                fill="transparent"
              />

              {/* Progress circle */}
              <circle
                cx={compact ? 40 : 50}
                cy={compact ? 40 : 50}
                r={radius}
                className={cn(getRingColor(), "transition-all duration-smooth ease-smooth")}
                strokeWidth="4"
                fill="transparent"
                strokeLinecap="round"
                strokeDasharray={circumference}
                strokeDashoffset={strokeDashoffset}
                style={{
                  filter: 'drop-shadow(0 0 6px currentColor)',
                }}
              />
            </svg>

            {/* Center content */}
            <div className="absolute inset-0 flex items-center justify-center">
              <div className="text-center">
                <div className={cn(
                  "font-bold text-white",
                  compact ? "text-sm" : "text-lg"
                )}>
                  {ritual.progress.percentage}%
                </div>
                {!compact && (
                  <div className="text-xs text-white/60">
                    {ritual.progress.current}/{ritual.progress.target}
                  </div>
                )}
              </div>
            </div>

            {/* Streak indicator */}
            {ritual.progress.streakDays > 0 && (
              <div className="absolute -bottom-1 -right-1">
                <div className="bg-orange-500 rounded-full px-1.5 py-0.5 flex items-center gap-1">
                  <Flame className="h-3 w-3 text-white" />
                  <span className="text-xs font-bold text-white">
                    {ritual.progress.streakDays}
                  </span>
                </div>
              </div>
            )}
          </div>

          {/* Content */}
          <div className="flex-1 min-w-0">
            {/* Header */}
            <div className="flex items-start justify-between mb-2">
              <div className="flex-1">
                <div className="flex items-center gap-2 mb-1">
                  <div className="text-white/60">
                    {getCategoryIcon()}
                  </div>
                  <h3 className={cn(
                    "font-semibold text-white line-clamp-1",
                    compact ? "text-sm" : "text-base"
                  )}>
                    {ritual.title}
                  </h3>
                </div>

                {!compact && (
                  <p className="text-xs text-white/70 line-clamp-2 mb-2">
                    {ritual.description}
                  </p>
                )}
              </div>

              {/* Category badge */}
              <Badge
                variant="freshman"
                className="text-xs border-white/20 text-white/70 capitalize"
              >
                {ritual.category}
              </Badge>
            </div>

            {/* Progress Stats */}
            <div className="grid grid-cols-2 gap-2 text-xs mb-3">
              <div>
                <span className="text-white/60">Participants:</span>
                <span className="ml-1 text-white font-medium">
                  {ritual.participation.totalParticipants}
                </span>
              </div>
              <div>
                <span className="text-white/60">Days left:</span>
                <span className="ml-1 text-white font-medium">
                  {ritual.duration.daysRemaining}
                </span>
              </div>
            </div>

            {/* Friends participating - Social Proof */}
            {ritual.participation.friendsParticipating.length > 0 && (
              <div className="mb-3">
                <div className="text-xs text-[#FFD700] font-medium">
                  {ritual.participation.friendsParticipating.slice(0, 2).join(', ')}
                  {ritual.participation.friendsParticipating.length > 2 &&
                    ` +${ritual.participation.friendsParticipating.length - 2} friends`
                  } participating
                </div>
              </div>
            )}

            {/* Motivational message */}
            {enablePsychology && ritual.motivationalMessage && (
              <div className="mb-3 p-2 bg-white/5 rounded text-xs text-white/80 italic">
                {ritual.motivationalMessage}
              </div>
            )}

            {/* Next milestone */}
            {ritual.rewards.nextMilestone && !ritual.progress.isCompleted && (
              <div className="mb-3 p-2 bg-[#FFD700]/10 border border-[#FFD700]/20 rounded">
                <div className="text-xs text-[#FFD700] font-medium mb-1">
                  Next milestone: {ritual.rewards.nextMilestone.description}
                </div>
                <div className="text-xs text-white/70">
                  {ritual.rewards.nextMilestone.pointsNeeded} points to unlock {ritual.rewards.nextMilestone.reward}
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Actions */}
        <div className="flex items-center justify-between pt-3 border-t border-white/10 mt-3">
          <div className="flex gap-2">
            {/* Check-in Button */}
            <Button
              variant={ritual.participation.hasCheckedInToday ? "default" : "outline"}
              size="sm"
              className={cn(
                "transition-all duration-fast ease-smooth",
                ritual.participation.hasCheckedInToday && "bg-green-600 hover:bg-green-600/90",
                !ritual.participation.hasCheckedInToday && "hover:scale-105 active:scale-95"
              )}
              onClick={handleCheckIn}
              disabled={ritual.participation.hasCheckedInToday}
            >
              {ritual.participation.hasCheckedInToday ? (
                <>
                  <CheckCircle className="h-4 w-4 mr-1" />
                  Checked In
                </>
              ) : (
                <>
                  <Target className="h-4 w-4 mr-1" />
                  Check In
                </>
              )}
            </Button>

            {/* Leaderboard button (if available) */}
            {ritual.leaderboard && (
              <Button
                variant="ghost"
                size="sm"
                className="text-white/70 hover:text-white"
                onClick={() => onViewLeaderboard?.(ritual.id)}
              >
                <Crown className="h-4 w-4 mr-1" />
                #{ritual.leaderboard.myRank}
              </Button>
            )}
          </div>

          {/* Secondary actions */}
          <div className="flex gap-1">
            {!compact && (
              <Button
                variant="ghost"
                size="sm"
                className="h-8 px-2 text-white/60 hover:text-white"
                onClick={() => onViewDetails?.(ritual.id)}
              >
                <TrendingUp className="h-4 w-4" />
              </Button>
            )}
          </div>
        </div>

        {/* Completion celebration overlay */}
        {ritual.progress.isCompleted && enablePsychology && (
          <div className="absolute inset-0 bg-gradient-to-br from-[#FFD700]/20 to-transparent pointer-events-none">
            <div className="absolute top-2 left-2">
              <Award className="h-6 w-6 text-[#FFD700] animate-pulse" />
            </div>
          </div>
        )}
      </Card>
    )
  }
)

RitualProgressCard.displayName = "RitualProgressCard"

export { RitualProgressCard }
// RitualData already exported above

"use client"

import * as React from "react"
import { cn } from "../../lib/utils"

export interface RitualProgressRingProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Personal progress percentage (0-100) */
  personal: number

  /** Campus-wide progress percentage (0-100) */
  campus: number

  /** Target value (for display) */
  target?: number

  /** Size variant */
  size?: "sm" | "md" | "lg"

  /** Show labels */
  showLabels?: boolean
}

const RitualProgressRing = React.forwardRef<HTMLDivElement, RitualProgressRingProps>(
  (
    {
      className,
      personal,
      campus,
      target = 100,
      size = "md",
      showLabels = true,
      ...props
    },
    ref
  ) => {
    const sizeConfig = {
      sm: { ring: 80, stroke: 6, fontSize: "text-lg" },
      md: { ring: 120, stroke: 8, fontSize: "text-2xl" },
      lg: { ring: 160, stroke: 10, fontSize: "text-3xl" },
    }

    const config = sizeConfig[size]
    const radius = (config.ring - config.stroke) / 2
    const circumference = 2 * Math.PI * radius
    const center = config.ring / 2

    // Calculate dash offsets for progress
    const personalOffset = circumference - (personal / 100) * circumference
    const campusOffset = circumference - (campus / 100) * circumference

    return (
      <div
        ref={ref}
        className={cn("flex flex-col items-center gap-4", className)}
        {...props}
      >
        {/* Ring Container */}
        <div className="relative" style={{ width: config.ring, height: config.ring }}>
          {/* SVG Rings */}
          <svg
            width={config.ring}
            height={config.ring}
            className="transform -rotate-90"
          >
            {/* Background Ring */}
            <circle
              cx={center}
              cy={center}
              r={radius}
              fill="none"
              stroke="rgba(255,255,255,0.08)"
              strokeWidth={config.stroke}
            />

            {/* Campus Progress Ring (outer) */}
            <circle
              cx={center}
              cy={center}
              r={radius}
              fill="none"
              stroke="rgba(255,255,255,0.3)"
              strokeWidth={config.stroke}
              strokeDasharray={circumference}
              strokeDashoffset={campusOffset}
              strokeLinecap="round"
              className="transition-all duration-smooth ease-liquid"
            />

            {/* Personal Progress Ring (inner, gold) */}
            <circle
              cx={center}
              cy={center}
              r={radius - config.stroke - 2}
              fill="none"
              stroke="#FFD700"
              strokeWidth={config.stroke - 2}
              strokeDasharray={circumference}
              strokeDashoffset={personalOffset}
              strokeLinecap="round"
              className="transition-all duration-smooth ease-liquid"
              style={{
                filter: "drop-shadow(0 0 8px rgba(255, 215, 0, 0.4))",
              }}
            />
          </svg>

          {/* Center Text */}
          <div className="absolute inset-0 flex flex-col items-center justify-center">
            <div className={cn("font-bold text-white", config.fontSize)}>
              {personal}%
            </div>
            <div className="text-xs text-white/50 font-medium">personal</div>
          </div>
        </div>

        {/* Labels */}
        {showLabels && (
          <div className="flex gap-6 text-sm">
            {/* Personal Progress */}
            <div className="flex items-center gap-2">
              <div className="w-3 h-3 rounded-full bg-[#FFD700]" />
              <div className="flex flex-col">
                <span className="text-xs text-white/50">Your Progress</span>
                <span className="font-semibold text-white">{personal}%</span>
              </div>
            </div>

            {/* Campus Progress */}
            <div className="flex items-center gap-2">
              <div className="w-3 h-3 rounded-full bg-white/30" />
              <div className="flex flex-col">
                <span className="text-xs text-white/50">Campus</span>
                <span className="font-semibold text-white">{campus}%</span>
              </div>
            </div>
          </div>
        )}
      </div>
    )
  }
)

RitualProgressRing.displayName = "RitualProgressRing"

export { RitualProgressRing }

/**
 * Space Card - Unified Polymorphic Component
 *
 * Single card molecule that handles all space display variants:
 * - default: Standard vertical card for grids
 * - discovery: Horizontal card with campus context for browse/discovery
 * - joined: Vertical card with unread badge for joined spaces
 * - compact: Minimal card for lists
 *
 * Uses SpaceData canonical type and SpaceActionHandler for events.
 *
 * @module molecules/space-card
 */

"use client"

import * as React from "react"
import { MotionDiv } from "../../shells/motion-safe"
import { transitions } from "../../lib/animations"
import {
  Users,
  MessageCircle,
  CheckCircle2,
  Plus,
  Pin,
  Flame,
  GraduationCap,
  ChevronRight,
  Bell
} from "lucide-react"
import { Card } from "../atoms/card"
import { Badge } from "../atoms/badge"
import { Button } from "../atoms/button"
import { Avatar } from "../atoms/avatar"
import { cn } from "../../lib/utils"
import type {
  SpaceData,
  SpaceCardVariant,
  SpaceCardLayout,
  SpaceActionHandler,
  UserPreview
} from "../../types/space.types"

export interface SpaceCardProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Space data (canonical type) */
  space: SpaceData

  /** Visual variant */
  variant?: SpaceCardVariant

  /** Layout orientation */
  layout?: SpaceCardLayout

  /** Whether to show action buttons */
  showActions?: boolean

  /** Whether to show stats */
  showStats?: boolean

  /** Whether to show campus context (friends, trending) */
  showCampusContext?: boolean

  /** Whether to show tags */
  showTags?: boolean

  /** Maximum tags to display */
  maxTags?: number

  /** Action handler (aggregated) */
  onAction?: SpaceActionHandler

  /** Legacy handlers (for backward compatibility) */
  onClick?: () => void
  onJoin?: () => void
  onLeave?: () => void
}

/**
 * Space Card Component
 */
const SpaceCard = React.forwardRef<HTMLDivElement, SpaceCardProps>(
  (
    {
      className,
      space,
      variant = "default",
      layout = "vertical",
      showActions = true,
      showStats = true,
      showCampusContext = false,
      showTags = true,
      maxTags = 3,
      onAction,
      onClick,
      onJoin,
      onLeave,
      ...props
    },
    ref
  ) => {
    // Determine layout based on variant
    const effectiveLayout = variant === "discovery" ? "horizontal" : layout

    // Campus context signals (for discovery variant)
    const campusSignals = React.useMemo(() => {
      const signals: Array<{ type: string; label: string; icon: React.ReactNode }> = []

      if (space.campus.friendsInSpace.length > 0) {
        const count = space.campus.friendsInSpace.length
        signals.push({
          type: "friends",
          label: `${count} ${count === 1 ? "friend" : "friends"} here`,
          icon: <Users className="h-3.5 w-3.5" />
        })
      }

      if (space.campus.isTrending) {
        signals.push({
          type: "trending",
          label: "Trending on campus",
          icon: <Flame className="h-3.5 w-3.5" />
        })
      }

      if (space.campus.primaryMajor) {
        signals.push({
          type: "major",
          label: space.campus.primaryMajor,
          icon: <GraduationCap className="h-3.5 w-3.5" />
        })
      }

      return signals
    }, [space.campus])

    // Handle actions with both new and legacy handlers
    const handleCardClick = () => {
      onClick?.()
      onAction?.({ type: "space.click" })
    }

    const handleJoin = (e: React.MouseEvent) => {
      e.stopPropagation()
      onJoin?.()
      onAction?.({ type: "space.join" })
    }

    const handleLeave = (e: React.MouseEvent) => {
      e.stopPropagation()
      onLeave?.()
      onAction?.({ type: "space.leave" })
    }

    // Render helper: Member avatars
    const renderMemberAvatars = () => {
      if (!space.memberAvatars || space.memberAvatars.length === 0) return null

      const displayAvatars = space.memberAvatars.slice(0, 4)
      const remaining = space.memberAvatars.length - 4

      return (
        <div className="flex items-center gap-2">
          <div className="flex -space-x-2">
            {displayAvatars.map((member, index) => (
              <div
                key={index}
                className="h-7 w-7 overflow-hidden rounded-md border-2 border-[#0c0c0c] bg-white/10 transition-smooth ease-liquid hover:z-10 hover:scale-110"
              >
                {member.avatar ? (
                  <img
                    src={member.avatar}
                    alt={member.name}
                    className="h-full w-full object-cover"
                  />
                ) : (
                  <div className="flex h-full w-full items-center justify-center bg-[#FFD700]/10 text-[10px] font-semibold text-[#FFD700]">
                    {member.name.split(" ").map((n) => n[0]).join("").slice(0, 2)}
                  </div>
                )}
              </div>
            ))}
            {remaining > 0 && (
              <div className="flex h-7 w-7 items-center justify-center overflow-hidden rounded-md border-2 border-[#0c0c0c] bg-white/10 text-[10px] font-semibold text-white/70">
                +{remaining}
              </div>
            )}
          </div>
          {variant !== "compact" && (
            <span className="text-xs text-white/70">
              {space.stats.memberCount} members
            </span>
          )}
        </div>
      )
    }

    // Render helper: Stats
    const renderStats = () => {
      if (!showStats) return null

      return (
        <div className="flex items-center gap-4 text-sm text-white/70">
          <div className="flex items-center gap-1.5">
            <Users className="h-4 w-4" aria-hidden="true" />
            <span>{space.stats.memberCount}</span>
          </div>
          <div className="flex items-center gap-1.5">
            <MessageCircle className="h-4 w-4" aria-hidden="true" />
            <span>{space.stats.postCount}</span>
          </div>
          {space.stats.activeToday > 0 && (
            <div className="flex items-center gap-1.5 text-[#FFD700]">
              <Flame className="h-4 w-4" aria-hidden="true" />
              <span className="text-xs">{space.stats.activeToday} active</span>
            </div>
          )}
        </div>
      )
    }

    // Render helper: Campus context badges
    const renderCampusContext = () => {
      if (!showCampusContext || campusSignals.length === 0) return null

      // Show primary signal only for compact variant
      const displaySignals = variant === "compact" ? campusSignals.slice(0, 1) : campusSignals

      return (
        <div className="flex flex-wrap gap-1.5">
          {displaySignals.map((signal, index) => (
            <Badge
              key={index}
              variant="sophomore"
              className="h-6 gap-1 px-2 text-xs bg-[#FFD700]/10 text-[#FFD700] border-[#FFD700]/20"
            >
              {signal.icon}
              <span>{signal.label}</span>
            </Badge>
          ))}
        </div>
      )
    }

    // Render helper: Tags
    const renderTags = () => {
      if (!showTags || space.tags.length === 0) return null

      const displayTags = space.tags.slice(0, maxTags)
      const remaining = space.tags.length - maxTags

      return (
        <div className="flex flex-wrap gap-1.5">
          {displayTags.map((tag, index) => (
            <Badge key={index} variant="sophomore" className="text-xs">
              #{tag}
            </Badge>
          ))}
          {remaining > 0 && (
            <Badge variant="freshman" className="text-xs">
              +{remaining}
            </Badge>
          )}
        </div>
      )
    }

    // Render helper: Action button
    const renderActionButton = () => {
      if (!showActions) return null

      const { isJoined, unreadCount } = space.userContext

      if (variant === "compact") {
        return (
          <Button
            variant="ghost"
            size="sm"
            className="h-8 w-8 p-0 shrink-0"
            onClick={isJoined ? handleLeave : handleJoin}
          >
            {isJoined ? (
              <CheckCircle2 className="h-4 w-4 text-[#FFD700]" />
            ) : (
              <Plus className="h-4 w-4" />
            )}
          </Button>
        )
      }

      if (variant === "discovery") {
        return (
          <Button
            size="sm"
            className="shrink-0"
            onClick={handleJoin}
          >
            <Plus className="mr-2 h-4 w-4" />
            Join Space
          </Button>
        )
      }

      if (isJoined) {
        return (
          <div className="flex items-center gap-2">
            {unreadCount > 0 && (
              <Badge variant="freshman" className="h-5 px-2 text-xs bg-[#FFD700] text-black">
                {unreadCount} new
              </Badge>
            )}
            <Button
              variant="outline"
              size="sm"
              className="flex-1 text-sm font-medium"
              onClick={handleLeave}
            >
              <CheckCircle2 className="mr-2 h-4 w-4" />
              Joined
            </Button>
          </div>
        )
      }

      return (
        <Button
          size="sm"
          className="w-full text-sm font-medium"
          onClick={handleJoin}
        >
          <Plus className="mr-2 h-4 w-4" />
          Join Space
        </Button>
      )
    }

    // Render helper: Cover photo
    const renderCover = () => {
      const coverHeight = variant === "compact" ? "h-16" : effectiveLayout === "horizontal" ? "h-full" : "h-32"

      return (
        <div className={cn("relative w-full overflow-hidden", coverHeight)}>
          {space.coverPhoto ? (
            <img
              src={space.coverPhoto}
              alt={`${space.name} cover`}
              className="h-full w-full object-cover transition-smooth ease-liquid group-hover:scale-105"
            />
          ) : (
            <div className="h-full w-full bg-gradient-to-br from-[#FFD700]/30 via-[#FFD700]/20 to-[#FFD700]/10 transition-smooth ease-liquid group-hover:from-[#FFD700]/40 group-hover:via-[#FFD700]/30 group-hover:to-[#FFD700]/20" />
          )}

          {/* Overlays */}
          <div className="absolute inset-0 flex items-start justify-between p-2">
            {/* Privacy badge (top-right) */}
            {variant !== "compact" && (
              <div className="ml-auto">
                <Badge
                  variant={space.privacy === "public" ? "secondary" : "default"}
                  className="text-xs backdrop-blur-sm bg-[#0c0c0c]/80"
                >
                  {space.privacy === "public" ? "Public" : space.privacy === "private" ? "Private" : "Hidden"}
                </Badge>
              </div>
            )}
          </div>

          {/* Unread indicator (joined variant) */}
          {variant === "joined" && space.userContext.unreadCount > 0 && (
            <div className="absolute top-2 left-2">
              <Badge variant="freshman" className="h-6 px-2 text-xs bg-[#FFD700] text-black">
                {space.userContext.unreadCount} new
              </Badge>
            </div>
          )}

          {/* Trending flame (discovery variant) */}
          {variant === "discovery" && space.campus.isTrending && (
            <div className="absolute top-2 left-2">
              <Badge variant="freshman" className="h-6 gap-1 px-2 text-xs bg-[#FFD700] text-black">
                <Flame className="h-3 w-3 fill-current" />
                Trending
              </Badge>
            </div>
          )}
        </div>
      )
    }

    // Compact variant (minimal, for lists)
    if (variant === "compact") {
      return (
        <MotionDiv
          whileHover={{ x: 4 }}
          transition={transitions.fast}
        >
          <Card
            ref={ref}
            className={cn(
              "group flex items-center gap-3 p-3 transition-all duration-smooth ease-liquid hover:shadow-md hover:border-[#FFD700]/50 cursor-pointer",
              className
            )}
            onClick={handleCardClick}
            {...props}
          >
            {/* Cover thumbnail */}
            <div className="relative h-12 w-16 shrink-0 overflow-hidden rounded">
              {space.coverPhoto ? (
                <img
                  src={space.coverPhoto}
                  alt={space.name}
                  className="h-full w-full object-cover"
                />
              ) : (
                <div className="h-full w-full bg-gradient-to-br from-[#FFD700]/30 to-[#FFD700]/10" />
              )}
            </div>

            {/* Content */}
            <div className="flex-1 min-w-0">
              <h4 className="font-semibold text-sm text-white truncate group-hover:text-[#FFD700] transition-smooth">
                {space.name}
              </h4>
              <div className="flex items-center gap-2 text-xs text-white/70">
                <span>{space.stats.memberCount} members</span>
                <span>‚Ä¢</span>
                <Badge variant="freshman" className="h-4 px-1 text-[10px]">
                  {space.category}
                </Badge>
              </div>
            </div>

            {/* Action */}
            {renderActionButton()}
          </Card>
        </MotionDiv>
      )
    }

    // Horizontal layout (for discovery variant)
    if (effectiveLayout === "horizontal") {
      return (
        <MotionDiv
          whileHover={{ y: -2 }}
          transition={transitions.fast}
        >
          <Card
            ref={ref}
            className={cn(
              "group flex overflow-hidden transition-all duration-smooth ease-liquid hover:shadow-lg hover:border-[#FFD700]/50 cursor-pointer",
              className
            )}
            onClick={handleCardClick}
            {...props}
          >
            {/* Cover photo (left side) */}
            <div className="relative w-48 shrink-0">
              {renderCover()}
            </div>

            {/* Content (right side) */}
            <div className="flex-1 p-4 space-y-3 min-w-0">
              {/* Header */}
              <div className="space-y-1">
                <div className="flex items-start justify-between gap-2">
                  <h3 className="font-semibold text-white line-clamp-1 transition-smooth ease-liquid group-hover:text-[#FFD700]">
                    {space.name}
                  </h3>
                  <Badge variant="freshman" className="shrink-0 text-xs">
                    {space.category}
                  </Badge>
                </div>
                {space.description && (
                  <p className="text-sm text-white/70 line-clamp-2">
                    {space.description}
                  </p>
                )}
              </div>

              {/* Campus context (discovery priority) */}
              {renderCampusContext()}

              {/* Stats */}
              {renderStats()}

              {/* Friends preview (if applicable) */}
              {space.campus.friendsInSpace.length > 0 && (
                <div className="flex items-center gap-2">
                  <div className="flex -space-x-2">
                    {space.campus.friendsInSpace.slice(0, 3).map((friend, index) => (
                      <div
                        key={index}
                        className="h-6 w-6 overflow-hidden rounded-full border-2 border-[#0c0c0c] bg-white/10"
                      >
                        {friend.avatar ? (
                          <img src={friend.avatar} alt={friend.name} className="h-full w-full object-cover" />
                        ) : (
                          <div className="flex h-full w-full items-center justify-center bg-[#FFD700]/10 text-[10px] font-semibold text-[#FFD700]">
                            {friend.name[0]}
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                  <span className="text-xs text-white/70">
                    {space.campus.friendsInSpace[0].name}
                    {space.campus.friendsInSpace.length > 1 && ` and ${space.campus.friendsInSpace.length - 1} ${space.campus.friendsInSpace.length === 2 ? "other" : "others"}`}
                  </span>
                </div>
              )}

              {/* Action button */}
              <div className="pt-1">
                {renderActionButton()}
              </div>
            </div>
          </Card>
        </MotionDiv>
      )
    }

    // Vertical layout (default, joined variants)
    return (
      <MotionDiv
        whileHover={{ y: -4 }}
        transition={transitions.fast}
      >
        <Card
          ref={ref}
          className={cn(
            "group overflow-hidden transition-all duration-smooth ease-liquid hover:shadow-lg hover:border-[#FFD700]/50 cursor-pointer",
            className
          )}
          onClick={handleCardClick}
          {...props}
        >
          {/* Cover Photo */}
          {renderCover()}

          {/* Content */}
          <div className="p-4 space-y-3">
            {/* Header */}
            <div className="space-y-1">
              <div className="flex items-start justify-between gap-2">
                <h3 className="font-semibold text-white line-clamp-1 transition-smooth ease-liquid group-hover:text-[#FFD700]">
                  {space.name}
                </h3>
                <Badge variant="freshman" className="shrink-0 text-xs">
                  {space.category}
                </Badge>
              </div>
              {space.description && (
                <p className="text-sm text-white/70 line-clamp-2">
                  {space.description}
                </p>
              )}
            </div>

            {/* Campus context (if enabled) */}
            {renderCampusContext()}

            {/* Tags */}
            {renderTags()}

            {/* Stats */}
            {renderStats()}

            {/* Member Previews */}
            {space.memberAvatars && space.memberAvatars.length > 0 && renderMemberAvatars()}

            {/* Action Button */}
            <div className="pt-2">
              {renderActionButton()}
            </div>
          </div>
        </Card>
      </MotionDiv>
    )
  }
)

SpaceCard.displayName = "SpaceCard"

export { SpaceCard }

"use client"

import * as React from "react"
import { UserCard } from "../molecules/user-card"
import { Tabs, TabsContent, TabsList, TabsTrigger } from "../atoms/tabs"
import { Button } from "../atoms/button"
import { Badge } from "../atoms/badge"
import { cn } from "../../lib/utils"

export interface Connection {
  id: string
  name: string
  handle: string
  avatarUrl?: string
  bio?: string
  major?: string
  academicYear?: string
  verified?: boolean
  mutualConnections?: number
  isFollowing?: boolean
}

export interface ConnectionListProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Array of connections */
  connections: Connection[]
  /** List type */
  variant?: "following" | "followers" | "both"
  /** Following list (only if variant is "both") */
  following?: Connection[]
  /** Show actions (Follow/Unfollow buttons) */
  showActions?: boolean
  /** Callback when follow/unfollow is clicked */
  onToggleFollow?: (connectionId: string, isFollowing: boolean) => void
  /** Callback when connection is clicked */
  onConnectionClick?: (connectionId: string) => void
}

const ConnectionList = React.forwardRef<HTMLDivElement, ConnectionListProps>(
  ({
    className,
    connections,
    variant = "following",
    following = [],
    showActions = true,
    onToggleFollow,
    onConnectionClick,
    ...props
  }, ref) => {
    if (variant === "both") {
      return (
        <div ref={ref} className={cn("w-full", className)} {...props}>
          <Tabs defaultValue="followers" className="w-full">
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="followers">
                Followers
                <span className="ml-2 text-xs text-muted-foreground">
                  {connections.length}
                </span>
              </TabsTrigger>
              <TabsTrigger value="following">
                Following
                <span className="ml-2 text-xs text-muted-foreground">
                  {following.length}
                </span>
              </TabsTrigger>
            </TabsList>

            <TabsContent value="followers" className="mt-6">
              {connections.length === 0 ? (
                <EmptyState text="No followers yet" />
              ) : (
                <div className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3">
                  {connections.map((connection) => (
                    <ConnectionCard
                      key={connection.id}
                      connection={connection}
                      showActions={showActions}
                      onToggleFollow={onToggleFollow}
                      onConnectionClick={onConnectionClick}
                    />
                  ))}
                </div>
              )}
            </TabsContent>

            <TabsContent value="following" className="mt-6">
              {following.length === 0 ? (
                <EmptyState text="Not following anyone yet" />
              ) : (
                <div className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3">
                  {following.map((connection) => (
                    <ConnectionCard
                      key={connection.id}
                      connection={connection}
                      showActions={showActions}
                      onToggleFollow={onToggleFollow}
                      onConnectionClick={onConnectionClick}
                    />
                  ))}
                </div>
              )}
            </TabsContent>
          </Tabs>
        </div>
      )
    }

    return (
      <div ref={ref} className={cn("w-full", className)} {...props}>
        {connections.length === 0 ? (
          <EmptyState
            text={
              variant === "following"
                ? "Not following anyone yet"
                : "No followers yet"
            }
          />
        ) : (
          <div className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3">
            {connections.map((connection) => (
              <ConnectionCard
                key={connection.id}
                connection={connection}
                showActions={showActions}
                onToggleFollow={onToggleFollow}
                onConnectionClick={onConnectionClick}
              />
            ))}
          </div>
        )}
      </div>
    )
  }
)

ConnectionList.displayName = "ConnectionList"

// Internal component for individual connection card
interface ConnectionCardProps {
  connection: Connection
  showActions?: boolean
  onToggleFollow?: (connectionId: string, isFollowing: boolean) => void
  onConnectionClick?: (connectionId: string) => void
}

function ConnectionCard({
  connection,
  showActions,
  onToggleFollow,
  onConnectionClick,
}: ConnectionCardProps) {
  const handleClick = () => {
    onConnectionClick?.(connection.id)
  }

  const handleToggleFollow = () => {
    onToggleFollow?.(connection.id, !connection.isFollowing)
  }

  // Format badge: major + year, or just verified status
  const badge = connection.major && connection.academicYear
    ? `${connection.major} ‚Ä¢ ${connection.academicYear}`
    : connection.verified
    ? "Verified"
    : undefined

  return (
    <div className="relative" onClick={handleClick}>
      <UserCard
        name={connection.name}
        handle={connection.handle}
        avatar={connection.avatarUrl}
        bio={connection.bio || (connection.mutualConnections ?
          `${connection.mutualConnections} mutual ${connection.mutualConnections === 1 ? 'connection' : 'connections'}`
          : undefined)}
        badge={badge}
        badgeVariant="secondary"
        actionLabel={showActions && onToggleFollow ? (connection.isFollowing ? "Following" : "Follow") : undefined}
        onAction={showActions && onToggleFollow ? handleToggleFollow : undefined}
        actionVariant={connection.isFollowing ? "outline" : "default"}
      />
    </div>
  )
}

// Empty state component
function EmptyState({ text }: { text: string }) {
  return (
    <div className="flex flex-col items-center justify-center py-12 px-4 text-center">
      <div className="h-16 w-16 rounded-full bg-muted flex items-center justify-center mb-4">
        <svg
          className="h-8 w-8 text-muted-foreground"
          fill="none"
          strokeWidth="1.5"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            d="M15 19.128a9.38 9.38 0 002.625.372 9.337 9.337 0 004.121-.952 4.125 4.125 0 00-7.533-2.493M15 19.128v-.003c0-1.113-.285-2.16-.786-3.07M15 19.128v.106A12.318 12.318 0 018.624 21c-2.331 0-4.512-.645-6.374-1.766l-.001-.109a6.375 6.375 0 0111.964-3.07M12 6.375a3.375 3.375 0 11-6.75 0 3.375 3.375 0 016.75 0zm8.25 2.25a2.625 2.625 0 11-5.25 0 2.625 2.625 0 015.25 0z"
          />
        </svg>
      </div>
      <p className="text-sm font-medium text-foreground mb-1">{text}</p>
      <p className="text-xs text-muted-foreground">
        Connect with people to build your network
      </p>
    </div>
  )
}

