              size="sm"
              className="h-6 w-6 p-0 text-red-500 hover:text-red-500"
              onClick={onDeleteElement}
              title="Delete element"
            >
              <Trash2 className="h-3.5 w-3.5" />
            </Button>
          </div>
        </div>

        {/* Description */}
        {selectedElement.description && (
          <p className="text-xs text-white/70 line-clamp-2">
            {selectedElement.description}
          </p>
        )}

        {/* Element Metadata */}
        <div className="flex items-center gap-2 flex-wrap">
          {selectedElement.complexity && (
            <Badge variant="sophomore" className="h-5 px-1.5 text-xs">
              {selectedElement.complexity}
            </Badge>
          )}
          {selectedElement.isNew && (
            <Badge variant="freshman" className="h-5 px-1.5 text-xs">
              NEW
            </Badge>
          )}
          <Badge variant="freshman" className="h-5 px-1.5 text-xs">
            {selectedElement.inputs.length} ‚¨ÖÔ∏è | ‚û°Ô∏è {selectedElement.outputs.length}
          </Badge>
        </div>
      </div>

      {/* Properties Form */}
      <div className="flex-1 overflow-y-auto">
        {/* Basic Properties */}
        <div className="px-3 py-3 space-y-3 border-b">
          <h4 className="text-xs font-semibold">Basic Properties</h4>

          <PropertyField
            label="Name"
            type="text"
            value={selectedElement.name}
            onChange={(value) => onPropertyChange?.('name', value)}
            placeholder="Element name"
          />

          <PropertyField
            label="Description"
            type="textarea"
            value={selectedElement.description}
            onChange={(value) => onPropertyChange?.('description', value)}
            placeholder="Describe what this element does"
          />
        </div>

        {/* Input Connections */}
        {selectedElement.inputs.length > 0 && (
          <div className="px-3 py-3 space-y-3 border-b">
            <div className="flex items-center justify-between">
              <h4 className="text-xs font-semibold">Input Connections</h4>
              <Badge variant="sophomore" className="h-5 px-1.5 text-xs">
                {selectedElement.inputs.length}
              </Badge>
            </div>

            <div className="space-y-2">
              {selectedElement.inputs.map((input) => (
                <DataMappingRow
                  key={input.id}
                  port={input}
                  sourceElementName={getConnectedElement(input.id, 'input')}
                />
              ))}
            </div>
          </div>
        )}

        {/* Output Connections */}
        {selectedElement.outputs.length > 0 && (
          <div className="px-3 py-3 space-y-3 border-b">
            <div className="flex items-center justify-between">
              <h4 className="text-xs font-semibold">Output Connections</h4>
              <Badge variant="sophomore" className="h-5 px-1.5 text-xs">
                {selectedElement.outputs.length}
              </Badge>
            </div>

            <div className="space-y-2">
              {selectedElement.outputs.map((output) => (
                <DataMappingRow
                  key={output.id}
                  port={output}
                  sourceElementName={getConnectedElement(output.id, 'output')}
                />
              ))}
            </div>
          </div>
        )}

        {/* Element Configuration */}
        <div className="px-3 py-3 space-y-3 border-b">
          <h4 className="text-xs font-semibold">Configuration</h4>

          {/* Render config fields based on element type */}
          {Object.keys(selectedElement.config).length > 0 ? (
            Object.entries(selectedElement.config).map(([key, value]) => {
              // Infer field type from value
              let fieldType: PropertyFieldType = 'text';
              if (typeof value === 'boolean') fieldType = 'boolean';
              else if (typeof value === 'number') fieldType = 'number';

              return (
                <PropertyField
                  key={key}
                  label={key.charAt(0).toUpperCase() + key.slice(1).replace(/([A-Z])/g, ' $1')}
                  type={fieldType}
                  value={value}
                  onChange={(newValue) => {
                    onPropertyChange?.('config', {
                      ...selectedElement.config,
                      [key]: newValue,
                    });
                  }}
                />
              );
            })
          ) : (
            <p className="text-xs text-white/70">
              No configuration options available
            </p>
          )}
        </div>

        {/* Advanced Settings */}
        <div className="px-3 py-3 space-y-3">
          <h4 className="text-xs font-semibold">Advanced</h4>

          <PropertyField
            label="Element ID"
            type="text"
            value={selectedElement.id}
            onChange={() => {}} // Read-only
            disabled
            help="Unique identifier for this element"
          />

          <PropertyField
            label="Page ID"
            type="text"
            value={selectedElement.pageId}
            onChange={() => {}} // Read-only
            disabled
            help="The page this element belongs to"
          />

          <div className="grid grid-cols-2 gap-2">
            <PropertyField
              label="Width"
              type="number"
              value={selectedElement.width}
              onChange={(value) => onPropertyChange?.('width', value)}
            />
            <PropertyField
              label="Height"
              type="number"
              value={selectedElement.height}
              onChange={(value) => onPropertyChange?.('height', value)}
            />
          </div>

          <div className="grid grid-cols-2 gap-2">
            <PropertyField
              label="X Position"
              type="number"
              value={selectedElement.x}
              onChange={(value) => onPropertyChange?.('x', value)}
            />
            <PropertyField
              label="Y Position"
              type="number"
              value={selectedElement.y}
              onChange={(value) => onPropertyChange?.('y', value)}
            />
          </div>
        </div>
      </div>

      {/* Footer */}
      <div className="px-3 py-2 border-t bg-white/5">
        <p className="text-[10px] text-white/70 text-center">
          üíæ Changes are saved automatically
        </p>
      </div>
    </FloatingPanel>
  );
}

HiveLabPropertiesPanel.displayName = 'HiveLabPropertiesPanel';

/**
 * HiveLab Toolbar Component
 *
 * Top toolbar for the HiveLab builder with file operations, undo/redo,
 * zoom controls, and tool actions.
 */

'use client';

import React from 'react';
import { cn } from '../../../lib/utils';
import { Button } from '../../atoms/button';
import {
  Save,
  Undo,
  Redo,
  Play,
  Settings,
  Download,
  Upload,
  Copy,
  Trash2,
  Grid3x3,
  ZoomIn,
  ZoomOut,
  Maximize2,
  Eye,
  Code,
  FileText,
} from 'lucide-react';

export interface HiveLabToolbarProps {
  /** Current tool name */
  toolName?: string;
  /** Can undo? */
  canUndo?: boolean;
  /** Can redo? */
  canRedo?: boolean;
  /** Current zoom level */
  zoom?: number;
  /** Is tool saved? */
  isSaved?: boolean;
  /** Is tool running (preview)? */
  isRunning?: boolean;
  /** Show grid? */
  showGrid?: boolean;
  /** Undo handler */
  onUndo?: () => void;
  /** Redo handler */
  onRedo?: () => void;
  /** Save handler */
  onSave?: () => void;
  /** Run/preview handler */
  onRun?: () => void;
  /** Settings handler */
  onSettings?: () => void;
  /** Export handler */
  onExport?: () => void;
  /** Import handler */
  onImport?: () => void;
  /** Delete handler */
  onDelete?: () => void;
  /** Duplicate handler */
  onDuplicate?: () => void;
  /** Zoom in handler */
  onZoomIn?: () => void;
  /** Zoom out handler */
  onZoomOut?: () => void;
  /** Zoom to fit handler */
  onZoomToFit?: () => void;
  /** Toggle grid handler */
  onToggleGrid?: () => void;
  /** View code handler */
  onViewCode?: () => void;
  /** View docs handler */
  onViewDocs?: () => void;
  /** Additional class names */
  className?: string;
}

export function HiveLabToolbar({
  toolName = 'Untitled Tool',
  canUndo = false,
  canRedo = false,
  zoom = 1,
  isSaved = true,
  isRunning = false,
  showGrid = true,
  onUndo,
  onRedo,
  onSave,
  onRun,
  onSettings,
  onExport,
  onImport,
  onDelete,
  onDuplicate,
  onZoomIn,
  onZoomOut,
  onZoomToFit,
  onToggleGrid,
  onViewCode,
  onViewDocs,
  className,
}: HiveLabToolbarProps) {
  const zoomPercentage = Math.round(zoom * 100);

  return (
    <div
      className={cn(
        'hivelab-toolbar flex items-center gap-2 px-4 py-2',
        'bg-[#0c0c0c]/95 backdrop-blur-sm border-b',
        className
      )}
    >
      {/* Tool name */}
      <div className="flex items-center gap-2 min-w-0">
        <h2 className="text-sm font-semibold truncate">{toolName}</h2>
        {!isSaved && (
          <span className="text-xs text-white/70">(unsaved)</span>
        )}
      </div>

      {/* Divider */}
      <div className="h-6 w-px bg-white/8 mx-2" />

      {/* File operations */}
      <div className="flex items-center gap-1">
        <Button
          variant="ghost"
          size="sm"
          onClick={onSave}
          disabled={isSaved}
          title="Save (Cmd+S)"
          className="h-8 w-8 p-0"
        >
          <Save className="h-4 w-4" />
        </Button>

        <Button
          variant="ghost"
          size="sm"
          onClick={onImport}
          title="Import tool"
          className="h-8 w-8 p-0"
        >
          <Upload className="h-4 w-4" />
        </Button>

        <Button
          variant="ghost"
          size="sm"
          onClick={onExport}
          title="Export tool"
          className="h-8 w-8 p-0"
        >
          <Download className="h-4 w-4" />
        </Button>

        <Button
          variant="ghost"
          size="sm"
          onClick={onDuplicate}
          title="Duplicate (Cmd+D)"
          className="h-8 w-8 p-0"
        >
          <Copy className="h-4 w-4" />
        </Button>

        <Button
          variant="ghost"
          size="sm"
          onClick={onDelete}
          title="Delete tool"
          className="h-8 w-8 p-0 text-red-500 hover:text-red-500"
        >
          <Trash2 className="h-4 w-4" />
        </Button>
      </div>

      {/* Divider */}
      <div className="h-6 w-px bg-white/8 mx-2" />

      {/* Edit operations */}
      <div className="flex items-center gap-1">
        <Button
          variant="ghost"
          size="sm"
          onClick={onUndo}
          disabled={!canUndo}
          title="Undo (Cmd+Z)"
          className="h-8 w-8 p-0"
        >
          <Undo className="h-4 w-4" />
        </Button>

        <Button
          variant="ghost"
          size="sm"
          onClick={onRedo}
          disabled={!canRedo}
          title="Redo (Cmd+Shift+Z)"
          className="h-8 w-8 p-0"
        >
          <Redo className="h-4 w-4" />
        </Button>
      </div>

      {/* Divider */}
      <div className="h-6 w-px bg-white/8 mx-2" />

      {/* View controls */}
      <div className="flex items-center gap-1">
        <Button
          variant="ghost"
          size="sm"
          onClick={onZoomOut}
          title="Zoom out (Cmd+-)"
          className="h-8 w-8 p-0"
        >
          <ZoomOut className="h-4 w-4" />
        </Button>

        <Button
          variant="ghost"
          size="sm"
          onClick={onZoomToFit}
          title="Reset zoom (Cmd+0)"
          className="h-8 px-2 min-w-[60px]"
        >
          <span className="text-xs font-medium">{zoomPercentage}%</span>
        </Button>

        <Button
          variant="ghost"
          size="sm"
          onClick={onZoomIn}
          title="Zoom in (Cmd+=)"
          className="h-8 w-8 p-0"
        >
          <ZoomIn className="h-4 w-4" />
        </Button>

        <Button
          variant="ghost"
          size="sm"
          onClick={onToggleGrid}
          title="Toggle grid (Cmd+G)"
          className={cn(
            'h-8 w-8 p-0',
            showGrid && 'bg-white/10'
          )}
        >
          <Grid3x3 className="h-4 w-4" />
        </Button>

        <Button
          variant="ghost"
          size="sm"
          onClick={onZoomToFit}
          title="Zoom to fit (Cmd+1)"
          className="h-8 w-8 p-0"
        >
          <Maximize2 className="h-4 w-4" />
        </Button>
      </div>

      {/* Spacer */}
      <div className="flex-1" />

      {/* Right actions */}
      <div className="flex items-center gap-1">
        <Button
          variant="ghost"
          size="sm"
          onClick={onViewCode}
          title="View generated code"
          className="h-8 gap-1.5 px-3"
        >
          <Code className="h-4 w-4" />
          <span className="text-xs">Code</span>
        </Button>

        <Button
          variant="ghost"
          size="sm"
          onClick={onViewDocs}
          title="View documentation"
          className="h-8 gap-1.5 px-3"
        >
          <FileText className="h-4 w-4" />
          <span className="text-xs">Docs</span>
        </Button>

        <Button
          variant="ghost"
          size="sm"
          onClick={onSettings}
          title="Tool settings"
          className="h-8 w-8 p-0"
        >
          <Settings className="h-4 w-4" />
        </Button>

        <Button
          variant={isRunning ? 'destructive' : 'default'}
          size="sm"
          onClick={onRun}
          title={isRunning ? 'Stop preview' : 'Run preview (Cmd+Enter)'}
          className="h-8 gap-1.5 px-3"
        >
          {isRunning ? (
            <>
              <span className="h-2 w-2 rounded-full bg-current animate-pulse" />
              <span className="text-xs font-medium">Stop</span>
            </>
          ) : (
            <>
              <Play className="h-4 w-4" />
              <span className="text-xs font-medium">Preview</span>
            </>
          )}
        </Button>
      </div>
    </div>
  );
}

HiveLabToolbar.displayName = 'HiveLabToolbar';

"use client"

import * as React from "react"
import { MotionNav } from "../../shells/motion-safe"
import { transitions } from "../../lib/animations"
import { Button } from "../atoms/button"
import { Badge } from "../atoms/badge"
import { HiveLogo } from "../atoms/hive-logo"
import { SearchBar } from "../molecules/search-bar"
import { NotificationItem } from "../molecules/notification-item"
import { cn } from "../../lib/utils"

export interface NavigationLink {
  label: string
  href: string
  isActive?: boolean
  badge?: number
}

export interface NavigationShellProps extends React.HTMLAttributes<HTMLDivElement> {
  currentUserName?: string
  currentUserAvatar?: string
  currentUserHandle?: string
  links?: NavigationLink[]
  notificationCount?: number
  notifications?: React.ComponentProps<typeof NotificationItem>[]
  onSearch?: (query: string) => void
  onNotificationClick?: (notification: React.ComponentProps<typeof NotificationItem>) => void
  onProfileClick?: () => void
  onSettingsClick?: () => void
  onSignOutClick?: () => void
  showSearch?: boolean
  /** Layout mode: header or sidebar */
  layout?: "header" | "sidebar"
  /** Whether sidebar is collapsed */
  isCollapsed?: boolean
  /** Callback to toggle layout */
  onToggleLayout?: () => void
}

const NavigationShell = React.forwardRef<HTMLDivElement, NavigationShellProps>(
  (
    {
      className,
      children,
      currentUserName = "Guest",
      currentUserAvatar,
      currentUserHandle = "@guest",
      links = [],
      notificationCount = 0,
      notifications = [],
      onSearch,
      onNotificationClick,
      onProfileClick,
      onSettingsClick,
      onSignOutClick,
      showSearch = true,
      layout = "header",
      isCollapsed = false,
      onToggleLayout,
      ...props
    },
    ref
  ) => {
    const [showNotifications, setShowNotifications] = React.useState(false)
    const [showUserMenu, setShowUserMenu] = React.useState(false)
    const notificationRef = React.useRef<HTMLDivElement>(null)
    const userMenuRef = React.useRef<HTMLDivElement>(null)

    const isSidebar = layout === "sidebar"

    // Generate initials from name
    const initials = currentUserName
      .split(" ")
      .map((n) => n[0])
      .join("")
      .toUpperCase()
      .slice(0, 2)

    // Close dropdowns when clicking outside
    React.useEffect(() => {
      const handleClickOutside = (event: MouseEvent) => {
        if (notificationRef.current && !notificationRef.current.contains(event.target as Node)) {
          setShowNotifications(false)
        }
        if (userMenuRef.current && !userMenuRef.current.contains(event.target as Node)) {
          setShowUserMenu(false)
        }
      }

      document.addEventListener("mousedown", handleClickOutside)
      return () => document.removeEventListener("mousedown", handleClickOutside)
    }, [])

    return (
      <div ref={ref} className={cn("flex", isSidebar ? "flex-row min-h-screen" : "flex-col min-h-screen", className)} {...props}>
        {/* Navigation - Header or Sidebar */}
        <MotionNav
          layout
          initial={false}
          animate={{
            width: isSidebar ? (isCollapsed ? 64 : 240) : "100%",
            height: isSidebar ? "100vh" : "auto"
          }}
          transition={transitions.slow}
          className={cn(
            "z-50 border-border bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/60",
            isSidebar ? "sticky top-0 h-screen border-r flex flex-col" : "sticky top-0 w-full border-b"
          )}
        >
          {isSidebar ? (
            /* Sidebar Layout */
            <>
              {/* Logo & Brand */}
              <div className={cn("flex items-center gap-2 p-4 border-b border-border", isCollapsed && "justify-center")}>
                <HiveLogo
                  variant="currentColor"
                  size={isCollapsed ? 24 : 32}
                  className="text-primary shrink-0"
                />
                {!isCollapsed && (
                  <span className="text-base font-bold text-foreground">
                    HIVE
                  </span>
                )}
              </div>

              {/* Navigation Links */}
              {links.length > 0 && (
                <nav className="flex-1 flex flex-col gap-1 p-2 overflow-y-auto">
                  {links.map((link, index) => (
                    <a
                      key={index}
                      href={link.href}
                      className={cn(
                        "relative flex items-center gap-3 rounded-md px-3 py-2 text-sm font-medium transition-smooth ease-liquid",
                        link.isActive
                          ? "bg-accent text-foreground"
                          : "text-muted-foreground hover:bg-accent/50 hover:text-foreground",
                        isCollapsed && "justify-center px-0"
                      )}
                      title={isCollapsed ? link.label : undefined}
                    >
                      {isCollapsed ? (
                        <span className="text-base">{link.label.charAt(0)}</span>
                      ) : (
                        <>
                          <span>{link.label}</span>
                          {link.badge !== undefined && link.badge > 0 && (
                            <Badge
                              variant="freshman"
                              className="ml-auto h-5 min-w-5 px-1 text-xs transition-smooth ease-liquid"
                            >
                              {link.badge > 99 ? "99+" : link.badge}
                            </Badge>
                          )}
                        </>
                      )}
                    </a>
                  ))}
                </nav>
              )}

              {/* Bottom Section: Notifications & User */}
              <div className="mt-auto border-t border-border p-2">
                {/* Notifications */}
                <div ref={notificationRef} className="relative mb-1">
                  <button
                    onClick={() => setShowNotifications(!showNotifications)}
                    className={cn(
                      "relative flex h-9 items-center gap-3 rounded-md text-muted-foreground transition-smooth ease-liquid hover:bg-accent hover:text-foreground",
                      isCollapsed ? "w-9 justify-center" : "w-full px-3"
                    )}
                    title={isCollapsed ? "Notifications" : undefined}
                  >
                    <svg className="h-5 w-5 shrink-0" fill="none" strokeWidth="2" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" d="M14.857 17.082a23.848 23.848 0 005.454-1.31A8.967 8.967 0 0118 9.75v-.7V9A6 6 0 006 9v.75a8.967 8.967 0 01-2.312 6.022c1.733.64 3.56 1.085 5.455 1.31m5.714 0a24.255 24.255 0 01-5.714 0m5.714 0a3 3 0 11-5.714 0" />
                    </svg>
                    {!isCollapsed && <span className="text-sm font-medium">Notifications</span>}
                    {notificationCount > 0 && (
                      <div className={cn(
                        "flex h-5 min-w-5 items-center justify-center rounded-full bg-destructive px-1 text-[10px] font-bold text-destructive-foreground",
                        isCollapsed ? "absolute -right-1 -top-1" : "ml-auto"
                      )}>
                        {notificationCount > 99 ? "99+" : notificationCount}
                      </div>
                    )}
                  </button>

                  {/* Notification Dropdown */}
                  {showNotifications && (
                    <div className={cn(
                      "absolute bottom-0 w-[400px] rounded-lg border border-border bg-card shadow-lg transition-all duration-smooth ease-liquid",
                      isCollapsed ? "left-16" : "left-full ml-2"
                    )}>
                      <div className="flex items-center justify-between border-b border-border p-4">
                        <h3 className="text-sm font-semibold text-foreground">Notifications</h3>
                        {notificationCount > 0 && (
                          <Badge variant="sophomore">{notificationCount} new</Badge>
                        )}
                      </div>
                      <div className="max-h-[400px] overflow-y-auto p-2">
                        {notifications.length > 0 ? (
                          <div className="flex flex-col gap-1">
                            {notifications.map((notification, index) => (
                              <div
                                key={index}
                                onClick={() => {
                                  onNotificationClick?.(notification)
                                  setShowNotifications(false)
                                }}
                              >
                                <NotificationItem {...notification} />
                              </div>
                            ))}
                          </div>
                        ) : (
                          <div className="flex flex-col items-center justify-center py-8">
                            <svg className="h-12 w-12 text-muted-foreground/30" fill="none" strokeWidth="1.5" stroke="currentColor" viewBox="0 0 24 24">
                              <path strokeLinecap="round" strokeLinejoin="round" d="M14.857 17.082a23.848 23.848 0 005.454-1.31A8.967 8.967 0 0118 9.75v-.7V9A6 6 0 006 9v.75a8.967 8.967 0 01-2.312 6.022c1.733.64 3.56 1.085 5.455 1.31m5.714 0a24.255 24.255 0 01-5.714 0m5.714 0a3 3 0 11-5.714 0" />
                            </svg>
                            <p className="mt-2 text-sm text-muted-foreground">No notifications yet</p>
                          </div>
                        )}
                      </div>
                    </div>
                  )}
                </div>

                {/* User Menu */}
                <div ref={userMenuRef} className="relative">
                  <button
                    onClick={() => setShowUserMenu(!showUserMenu)}
                    className={cn(
                      "flex items-center gap-3 rounded-md transition-smooth ease-liquid hover:bg-accent",
                      isCollapsed ? "h-9 w-9 justify-center p-0" : "w-full p-2"
                    )}
                    title={isCollapsed ? currentUserName : undefined}
                  >
                    <div className={cn("overflow-hidden rounded border border-border bg-muted", isCollapsed ? "h-7 w-7" : "h-8 w-7 shrink-0")}>
                      {currentUserAvatar ? (
                        <img src={currentUserAvatar} alt={currentUserName} className="h-full w-full object-cover" />
                      ) : (
                        <div className="flex h-full w-full items-center justify-center bg-primary/10 text-[10px] font-semibold text-primary">
                          {initials}
                        </div>
                      )}
                    </div>
                    {!isCollapsed && (
                      <div className="flex flex-1 flex-col items-start min-w-0">
                        <span className="truncate text-xs font-semibold text-foreground max-w-full">
                          {currentUserName}
                        </span>
                        <span className="truncate text-[10px] text-muted-foreground max-w-full">
                          {currentUserHandle}
                        </span>
                      </div>
                    )}
                  </button>

                  {/* User Menu Dropdown */}
                  {showUserMenu && (
                    <div className={cn(
                      "absolute bottom-0 w-[280px] rounded-lg border border-border bg-card shadow-lg transition-all duration-smooth ease-liquid",
                      isCollapsed ? "left-16" : "left-full ml-2"
                    )}>
                      <div className="border-b border-border p-3">
                        <div className="flex items-center gap-3">
