"use client"

import * as React from "react"
import { Card, CardContent, CardHeader, CardTitle } from "../atoms/card"
import { Button } from "../atoms/button"
import { Badge } from "../atoms/badge"
import { cn } from "../../lib/utils"
import { Clock, MapPin, ChevronRight, Calendar as CalendarIcon, Plus, Upload } from "lucide-react"

export interface ScheduleEvent {
  id: string
  title: string
  type: "class" | "event" | "office-hours"
  startTime: string // "9:00 AM"
  endTime: string // "10:50 AM"
  location: string
  spaceId?: string
  spaceName?: string
  instructor?: string
  color?: "blue" | "green" | "purple" | "orange" | "red" | "yellow"
  rsvpStatus?: "going" | "interested" | null
}

export interface ProfileScheduleWidgetProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Today's events */
  events: ScheduleEvent[]

  /** Show only upcoming events (filter past events) */
  showOnlyUpcoming?: boolean

  /** Event click handler */
  onEventClick?: (eventId: string) => void

  /** RSVP handler */
  onRsvp?: (eventId: string) => void

  /** View full calendar handler */
  onViewFullCalendar?: () => void

  /** Import classes handler */
  onImportClasses?: () => void

  /** Currently selected date (defaults to today) */
  date?: Date
}

/**
 * Profile Schedule Widget
 *
 * Prominent display of today's classes and events on profile.
 * Key feature: Students can import their class schedule.
 *
 * Design Strategy:
 * - Shows today's schedule at a glance
 * - Upcoming events highlighted
 * - Quick RSVP for events
 * - Import schedule CTA if empty
 * - Timeline view with current time indicator
 *
 * Differentiation:
 * - Class import integration (from university registrar)
 * - Combined classes + social events
 * - "Happening now" indicator
 * - Quick actions (RSVP, navigate)
 */
const ProfileScheduleWidget = React.forwardRef<HTMLDivElement, ProfileScheduleWidgetProps>(
  ({
    className,
    events,
    showOnlyUpcoming = false,
    onEventClick,
    onRsvp,
    onViewFullCalendar,
    onImportClasses,
    date = new Date(),
    ...props
  }, ref) => {
    const now = new Date()
    const currentHour = now.getHours()
    const currentMinute = now.getMinutes()

    // Filter upcoming events if requested
    const displayEvents = React.useMemo(() => {
      if (!showOnlyUpcoming) return events

      return events.filter(event => {
        const [hourStr, minuteStr] = event.startTime.split(':')
        const hour = parseInt(hourStr)
        const minute = parseInt(minuteStr.split(' ')[0])
        const isPM = event.startTime.includes('PM')

        const eventHour = isPM && hour !== 12 ? hour + 12 : hour
        const eventTime = eventHour * 60 + minute
        const currentTime = currentHour * 60 + currentMinute

        return eventTime >= currentTime
      })
    }, [events, showOnlyUpcoming, currentHour, currentMinute])

    // Check if an event is happening now
    const isHappeningNow = (event: ScheduleEvent) => {
      const parseTime = (timeStr: string) => {
        const [hourStr, minuteStr] = timeStr.split(':')
        const hour = parseInt(hourStr)
        const minute = parseInt(minuteStr.split(' ')[0])
        const isPM = timeStr.includes('PM')
        return (isPM && hour !== 12 ? hour + 12 : hour) * 60 + minute
      }

      const startTime = parseTime(event.startTime)
      const endTime = parseTime(event.endTime)
      const currentTime = currentHour * 60 + currentMinute

      return currentTime >= startTime && currentTime <= endTime
    }

    if (displayEvents.length === 0) {
      return (
        <Card ref={ref} className={cn("", className)} {...props}>
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle className="flex items-center gap-2">
                <CalendarIcon className="h-5 w-5" />
                Today's Schedule
              </CardTitle>
              {onViewFullCalendar && (
                <Button variant="ghost" size="sm" onClick={onViewFullCalendar}>
                  <CalendarIcon className="h-4 w-4 mr-2" />
                  Full Calendar
                </Button>
              )}
            </div>
          </CardHeader>
          <CardContent>
            <div className="text-center py-8">
              <div className="h-16 w-16 rounded-full bg-muted flex items-center justify-center mx-auto mb-4">
                <CalendarIcon className="h-8 w-8 text-muted-foreground" />
              </div>
              <p className="text-sm font-medium text-foreground mb-1">
                No events today
              </p>
              <p className="text-xs text-muted-foreground mb-4">
                Import your class schedule to see it here
              </p>
              {onImportClasses && (
                <div className="flex gap-2 justify-center">
                  <Button variant="outline" size="sm" onClick={onImportClasses}>
                    <Upload className="h-4 w-4 mr-2" />
                    Import Classes
                  </Button>
                  {onViewFullCalendar && (
                    <Button variant="outline" size="sm" onClick={onViewFullCalendar}>
                      <Plus className="h-4 w-4 mr-2" />
                      Add Event
                    </Button>
                  )}
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      )
    }

    return (
      <Card ref={ref} className={cn("", className)} {...props}>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="flex items-center gap-2">
              <CalendarIcon className="h-5 w-5" />
              Today's Schedule
              <Badge variant="sophomore" className="ml-2">
                {displayEvents.length} {displayEvents.length === 1 ? 'event' : 'events'}
              </Badge>
            </CardTitle>
            {onViewFullCalendar && (
              <Button variant="ghost" size="sm" onClick={onViewFullCalendar}>
                View All
                <ChevronRight className="h-4 w-4 ml-1" />
              </Button>
            )}
          </div>
        </CardHeader>
        <CardContent>
          <div className="space-y-2">
            {displayEvents.map((event, index) => {
              const isNow = isHappeningNow(event)

              return (
                <div
                  key={event.id}
                  className={cn(
                    "p-3 rounded-lg border transition-all cursor-pointer",
                    isNow && "border-primary bg-primary/5",
                    !isNow && "hover:bg-accent/50"
                  )}
                  onClick={() => onEventClick?.(event.id)}
                >
                  {/* Event Header */}
                  <div className="flex items-start justify-between gap-2 mb-2">
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center gap-2 mb-1">
                        <h4 className="text-sm font-semibold text-foreground line-clamp-1">
                          {event.title}
                        </h4>
                        {isNow && (
                          <Badge variant="freshman" className="h-5 text-[9px] bg-primary">
                            Happening now
                          </Badge>
                        )}
                      </div>
                      {event.instructor && (
                        <p className="text-xs text-muted-foreground">
                          {event.instructor}
                        </p>
                      )}
                      {event.spaceName && !event.instructor && (
                        <p className="text-xs text-muted-foreground">
                          {event.spaceName}
                        </p>
                      )}
                    </div>

                    {/* Event Type Badge */}
                    <Badge
                      variant="freshman"
                      className={cn(
                        "text-[9px] h-5 shrink-0",
                        event.type === "class" && "border-blue-500/50 text-blue-600",
                        event.type === "event" && "border-green-500/50 text-green-600",
                        event.type === "office-hours" && "border-purple-500/50 text-purple-600"
                      )}
                    >
                      {event.type === "class" ? "Class" : event.type === "event" ? "Event" : "Office Hours"}
                    </Badge>
                  </div>

                  {/* Time & Location */}
                  <div className="flex items-center justify-between text-xs text-muted-foreground mb-2">
                    <div className="flex items-center gap-3">
                      <div className="flex items-center gap-1">
                        <Clock className="h-3 w-3" />
                        <span>{event.startTime} - {event.endTime}</span>
                      </div>
                      <div className="flex items-center gap-1">
                        <MapPin className="h-3 w-3" />
                        <span className="truncate">{event.location}</span>
                      </div>
                    </div>
                  </div>

                  {/* Actions for Events (not classes) */}
                  {event.type === "event" && onRsvp && (
                    <div className="flex gap-2 mt-2 pt-2 border-t">
                      {event.rsvpStatus === "going" ? (
                        <Badge variant="freshman" className="text-[9px] h-5 bg-[#FFD700] text-black">
                          You're going ‚úì
                        </Badge>
                      ) : (
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={(e: React.MouseEvent) => {
                            e.stopPropagation()
                            onRsvp(event.id)
                          }}
                          className="h-7 text-xs"
                        >
                          RSVP
                        </Button>
                      )}
                    </div>
                  )}
                </div>
              )
            })}
          </div>

          {/* Import Classes CTA (if there are events but might want more) */}
          {onImportClasses && displayEvents.length < 3 && (
            <div className="mt-4 pt-4 border-t">
              <Button variant="outline" size="sm" className="w-full" onClick={onImportClasses}>
                <Upload className="h-4 w-4 mr-2" />
                Import Your Class Schedule
              </Button>
            </div>
          )}
        </CardContent>
      </Card>
    )
  }
)

ProfileScheduleWidget.displayName = "ProfileScheduleWidget"

export { ProfileScheduleWidget }

/**
 * Profile UI Types - Spec Compliant
 * Based on spec.md User Profile Schema (lines 2231-2249)
 */

/**
 * Privacy levels for profile widgets
 * @see spec.md:2161-2165
 */
export type PrivacyLevel = 'visible' | 'private' | 'ghost';

/**
 * User types in the system
 */
export type UserType = 'student' | 'faculty' | 'staff' | 'admin';

/**
 * Connection types between users
 */
export type ConnectionType = 'connection' | 'friend';

/**
 * Badge variants for profile display
 */
export interface ProfileBadge {
  label: string;
  variant?: 'default' | 'secondary' | 'outline' | 'destructive';
  icon?: React.ReactNode;
}

/**
 * Privacy settings for profile widgets
 * Each widget can have independent privacy controls
 */
export interface WidgetPrivacy {
  level: PrivacyLevel;
  customMessage?: string; // Optional message when content is hidden
}

/**
 * Profile Identity (Core identifying information)
 * @see spec.md:2232
 */
export interface ProfileIdentity {
  id: string;
  /** Backend only - never displayed to users */
  username?: string;
  email: string;
  fullName: string;
  bio?: string;
  /** Primary avatar URL */
  avatarUrl?: string;
  /** Photo carousel (max 5 photos, 3:4 aspect ratio) */
  photos?: string[];
  pronouns?: string;
}

/**
 * Academic Information
 * @see spec.md:2233
 */
export interface ProfileAcademic {
  /** REQUIRED field per spec */
  major: string;
  graduationYear?: number;
  academicYear?: 'Freshman' | 'Sophomore' | 'Junior' | 'Senior' | 'Graduate';
  campusId: 'ub-buffalo'; // Hardcoded for vBETA
  userType: UserType;
  facultyVerified?: boolean;
  /** Faculty only: List of courses they teach */
  courses?: string[];
}

/**
 * Interests from HIVE_INTERESTS.md
 * @see spec.md:2234
 */
export interface ProfileInterests {
  selectedInterests: string[];
  categories?: string[]; // Interest categories
}

/**
 * Social Metrics
 * @see spec.md:2235
 */
export interface ProfileSocial {
  followerCount: number;
  followingCount: number;
  connectionCount: number;
  friendCount: number;
  /** Connection strength scores (userId -> strength 0-1) */
  connectionStrength?: Record<string, number>;
  mutualConnections?: string[];
  mutualSpaces?: string[];
}

/**
 * Privacy Settings
 * @see spec.md:2236
 */
export interface ProfilePrivacy {
  /** Overall profile visibility */
  profileVisibility: 'public' | 'campus' | 'connections';
  /** Appear in member searches */
  searchable: boolean;
  /** Widget-level privacy controls */
  widgets: {
    myActivity: WidgetPrivacy;
    mySpaces: WidgetPrivacy;
    myConnections: WidgetPrivacy;
    myClasses?: WidgetPrivacy; // Faculty only
    hivelab?: WidgetPrivacy;
  };
}

/**
 * Activity Tracking
 * @see spec.md:2237
 */
export interface ProfileActivity {
  joinedSpaces: string[];
  lastActive: Date;
  profileCompletionScore: number; // 0-100
  recentActivity?: ProfileActivityItem[];
}

/**
 * Individual activity item
 * @see spec.md:2246-2249
 */
export interface ProfileActivityItem {
  id: string;
  userId: string;
  activityType: 'post' | 'comment' | 'reaction' | 'space_join' | 'connection';
  targetId: string;
  targetType: string;
  timestamp: Date;
  campusId: 'ub-buffalo';
  isPublic: boolean;
  // Display fields
  title?: string;
  description?: string;
  metadata?: Record<string, any>;
}

/**
 * Account Status
 * @see spec.md:2238
 */
export interface ProfileStatus {
  isActive: boolean;
  isVerified: boolean;
  isModerator: boolean;
  isSpaceLeader: boolean; // Has HiveLab access
  facultyVerified?: boolean;
  createdAt: Date;
  updatedAt: Date;
}

/**
 * Complete Profile (Spec-Compliant)
 * Main profile interface matching spec.md schema
 */
export interface Profile {
  identity: ProfileIdentity;
  academic: ProfileAcademic;
  interests: ProfileInterests;
  social: ProfileSocial;
  privacy: ProfilePrivacy;
  activity: ProfileActivity;
  status: ProfileStatus;
}

/**
 * Connection between users
 * @see spec.md:2240-2244
 */
export interface Connection {
  userId1: string;
  userId2: string;
  connectionType: ConnectionType;
  campusId: 'ub-buffalo';
  // Metrics
  connectionStrength: number; // 0-1, formula: (Interactions √ó 0.4) + (SharedSpaces √ó 0.3) + (MutualConnections √ó 0.3)
  mutualConnectionCount: number;
  sharedSpaceCount: number;
  // Status
  status: 'pending' | 'accepted' | 'blocked';
  // Timestamps
  createdAt: Date;
  acceptedAt?: Date;
  lastInteraction?: Date;
}

/**
 * Simplified profile for UI components
 * Flattened version for easier component consumption
 */
export interface UIProfile {
  // Identity
  id: string;
  fullName: string;
  email: string;
  handle: string; // Derived from email (username@buffalo.edu)
  avatarUrl?: string;
  photos?: string[];
  bio?: string;
  pronouns?: string;

  // Academic
  major: string;
  graduationYear?: number;
  academicYear?: string;
  userType: UserType;
  facultyVerified?: boolean;
  courses?: string[]; // Faculty only

  // Social stats
  connectionCount: number;
  friendCount: number;
  spaceCount: number;
  postCount: number;
  mutualConnections?: number;
  mutualSpaces?: number;

  // Status
  verified: boolean;
  isOwnProfile: boolean;
  isConnected: boolean;
  isSpaceLeader: boolean;

  // Privacy
  profileVisibility: 'public' | 'campus' | 'connections';
  widgetPrivacy: ProfilePrivacy['widgets'];

  // Display
  badges: ProfileBadge[];
  completionScore: number;
  lastActive?: Date;
}

/**
 * Convert spec Profile to UIProfile
 * Adapter for component consumption
 */
export function profileToUIProfile(
  profile: Profile,
  viewerContext?: {
    isOwnProfile?: boolean;
    isConnected?: boolean;
    mutualConnections?: number;
    mutualSpaces?: number;
  }
): UIProfile {
  const handle = profile.identity.email.split('@')[0];

  // Generate badges based on status and type
  const badges: ProfileBadge[] = [];

  if (profile.academic.facultyVerified) {
    badges.push({ label: 'Faculty', variant: 'default' });
  }

  if (profile.status.isSpaceLeader) {
    badges.push({ label: 'Space Leader', variant: 'default' });
  }

  if (profile.status.isModerator) {
    badges.push({ label: 'Moderator', variant: 'secondary' });
  }

  // Check if recently active (within last 30 minutes)
  const isRecentlyActive = profile.activity.lastActive &&
    (Date.now() - profile.activity.lastActive.getTime()) < 30 * 60 * 1000;

  if (isRecentlyActive && viewerContext?.isOwnProfile) {
    badges.push({ label: 'Active Now', variant: 'secondary' });
  }

  return {
    id: profile.identity.id,
    fullName: profile.identity.fullName,
    email: profile.identity.email,
    handle,
    avatarUrl: profile.identity.avatarUrl,
    photos: profile.identity.photos,
    bio: profile.identity.bio,
    pronouns: profile.identity.pronouns,

    major: profile.academic.major,
    graduationYear: profile.academic.graduationYear,
    academicYear: profile.academic.academicYear,
    userType: profile.academic.userType,
    facultyVerified: profile.academic.facultyVerified,
    courses: profile.academic.courses,

    connectionCount: profile.social.connectionCount,
    friendCount: profile.social.friendCount,
    spaceCount: profile.activity.joinedSpaces.length,
    postCount: 0, // Would need to be calculated
    mutualConnections: viewerContext?.mutualConnections,
    mutualSpaces: viewerContext?.mutualSpaces,

    verified: profile.status.isVerified,
    isOwnProfile: viewerContext?.isOwnProfile ?? false,
    isConnected: viewerContext?.isConnected ?? false,
    isSpaceLeader: profile.status.isSpaceLeader,

    profileVisibility: profile.privacy.profileVisibility,
    widgetPrivacy: profile.privacy.widgets,

    badges,
    completionScore: profile.activity.profileCompletionScore,
    lastActive: profile.activity.lastActive,
  };
}

/**
 * Check if widget should be visible based on privacy settings
 */
export function canViewWidget(
  widget: WidgetPrivacy,
  viewerContext: {
    isOwnProfile: boolean;
    isConnected: boolean;
  }
): boolean {
  if (viewerContext.isOwnProfile) return true;

  switch (widget.level) {
    case 'visible':
      return true;
    case 'private':
      return viewerContext.isConnected;
    case 'ghost':
      return false;
    default:
      return false;
  }
}

/**
 * Get privacy indicator for UI display
 */
export function getPrivacyIndicator(level: PrivacyLevel): {
  icon: string;
  label: string;
  description: string;
} {
  switch (level) {
    case 'visible':
      return {
        icon: 'üåç',
        label: 'Campus Visible',
        description: 'Everyone on campus can see this'
      };
    case 'private':
      return {
        icon: 'üîí',
        label: 'Connections Only',
        description: 'Only your connections can see this'
      };
    case 'ghost':
      return {
        icon: 'üëª',
        label: 'Private',
        description: 'Hidden from everyone (you appear inactive)'
      };
  }
}

"use client"

import * as React from "react"
import { cn } from "../../lib/utils"
import { Dialog, DialogContent } from "../atoms/dialog"
import { Button } from "../atoms/button"
import { Input } from "../atoms/input"
import { Textarea } from "../atoms/textarea"
import { Trophy, Sparkles, Zap, PartyPopper, Check, Loader2 } from "lucide-react"

export interface RitualAction {
  id: string
  name: string
  description: string
  type: "manual" | "upload" | "text" | "number"
  required?: boolean
  placeholder?: string
  reward?: string
}

export interface RitualActionModalProps {
  /** Whether modal is open */
  open: boolean

  /** Close handler */
  onClose: () => void

  /** Action to complete */
  action: RitualAction

  /** Ritual name (for context) */
  ritualName?: string

  /** Completion handler */
  onComplete: (data: Record<string, any>) => void | Promise<void>

  /** Loading state */
  isLoading?: boolean
}

const RitualActionModal = React.forwardRef<HTMLDivElement, RitualActionModalProps>(
  (
    {
      open,
      onClose,
      action,
      ritualName,
      onComplete,
      isLoading = false,
    },
    ref
  ) => {
    const [formData, setFormData] = React.useState<Record<string, any>>({})
    const [files, setFiles] = React.useState<File[]>([])
    const [isSubmitting, setIsSubmitting] = React.useState(false)
    const [showCelebration, setShowCelebration] = React.useState(false)

    const handleSubmit = async (e: React.FormEvent) => {
      e.preventDefault()

      if (isSubmitting || isLoading) return

      setIsSubmitting(true)

      // Show celebration animation
      setShowCelebration(true)

      // Wait for celebration, then complete
      setTimeout(async () => {
        try {
          await onComplete({
            actionId: action.id,
            ...formData,
            files: files.length > 0 ? files : undefined,
          })
          onClose()
        } catch (error) {
          console.error("Error completing action:", error)
          setShowCelebration(false)
        } finally {
          setIsSubmitting(false)
        }
      }, 1200)
    }

    const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
      const selectedFiles = Array.from(e.target.files || [])
      setFiles(selectedFiles)
    }

    const isValid =
      action.type === "manual" ||
      (action.type === "upload" && files.length > 0) ||
      (action.type === "text" && formData.text?.trim()) ||
      (action.type === "number" && !isNaN(formData.value))

    // Celebration Screen
    if (showCelebration) {
      return (
        <Dialog open={open} onOpenChange={onClose}>
          <DialogContent
            ref={ref}
            className="bg-gradient-to-br from-[#0c0c0c] to-[#1a1a1a] border border-[#FFD700]/30 max-w-md overflow-hidden"
          >
            <div className="relative flex flex-col items-center justify-center py-16 space-y-6">
              {/* Animated celebration background */}
              <div className="absolute inset-0 bg-gradient-to-br from-[#FFD700]/10 via-transparent to-[#FFD700]/5 animate-pulse" />

              {/* Floating particles */}
              <div className="absolute inset-0 overflow-hidden">
                <Sparkles className="absolute top-10 left-10 h-6 w-6 text-[#FFD700] animate-bounce" style={{ animationDelay: "0s" }} />
                <Sparkles className="absolute top-20 right-16 h-4 w-4 text-[#FFD700]/70 animate-bounce" style={{ animationDelay: "0.2s" }} />
                <Sparkles className="absolute bottom-16 left-16 h-5 w-5 text-[#FFD700]/80 animate-bounce" style={{ animationDelay: "0.4s" }} />
                <Zap className="absolute top-16 right-10 h-7 w-7 text-[#FFD700] animate-pulse" style={{ animationDelay: "0.1s" }} />
                <PartyPopper className="absolute bottom-20 right-20 h-6 w-6 text-[#FFD700]/60 animate-bounce" style={{ animationDelay: "0.3s" }} />
              </div>

              {/* Main trophy animation */}
              <div className="relative z-10 animate-bounce">
                <div className="h-24 w-24 rounded-full bg-gradient-to-br from-[#FFD700] to-[#FFA500] flex items-center justify-center shadow-[0_0_40px_rgba(255,215,0,0.4)]">
                  <Trophy className="h-12 w-12 text-black" />
                </div>
              </div>

              <div className="relative z-10 text-center space-y-2">
                <h2 className="text-3xl font-bold text-white">Amazing!</h2>
                <p className="text-lg text-[#FFD700] font-semibold">
                  +1 Step Closer
                </p>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      )
    }

    // Main Action Screen - Redesigned to be engaging, not form-like
    return (
      <Dialog open={open} onOpenChange={onClose}>
        <DialogContent
          ref={ref}
          className="bg-[#0c0c0c] border border-white/8 sm:max-w-md p-0 overflow-hidden"
        >
          {/* Reward Header - Visual emphasis */}
          {action.reward && (
            <div className="relative bg-gradient-to-r from-[#FFD700]/20 to-[#FFD700]/10 border-b border-[#FFD700]/30 px-6 py-4">
              <div className="absolute top-0 right-0 h-full w-32 bg-gradient-to-l from-[#FFD700]/10 to-transparent" />
              <div className="relative flex items-center gap-3">
                <div className="h-12 w-12 rounded-full bg-[#FFD700]/20 border border-[#FFD700]/40 flex items-center justify-center flex-shrink-0">
                  <Trophy className="h-6 w-6 text-[#FFD700]" />
                </div>
                <div>
                  <p className="text-xs text-white/70 uppercase tracking-wide font-medium">Unlock</p>
                  <p className="text-base font-bold text-[#FFD700]">{action.reward}</p>
                </div>
              </div>
            </div>
          )}

          <form onSubmit={handleSubmit} className="px-6 py-6 space-y-6">
            {/* Action Title - Bold and engaging */}
            <div className="space-y-2">
              <h2 className="text-2xl font-bold text-white leading-tight">
                {action.name}
              </h2>
              <p className="text-sm text-white/70 leading-relaxed">
                {action.description}
              </p>
            </div>

            {/* Interactive Input Area - Visual and engaging */}
            <div>
              {action.type === "text" && (
                <Textarea
                  value={formData.text || ""}
                  onChange={(e: React.ChangeEvent) => setFormData({ ...formData, text: (e.target as HTMLInputElement).value })}
                  placeholder={action.placeholder || "Share your thoughts..."}
                  required={action.required}
                  className="bg-white/5 border-white/8 text-white placeholder:text-white/50 text-base min-h-[120px] focus:border-[#FFD700]/50 focus:ring-1 focus:ring-[#FFD700]/50 transition-all"
                />
              )}

              {action.type === "number" && (
                <Input
                  type="number"
                  step="0.1"
                  value={formData.value || ""}
                  onChange={(e: React.ChangeEvent) => setFormData({ ...formData, value: parseFloat((e.target as HTMLInputElement).value) })}
                  placeholder={action.placeholder || "0"}
                  required={action.required}
                  className="bg-white/5 border-white/8 text-white text-3xl font-bold text-center h-20 placeholder:text-white/50 focus:border-[#FFD700]/50 focus:ring-1 focus:ring-[#FFD700]/50 transition-all"
                />
              )}

              {action.type === "upload" && (
                <label className={cn(
                  "relative flex flex-col items-center justify-center h-40 rounded-lg border-2 border-dashed cursor-pointer transition-all",
                  files.length > 0
                    ? "border-[#FFD700] bg-[#FFD700]/10"
                    : "border-white/20 hover:border-[#FFD700]/50 hover:bg-white/5"
                )}>
                  <input
                    type="file"
                    onChange={handleFileChange}
                    accept="image/*,video/*"
                    multiple
                    required={action.required}
                    className="hidden"
                  />
                  {files.length > 0 ? (
                    <div className="flex flex-col items-center space-y-2">
                      <div className="h-12 w-12 rounded-full bg-[#FFD700] flex items-center justify-center">
                        <Check className="h-6 w-6 text-black" />
                      </div>
                      <p className="text-sm font-semibold text-white">
                        {files.length} file{files.length > 1 ? "s" : ""} selected
                      </p>
                      <p className="text-xs text-white/50 px-4 text-center truncate max-w-full">
                        {files.map((f) => f.name).join(", ")}
                      </p>
                    </div>
                  ) : (
                    <div className="flex flex-col items-center space-y-3">
                      <div className="h-12 w-12 rounded-full bg-white/10 flex items-center justify-center">
                        <Zap className="h-6 w-6 text-white/70" />
                      </div>
                      <div className="text-center">
                        <p className="text-sm font-medium text-white">Tap to upload</p>
                        <p className="text-xs text-white/50 mt-1">Show your progress</p>
                      </div>
                    </div>
                  )}
                </label>
              )}

              {action.type === "manual" && (
                <div className="flex flex-col items-center justify-center py-8 space-y-4">
                  <div className="relative">
                    <div className="absolute inset-0 bg-[#FFD700]/20 blur-2xl animate-pulse" />
                    <div className="relative h-20 w-20 rounded-full bg-gradient-to-br from-[#FFD700] to-[#FFA500] flex items-center justify-center shadow-lg">
                      <Sparkles className="h-10 w-10 text-black" />
                    </div>
                  </div>
                  <p className="text-sm text-white/70 text-center">
                    Tap below to mark complete
                  </p>
                </div>
              )}
            </div>

            {/* Progress indicator for ritual */}
            {ritualName && (
              <div className="flex items-center justify-center gap-2 text-xs text-white/50">
                <div className="h-1.5 w-1.5 rounded-full bg-[#FFD700]" />
                <span>{ritualName}</span>
              </div>
            )}

            {/* Action Button - Prominent and engaging */}
            <div className="space-y-3">
              <Button
                type="submit"
                size="lg"
                disabled={!isValid || isSubmitting || isLoading}
                className={cn(
                  "w-full h-14 text-base font-bold transition-all",
                  isValid
                    ? "bg-white text-black hover:bg-[#FFD700] hover:text-black hover:scale-[1.02] active:scale-[0.98]"
                    : "bg-white/10 text-white/30 cursor-not-allowed"
                )}
              >
                {isSubmitting || isLoading ? (
                  <>
                    <Loader2 className="mr-2 h-5 w-5 animate-spin" />
                    Completing...
                  </>
                ) : (
                  <>
                    {action.type === "manual" ? "Mark Complete" : "Submit & Continue"}
                  </>
                )}
              </Button>

              <button
                type="button"
                onClick={onClose}
                disabled={isSubmitting || isLoading}
                className="w-full py-2 text-sm text-white/70 hover:text-white transition-colors"
              >
                Maybe later
              </button>
            </div>
          </form>
        </DialogContent>
      </Dialog>
    )
  }
)

