"use client"

import * as React from "react"
import { Calendar, X, Plus, Trash2 } from "lucide-react"
import { cn } from "../../lib/utils"
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription, DialogFooter } from "../atoms/dialog"
import { Button } from "../atoms/button"
import { Input } from "../atoms/input"
import { Label } from "../atoms/label"
import { Textarea } from "../atoms/textarea"
import { Switch } from "../atoms/switch"
import { Badge } from "../atoms/badge"

// Poll Modal
export interface PollModalProps {
  open: boolean
  onOpenChange: (open: boolean) => void
  onSubmit: (data: PollData) => void
}

export interface PollData {
  question: string
  options: string[]
  settings: {
    anonymous: boolean
    multipleChoice: boolean
    endDate?: string
  }
}

export const PollModal = ({ open, onOpenChange, onSubmit }: PollModalProps) => {
  const [question, setQuestion] = React.useState("")
  const [options, setOptions] = React.useState(["", ""])
  const [anonymous, setAnonymous] = React.useState(false)
  const [multipleChoice, setMultipleChoice] = React.useState(false)
  const [endDate, setEndDate] = React.useState("")

  const addOption = () => {
    if (options.length < 5) {
      setOptions([...options, ""])
    }
  }

  const removeOption = (index: number) => {
    if (options.length > 2) {
      setOptions(options.filter((_, i) => i !== index))
    }
  }

  const updateOption = (index: number, value: string) => {
    const newOptions = [...options]
    newOptions[index] = value
    setOptions(newOptions)
  }

  const handleSubmit = () => {
    onSubmit({
      question,
      options: options.filter(o => o.trim() !== ""),
      settings: {
        anonymous,
        multipleChoice,
        endDate: endDate || undefined,
      },
    })
    // Reset form
    setQuestion("")
    setOptions(["", ""])
    setAnonymous(false)
    setMultipleChoice(false)
    setEndDate("")
    onOpenChange(false)
  }

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-[500px]">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <span className="text-xl">üìä</span>
            Create Poll
          </DialogTitle>
          <DialogDescription>
            Ask a question and get quick feedback from space members
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4 py-4">
          {/* Question */}
          <div className="space-y-2">
            <Label htmlFor="poll-question">Question</Label>
            <Input
              id="poll-question"
              placeholder="What's your question?"
              value={question}
              onChange={(e: React.ChangeEvent) => setQuestion((e.target as HTMLInputElement).value)}
            />
          </div>

          {/* Options */}
          <div className="space-y-2">
            <Label>Options (2-5)</Label>
            {options.map((option, index) => (
              <div key={index} className="flex items-center gap-2">
                <Input
                  placeholder={`Option ${index + 1}`}
                  value={option}
                  onChange={(e: React.ChangeEvent) => updateOption(index, (e.target as HTMLInputElement).value)}
                />
                {options.length > 2 && (
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => removeOption(index)}
                  >
                    <Trash2 className="w-4 h-4" />
                  </Button>
                )}
              </div>
            ))}
            {options.length < 5 && (
              <Button
                variant="outline"
                size="sm"
                onClick={addOption}
                className="w-full"
              >
                <Plus className="w-4 h-4 mr-2" />
                Add Option
              </Button>
            )}
          </div>

          {/* Settings */}
          <div className="space-y-3 border-t border-white/8 pt-3">
            <div className="flex items-center justify-between">
              <Label htmlFor="poll-anonymous">Anonymous voting</Label>
              <Switch
                id="poll-anonymous"
                checked={anonymous}
                onCheckedChange={setAnonymous}
              />
            </div>
            <div className="flex items-center justify-between">
              <Label htmlFor="poll-multiple">Multiple choice</Label>
              <Switch
                id="poll-multiple"
                checked={multipleChoice}
                onCheckedChange={setMultipleChoice}
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="poll-end">End date (optional)</Label>
              <Input
                id="poll-end"
                type="datetime-local"
                value={endDate}
                onChange={(e: React.ChangeEvent) => setEndDate((e.target as HTMLInputElement).value)}
              />
            </div>
          </div>
        </div>

        <DialogFooter>
          <Button variant="outline" onClick={() => onOpenChange(false)}>
            Cancel
          </Button>
          <Button
            onClick={handleSubmit}
            disabled={!question || options.filter(o => o.trim() !== "").length < 2}
          >
            Create Poll
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  )
}

