"use client"

import * as React from "react"
import type { User, Space, Connection } from "@hive/core"

/**
 * Campus Context System
 * Provides unified campus state and context across all slices
 */

// Campus location and proximity data
export interface CampusLocation {
  id: string
  name: string
  type: 'building' | 'outdoor' | 'dorm' | 'academic' | 'recreational'
  coordinates?: {
    lat: number
    lng: number
  }
  address?: string
  campusZone: 'north' | 'south' | 'center' | 'off-campus'
}

export interface CampusProximityData {
  currentBuilding?: CampusLocation
  nearbyBuildings: CampusLocation[]
  walkingDistance: Record<string, number> // locationId -> minutes
  frequentLocations: CampusLocation[]
}

// Campus community and social data
export interface CampusActivityStream {
  recentPosts: number
  activeSpaces: number
  onlineUsers: number
  trending: {
    hashtags: string[]
    spaces: string[]
    events: string[]
  }
}

export interface UpcomingCampusEvent {
  id: string
  name: string
  startTime: string
  location: CampusLocation
  spaceId?: string
  attendeeCount: number
  type: 'academic' | 'social' | 'athletic' | 'cultural'
}

// Tool context and suggestions
export interface ToolSuggestion {
  toolId: string
  name: string
  description: string
  relevanceScore: number
  context: 'feed' | 'spaces' | 'profile' | 'general'
  usageHint: string
}

export interface Tool {
  id: string
  name: string
  description: string
  createdBy: string
  spaceId?: string
  usageCount: number
  category: 'productivity' | 'social' | 'academic' | 'creative'
}

// Main campus context interface
export interface CampusContextValue {
  // Campus identification
  campus: {
    id: 'ub-buffalo'
    name: 'University at Buffalo'
    timezone: 'America/New_York'
    academicYear: '2024-2025'
    currentSemester: 'fall'
  }

  // Location and proximity context
  location: {
    current: CampusLocation | null
    proximity: CampusProximityData
    buildings: CampusLocation[]
  }

  // Community and social context
  community: {
    userSpaces: Space[]
    suggestedSpaces: Space[]
    connections: Connection[]
    campusActivity: CampusActivityStream
  }

  // Temporal context
  temporal: {
    timeOfDay: 'morning' | 'afternoon' | 'evening' | 'night'
    academicPeriod: 'regular' | 'finals' | 'break' | 'rush'
    events: UpcomingCampusEvent[]
  }

  // Tool context
  tools: {
    contextualSuggestions: ToolSuggestion[]
    recentlyUsed: Tool[]
    spaceTools: Record<string, Tool[]>
  }

  // Context actions
  actions: {
    updateLocation: (location: CampusLocation | null) => void
    refreshCommunityData: () => Promise<void>
    updateToolContext: (context: 'feed' | 'spaces' | 'profile') => void
    requestLocationPermission: () => Promise<boolean>
  }
}

// Campus context React context
const CampusContext = React.createContext<CampusContextValue | undefined>(undefined)

// Campus context provider props
export interface CampusProviderProps {
  children: React.ReactNode
  user?: User
  initialLocation?: CampusLocation
}

/**
 * Campus Context Provider
 * Root provider for campus-wide context
 */
export function CampusProvider({
  children,
  user,
  initialLocation
}: CampusProviderProps) {
  // Location state
  const [currentLocation, setCurrentLocation] = React.useState<CampusLocation | null>(initialLocation || null)
  const [proximityData, setProximityData] = React.useState<CampusProximityData>({
    nearbyBuildings: [],
    walkingDistance: {},
    frequentLocations: []
  })

  // Community state
  const [userSpaces, setUserSpaces] = React.useState<Space[]>([])
  const [suggestedSpaces, setSuggestedSpaces] = React.useState<Space[]>([])
  const [connections, setConnections] = React.useState<Connection[]>([])
  const [campusActivity, setCampusActivity] = React.useState<CampusActivityStream>({
    recentPosts: 0,
    activeSpaces: 0,
    onlineUsers: 0,
    trending: { hashtags: [], spaces: [], events: [] }
  })

  // Temporal state
  const [timeOfDay, setTimeOfDay] = React.useState<'morning' | 'afternoon' | 'evening' | 'night'>('morning')
  const [academicPeriod, setAcademicPeriod] = React.useState<'regular' | 'finals' | 'break' | 'rush'>('regular')
  const [upcomingEvents, setUpcomingEvents] = React.useState<UpcomingCampusEvent[]>([])

  // Tool state
  const [contextualSuggestions, setContextualSuggestions] = React.useState<ToolSuggestion[]>([])
  const [recentlyUsedTools, setRecentlyUsedTools] = React.useState<Tool[]>([])
  const [spaceTools, setSpaceTools] = React.useState<Record<string, Tool[]>>({})

  // Campus buildings data (could be loaded from API)
  const campusBuildings: CampusLocation[] = React.useMemo(() => [
    {
      id: 'knox-hall',
      name: 'Knox Hall',
      type: 'dorm',
      campusZone: 'north',
      coordinates: { lat: 43.0015, lng: -78.7874 }
    },
    {
      id: 'student-union',
      name: 'Student Union',
      type: 'recreational',
      campusZone: 'center',
      coordinates: { lat: 43.0021, lng: -78.7890 }
    },
    {
      id: 'capen-hall',
      name: 'Capen Hall',
      type: 'academic',
      campusZone: 'center',
      coordinates: { lat: 43.0018, lng: -78.7881 }
    },
    {
      id: 'davis-hall',
      name: 'Davis Hall',
      type: 'academic',
      campusZone: 'north',
      coordinates: { lat: 43.0025, lng: -78.7885 }
    }
  ], [])

  // Time of day calculation
  React.useEffect(() => {
    const updateTimeOfDay = () => {
      const hour = new Date().getHours()
      if (hour >= 6 && hour < 12) setTimeOfDay('morning')
      else if (hour >= 12 && hour < 17) setTimeOfDay('afternoon')
      else if (hour >= 17 && hour < 22) setTimeOfDay('evening')
      else setTimeOfDay('night')
    }

    updateTimeOfDay()
    const interval = setInterval(updateTimeOfDay, 60000) // Update every minute

    return () => clearInterval(interval)
  }, [])

  // Academic period calculation (simplified)
  React.useEffect(() => {
    const today = new Date()
    const month = today.getMonth()

    // Simplified academic calendar
    if (month >= 8 && month <= 11) {
      setAcademicPeriod('regular') // Fall semester
    } else if (month >= 0 && month <= 4) {
      setAcademicPeriod('regular') // Spring semester
    } else {
      setAcademicPeriod('break') // Summer break
    }
  }, [])

  // Location actions
  const updateLocation = React.useCallback((location: CampusLocation | null) => {
    setCurrentLocation(location)

    if (location) {
      // Update proximity data based on new location
      const nearby = campusBuildings.filter(building =>
        building.id !== location.id &&
        building.campusZone === location.campusZone
      )

      setProximityData(prev => ({
        ...prev,
        currentBuilding: location,
        nearbyBuildings: nearby.slice(0, 5) // Limit to 5 nearby
      }))
    }
  }, [campusBuildings])

  // Refresh community data
  const refreshCommunityData = React.useCallback(async () => {
    if (!user) return

    try {
      // This would integrate with actual API calls
      // For now, using mock data structure

      // Simulate loading user spaces and community data
      setCampusActivity({
        recentPosts: Math.floor(Math.random() * 100) + 20,
        activeSpaces: Math.floor(Math.random() * 50) + 10,
        onlineUsers: Math.floor(Math.random() * 500) + 100,
        trending: {
          hashtags: ['#finals', '#ubpride', '#studygroup'],
          spaces: ['Engineering Study Hub', 'Dorm Life', 'Campus Events'],
          events: ['Fall Festival', 'Career Fair', 'Basketball Game']
        }
      })
    } catch (error) {
      console.error('Failed to refresh community data:', error)
    }
  }, [user])

  // Update tool context based on current slice
  const updateToolContext = React.useCallback((context: 'feed' | 'spaces' | 'profile') => {
    // Generate contextual tool suggestions based on current slice
    const suggestions: ToolSuggestion[] = []

    switch (context) {
      case 'feed':
        suggestions.push(
          {
            toolId: 'quick-poll',
            name: 'Quick Poll',
            description: 'Create instant polls for your feed',
            relevanceScore: 0.9,
            context: 'feed',
            usageHint: 'Get quick opinions from your campus community'
          },
          {
            toolId: 'event-creator',
            name: 'Event Creator',
            description: 'Announce campus events',
            relevanceScore: 0.8,
            context: 'feed',
            usageHint: 'Share events happening on campus'
          }
        )
        break

      case 'spaces':
        suggestions.push(
          {
            toolId: 'space-manager',
            name: 'Space Manager',
            description: 'Manage your space community',
            relevanceScore: 0.95,
            context: 'spaces',
            usageHint: 'Organize your space activities and members'
          },
          {
            toolId: 'resource-library',
            name: 'Resource Library',
            description: 'Share resources with space members',
            relevanceScore: 0.85,
            context: 'spaces',
            usageHint: 'Build a shared knowledge base'
          }
        )
        break

      case 'profile':
        suggestions.push(
          {
            toolId: 'connection-finder',
            name: 'Connection Finder',
            description: 'Find classmates and dormmates',
            relevanceScore: 0.9,
            context: 'profile',
            usageHint: 'Expand your campus network'
          },
          {
            toolId: 'schedule-sync',
            name: 'Schedule Sync',
            description: 'Coordinate with friends',
            relevanceScore: 0.8,
            context: 'profile',
            usageHint: 'Share availability with connections'
          }
        )
        break
    }

    setContextualSuggestions(suggestions)
  }, [])

  // Request location permission
  const requestLocationPermission = React.useCallback(async (): Promise<boolean> => {
    if (!navigator.geolocation) {
      return false
    }

    try {
      const permission = await navigator.permissions.query({ name: 'geolocation' })

      if (permission.state === 'granted') {
        return true
      } else if (permission.state === 'prompt') {
        // Request permission
        return new Promise((resolve) => {
          navigator.geolocation.getCurrentPosition(
            () => resolve(true),
            () => resolve(false)
          )
        })
      }

      return false
    } catch {
      return false
    }
  }, [])

  // Initialize context on mount
  React.useEffect(() => {
    if (user) {
      refreshCommunityData()
      updateToolContext('feed') // Default context
    }
  }, [user, refreshCommunityData, updateToolContext])

  const contextValue: CampusContextValue = React.useMemo(() => ({
    campus: {
      id: 'ub-buffalo',
      name: 'University at Buffalo',
      timezone: 'America/New_York',
      academicYear: '2024-2025',
      currentSemester: 'fall'
    },

    location: {
      current: currentLocation,
      proximity: proximityData,
      buildings: campusBuildings
    },

    community: {
      userSpaces,
      suggestedSpaces,
      connections,
      campusActivity
    },

    temporal: {
      timeOfDay,
      academicPeriod,
      events: upcomingEvents
    },

    tools: {
      contextualSuggestions,
      recentlyUsed: recentlyUsedTools,
      spaceTools
    },

    actions: {
      updateLocation,
      refreshCommunityData,
      updateToolContext,
      requestLocationPermission
    }
  }), [
    currentLocation,
    proximityData,
    campusBuildings,
    userSpaces,
    suggestedSpaces,
    connections,
    campusActivity,
    timeOfDay,
    academicPeriod,
    upcomingEvents,
    contextualSuggestions,
    recentlyUsedTools,
    spaceTools,
    updateLocation,
    refreshCommunityData,
    updateToolContext,
    requestLocationPermission
  ])

  return (
    <CampusContext.Provider value={contextValue}>
      {children}
    </CampusContext.Provider>
  )
}

/**
 * Hook to use campus context
 */
export function useCampusContext(): CampusContextValue {
  const context = React.useContext(CampusContext)

  if (context === undefined) {
    throw new Error('useCampusContext must be used within a CampusProvider')
  }

  return context
}

/**
 * Hook for location-specific context
 */
export function useCampusLocation() {
  const { location, actions } = useCampusContext()

  return {
    currentLocation: location.current,
    nearbyBuildings: location.proximity.nearbyBuildings,
    campusBuildings: location.buildings,
    updateLocation: actions.updateLocation,
    requestPermission: actions.requestLocationPermission
  }
}

/**
 * Hook for community-specific context
 */
export function useCampusCommunity() {
  const { community, actions } = useCampusContext()

  return {
    userSpaces: community.userSpaces,
    suggestedSpaces: community.suggestedSpaces,
    connections: community.connections,
    campusActivity: community.campusActivity,
    refreshData: actions.refreshCommunityData
  }
}

/**
 * Hook for temporal context
 */
export function useCampusTemporal() {
  const { temporal } = useCampusContext()

  return {
    timeOfDay: temporal.timeOfDay,
    academicPeriod: temporal.academicPeriod,
    upcomingEvents: temporal.events,
    isStudyTime: temporal.timeOfDay === 'evening' || temporal.timeOfDay === 'afternoon',
    isFinalsTime: temporal.academicPeriod === 'finals',
    isSocialTime: temporal.timeOfDay === 'evening' && temporal.academicPeriod !== 'finals'
  }
}

/**
 * Hook for tool context
 */
export function useCampusTools() {
  const { tools, actions } = useCampusContext()

  return {
    contextualSuggestions: tools.contextualSuggestions,
    recentlyUsed: tools.recentlyUsed,
    spaceTools: tools.spaceTools,
    updateContext: actions.updateToolContext
  }
}

/**
 * HiveLab Builder Context
 *
 * React Context + useReducer for managing complete builder state.
 * Handles elements, connections, viewport, selection, and history.
 */

'use client';

import React, { createContext, useContext, useReducer, useCallback, type ReactNode } from 'react';
import type {
  HiveLabState,
  HiveLabAction,
  Tool,
  Page,
  Element,
  Connection,
  Viewport,
  CanvasMode,
  PanelState,
} from '../types/hivelab.types';
import { generatePageId, generateElementId, generateConnectionId } from '../lib/hivelab-utils';

// ============================================================================
// Initial State Factory
// ============================================================================

function createDefaultTool(): Tool {
  const defaultPageId = generatePageId();

  return {
    id: generateElementId('tool'),
    name: 'Untitled Tool',
    description: '',
    icon: 'üõ†Ô∏è',
    version: '1.0.0',
    createdBy: '',
    spaceId: undefined,
    pages: [
      {
        id: defaultPageId,
        name: 'Page 1',
        description: 'Main page',
        x: 0,
        y: 0,
        width: 1200,
        height: 800,
        type: 'default',
        elements: [],
        connections: [],
      },
    ],
    startPage: defaultPageId,
    globalVariables: {},
    globalStyles: {},
    status: 'draft',
    visibility: 'private',
    deployedTo: [],
    uses: 0,
    forks: 0,
    rating: 0,
    permissions: {
      canFork: true,
      canEdit: [],
      requiresApproval: false,
    },
    createdAt: new Date(),
    updatedAt: new Date(),
  };
}

function createInitialState(): HiveLabState {
  const tool = createDefaultTool();

  return {
    tool,
    currentPage: tool.startPage,
    selectedElements: [],
    selectedConnection: null,
    viewport: {
      x: 0,
      y: 0,
      zoom: 1,
    },
    canvasMode: 'select',
    connectionDraft: {
      isConnecting: false,
    },
    selectionBox: {
      x: 0,
      y: 0,
      width: 0,
      height: 0,
      active: false,
    },
    panels: {
      library: {
        isOpen: true,
        width: 320,
        isDocked: true,
      },
      properties: {
        isOpen: true,
        width: 320,
        isDocked: true,
      },
      layers: {
        isOpen: false,
        width: 280,
        isDocked: true,
      },
      versions: {
        isOpen: false,
        width: 320,
        isDocked: true,
      },
    },
    history: {
      past: [],
      future: [],
    },
    isDraggingElement: false,
    hoveredPort: null,
    showGrid: true,
    snapToGrid: true,
  };
}

// ============================================================================
// Reducer
// ============================================================================

function hiveLabReducer(state: HiveLabState, action: HiveLabAction): HiveLabState {
  switch (action.type) {
    // Tool actions
    case 'LOAD_TOOL':
      return {
        ...state,
        tool: action.tool,
        currentPage: action.tool.startPage,
        selectedElements: [],
        selectedConnection: null,
        history: { past: [], future: [] },
      };

    case 'UPDATE_TOOL':
      return {
        ...state,
        tool: {
          ...state.tool,
          ...action.updates,
          updatedAt: new Date(),
        },
      };

    // Page actions
    case 'ADD_PAGE':
      return {
        ...state,
        tool: {
          ...state.tool,
          pages: [...state.tool.pages, action.page],
          updatedAt: new Date(),
        },
      };

    case 'DELETE_PAGE': {
      const pages = state.tool.pages.filter(p => p.id !== action.pageId);
      const newCurrentPage = action.pageId === state.currentPage
        ? pages[0]?.id || ''
        : state.currentPage;

      return {
        ...state,
        tool: {
          ...state.tool,
          pages,
          startPage: state.tool.startPage === action.pageId ? pages[0]?.id : state.tool.startPage,
          updatedAt: new Date(),
        },
        currentPage: newCurrentPage,
      };
    }

    case 'UPDATE_PAGE':
      return {
        ...state,
        tool: {
          ...state.tool,
          pages: state.tool.pages.map(page =>
            page.id === action.pageId ? { ...page, ...action.updates } : page
          ),
          updatedAt: new Date(),
        },
      };

    case 'SET_CURRENT_PAGE':
      return {
        ...state,
        currentPage: action.pageId,
        selectedElements: [],
        selectedConnection: null,
      };

    case 'MOVE_PAGE':
      return {
        ...state,
        tool: {
          ...state.tool,
          pages: state.tool.pages.map(page =>
            page.id === action.pageId
              ? { ...page, x: action.x, y: action.y }
              : page
          ),
          updatedAt: new Date(),
        },
      };

    // Element actions
    case 'ADD_ELEMENT':
      return {
        ...state,
        tool: {
          ...state.tool,
          pages: state.tool.pages.map(page =>
            page.id === action.pageId
              ? { ...page, elements: [...page.elements, action.element] }
              : page
          ),
          updatedAt: new Date(),
        },
      };

    case 'REMOVE_ELEMENT': {
      return {
        ...state,
        tool: {
          ...state.tool,
          pages: state.tool.pages.map(page =>
            page.id === action.pageId
              ? {
                  ...page,
                  elements: page.elements.filter(el => el.id !== action.elementId),
                  connections: page.connections.filter(
                    conn =>
                      conn.sourceElementId !== action.elementId &&
                      conn.targetElementId !== action.elementId
                  ),
                }
              : page
          ),
          updatedAt: new Date(),
        },
        selectedElements: state.selectedElements.filter(id => id !== action.elementId),
      };
    }

    case 'UPDATE_ELEMENT':
      return {
        ...state,
        tool: {
          ...state.tool,
          pages: state.tool.pages.map(page =>
            page.id === action.pageId
              ? {
                  ...page,
                  elements: page.elements.map(el =>
                    el.id === action.elementId ? { ...el, ...action.updates } : el
                  ),
                }
              : page
          ),
          updatedAt: new Date(),
        },
      };

    case 'MOVE_ELEMENT':
      return {
        ...state,
        tool: {
          ...state.tool,
          pages: state.tool.pages.map(page =>
            page.id === action.pageId
              ? {
                  ...page,
                  elements: page.elements.map(el =>
                    el.id === action.elementId
                      ? { ...el, x: action.x, y: action.y }
                      : el
                  ),
                }
              : page
          ),
          updatedAt: new Date(),
        },
      };

    case 'DUPLICATE_ELEMENT': {
      const page = state.tool.pages.find(p => p.id === action.pageId);
      const element = page?.elements.find(el => el.id === action.elementId);

      if (!page || !element) return state;

      const newElement: Element = {
        ...element,
        id: generateElementId(element.type),
        x: element.x + 20,
        y: element.y + 20,
        inputs: element.inputs.map(port => ({
          ...port,
          id: `${generateElementId(element.type)}-${port.name}`,
        })),
        outputs: element.outputs.map(port => ({
          ...port,
          id: `${generateElementId(element.type)}-${port.name}`,
        })),
      };

      return {
        ...state,
        tool: {
          ...state.tool,
          pages: state.tool.pages.map(p =>
            p.id === action.pageId
              ? { ...p, elements: [...p.elements, newElement] }
              : p
          ),
          updatedAt: new Date(),
        },
      };
    }

    // Connection actions
    case 'CREATE_CONNECTION':
      return {
        ...state,
        tool: {
          ...state.tool,
          pages: state.tool.pages.map(page =>
            page.id === action.pageId
              ? { ...page, connections: [...page.connections, action.connection] }
              : page
          ),
          updatedAt: new Date(),
        },
      };

    case 'DELETE_CONNECTION':
      return {
        ...state,
        tool: {
          ...state.tool,
          pages: state.tool.pages.map(page =>
            page.id === action.pageId
              ? {
                  ...page,
                  connections: page.connections.filter(c => c.id !== action.connectionId),
                }
              : page
          ),
          updatedAt: new Date(),
        },
        selectedConnection: state.selectedConnection === action.connectionId
          ? null
          : state.selectedConnection,
      };

    case 'UPDATE_CONNECTION':
      return {
        ...state,
        tool: {
          ...state.tool,
          pages: state.tool.pages.map(page =>
            page.id === action.pageId
              ? {
                  ...page,
                  connections: page.connections.map(conn =>
                    conn.id === action.connectionId
                      ? { ...conn, ...action.updates }
                      : conn
                  ),
                }
              : page
          ),
          updatedAt: new Date(),
        },
      };

    // Selection actions
    case 'SELECT_ELEMENTS':
      return {
        ...state,
        selectedElements: action.elementIds,
        selectedConnection: null,
      };

    case 'SELECT_CONNECTION':
      return {
        ...state,
        selectedConnection: action.connectionId,
        selectedElements: [],
      };

    case 'CLEAR_SELECTION':
      return {
        ...state,
        selectedElements: [],
        selectedConnection: null,
      };

    // Viewport actions
    case 'UPDATE_VIEWPORT':
      return {
        ...state,
        viewport: {
          ...state.viewport,
          ...action.viewport,
        },
      };

    case 'ZOOM_TO_FIT': {
      // Calculate bounding box for all elements on current page
      const currentPage = state.tool.pages.find(p => p.id === state.currentPage);
      if (!currentPage || currentPage.elements.length === 0) return state;

      // This would use calculateViewportToFit from utils
      // Simplified here for now
      return state;
    }

    case 'ZOOM_TO_PAGE': {
      const page = state.tool.pages.find(p => p.id === action.pageId);
      if (!page) return state;

      // This would use calculateViewportForPage from utils
      return {
        ...state,
        currentPage: action.pageId,
      };
    }

    case 'ZOOM_TO_SELECTION': {
      if (state.selectedElements.length === 0) return state;

      // This would calculate viewport to fit selected elements
      return state;
    }

    // Canvas mode actions
    case 'SET_CANVAS_MODE':
      return {
        ...state,
        canvasMode: action.mode,
      };

    // Connection draft actions
    case 'START_CONNECTION':
      return {
        ...state,
        connectionDraft: {
          isConnecting: true,
          sourceElementId: action.sourceElementId,
          sourcePortId: action.sourcePortId,
          sourceType: action.sourceType,
        },
        canvasMode: 'connect',
      };

    case 'UPDATE_CONNECTION_MOUSE':
      return {
        ...state,
        connectionDraft: {
          ...state.connectionDraft,
          mousePosition: action.position,
        },
      };

    case 'END_CONNECTION':
      return {
        ...state,
        connectionDraft: {
          isConnecting: false,
        },
        canvasMode: 'select',
      };

    // Selection box actions
    case 'START_SELECTION_BOX':
      return {
        ...state,
        selectionBox: {
          x: action.x,
          y: action.y,
          width: 0,
          height: 0,
          active: true,
        },
        canvasMode: 'multiselect',
      };

    case 'UPDATE_SELECTION_BOX':
      return {
        ...state,
        selectionBox: {
          x: action.x,
          y: action.y,
          width: action.width,
          height: action.height,
          active: true,
        },
      };

    case 'END_SELECTION_BOX':
      return {
        ...state,
        selectionBox: {
          ...state.selectionBox,
          active: false,
        },
        canvasMode: 'select',
      };

    // Panel actions
    case 'TOGGLE_PANEL':
      return {
        ...state,
        panels: {
          ...state.panels,
          [action.panel]: {
            ...state.panels[action.panel],
            isOpen: !state.panels[action.panel].isOpen,
          },
        },
      };

    case 'UPDATE_PANEL':
      return {
        ...state,
        panels: {
          ...state.panels,
          [action.panel]: {
            ...state.panels[action.panel],
            ...action.state,
          },
        },
      };

    // History actions
    case 'UNDO': {
      const { past, future } = state.history;
      if (past.length === 0) return state;

      const previous = past[past.length - 1];
      const newPast = past.slice(0, past.length - 1);

      return {
        ...state,
        tool: previous,
        history: {
          past: newPast,
          future: [state.tool, ...future],
        },
      };
    }

    case 'REDO': {
      const { past, future } = state.history;
      if (future.length === 0) return state;

      const next = future[0];
      const newFuture = future.slice(1);

      return {
        ...state,
        tool: next,
        history: {
          past: [...past, state.tool],
          future: newFuture,
        },
      };
    }

    case 'PUSH_HISTORY':
      return {
        ...state,
        history: {
          past: [...state.history.past, state.tool],
          future: [],
        },
      };

    // UI actions
    case 'SET_DRAGGING_ELEMENT':
      return {
        ...state,
        isDraggingElement: action.isDragging,
      };

    case 'SET_HOVERED_PORT':
      return {
        ...state,
        hoveredPort: action.portId,
      };

    case 'TOGGLE_GRID':
      return {
        ...state,
        showGrid: !state.showGrid,
      };

    case 'TOGGLE_SNAP_TO_GRID':
      return {
        ...state,
        snapToGrid: !state.snapToGrid,
      };

    default:
      return state;
  }
}

