/**
 * Canvas Viewport Hook
 *
 * Handles pan, zoom, and viewport transformations for the HiveLab canvas.
 */

'use client';

import { useCallback, useEffect, useRef } from 'react';
import { useHiveLab, useHiveLabActions } from '../contexts/hivelab-context';
import {
  clampZoom,
  calculateZoomDelta,
  screenToCanvas,
  canvasToScreen,
  type Position,
} from '../lib/hivelab-utils';

interface UseCanvasViewportOptions {
  containerRef: React.RefObject<HTMLElement>;
  minZoom?: number;
  maxZoom?: number;
  zoomSpeed?: number;
}

export function useCanvasViewport({
  containerRef,
  minZoom = 0.1,
  maxZoom = 4,
  zoomSpeed = 0.001,
}: UseCanvasViewportOptions) {
  const { state } = useHiveLab();
  const { updateViewport } = useHiveLabActions();

  const isPanning = useRef(false);
  const lastMousePos = useRef<Position>({ x: 0, y: 0 });

  // ============================================================================
  // Pan Handlers
  // ============================================================================

  const startPan = useCallback((clientX: number, clientY: number) => {
    isPanning.current = true;
    lastMousePos.current = { x: clientX, y: clientY };
  }, []);

  const updatePan = useCallback((clientX: number, clientY: number) => {
    if (!isPanning.current) return;

    const dx = clientX - lastMousePos.current.x;
    const dy = clientY - lastMousePos.current.y;

    updateViewport({
      x: state.viewport.x + dx,
      y: state.viewport.y + dy,
    });

    lastMousePos.current = { x: clientX, y: clientY };
  }, [state.viewport, updateViewport]);

  const endPan = useCallback(() => {
    isPanning.current = false;
  }, []);

  // ============================================================================
  // Zoom Handlers
  // ============================================================================

  const zoomAt = useCallback(
    (clientX: number, clientY: number, deltaZoom: number) => {
      if (!containerRef.current) return;

      const container = containerRef.current;
      const rect = container.getBoundingClientRect();

      // Get mouse position relative to container
      const mouseX = clientX - rect.left;
      const mouseY = clientY - rect.top;

      // Calculate new zoom
      const currentZoom = state.viewport.zoom;
      const newZoom = clampZoom(currentZoom + deltaZoom);

      if (newZoom === currentZoom) return;

      // Calculate zoom center point in canvas coordinates
      const canvasPointBefore = screenToCanvas(
        { x: mouseX, y: mouseY },
        state.viewport
      );

      // Apply new zoom
      const newViewport = { ...state.viewport, zoom: newZoom };

      // Calculate where the point is now with new zoom
      const canvasPointAfter = screenToCanvas(
        { x: mouseX, y: mouseY },
        newViewport
      );

      // Adjust viewport position to keep point under mouse
      const dx = (canvasPointAfter.x - canvasPointBefore.x) * newZoom;
      const dy = (canvasPointAfter.y - canvasPointBefore.y) * newZoom;

      updateViewport({
        x: newViewport.x + dx,
        y: newViewport.y + dy,
        zoom: newZoom,
      });
    },
    [containerRef, state.viewport, updateViewport]
  );

  const zoomIn = useCallback(() => {
    if (!containerRef.current) return;

    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.width / 2;
    const centerY = rect.height / 2;

    zoomAt(rect.left + centerX, rect.top + centerY, 0.1);
  }, [containerRef, zoomAt]);

  const zoomOut = useCallback(() => {
    if (!containerRef.current) return;

    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.width / 2;
    const centerY = rect.height / 2;

    zoomAt(rect.left + centerX, rect.top + centerY, -0.1);
  }, [containerRef, zoomAt]);

  const resetZoom = useCallback(() => {
    updateViewport({ zoom: 1 });
  }, [updateViewport]);

  // ============================================================================
  // Event Listeners
  // ============================================================================

  useEffect(() => {
    const container = containerRef.current;
    if (!container) return;

    // Mouse wheel zoom
    const handleWheel = (e: WheelEvent) => {
      e.preventDefault();

      const delta = calculateZoomDelta(e.deltaY);
      zoomAt(e.clientX, e.clientY, -delta * zoomSpeed * 100);
    };

    // Middle mouse button or space + drag for panning
    const handleMouseDown = (e: MouseEvent) => {
      // Middle mouse button
      if (e.button === 1) {
        e.preventDefault();
        startPan(e.clientX, e.clientY);
      }
      // Space + left mouse (handled by canvas mode in parent)
      else if (e.button === 0 && state.canvasMode === 'pan') {
        e.preventDefault();
        startPan(e.clientX, e.clientY);
      }
    };

    const handleMouseMove = (e: MouseEvent) => {
      if (isPanning.current) {
        updatePan(e.clientX, e.clientY);
      }
    };

    const handleMouseUp = () => {
      endPan();
    };

    // Touch events for mobile
    const handleTouchStart = (e: TouchEvent) => {
      if (e.touches.length === 2) {
        e.preventDefault();
        // Start pinch zoom (would need additional implementation)
      } else if (e.touches.length === 1) {
        const touch = e.touches[0];
        startPan(touch.clientX, touch.clientY);
      }
    };

    const handleTouchMove = (e: TouchEvent) => {
      if (e.touches.length === 1) {
        const touch = e.touches[0];
        updatePan(touch.clientX, touch.clientY);
      }
    };

    const handleTouchEnd = () => {
      endPan();
    };

    container.addEventListener('wheel', handleWheel, { passive: false });
    container.addEventListener('mousedown', handleMouseDown);
    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('mouseup', handleMouseUp);
    container.addEventListener('touchstart', handleTouchStart, { passive: false });
    container.addEventListener('touchmove', handleTouchMove, { passive: false });
    container.addEventListener('touchend', handleTouchEnd);

    return () => {
      container.removeEventListener('wheel', handleWheel);
      container.removeEventListener('mousedown', handleMouseDown);
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mouseup', handleMouseUp);
      container.removeEventListener('touchstart', handleTouchStart);
      container.removeEventListener('touchmove', handleTouchMove);
      container.removeEventListener('touchend', handleTouchEnd);
    };
  }, [
    containerRef,
    state.canvasMode,
    zoomAt,
    zoomSpeed,
    startPan,
    updatePan,
    endPan,
  ]);

  // ============================================================================
  // Keyboard Shortcuts
  // ============================================================================

  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      // Zoom shortcuts
      if ((e.metaKey || e.ctrlKey) && e.key === '0') {
        e.preventDefault();
        resetZoom();
      } else if ((e.metaKey || e.ctrlKey) && e.key === '=') {
        e.preventDefault();
        zoomIn();
      } else if ((e.metaKey || e.ctrlKey) && e.key === '-') {
        e.preventDefault();
        zoomOut();
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [zoomIn, zoomOut, resetZoom]);

  // ============================================================================
  // Return API
  // ============================================================================

  return {
    viewport: state.viewport,
    zoomIn,
    zoomOut,
    resetZoom,
    zoomAt,
    screenToCanvas: useCallback(
      (pos: Position) => screenToCanvas(pos, state.viewport),
      [state.viewport]
    ),
    canvasToScreen: useCallback(
      (pos: Position) => canvasToScreen(pos, state.viewport),
      [state.viewport]
    ),
  };
}

/**
 * Connection Creation Hook
 *
 * Handles wire dragging logic for creating connections between element ports.
 */

'use client';

import { useCallback, useEffect } from 'react';
import { useHiveLab, useHiveLabActions } from '../contexts/hivelab-context';
import {
  isCompatibleTypes,
  generateConnectionId,
  generateDraftConnectionPath,
  generateConnectionPath,
  getPortById,
  type Position,
  type DataType,
  type Connection,
  type Element,
  type Port,
} from '../lib/hivelab-utils';
import { DATA_TYPE_COLORS } from '../types/hivelab.types';

interface UseConnectionCreationOptions {
  containerRef: React.RefObject<HTMLElement>;
  onConnectionCreated?: (connection: Connection) => void;
  onConnectionFailed?: (reason: string) => void;
}

export function useConnectionCreation({
  containerRef,
  onConnectionCreated,
  onConnectionFailed,
}: UseConnectionCreationOptions) {
  const { state } = useHiveLab();
  const {
    startConnection,
    updateConnectionMouse,
    endConnection,
    createConnection,
  } = useHiveLabActions();

  // ============================================================================
  // Current Page Data
  // ============================================================================

  const currentPage = state.tool.pages.find(p => p.id === state.currentPage);
  const elements = currentPage?.elements || [];
  const connections = currentPage?.connections || [];

  // ============================================================================
  // Connection Validation
  // ============================================================================

  const canConnect = useCallback(
    (sourceElement: Element, sourcePort: Port, targetElement: Element, targetPort: Port): boolean => {
      // Can't connect to self
      if (sourceElement.id === targetElement.id) {
        return false;
      }

      // Source must be output, target must be input
      if (sourcePort.side !== 'output' || targetPort.side !== 'input') {
        return false;
      }

      // Check type compatibility
      if (!isCompatibleTypes(sourcePort.type, targetPort.type)) {
        return false;
      }

      // Check if connection already exists
      const connectionExists = connections.some(
        conn =>
          conn.sourceElementId === sourceElement.id &&
          conn.sourcePortId === sourcePort.id &&
          conn.targetElementId === targetElement.id &&
          conn.targetPortId === targetPort.id
      );

      if (connectionExists) {
        return false;
      }

      // Check if target port already has a connection (inputs can only have one)
      const targetHasConnection = connections.some(
        conn =>
          conn.targetElementId === targetElement.id &&
          conn.targetPortId === targetPort.id
      );

      if (targetHasConnection) {
        return false;
      }

      return true;
    },
    [connections]
  );

  // ============================================================================
  // Find Port at Position
  // ============================================================================

  const findPortAtPosition = useCallback(
    (pos: Position): { element: Element; port: Port } | null => {
      for (const element of elements) {
        // Check all ports (inputs and outputs)
        for (const port of [...element.inputs, ...element.outputs]) {
          const portPos = getPortPosition(element, port);

          // Port hit detection radius
          const hitRadius = 12;

          const distance = Math.sqrt(
            Math.pow(pos.x - portPos.x, 2) + Math.pow(pos.y - portPos.y, 2)
          );

          if (distance <= hitRadius) {
            return { element, port };
          }
        }
      }

      return null;
    },
    [elements]
  );

  // ============================================================================
  // Start Connection
  // ============================================================================

  const handlePortMouseDown = useCallback(
    (element: Element, port: Port, e: React.MouseEvent) => {
      e.stopPropagation();

      // Only start connections from output ports
      if (port.side !== 'output') {
        return;
      }

      startConnection(element.id, port.id, port.type);
    },
    [startConnection]
  );

  // ============================================================================
  // Update Draft Connection
  // ============================================================================

  useEffect(() => {
    if (!state.connectionDraft.isConnecting || !containerRef.current) return;

    const container = containerRef.current;

    const handleMouseMove = (e: MouseEvent) => {
      const rect = container.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;

      // Convert to canvas coordinates
      const canvasPos = screenToCanvas({ x, y }, state.viewport);

      updateConnectionMouse(canvasPos);
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, [
    state.connectionDraft.isConnecting,
    state.viewport,
    containerRef,
    updateConnectionMouse,
  ]);

  // ============================================================================
  // Complete Connection
  // ============================================================================

  const completeConnection = useCallback(
    (targetElement: Element, targetPort: Port) => {
      if (!state.connectionDraft.isConnecting) return;
      if (!state.connectionDraft.sourceElementId || !state.connectionDraft.sourcePortId) return;

      const sourceElement = elements.find(el => el.id === state.connectionDraft.sourceElementId);
      if (!sourceElement) {
        endConnection();
        return;
      }

      const sourcePort = getPortById(sourceElement, state.connectionDraft.sourcePortId);
      if (!sourcePort) {
        endConnection();
        return;
      }

      // Validate connection
      if (!canConnect(sourceElement, sourcePort, targetElement, targetPort)) {
        onConnectionFailed?.('Invalid connection');
        endConnection();
        return;
      }

      // Create connection
      const path = generateConnectionPath(sourceElement, sourcePort, targetElement, targetPort);
      const primaryType = getPrimaryType(sourcePort.type);
      const color = DATA_TYPE_COLORS[primaryType];

      const newConnection: Connection = {
        id: generateConnectionId(),
        sourceElementId: sourceElement.id,
        sourcePortId: sourcePort.id,
        targetElementId: targetElement.id,
        targetPortId: targetPort.id,
        path,
        color,
        pageId: state.currentPage,
      };

      createConnection(newConnection, state.currentPage);
      onConnectionCreated?.(newConnection);
      endConnection();
    },
    [
      state.connectionDraft,
      state.currentPage,
      elements,
      canConnect,
      createConnection,
      endConnection,
      onConnectionCreated,
      onConnectionFailed,
    ]
  );

  // ============================================================================
  // Handle Port Click (End Connection)
  // ============================================================================

  const handlePortClick = useCallback(
    (element: Element, port: Port) => {
      if (state.connectionDraft.isConnecting && port.side === 'input') {
        completeConnection(element, port);
      }
    },
    [state.connectionDraft.isConnecting, completeConnection]
  );

  // ============================================================================
  // Cancel Connection
  // ============================================================================

  useEffect(() => {
    if (!state.connectionDraft.isConnecting) return;

    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape') {
        endConnection();
      }
    };

    const handleClick = (e: MouseEvent) => {
      // Cancel if clicking on empty canvas
      if (e.target === containerRef.current) {
        endConnection();
      }
    };

    window.addEventListener('keydown', handleEscape);
    window.addEventListener('click', handleClick);

    return () => {
      window.removeEventListener('keydown', handleEscape);
      window.removeEventListener('click', handleClick);
    };
  }, [state.connectionDraft.isConnecting, containerRef, endConnection]);

  // ============================================================================
  // Get Draft Connection Path
  // ============================================================================

  const getDraftConnectionPath = useCallback((): string | null => {
    if (!state.connectionDraft.isConnecting) return null;
    if (!state.connectionDraft.sourceElementId || !state.connectionDraft.sourcePortId) return null;
    if (!state.connectionDraft.mousePosition) return null;

    const sourceElement = elements.find(el => el.id === state.connectionDraft.sourceElementId);
    if (!sourceElement) return null;

    const sourcePort = getPortById(sourceElement, state.connectionDraft.sourcePortId);
    if (!sourcePort) return null;

    return generateDraftConnectionPath(
      sourceElement,
      sourcePort,
      state.connectionDraft.mousePosition
    );
  }, [state.connectionDraft, elements]);

  // ============================================================================
  // Get Connection Color
  // ============================================================================

  const getConnectionColor = useCallback((connection: Connection): string => {
    return connection.color || 'hsl(var(--muted))';
  }, []);

  // ============================================================================
  // Return API
  // ============================================================================

  return {
    isConnecting: state.connectionDraft.isConnecting,
    draftConnectionPath: getDraftConnectionPath(),
    draftConnectionColor: state.connectionDraft.sourceType
      ? DATA_TYPE_COLORS[getPrimaryType(state.connectionDraft.sourceType)]
      : undefined,
    handlePortMouseDown,
    handlePortClick,
    findPortAtPosition,
    canConnect,
    getConnectionColor,
  };
}

// Helper function (would be imported from utils in real implementation)
function getPortPosition(element: Element, port: Port): Position {
  const ports = port.side === 'input' ? element.inputs : element.outputs;
  const portIndex = ports.findIndex(p => p.id === port.id);

  if (portIndex === -1) {
    return { x: element.x, y: element.y + element.height / 2 };
  }

  const portSpacing = element.height / (ports.length + 1);
  const yOffset = portSpacing * (portIndex + 1);

  return {
    x: port.side === 'input' ? element.x : element.x + element.width,
    y: element.y + yOffset,
  };
}

function screenToCanvas(screenPos: Position, viewport: any): Position {
  return {
    x: (screenPos.x - viewport.x) / viewport.zoom,
    y: (screenPos.y - viewport.y) / viewport.zoom,
  };
}

function getPrimaryType(type: DataType | DataType[]): DataType {
  return Array.isArray(type) ? type[0] : type;
}

"use client"

import { useState, useEffect, createContext, useContext, useCallback } from "react"

// Shell state interface for managing global app state
export interface ShellState {
  isLoading: boolean
  error: string | null
  sidebarOpen: boolean
  commandPaletteOpen: boolean
  notifications: ShellNotification[]
  theme: 'light' | 'dark' | 'system'
  user: any | null
}

export interface ShellNotification {
  id: string
  title: string
  message?: string
  type: 'info' | 'success' | 'warning' | 'error'
  timestamp: Date
  read: boolean
  actions?: Array<{
    label: string
    action: () => void
    variant?: 'default' | 'destructive'
  }>
}

export interface ShellActions {
  setLoading: (loading: boolean) => void
  setError: (error: string | null) => void
  setSidebarOpen: (open: boolean) => void
  setCommandPaletteOpen: (open: boolean) => void
  addNotification: (notification: Omit<ShellNotification, 'id' | 'timestamp' | 'read'>) => void
  removeNotification: (id: string) => void
  markNotificationRead: (id: string) => void
  clearNotifications: () => void
  setTheme: (theme: 'light' | 'dark' | 'system') => void
  setUser: (user: any | null) => void
}

export interface ShellContextType extends ShellState, ShellActions {}

// Create shell context
const ShellContext = createContext<ShellContextType | null>(null)

// useShell hook
export function useShell(): ShellContextType {
  const context = useContext(ShellContext)
  if (!context) {
    // During SSR or when context is not available, provide a safe fallback
    if (typeof window === 'undefined') {
      // SSR fallback - provide minimal shell state without hooks
      return {
        isLoading: false,
        error: null,
        sidebarOpen: false,
        commandPaletteOpen: false,
        notifications: [],
        theme: 'system',
        user: null,
        setLoading: () => {},
        setError: () => {},
        setSidebarOpen: () => {},
        setCommandPaletteOpen: () => {},
        addNotification: () => {},
        removeNotification: () => {},
        markNotificationRead: () => {},
        clearNotifications: () => {},
        setTheme: () => {},
        setUser: () => {},
      }
    }
    // Return a default implementation if no provider
    return createDefaultShellContext()
  }
  return context
}

// Create default shell context for when no provider exists
function createDefaultShellContext(): ShellContextType {
  const [state, setState] = useState<ShellState>({
    isLoading: false,
    error: null,
    sidebarOpen: false,
    commandPaletteOpen: false,
    notifications: [],
    theme: 'system',
    user: null,
  })

  const setLoading = useCallback((loading: boolean) => {
    setState(prev => ({ ...prev, isLoading: loading }))
  }, [])

  const setError = useCallback((error: string | null) => {
    setState(prev => ({ ...prev, error }))
  }, [])

  const setSidebarOpen = useCallback((open: boolean) => {
    setState(prev => ({ ...prev, sidebarOpen: open }))
  }, [])

  const setCommandPaletteOpen = useCallback((open: boolean) => {
    setState(prev => ({ ...prev, commandPaletteOpen: open }))
  }, [])

  const addNotification = useCallback((notification: Omit<ShellNotification, 'id' | 'timestamp' | 'read'>) => {
    const newNotification: ShellNotification = {
      ...notification,
      id: `notification-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
      timestamp: new Date(),
      read: false,
    }

    setState(prev => ({
      ...prev,
      notifications: [newNotification, ...prev.notifications]
    }))
  }, [])

  const removeNotification = useCallback((id: string) => {
    setState(prev => ({
      ...prev,
      notifications: prev.notifications.filter(n => n.id !== id)
    }))
  }, [])

  const markNotificationRead = useCallback((id: string) => {
    setState(prev => ({
      ...prev,
      notifications: prev.notifications.map(n =>
        n.id === id ? { ...n, read: true } : n
      )
    }))
  }, [])

  const clearNotifications = useCallback(() => {
    setState(prev => ({ ...prev, notifications: [] }))
  }, [])

  const setTheme = useCallback((theme: 'light' | 'dark' | 'system') => {
    setState(prev => ({ ...prev, theme }))
  }, [])

  const setUser = useCallback((user: any | null) => {
    setState(prev => ({ ...prev, user }))
  }, [])

  return {
    ...state,
    setLoading,
    setError,
    setSidebarOpen,
    setCommandPaletteOpen,
    addNotification,
    removeNotification,
    markNotificationRead,
    clearNotifications,
    setTheme,
    setUser,
  }
}

// Provider component
export const ShellProvider = ShellContext.Provider

// Create shell value for provider
export function createShellValue(): ShellContextType {
  return createDefaultShellContext()
}

"use client"

import { useState, useEffect, createContext, useContext, useCallback } from "react"

// Welcome mat state for onboarding and feature discovery
export interface WelcomeMatState {
  isOpen: boolean
  currentStep: number
  totalSteps: number
  completedSteps: Set<string>
  skippedSteps: Set<string>
  currentFlow: WelcomeMatFlow | null
}

export interface WelcomeMatStep {
  id: string
  title: string
  description: string
  content?: React.ReactNode
  target?: string // CSS selector for highlighting
  position?: 'top' | 'bottom' | 'left' | 'right' | 'center'
  required?: boolean
  canSkip?: boolean
  action?: {
    label: string
    handler: () => void | Promise<void>
  }
  validation?: () => boolean | Promise<boolean>
}

export interface WelcomeMatFlow {
  id: string
  name: string
  description: string
  steps: WelcomeMatStep[]
  triggers: {
    route?: string
    event?: string
    condition?: () => boolean
  }
  persistent?: boolean // Should persist across sessions
  priority?: number
}

export interface WelcomeMatActions {
  openFlow: (flowId: string) => void
  closeFlow: () => void
  nextStep: () => void
  previousStep: () => void
  skipStep: (stepId?: string) => void
  completeStep: (stepId?: string) => void
  jumpToStep: (stepIndex: number) => void
  registerFlow: (flow: WelcomeMatFlow) => void
  unregisterFlow: (flowId: string) => void
  markFlowCompleted: (flowId: string) => void
  resetFlow: (flowId: string) => void
  setStepTarget: (target: string) => void
}

export interface WelcomeMatContextType extends WelcomeMatState, WelcomeMatActions {
  flows: Map<string, WelcomeMatFlow>
  completedFlows: Set<string>
}

// Create welcome mat context
const WelcomeMatContext = createContext<WelcomeMatContextType | null>(null)

// useWelcomeMat hook
export function useWelcomeMat(): WelcomeMatContextType {
  const context = useContext(WelcomeMatContext)
  if (!context) {
    // During SSR or when context is not available, provide a safe fallback
    if (typeof window === 'undefined') {
      // SSR fallback - provide minimal welcome mat state without hooks
      return {
        isOpen: false,
        currentStep: 0,
        totalSteps: 0,
        completedSteps: new Set(),
        skippedSteps: new Set(),
        currentFlow: null,
        flows: new Map(),
        completedFlows: new Set(),
        openFlow: () => {},
        closeFlow: () => {},
        nextStep: () => {},
        previousStep: () => {},
        skipStep: () => {},
        completeStep: () => {},
        jumpToStep: () => {},
        registerFlow: () => {},
        unregisterFlow: () => {},
        markFlowCompleted: () => {},
        resetFlow: () => {},
        setStepTarget: () => {},
      }
    }
    // Return a default implementation if no provider
    return createDefaultWelcomeMatContext()
  }
  return context
}

// Create default welcome mat context
function createDefaultWelcomeMatContext(): WelcomeMatContextType {
  const [state, setState] = useState<WelcomeMatState & { flows: Map<string, WelcomeMatFlow>, completedFlows: Set<string> }>({
    isOpen: false,
    currentStep: 0,
    totalSteps: 0,
    completedSteps: new Set(),
    skippedSteps: new Set(),
    currentFlow: null,
    flows: new Map(),
    completedFlows: new Set(),
  })

  const openFlow = useCallback((flowId: string) => {
    const flow = state.flows.get(flowId)
    if (!flow) {
      console.warn(`WelcomeMat: Flow "${flowId}" not found`)
      return
    }

    setState(prev => ({
      ...prev,
      isOpen: true,
      currentFlow: flow,
      currentStep: 0,
      totalSteps: flow.steps.length,
      completedSteps: new Set(),
      skippedSteps: new Set(),
    }))
  }, [state.flows])

  const closeFlow = useCallback(() => {
    setState(prev => ({
      ...prev,
      isOpen: false,
      currentFlow: null,
      currentStep: 0,
      totalSteps: 0,
      completedSteps: new Set(),
      skippedSteps: new Set(),
    }))
  }, [])

  const nextStep = useCallback(() => {
    setState(prev => {
      if (!prev.currentFlow || prev.currentStep >= prev.totalSteps - 1) {
        return prev
      }
      return {
        ...prev,
        currentStep: prev.currentStep + 1
      }
    })
  }, [])

  const previousStep = useCallback(() => {
    setState(prev => {
      if (prev.currentStep <= 0) {
        return prev
      }
      return {
        ...prev,
        currentStep: prev.currentStep - 1
      }
    })
  }, [])

  const skipStep = useCallback((stepId?: string) => {
    setState(prev => {
      if (!prev.currentFlow) return prev

      const currentStepId = stepId || prev.currentFlow.steps[prev.currentStep]?.id
      if (!currentStepId) return prev

      const newSkippedSteps = new Set(prev.skippedSteps)
      newSkippedSteps.add(currentStepId)

      return {
        ...prev,
        skippedSteps: newSkippedSteps
      }
    })
    nextStep()
  }, [nextStep])

  const completeStep = useCallback((stepId?: string) => {
    setState(prev => {
      if (!prev.currentFlow) return prev

      const currentStepId = stepId || prev.currentFlow.steps[prev.currentStep]?.id
      if (!currentStepId) return prev

      const newCompletedSteps = new Set(prev.completedSteps)
      newCompletedSteps.add(currentStepId)

      return {
        ...prev,
        completedSteps: newCompletedSteps
      }
    })
    nextStep()
  }, [nextStep])

  const jumpToStep = useCallback((stepIndex: number) => {
    setState(prev => {
      if (!prev.currentFlow || stepIndex < 0 || stepIndex >= prev.totalSteps) {
        return prev
      }
      return {
        ...prev,
        currentStep: stepIndex
      }
    })
  }, [])

  const registerFlow = useCallback((flow: WelcomeMatFlow) => {
    setState(prev => {
      const newFlows = new Map(prev.flows)
      newFlows.set(flow.id, flow)
      return {
        ...prev,
        flows: newFlows
      }
    })
  }, [])

  const unregisterFlow = useCallback((flowId: string) => {
    setState(prev => {
      const newFlows = new Map(prev.flows)
      newFlows.delete(flowId)
      return {
        ...prev,
        flows: newFlows
      }
    })
  }, [])

  const markFlowCompleted = useCallback((flowId: string) => {
    setState(prev => {
      const newCompletedFlows = new Set(prev.completedFlows)
      newCompletedFlows.add(flowId)
      return {
        ...prev,
        completedFlows: newCompletedFlows
      }
    })
  }, [])

  const resetFlow = useCallback((flowId: string) => {
    setState(prev => {
      const newCompletedFlows = new Set(prev.completedFlows)
      newCompletedFlows.delete(flowId)
      return {
        ...prev,
        completedFlows: newCompletedFlows
      }
    })
  }, [])

  const setStepTarget = useCallback((target: string) => {
    // This would highlight the target element
    console.log(`WelcomeMat: Highlighting target "${target}"`)
  }, [])

  return {
    ...state,
    openFlow,
    closeFlow,
    nextStep,
    previousStep,
    skipStep,
    completeStep,
    jumpToStep,
    registerFlow,
    unregisterFlow,
    markFlowCompleted,
    resetFlow,
    setStepTarget,
  }
}

// Provider component
export const WelcomeMatProvider = WelcomeMatContext.Provider

// Create welcome mat value for provider
export function createWelcomeMatValue(): WelcomeMatContextType {
  return createDefaultWelcomeMatContext()
}

