// Navigation Item Type
export interface NavItem {
  id: string;
  label: string;
  icon: React.ReactNode | string;
  path: string;
  badge?: number | string;
  children?: NavItem[];
  requiresAuth?: boolean;
  mobileOnly?: boolean;
  desktopOnly?: boolean;
}

// Universal Navigation Bar
export const UniversalNavBar: React.FC<{
  items: NavItem[];
  orientation?: 'horizontal' | 'vertical';
  variant?: 'default' | 'compact' | 'mobile';
  className?: string;
}> = ({ items, orientation = 'horizontal', variant = 'default', className }) => {
  const pathname = usePathname();
  const router = useRouter();

  const isActive = (path: string) => {
    if (path === '/') return pathname === path;
    return pathname?.startsWith(path);
  };

  const handleNavClick = (item: NavItem) => {
    router.push(item.path);
  };

  return (
    <nav
      className={cn(
        'universal-nav',
        orientation === 'vertical' ? 'flex-col space-y-2' : 'flex items-center gap-2',
        variant === 'compact' && 'p-2',
        variant === 'mobile' && 'fixed bottom-0 left-0 right-0 bg-black border-t border-white/10',
        className
      )}
      aria-label="Main navigation"
    >
      {items.map((item) => (
        <NavButton
          key={item.id}
          item={item}
          isActive={isActive(item.path)}
          onClick={() => handleNavClick(item)}
          variant={variant}
        />
      ))}
    </nav>
  );
};

